INSERT INTO "tbnews" VALUES ('1040', 'B010505', 'B010000', 'នាយក​រដ្ឋ​មន្ត្រី​ថៃ​សំដៅ​ក្រុម​អាវ​ក្រហម​ជា​អ្នក​នៅ​ពី​ក្រោយ​ការ​វាយប្រហារ​គ្រាប់​បែក​', 'មាន​បុគ្គល​ ឬ​ក្រុម​ជា​ច្រើន​ ដែល​រក​តែ​បំផ្លាញ​ប្រទេស​របស់​យើង។ ការ​វាយប្រហារ​កន្លង​មក​ជា​ការ​ធ្វើ​ឡើង​ដោយ​មាន​ការ​បញ្ចុះបញ្ចូល​ផ្នែក​នយោបាយ...', 'http://news.sabay.com.kh/article/454902', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/featured85-285x170.jpg?ebb82d', '19 August, 2015', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1041', 'B010505', 'B010000', 'គន្លឹះ​ក្នុង​ការ​ស្រាវជ្រាវ​ឯកសារ​នៅ​លើ​បណ្តាញ​អ៊ីនធឺណិត​របស់​ជន​ពិការភ្នែក', 'បើ​យើង​ធ្វើ​ដំណើរ​ពី​ភ្នំពេញ​ទៅ​ខេត្ត​កំពង់ចាម ឆ្លងកាត់​ទន្លេ​មួយ​គ្មាន​ទឹក គឺ​ទន្លេអ៊ំ ហើយ​នៅ​ខាង​ឆ្វេងដៃ មាន​សាលារៀន​មួយ​ គឺ​សាលា​កុមារ​ពិការ​ភ្នែក និង គថ្លង់ ។ នៅ​ក្នុង...', 'http://news.sabay.com.kh/article/453000', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/Feature-618x3285-285x170.jpg?ebb82d', '19 August, 2015', '0', '0', '0', 't', '2015-08-19', 'f');
