INSERT INTO "tbnews" VALUES ('1043', 'B010505', 'B010000', 'បុរស​ព្នង បឹប​ តស៊ូ​ពី​ជីវភាព​ក្រខ្សត់ រហូត​ក្លាយ​ជា​ប្រធាន​ផ្នែក​នៅ NGO', 'មាន​ស្រុកកំណើត​នៅ​​ភូមិ​ដោះ​ក្រមុំ ឃុំ​សុខដុម ស្រុក​សែនមនោរម្យ បឹប ឬឈ្មោះខ្មែរ ឌួង វណ្ណរ៉ា បាន​កើត​ក្នុង​​គ្រួសារ​​ជនជាតិ​ភាគតិច​ព្នង​​ មាន​សមាជិកចំនួន ៧ ​នាក់។ ឈាម​ជ័រ​ជា​...', 'http://news.sabay.com.kh/article/454821', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/Feature-618x3284-285x170.jpg?ebb82d', '19 August, 2015', '0', '0', '0', 't', '2015-08-19', 'f');
