INSERT INTO "tbnews" VALUES ('1045', 'B010201', 'B010000', 'បាល់ទាត់​មហា​ជន​​​​ខេត្ត​កំពង់​ឆ្នាំង​កើត​ឡើង​ម្ដង​ទៀត​ក្រោយ​​បាត់​មុខ​ជិត​១០​ឆ្នាំ​​', '​ពាន​រង្វាន់​បាល់ទាត់​​មហាជន​ខេត្ត​កំពង់​ឆ្នាំង​​បាន​កើត​ឡើង​​កាល​ពី​ថ្ងៃ​ទី១៧ ខែ​សីហា ឆ្នាំ​២០១៥ ម្ដង​ទៀត​នៅ​ទីលាន​​កីឡដ្ឋាន​ខេត្ត...', 'http://news.sabay.com.kh/article/454185', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/fea-kom-285x170.jpg?ebb82d', '19 August, 2015', '0', '0', '0', 't', '2015-08-19', 'f');
