INSERT INTO "tbnews" VALUES ('1053', 'B020202', 'B020000', 'ព្យាករណ៍​បាល់ទាត់​អង់គ្លេស​ឆេ​ម​ពាន​ស៊ី​ប លី​ក​វ័ន និង​ហ្វា​ខាប់ក្រុម Hull, Leeds, Wolvers និង Wigan គឺជា​ជម្រើស​ល្អ', 'ការ​ប្រកួត​បាល់ទាត់​អង់គ្លេស​ឆេ​ម​ពាន​ស៊ី​ប លី​ក​វ័ន និង​ហ្វា​ខាប់ សម្រាប់​យប់​ថ្ងៃ​ពុធ​នេះ មាន 11 ប្រកួត ។ Hull, Leeds, Wolvers និង Wigan ជា​ជម្រើស​ល្អ ។  អង់គ្លេស​ឆេ​ម​ពាន​ស៊ីប ជុំ​ទី 3 Bristol  City V Leeds', 'http://kohsantepheapdaily.com.kh/article/127079.html', 'http://i.kspg.co/2015/8/34d9f443-10c0-43be-8a38-4264492c51cb.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៤៣', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1054', 'B020302', 'B020000', 'ស្ថានទូត​អា​មេ​រិ​ក​ចាក់​បញ្ចាំង​ជា​អន្តរជាតិ​លើក​ដំបូងភាពយន្ត​ឯកសារ « វាល​ពិឃាត » របស់​លោក​ហាំ​ង ង៉ោ', 'រាជធានី​ភ្នំពេញ ៖ ស្ថានទូត​អា​មេ​រិ​ក​នៅ​រាជធានី​ភ្នំពេញ​សហការ​ជាមួយ​មជ្ឈមណ្ឌល​បុប្ផា​ណា ប្រកាស​អញ្ជើញ​អ្នក​សារព័ត៌មាន​ចូលរួម​នៅ​ក្នុង​ពិធី​បញ្ចាំង​ជា​អន្តរជាតិ​លើក​ដំបូង​នូវ​ភាពយន្ត​ឯកសារ​ថ្មី​មាន​ចំណងជើង​ថា', 'http://kohsantepheapdaily.com.kh/article/127070.html', 'http://i.kspg.co/2015/8/e4a37727-6ffa-448f-9cb5-a89bf51ce12c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:១៨', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1055', 'B020302', 'B020000', 'គៀត សុ​វ​ណ្ណា​ឡា​ង ស្ទាក់ស្ទើរ​ចាកចោល​អាជីព​ចម្រៀង​', '​រាជធានី​ភ្នំពេញ ៖ មួយ​រយៈពេល​ចុង​ក្រោយ​នេះ​អាជីព​សិល្បៈ​ភាពយន្ត​របស់​កញ្ញា​គៀត សុ​វ​ណ្ណា​ឡា​ង ស្ទុះ​ឡើង​វិញ​ធ្វើ​ឱ្យ​នាង​រីករាយ និង​មាន​សង្ឃឹម​ចំពោះ​អាជីព​មួយ​នេះ ។ ខណៈ​អាជីព​ភាពយន្ត​លូតលាស់ លេចឮ​ថា គៀត សុ​វ​ណ្ណា​ឡា​ង ', 'http://kohsantepheapdaily.com.kh/article/127069.html', 'http://i.kspg.co/2015/8/132bba99-202d-46bf-acd6-4eb73f2a3474.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:១៤', '0', '0', '0', 't', '2015-08-19', 'f');
