INSERT INTO "tbnews" VALUES ('1057', 'B020101', 'B020000', 'អ្នកតំណាង​រាស្ត្រ​កំពង់ឆ្នាំង​ជួយ​សាងសង់​ផ្ទះ​ជូន​ស្ត្រី​ចាស់ជរាក្រីក្រ​នៅ​ឃុំ​រលាប្អៀរ', 'ខេត្តកំពង់ឆ្នាំង​ ៖ កាល​ពី​ព្រឹក​ថៃ្ង​ទី​១៨ សីហា ២០១៥ លោក​ជំទាវ កែ ច័ន្ទ​មុនី អ្នកតំណាង​រាស្ត្រ​គណបក្ស​ប្រជាជន​កម្ពុជា​មណ្ឌល​កំពង់ឆ្នាំង រួម​ដំណើរ​ដោយ​លោក​ជំទាវ ដោក សុទ្ធា ប្រធាន​ក្រុមប្រឹក្សា​ខេត្ត', 'http://kohsantepheapdaily.com.kh/article/127107.html', 'http://i.kspg.co/2015/8/4fc1c8f8-f944-459b-8ade-e6416c3e36fb.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:១៦', '0', '0', '0', 't', '2015-08-19', 'f');
