INSERT INTO "tbnews" VALUES ('1069', 'B020101', 'B020000', 'ក្រសួង​ដែនដី​ប្រតិកម្ម​ការ​ផ្សាយ​វិទ្យុ RFA ថា​កម្ពុជា​ខ្វះ​ការ​អនុវត្ត​ច្បាប់​លើ​សិទ្ធិ​គ្រប់គ្រង​ដីធ្លី', 'រាជធានី​ភ្នំពេញ ៖ ក្រសួង​រៀបចំ​ដែនដី នគរូបនីយកម្ម និង​សំណង់​បាន​ចេញ​សេចក្តី​ប្រកាស​របស់​ខ្លួន​ប្រតិកម្ម​ចំពោះ​ការ​ផ្សាយ​របស់​វិទ្យុ​អាស៊ី​សេរី​(RFA)​ថា កម្ពុជា​នៅ​ខ្វះ​ការ​អនុវត្ត​ច្បាប់​ដើម្បី​ធានា​សុវត្ថិភាព​សិទ្ធិ​ក្នុង​ការ​គ្រប់គ្រង​ដីធ្លី ', 'http://kohsantepheapdaily.com.kh/article/127084.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០៨', '0', '0', '0', 't', '2015-08-19', 'f');
