INSERT INTO "tbnews" VALUES ('1073', 'B020101', 'B020000', 'សម្តេចតេជោ ​ហ៊ុន សែន ​អំពាវនាវឲ្យពលរដ្ឋ​គោរពច្បាប់ចរាចរណ៍', 'រាជធានីភ្នំពេញ ៖​ ជាថ្មីម្តងទៀត ​សម្តេចអគ្គមហាសេនាបតីតេជោ ​ហ៊ុន សែន ​បានអំពាវនាវឲ្យពលរដ្ឋ​ទំាងអស់គោរពច្បាប់​ចរាចរណ៍អោយ​បានខ្ជាប់ខ្ជួន ចៀសវាង ការ ដកហូតប័ណ្ណបើកបរ​ និងផាកពិន័យធ្ងន់ធ្ងរ ​នៅពេលច្បាប់ចរាចរណ៍​ផ្លូវគោកថ្មី', 'http://kohsantepheapdaily.com.kh/article/127066.html', 'http://i.kspg.co/2015/8/0f74a05a-ab38-4807-bc0a-f6065cd6e8b0.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៥៣', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1074', 'B020101', 'B020000', 'មុននេះបន្តិច! តុលាការ​ខេត្ត​ប៉ៃលិនសម្រេច​កាត់ទោសឃាត​ករ​សម្លាប់ស្មៀន-ប្រពន្ធ និងកូន​ ដាក់គុក​មួយ​ជីវិត​', 'ខេត្តប៉ៃលិន ៖ សេចក្តីរាយការណ៍​បានឱ្យ​ដឹងនាវេលាម៉ោង​០៩ និង​២០នាទីព្រឹកថ្ងៃទី​១៩ សីហា ២០១៥ នេះ​ថា ជន​សង្ស័យ​​ជា​ឃាត​ករ​សម្លាប់​​​ស្មៀនឃុំ ប្រពន្ធ​និង​កូនស្រី​តូច​ក្នុងផ្ទះ​ចាក់​សាំង​ដុត​សម្លាប់​ផ្តាច់​ពូជនោះ ត្រូវបាន​តុលាការ​សម្រេច​ចោទពីបទ​ឃាតកម្ម​គិត​ទុក​ជាមុន ដោយ​ត្រូវ​ជាប់ពន្ធនាគារ​អស់​មួយជីវិត និង​ត្រូវបង់ប្រាក់​ចំនួន​៧០​លានរៀល​ដល់​គ្រួសារ​ជនរងគ្រោះ ។', 'http://kohsantepheapdaily.com.kh/article/127065.html', 'http://i.kspg.co/2015/8/014b0bf5-bc76-432a-927e-13490eca3975.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៦', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1075', 'B020101', 'B020000', 'សាលាស្រុក​អន្លង់​វែង​ប្រកាស​មន្ត្រីរាជការ​ចូល​កាន់​មុខ​តំណែង​៣​រូប​', '​ខេត្ត​ឧត្តរមានជ័យ ៖ ​មន្ត្រី​សាលាស្រុក​អន្លង់​​វែង​៣​រូប ​ត្រូវ​បាន​ប្រកាស​តែងតាំង​ចូល​កាន់​មុខ​តំណែ​ង​កាលពី​ព្រឹក​ថ្ងៃ​ទី​១៨ ​សីហា​ ២០១៥​ ។ ពិធី​នេះ​បាន​រៀបចំ​នៅ​ក្នុង​បរិវេណ​​សាលាស្រុក​អន្លង់​​វែង', 'http://kohsantepheapdaily.com.kh/article/127064.html', 'http://i.kspg.co/2015/8/78c6864d-2740-4edc-92b1-83567a28a036.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៥', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1076', 'B020101', 'B020000', 'ថ្នាក់ដឹកនាំ​សង្ឃ​ខេត្ត​ និង​ស្រុក​រលាប្អៀរ​​​ធ្វើ​សង្ឃកម្ម​ជម្រះ​ទោស​ភិក្ខុ ឌិ​ត ប៉ែន ដែល​​វាយ​សាមណេរឱ្យរបួស', 'ខេត្តកំពង់ឆ្នាំង ​៖ ក្រោយ​ពី​មាន​បាតុភាព ព្រះ​ភិក្ខុ ឌិ​ត ប៉ែន ដែល​គ​ង់ក្នុង​វត្ត​ធម្ម​វ័​ន្ត ក្នុងភូមិ​ត្រពាំង​អ​ញ្ចាញ ឃុំ​ស្វាយជ្រុំ​ ស្រុក​រលាប្អៀរ បាន​យក​ខ្យែ​ភ្លើង​បត់​ជា​២វាយ​​ទៅ​លើ​កូន​នេន ព្រះ​ជន​១១​វស្សា', 'http://kohsantepheapdaily.com.kh/article/127063.html', 'http://i.kspg.co/2015/8/b109d628-59c7-43da-a7a6-c578defa7fc0.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៤', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1077', 'B020101', 'B020000', 'សម្តេច ​ហេង សំរិន ​កំពុងធ្វើសវនាការ​ជាមួយឯកអគ្គរដ្ឋទូត​ហ្វីលីពីនទើបតែងតំាង', 'រាជធានីភ្នំពេញ ៖ ​សម្តេចអគ្គមហាពញាចក្រី​ ហេង សំរិន ​ប្រធានរដ្ឋសភា ​បានអនុញ្ញាតឲ្យ​ឯកអគ្គរដ្ឋទូតទើបតែង​តំាងថ្មីនៃ​សាធារណរដ្ឋហ្វីលីពីន​ប្រចំាកម្ពុជាលោក ', 'http://kohsantepheapdaily.com.kh/article/127062.html', 'http://i.kspg.co/2015/8/35d22ef0-ade7-499c-a4be-22130dc76323.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣៦', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1078', 'B020101', 'B020000', 'លោក ប៉ា សុជាតិវង្ស​ ៖ ផ្សារដើមគនឹង​ជួសជុលតាមគំរូផ្សារធំថ្មី​នៅឆ្នាំ២០២១', 'រាជធានីភ្នំពេញ ​៖ អភិបាលនៃគណៈអភិបាល​រាជធានីភ្នំពេញ​ លោក ប៉ា សុជាតិវង្ស​ ​បានមានប្រសាសន៍ថា ​ផ្សារដើមគនឹងត្រូវធ្វើ​ការជួសជុល​យកគំរូតាមផ្សារធំថ្មី​នៅ​ឆ្នាំ២០២១ ។ ', 'http://kohsantepheapdaily.com.kh/article/127060.html', 'http://i.kspg.co/2015/8/6de14028-527c-4d27-9dd3-ead6ea89c1e0.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:២៨', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1079', 'B020401', 'B020000', 'តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃស​ប្តា​ហ៍​ទី​3​នៃ​ខែសីហា 2015', 'រាជធានី​ភ្នំពេញ ៖ តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ​នៅ​ផ្សារ​ធំ​ថ្មី​ក្នុង​ស​ប្តា​ហ៍​ទី​3 នៃ​ខែ សីហា ២០១៥ នេះ មុខទំនិញ សាច់​ជ្រូក   សាច់​គោ និង​បន្លែ មាន ការ​ប្រែប្រួល​តម្លៃ​តិចតួច ។ សូម​មើល​តារាង​ខាងក្រោម ៖', 'http://kohsantepheapdaily.com.kh/article/127103.html', 'http://i.kspg.co/2015/8/beeb6d8e-0485-4701-b36a-530e51ed27dd.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៣៨', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1080', 'B020401', 'B020000', 'ការ​រីក​ចម្រើន​សេដ្ឋកិច្ច​ជំរុញ​អោយ​មាន​កំណើន​វិនិយោគ​លើ​វិស័យ​អចលនទ្រព្យ', 'រាជធានី​ភ្នំពេញ ៖ ទន្ទឹម​នឹង​កំណើនសេដ្ឋកិច្ច​កម្ពុជា​ស្ថិត​ក្នុង​រង្វង់​អត្រា​៧​ភាគរយ​ប្រចាំឆ្នាំ​កំណើន​នៃ​អភិវឌ្ឍន៍​លើ​វិស័យ​អចលនទ្រព្យ ក៏​មាន​សន្ទុះ​កើនឡើង​គួរ​ឲ្យ​កត់សម្គាល់​ផង​ដែរ ក្នុង​រយៈពេល​៥​ឆ្នាំ​ចុង​ក្រោយ​នេះ ។', 'http://kohsantepheapdaily.com.kh/article/127102.html', 'http://i.kspg.co/2015/8/cea140c9-c1e1-48c7-93d2-08e0f4ad7438.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៣៤', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1081', 'B030101', 'B030000', 'សាសា ត្រូវតុលាការ ខេត្តបាត់ដំបង កោះហៅ ឲ្យចូលបំភ្លឺក្នុង សំណុំរឿងបង្ក គ្រោះថ្នាក់ចរាចរ នៅសប្ដាហ៍ក្រោយ', '<strong>បាត់ដំបង៖</strong> លោក តូច សុភក្ដី ព្រះរាជអាជ្ញារង នៃសាលាដំបូង ខេត្តបាត់ដំបង បានថ្លែងប្រាប់ FreshNews នៅរសៀលថ្ងៃទី១៩ ខែសីហា ឆ្នាំ២០១៥នេះថា ក្រោយពីតុលាការ បានសួរដេញដោល មិត្តភក្តិពីរនាក់ របស់ កញ្ញា សាសា ក្នុងសំណុំរឿង គ្រោះថ្នាក់ចរាចរ កាលពីថ្ងៃទី២ ខែកក្កដា កន្លងទៅរួចមក កាលពីពេលថ្មីៗនេះ តុលាការបាន សម្រេចចេញដីកា កោះហៅកញ្ញា ឯក សុជាតា ហៅ សាសា ឲ្យចូលបំភ្លឺជុំវិញ ករណីបង្ក គ្រោះថ្នាក់ខាងលើ នៅសប្ដាហ៍ក្រោយនេះ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97437-2015-08-19-08-16-04.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-053.jpg', 'Wednesday, 19 August 2015 15:12', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1082', 'B030101', 'B030000', 'ផ្ញើសារទៅ ថ្ងៃអនាគត តាមរយៈ Hennessy Time Barrel', '<strong>ភ្នំពេញ ៖</strong> នៅក្នុងឱកាស នៃការប្រារព្ធខួបលើកទី ២៥០ឆ្នាំរបស់ខ្លួន ស្រា Hennessy បានបង្កើត Time Barrel ដែលឧបករណ៍ សម្រាប់កត់ត្រា សារទៅកាន់អនាគត។ Time Barrel បានកត់ត្រាទុកនូវរូបភាពនៃការ ស្រមើល ស្រមៃ និងស្មារតីច្នៃប្រឌិត របស់អ្នកចូលចិត្តស្រា Hennessy នៅទូទាំងពិភពលោក។', 'http://www.dap-news.com/2011-06-14-02-39-55/97435----hennessy-time-barrel.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-049.jpg', 'Wednesday, 19 August 2015 15:01', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1083', 'B030101', 'B030000', 'រដ្ឋមន្រ្តីក្រសួងអប់រំ បង្ហាញលក្ខខណ្ឌ ចំនួន៥ ដែលនាំឲ្យអ្នកប្រឡង បាក់ឌុប ធ្លាក់ដោយស្វ័យប្រវត្តិ', '<b>ភ្នំពេញ៖</b> ជាមួយការឈានចូលមកដល់ នៃការប្រឡងមធ្យមសិក្សាទុតិយភូមិ ហៅថាបាក់ឌុប នៅថ្ងៃចន្ទ សប្តាហ៍ &nbsp;ក្រោយនេះ រដ្ឋមន្រ្តីក្រសួងអប់រំ យុវជន និងកីឡា លោក ហង់ ជួនណារ៉ុន បានបង្ហាញជំហរយ៉ាងម៉ឺងម៉ាត់ និងតឹង រឹងបំផុត មិនមានការបន្ធូបន្ថយអាណិតអាសូរណាមួយសម្រាប់បេក្ខជនឡើយ ពោលពួកគេនឹងធ្លាក់ដោយស្វ័យប្រវ &nbsp;ត្តិ ប្រសិនបើធ្វើខុសទៅនឹងលក្ខខណ្ឌទាំង៥ ដែលក្រសួងបានដាក់ចេញ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97434-2015-08-19-07-58-05.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/ST-111.jpg', 'Wednesday, 19 August 2015 14:57', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1084', 'B030101', 'B030000', 'សាខាគយ ខេត្តព្រះសីហនុ ប្រជុំផ្សព្វផ្សាយ ផែនការ ប្រមូលពន្ធអាករ លើយានយន្ត ដែលមិនទាន់បាន បង់ពន្ធជូនរដ្ឋ', '<strong>ព្រះសីហនុ៖</strong> សាខាគយខេត្តព្រះសីហនុ បានបើកកិច្ចប្រជុំ ផ្សព្វផ្សាយលិខិតរបស់អគ្គនាយកដ្ឋានគយ និងរដ្ឋាករកម្ពុជា លេខ ១០៤៩ អគរ ចុះថ្ងៃទី៣១ ខែកក្កដា ឆ្នាំ២០១៥ ស្តីពីការអនុវត្តផែនការសកម្មភាព នៃការប្រមូលពន្ធអាករ និងប្រាក់ពិន័យលើយានយន្តមានស្រាប់ក្នុងប្រទេស ដែលមិនបានបំពេញកាតព្វកិច្ចពន្ធអាករ ជាមួយមន្រ្តីពាក់ព័ន្ធនានា ចំណុះឲ្យសាលាខេត្តព្រះសីហនុ កាលពីល្ងាចថ្ងៃទី១៨ ខែសីហា ឆ្នាំ២០១៥ នៅសាលប្រជុំសាលាខេត្តព្រះសីហនុ ដោយមានការអញ្ជើញចូលរួម ពីលោក ឈិត សុខុន អភិបាលខេត្តព្រះសីហនុ និងលោក យ៉ឹក យូ ប្រធានសាខាគយ និងរដ្ឋាករខេត្តព្រះសីហនុ&nbsp; ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97433-2015-08-19-07-52-43.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0468.jpg', 'Wednesday, 19 August 2015 14:51', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1085', 'B030101', 'B030000', 'ប្រតិភូក្រសួង កិច្ចការនារី នាំយកថវិកា ផ្តល់ជូនក្រុមគ្រួសារ រងគ្រោះថ្នាក់ ចរាចរណ៍ស្លាប់ ១៧នាក់ នៅខេត្តឧត្តរមានជ័យ ថ្ងៃបុណ្យ៧ថ្ងៃ', '<strong>ភ្នំពេញ ៖</strong> កាលពីវេលាម៉ោង៣រសៀលថ្ងៃទី១៨ ខែសីហា ឆ្នាំ២០១៥ ប្រតិភូក្រសួង កិច្ចការនារី ដឹកនាំដោយ លោកស្រី អ៊ឹង កន្ថាផាវី រដ្ឋមន្ត្រីក្រសួង កិច្ចការនារី និងជាប្រធាន ក្រុមការងារថ្នាក់ ជាតចុះជួយខណ្ឌដង្កោ រួមនិងប្រតិភូអម ដំណើរមានលោកស្រី គ្រួច សំអាន សមាជិកា នៃរដ្ឋ សភា និងជាតំណាងរាស្រ្ត មណ្ឌលភ្នំពេញ លោកស្រី ទុង សុគន្ធ អនុរដ្ឋលេខាធិការ ទីស្តីការ គណៈរដ្ឋមន្ត្រី និងរដ្ឋបាលខណ្ឌដង្កោ បានទទួលការអញ្ជើញ ចូលរួមបុណ្យសពដល់ខ្នងផ្ទះប្រជា ពលរដ្ឋក្រុមគ្រួសាររងគ្រោះ ក្នុងមូលដ្ឋានសង្កាត់ព្រៃស ដែលបានជួបគ្រោះ ថ្នាក់ចរាចរណ៍នៅ ស្រុកអន្លង់វែង ខេត្តឧត្តរមានជ័យ បណ្តាលឲ្យស្លាប់និងរងរបួសកាលពីថ្ងៃទី១៣ ខែសីហា ឆ្នាំ២០១៥កន្លងទៅ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97431-2015-08-19-06-48-32.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0740.jpg', 'Wednesday, 19 August 2015 13:47', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1086', 'B030101', 'B030000', 'ទស្សនៈនិងមតិៈ បេសកកម្មនាំផែនទី មកកម្ពុជារបស់ UN ទំនងអត់ន័យ ខណៈអ្នកចោទនាំគ្នា គេចទៅក្រៅ ប្រទេសអស់ !', '<strong>ភ្នំពេញៈ</strong> ផែនទីខ្មែរដែលតម្កល់ទុកនៅ UN តាំងពីឆ្នាំ១៩៦៤ ត្រូវបានមន្ត្រី UN នាំយកមកដល់ប្រទេស កម្ពុជាហើយ យោងតាមសំណើសុំខ្ចី យកមកផ្ទៀងផ្ទាត់ជាមួយផែនទី ដែលរាជរដ្ឋាភិបាលកម្ពុជាមាន រួមទាំង ផែនទី ដែលបានមកពីប្រភពផ្សេងៗទៀត ដើម្បីឲ្យប្រាកដថាតើវាដូចគ្នាឬអត់?', 'http://www.dap-news.com/2011-06-14-02-39-55/97430----un-----.html', 'img/dapnews.png', 'Wednesday, 19 August 2015 13:44', '0', '0', '0', 't', '2015-08-19', 'f');
