INSERT INTO "tbnews" VALUES ('1134', 'B020102', 'B020000', 'ពលរដ្ឋ​ចាប់​ចោរឆក់​កាបូប​ម្នាក់​ប្រគល់​ឱ្យ​ប៉ូលិស​ប៉ុស្តិ៍​ភ្នំពេញ​ថ្មី', 'រាជធានី​ភ្នំពេញ ៖ ជនសង្ស័យ​ម្នាក់​ក្នុង​ចំណោម​២​នាក់ ត្រូវ​ប្រជាពលរដ្ឋ​នាំ​គ្នា​ព័ទ្ធ​ចាប់​បាន ខណៈ​ធ្វើ​សកម្មភាព​ឆក់​កាបូប​ពី​ជន​បរ​ទេស រួច​ព្យាយាម​ជិះ​ម៉ូតូ​គេចខ្លួន តែ​បាន​ដួល​ម៉ូតូ​ក៏​ត្រូវ​ចាប់​បាន​ម្នាក់​តែ​ម្តង ដែល​ហេតុការណ៍​នេះ​កើតឡើង​កាលពី​វេលា​ម៉ោង​១១ និង​​៣០​នាទី​ព្រឹក​ថ្ងៃ​ទី​១៩ សីហា ២០១៥ ស្ថិត​នៅ​ចំណុច​ផ្លូវ​លេខ​១៩៨៦ កែង​ផ្លូវ​១០០៣ ក្នុងភូមិ​អូរ​ទំនប់ សង្កាត់​ភ្នំពេញ​ថ្មី ខណ្ឌ​', 'http://kohsantepheapdaily.com.kh/article/127122.html', 'http://i.kspg.co/2015/8/92dc5eb8-0b00-457a-8a06-d51ac38f7c60.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:០៦', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1135', 'B020101', 'B020000', 'ប្រជាពលរដ្ឋ​សំណូមពរ​ឱ្យ​បញ្ចុះតម្លៃ តែ​ម្ចាស់​កំពង់ចម្លង​ទន្លេសេសាន​ថា​តម្លៃ​សមរម្យ​ហើយ', 'ខេត្តរតនគិរី ៖ ប្រជាពលរដ្ឋ​រស់នៅ​ស្រុក​អណ្តូង​មាន​ក៏​ដូច​ជា​អ្នក​ធ្វើ​ដំណើរ​ទូទៅ​បាន​សំណូមពរ​សុំ​ឱ្យ​ម្ចាស់​កំពង់ចម្លង​ទន្លេ​សេសាន​​បញ្ចុះតម្លៃ​ខ្លះ ព្រោះថា​បច្ចុប្បន្ន​នេះ​តម្លៃ​នៃ​ការ​ឆ្លង​ដ​គឺ​ថ្លៃ​ពេក​ហើយ ធ្វើ​ឱ្យ​ប៉ះពាល់​ដល់​ចំណូ​ច និង​ជីវភាព​រស់នៅ​របស់​ពួក​គាត់ ។', 'http://kohsantepheapdaily.com.kh/article/127146.html', 'http://i.kspg.co/2015/8/a7305998-ceda-415b-9aaf-12267a5b8728.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៩:៥៥', '0', '0', '0', 't', '2015-08-19', 'f');
