INSERT INTO "tbnews" VALUES ('1200', 'B020202', 'B020000', 'PUMA ផលិត​ស្បែកជើង​មាន​លម្អ​ទ្រង់ទ្រាយ​សក់សេះ​របស់​កីឡាករ​ម៉ា​រី​យ៉ូ បា​ឡូ​តេ​ល​លី', 'ទោះបី​អនាគត​អាជីព​បាល់ទាត់​របស់​កីឡាករ​ខ្សែប្រយុទ្ធ​ក្លិប Liverpool ម៉ា​រី​យូ​៉ បា​ឡូ​តេ​ល​លី មិន​ទាន់​ប្រាកដប្រជា​យ៉ាងណា​ក៏ដោយ ក៏​កីឡាករ​ខ្សែប្រយុទ្ធ​អាយុ​២៥​ឆ្នាំ​រូប​នេះ​មានការ​ងារ​ជា​ច្រើន​ដែល​ត្រូវធ្វើ ។', 'http://kohsantepheapdaily.com.kh/article/127123.html', 'http://i.kspg.co/2015/8/84ea0f1a-7e01-4fff-9ed4-1cb009d50790.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:០៧', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1201', 'B020302', 'B020000', 'គង់ ច័ន្ទ​ស្រី​មុំ ធ្វើ​ខួបកំណើត​ឆ្លៀត​ប្រកាស​ពីមុខ​ជំនួញ', 'រាជធានី​ភ្នំពេញ ៖ កញ្ញា​គង់ ច័ន្ទ​ស្រី​មុំ​បាន​រៀបចំ​ពិធី​ខួបកំណើត​គម្រប់​២៥​ឆ្នាំ កាលពី​រាត្រី​ថ្ងៃ​ទី​១៨ សីហា ឆ្នាំ​២០១៥ នៅ​ភោជនីយដ្ឋាន​BOTTOMS UP ខាងក្រោយ​ស្ថានទូត​រ៉ុ​ស្ស៊ី​ក្បែរ​តំបន់​កោះ​ពេជ្រ​នៃ​រាជធានី​ភ្នំពេញ ។ ', 'http://kohsantepheapdaily.com.kh/article/127175.html', 'http://i.kspg.co/2015/8/1fd1786d-8947-4e8e-b7d9-3cb7a09e025e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០១', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1202', 'B020302', 'B020000', 'ទូរទស្សន៍​ភី​ភី​ស៊ី​ធី​វី​ត្រៀម​រៀបចំ​ព្រឹត្តិការណ៍​ដ៏​ធំនៅ​កម្ពុជា​គឺ​កម្មវិធី​ពានរង្វាន់​តារា​ឯក​សាំង​ហ្គា​ពួរ', 'រាជធានី​ភ្នំពេញ ៖ ក្រុមហ៊ុន​ភី​ភី​ស៊ី​ធី​វី PPCTV Co., Ltd គឺជា​ក្រុមហ៊ុន​ទូរគមនាគមន៍​ឈានមុខ​គេ​ក្នុង​ព្រះរាជាណាចក្រ​កម្ពុជា ដែល​ផ្គត់ផ្គង់​នូវ​សេវាកម្ម​ជា​ច្រើន ដូច​ជា​ទូរទស្សន៍​ខ្សែកាប អីុ​ន​ធើ​រ​ណេ​ត​អ៊ុ​ប​ទិ​ក', 'http://kohsantepheapdaily.com.kh/article/127174.html', 'http://i.kspg.co/2015/8/e061103f-ccd3-4752-b877-74556694fb2f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៥៦', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1203', 'B020301', 'B020000', 'អង្គរវត្ត​ជាប់​ចំណាត់ថ្នាក់​ជា​កន្លែង​ទាក់ទាញអ្នកទេសចរ​ច្រើន​ជាងគេ​លើ​ពិភពលោក', 'គេហទំព័រ​ទេសចរណ៍​ដ៏​ល្បី​របស់​ពិភពលោក Lonely Planet បាន​វាយតម្លៃ​លើ​តំបន់​រមណីយដ្ឋាន​ប្រវត្តិសាស្ត្រ​ក្នុង​ចំណោម​៥០០ កន្លែង ក្នុង​នោះ​ប្រាសាទអង្គរវត្ត​ជាប់​ចំណាត់ថ្នាក់​លេខ​១ ក្នុង​បញ្ជី​តារាង ។ ការ​វាយតម្លៃ​នេះ​', 'http://kohsantepheapdaily.com.kh/article/127167.html', 'http://i.kspg.co/2015/8/9fed6f3e-6b61-406d-b799-af3f7a84c065.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៣៦', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1204', 'B020105', 'B020000', 'នាឡិកា​អេ​ឡិច​ត្រូ​និក Moto 360 របស់​ក្រុមហ៊ុន Motorola នឹង​លេច​រូបរាង​2​ទំហំ​ទៀត', 'ក្រុមហ៊ុន Motorola ត្រៀម​ផលិត​នាឡិកា​អេ​ឡិច​ត្រូ​និក Moto 360 ស៊េរី​ថ្មី​២​ម៉ូ​ដែល​ទៀត​គឺ «360S» និង «360L» ។ នាឡិកា​ស៊េរី​ថ្មី​ទាំង​ពីរ​ម៉ូ​ដែល​នេះ​នឹង​ត្រូវ​ដាក់​លក់​ក្នុង​ប្រទេស​ប្រេ​ស៊ី​ល ។ ', 'http://kohsantepheapdaily.com.kh/article/127128.html', 'http://i.kspg.co/2015/8/1c78eefe-0c6b-40b2-84f7-6f6b965e9e01.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:១៨', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1205', 'B020102', 'B020000', 'ជន​សង្ស័យ៤​នាក់​ជិះ​ម៉ូតូ​ឃើញ​សមត្ថកិច្ច ​​ព្យាយាម​ជិះ​រត់ ​ត្រូវ​សមត្ថកិច្ច​ដេញ​ចាប់​​បាន​២​នាក់​​', 'រាជ​ធានីភ្នំពេញ ៖ សមត្ថកិច្ច​ខណ្ឌចំការមន​ជិះ​ល្បាត​ក្នុង​មូលដ្ឋាន​បានប្រទះ​ឃើញ​ជនសង្ស័យ​ចំនួន​៤​នាក់​ជិះ​ម៉ូតូ​២​គ្រឿង​គួរ​ឱ្យ​សង្ស័យ​ក៏​ដេញ​តាម ចំណែក​​ជនសង្ស័យ​គ្រាន់​តែ​ឃើញ​សមត្ថ​កិច្ច​ភ្លាម​បាន​ព្យាយាម​ជិះ', 'http://kohsantepheapdaily.com.kh/article/127154.html', 'http://i.kspg.co/2015/8/ce87d792-a997-4916-8426-8ec6fe28dea3.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:០៦', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1206', 'B020102', 'B020000', 'មុននេះ! ចាប់​បាន​ជនសង្ស័យ​ឆក់​ទូរស័ព្ទ​ម្នាក់​នៅ​ក្នុង​កាស៊ីណូ «​ហ្គោ​ល​ឌិន ក្រោ​ន​»', 'ក្រុង​ប៉ោយ​ប៉ែ​ត ៖ សេចក្តីរាយការណ៍​បានឱ្យ​ដឹងថា កម្លាំង​សន្តិសុខ​កាស៊ីណូ​ហ្គោ​ល​ឌិន​ក្រោ​ន សហការ​ជាមួយ​សមត្ថកិច្ច​ច្រក​ទ្វារ បាន​ធ្វើការ​ព័ទ្ធ​ចាប់​ជនសង្ស័យ​ម្នាក់ ដែល​ធ្វើ​សកម្មភាព​ឆក់​ទូរស័ព្ទ​ដៃ​ពី​ស្ត្រី​ជនជាតិ​ថៃ​ម្នាក់​បាន​សម្រេច ហើយ​ព្យាយាម​គេចខ្លួន នា​វេលា​ម៉ោង​០៤ និង​៤៥​នាទី​រសៀល​ថ្ងៃ​ទី​១៩ សីហា ២០១៥ នេះ ស្ថិត​នៅ​ចំណុច​ក្នុង​បរិវេណ​កាស៊ីណូ​ហ្គោ​ល​ឌិន​ក្រោ​ន ។ ', 'http://kohsantepheapdaily.com.kh/article/127137.html', 'http://i.kspg.co/2015/8/00d3bd73-04af-4693-8c2e-711a53be554c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:០៣', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1207', 'B020102', 'B020000', 'ស្គាល់​អត្តសញ្ញាណ​ជន​រង​គ្រោះ​​ដែល​ឃាតករ​​រិត​.ក​សម្លាប់​ហើយ គឺ​ជា​មន្ត្រី​​មន្ទីរ​ធម្មការ​ខេត្ត​', 'ខេត្តកំពង់ឆ្នាំង ៖ ឃាតកម្មដោយ​រិ​ត.ក និង​វាយ​សម្លាប់​ជន​រង​គ្រោះ​ចោល​នៅ​ចំណុច​ស្តុំ ស្ថិត​ក្នុង​ភូមិក្រោល​ជី ឃុំ​ពពេល ស្រុក​បរិបូរ​ណ៍ ​កាលពី​ព្រឹក​ថ្ងៃ​ទី​១៧ ​សីហា ​២០១៥ ពេល​នេះ​បាន​ស្គាល់អត្តសញ្ញាណ​ហើយ ', 'http://kohsantepheapdaily.com.kh/article/127101.html', 'http://i.kspg.co/2015/8/39247a8c-9f57-4a13-8823-cb6982701b20.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៣២', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1208', 'B020101', 'B020000', 'ភ្លៀង​លាយ​ខ្យល់​ក​ន្ទ្រា​ក់​ ​ធ្វើ​ឱ្យ​រលំ​​និង​រ​បើក​ដំបូល​​ផ្ទះ​​​ពលរដ្ឋ​ ​ត្រូវ​បាន​នគរបាល​​​ជួយ​សាង​សង់​ឡើងវិញ', 'ខេត្តសៀមរាប​ ៖ កម្លាំង​សមត្ថកិច្ច​នគរបាល​អធិការ​​ដ្ឋាន​ស្រុក​វ៉ា​រិ​ន ក្រោម​ការ​បញ្ជា​ផ្ទល់​ដោយ​ លោក​វរសេនីយទោ ណុ​ប សា​រ៉ា​ក់ អធិការ​នគរបាល​ស្រុក​វ៉ា​រិ​ន ​បានឱ្យ​កម្លាំង​ក្រោម​ឱវាទ​ចុះ​ជួយ​សាងសង់​ផ្ទះ​ជួន​ប្រជាពលរដ្ឋ​ឡើង​វិញ', 'http://kohsantepheapdaily.com.kh/article/127180.html', 'http://i.kspg.co/2015/8/61e2e0ed-fde2-410e-9b39-88ac284a8a1c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១២:៥៥', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1209', 'B020101', 'B020000', 'លោក ​ហោ​ ណំាហុង​ ​៖ ផែនទីរបស់អ.ស.ប​​ និង ​​របស់រាជរដ្ឋាភិបាល​កម្ពុជាដូចគ្នា', 'រាជធានីភ្នំពេញ ៖​ ​ប្រមុខការទូតកម្ពុជា​​លោកឧបនាយករដ្ឋមន្រ្តី​ ​ហោ ណាំហុង​ ​បានមានប្រសាសន៍​​នៅប៉ុន្មាននាទីមុននេះថា ​​ផែនទី១៨ផ្ទំាង​ខ្នាតUTM ​១/១០០.០០ របស់​', 'http://kohsantepheapdaily.com.kh/article/127178.html', 'http://i.kspg.co/2015/8/e268244d-39e8-4259-929d-63f9924f4dfa.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០៩', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1210', 'B020101', 'B020000', 'ផែនទី​ពី អ​.​ស​.​ប ថ្ងៃនេះ​មានការ​ផ្ទៀងផ្ទាត់​ជា​ប្រវត្តិសាស្ត្រ​រក​ភាព​ត្រឹមត្រូវ​នៃ​ផែនទី​កម្ពុជា', 'រាជធានី​ភ្នំពេញ ៖ ផែនទី​ចំនួន​១៨​ផ្ទាំង​ពី​អង្គការសហប្រជាជាតិ (​អ​.​ស​.​ប​) បាន​នាំ​មក​ដល់​កម្ពុជា​ហើយ កាលពី​វេលា​ម៉ោងជាង​១០​យប់​ថ្ងៃ​ទី​១៨ សីហា ហើយ​ពិធី​ប្រគល់​-​ទទួល និង​ផ្ទៀងផ្ទាត់​ផែនទី​នឹង​ធ្វើ​ឡើងជា​ផ្លូវការ​នៅ​វិមាន​សន្តិភាព ទីស្ដីការ​នាយក​រដ្ឋមន្ដ្រី នា​ថ្ងៃ​ទី​២០ សីហា ', 'http://kohsantepheapdaily.com.kh/article/127173.html', 'http://i.kspg.co/2015/8/46063873-70b0-4434-b31b-772ff6bba247.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៤៩', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1211', 'B020101', 'B020000', '​លោក​កិត្តិ​សេដ្ឋាបណ្ឌិត គាត ឈុន ​​អញ្ជើញ​​​ក្នុង​ពិធី​បញ្ចុះ​បឋមសីលា​បើក​​ការ​ដ្ឋាន​សាង​សង់​រោង​ចក្រ​ថ្មី​របស់​​ក្រុម​ហ៊ុន​កូកាកូឡា', '​រាជ​ធានី​ភ្នំពេញ ៖ ​លោក​កិត្តិ​សេដ្ឋា​បណ្ឌិត​ គាត ឈុន ​ឧបនាយករដ្ឋ​មន្ត្រី​ប្រចាំ​ការ​ និង​ជា​អនុប្រធាន​ទី១​ក្រុម​ប្រឹក្សា​អភិវឌ្ឍន៍​កម្ពុជា ​អញ្ជើញ​ជា​អធិបតីភាព​ក្នុង​ពិធី​បញ្ចុះ​បឋម​សីលា​បើក​ការ​ដ្ឋាន​​សាង​សង់​រោង​ចក្រ​​ថ្មី​របស់​ក្រុមហ៊ុន​ភេសជ្ជៈ​កូកាកូឡា', 'http://kohsantepheapdaily.com.kh/article/127165.html', 'http://i.kspg.co/2015/8/6df6e74e-d019-49be-8eea-95c9725f1573.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:១៥', '0', '0', '0', 't', '2015-08-20', 'f');
