INSERT INTO "tbnews" VALUES ('1213', 'B020101', 'B020000', 'លោក ប៉ា ​សុជាតិវង្ស ៖​ ឆ្នាំ២០១៦ ​អត់ឲ្យមានចំណត​ក្រុមហ៊ុន​រថយន្តដឹកអ្នកដំណើរក្នុងក្រុង', 'រាជធានីភ្នំពេញ ៖​ អភិបាលនៃ​គណៈអភិបាលរាជធានីភ្នំពេញ ​លោក ប៉ា សុជាតិវង្ស ​បានប្រកាសឲ្យក្រុមហ៊ុន​ដឹកជញ្ជូនតាមរថយន្តក្រុង ​ឬរថយន្តធំៗ​រកចំណតក្រៅក្រុងឲ្យ​', 'http://kohsantepheapdaily.com.kh/article/127162.html', 'http://i.kspg.co/2015/8/ea5af818-d01d-4474-9f9d-fc6157814387.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:០១', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1214', 'B020101', 'B020000', 'រូបភាព​ផែនទី និង​ទិដ្ឋភាព​នៅ​ក្នុង​ពិធី​ប្រគល់​-​ទទួល​និង​ផ្ទៀងផ្ទាត់​ផែនទី', 'រូបភាព​ផែនទី និង​ទិដ្ឋភាព​នៅ​ក្នុង​ពិធី​ប្រគល់​-​ទទួល​និង​ផ្ទៀងផ្ទាត់​ផែនទី ៖ ', 'http://kohsantepheapdaily.com.kh/article/127161.html', 'http://i.kspg.co/2015/8/545ba41e-0845-4382-8b15-861ef7a93db7.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤២', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1215', 'B020101', 'B020000', 'ការផ្ទៀងផ្ទាត់ផែនទី​ត្រឹមម៉ោងជាង៩ព្រឹកនេះ ​៖ ផែនទី៣ផ្ទំាង​របស់រាជរដ្ឋាភិបាល​និងអ.ស.ប ដូចគ្នា', 'រាជធានីភ្នំពេញ ៖ ​បន្ទាប់ពីប្រគល់និង​ទទួលផែនទីបានបញ្ចប់ ​ការផ្ទៀងផ្ទាត់ផែនទី​១៨ផ្ទំាង​បានចាប់ផ្តើមឡើងហើយ ​ដោយក្នុងនោះ​គិតត្រឹមម៉ោងជាង៩ព្រឹកនេះ​ទី២០​សីហានេះ ផែនទី៣ផ្ទំាង​របស់រាជរដ្ឋាភិបាល​និងអង្គការសហប្រជាជាតិ​ (អ.ស.ប)​ដូចគ្នា ។', 'http://kohsantepheapdaily.com.kh/article/127160.html', 'http://i.kspg.co/2015/8/8a4f8116-6a60-4c95-b565-4d8910c95d70.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣៨', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1216', 'B020101', 'B020000', 'ចុង​សប្តាហ៍​នេះ​ តំបន់​មាត់​សមុទ្រ​នឹង​មាន​ភ្លៀង​ធ្លាក់​ច្រើន និង​ខ្យល់​កន្ត្រាក់​ដោយ​សារ​ឥទ្ធិ​ពល​ព្យុះ​ហ្គោនី', 'ចុង​សប្តាហ៍​នេះ​ តំបន់​មាត់​សមុទ្រ​នឹង​មាន​ភ្លៀង​ធ្លាក់​ច្រើន និង​ខ្យល់​កន្ត្រាក់​ដោយ​សារ​ឥទ្ធិ​ពល​ព្យុះ​ហ្គោនី', 'http://kohsantepheapdaily.com.kh/article/127155.html', 'http://i.kspg.co/2015/8/21971909-1793-4840-acaf-a660ba863667.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:១៩', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1217', 'B020101', 'B020000', 'សន្តិ​សុខម្នាក់​ដេក​ស្លាប់​មុខ​ហាង​លក់​មាស​-ពេជ្រមុខ​ផ្សារ​សាមគ្គីផ្អើល​ទាំង​ព្រឹក', 'រាជ​ធានី​ភ្នំពេញ​ ៖ តាម​សេចក្តី​រាយការណ៍​បាន​ឱ្យ​ដឹង​ថា សន្តិ​សុខ​យាម​ហាង​មាស​ពេជ្រ​​មួយ​កន្លែង​យី​ហោ​ហង្ស​មាស​បាន​ដេក​ស្លាប់​នៅ​មុខ​ហាង​ត្រូវ​បាន​គេ​ប្រទះ​ឃើញ​បង្ក​ឱ្យ​មានការ​ភ្ញាក់​ផ្អើល​', 'http://kohsantepheapdaily.com.kh/article/127152.html', 'http://i.kspg.co/2015/8/3879f928-94fe-41c5-bd55-966101a122e7.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៤៩', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1218', 'B020401', 'B020000', 'ឥណ្ឌូ​ណេ​ស៊ី​មាន​សុទិដ្ឋិនិយម​លើ​ការ​ដោះ​ដូរ​ពាណិជ្ជកម្ម​ជាមួយកម្ពុជា បើ​ទោះជា​ទំហំ​ពាណិជ្ជកម្ម​រវាង​ប្រទេស​ទាំង​2​ធ្លាក់​ចុះ​បន្តិច​ក្ដី', 'រាជធានី​ភ្នំពេញ ៖ ទំហំ​ទឹកប្រាក់​នៃ​ការ​ដោះ​ដូរ​ពាណិជ្ជកម្ម​ទ្វេភាគី​រវាង​កម្ពុជា​-​ឥណ្ឌូ​ណេ​ស៊ី មានការ​ធ្លាក់​ចុះ​ជាង​១០​ភាគរយ​ក្នុង​ឆមាស​ទី​មួយ​ឆ្នាំ​នេះ បើ​ធៀប​នឹង​រយៈពេល​ដូច​គ្នា​កាលពី​ឆ្នាំ​២០១៤ ។ នេះ​បើ​យោង​តាម​', 'http://kohsantepheapdaily.com.kh/article/127177.html', 'http://i.kspg.co/2015/8/1e4c256e-47f0-4859-bf17-c3667ab5e01d.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០៩', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1219', 'B020401', 'B020000', 'ឧស្សាហកម្ម​កៅស៊ូ​វៀតណាម​ត្រូវការ​យន្តការ​អោយ​មាន​សន្ទុះ​ឡើង​វិញ', 'វៀតណាម ៖ តាម​ការ​លើក​ឡើង​នៅ​ក្នុង​កិច្ចប្រជុំ​មួយ​ស្តី​ពី​វឌ្ឍនភាព​របស់​ក្រុមហ៊ុន​កៅស៊ូ Vietnam Rubber Group ដែល​ប្រព្រឹត្ត​ទៅ កាលពី​ថ្ងៃ​ទី​១៧ សីហា លោក​ឧបនាយករដ្ឋមន្ត្រី Vu Van Ninh', 'http://kohsantepheapdaily.com.kh/article/127176.html', 'http://i.kspg.co/2015/8/cd2f3b17-d314-4e2c-b736-a9d9d5172c78.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០៥', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1220', 'B030101', 'B030000', 'នគរបាលខណ្ឌ ប្ញស្សីកែវ បង្រ្កាប ចោរ គាស់ផ្ទះនិង កាច់កម៉ូតូដ៏សកម្មពីរនាក់ ក្រោយធ្វើសកម្មភាព', '<strong>ភ្នំពេញ ៖</strong> កម្លាំងនគរបាល ខណ្ឌប្ញស្សីកែវ បានឃាត់ខ្លួនមុខសញ្ញាគាស់ ផ្ទះនិងកាច់កយកម៉ូតូ បានពីរនាក់ កាលពីវេលាម៉ោង១២និង៣០នាទីរសៀលថ្ងៃទី១៩ ខែសីហា ឆ្នាំ២០១៥ បន្ទាប់ពីជនសង្ស័យ បានធ្វើសកម្មភាពគាស់ផ្ទះប្រជាពលរដ្ឋ កាលពីយប់ថ្ងៃទី២៤ ខែកក្កដា ឆ្នាំ២០១៥កន្លងមក ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97495-2015-08-20-06-28-36.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0850.jpg', 'Thursday, 20 August 2015 13:27', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1221', 'B030101', 'B030000', 'សិក្សាជាមួយ សាស្ត្រាចារ្យ ដែលមាន បទពិសោធន៍ ជាង១០ឆ្នាំ លើកិច្ចការរដ្ឋបាល និងលេខាធិការ', 'null', 'http://www.dap-news.com/2011-06-14-02-39-55/97494-2015-08-20-06-23-08.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/July/id-010/chanroth015-july-326.jpg', 'Thursday, 20 August 2015 13:22', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1222', 'B030101', 'B030000', 'តំបន់ ប្រតិបត្តិការសឹករង កំពង់ធំ ផ្សព្វផ្សាយករណី លោកហុង សុខហួរ សមាជិព្រឹទ្ធសភា ក្លែងបន្លំឯកសារ បង្កឲ្យមហាជន យល់ច្រឡំ', '<strong>កំពង់ធំ៖</strong> ពីធីផ្សព្វផ្សាយ ស្តីពីការក្លែងបន្លំឯកសារ របស់លោក ហុង សុខហួរ សមាជិកព្រឹទ្ធសភា ព្យាយាមបន្លៃពីបញ្ហាការ ចុះហត្ថលេខាលើសន្ធិសញ្ញា ដែលបានកំណត់ព្រំដែន រវាងប្រទេសទាំង២ លើខ្សែបន្ទាត់ព្រំដែននាបច្ចុប្បន្ន ដល់នាយទាហាននាយរង និងពលទាហាន ចំណុះឲ្យតំបន់ប្រតិបត្តិការសឹករងកំពង់ធំ ដោយមានការចូលរួម ៩៥នាក់ នៅព្រឹកថ្ងៃទី២០ ខែសីហា ឆ្នាំ២០១៥។', 'http://www.dap-news.com/2011-06-14-02-39-55/97493-2015-08-20-05-53-10.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0500.jpg', 'Thursday, 20 August 2015 12:52', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1223', 'B030101', 'B030000', 'តើលោក អ្នកចង់ប្រើប្រាស់ កម្មវិធីគណនេយ្យ មួយណា ?', 'បើលោកអ្នក មានការពិបាក ក្នុងការជ្រើសរើស កម្មវិធីគណនេយ្យ ឲ្យត្រូវជាមួយមុខរបរ ឬស្ថានភាព អាជីវកម្មរបស់ខ្លួន Leopard Business Consultancy Co., Ltd.(LBC) ជាក្រុមហ៊ុនដែលមានឯកទេសច្បាស់លាស់ លើវិជ្ជាជីវនេះ អាចជួយលោកអ្នកបានគ្រប់បែបយ៉ាង។ LBC ផ្តល់ជូនលោកអ្នករួមមាន៖', 'http://www.dap-news.com/2011-06-14-02-39-55/97490-2015-08-20-05-20-20.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/June/id-010/chanroth015-ju-119.jpg', 'Thursday, 20 August 2015 12:20', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1224', 'B030101', 'B030000', 'លោក លឹម គានហោ ឧត្ថមថវិកាដល់លោក ជ័យ ប៊ុនឈឿ ៥០០ដុល្លារ សម្រាប់ព្យាបាលជម្ងឺ', '<strong>បាត់ដំបង ៖</strong> បន្ទាប់ពីមជ្ឈមណ្ឌល ព័ត៌មានដើមអម្ពិល បានផ្សាយព័ត៌មានពីលោក ជ័យ ប៊ុនឈឿ ដង្ហោយ ស្វែងរកជំនួយពីសប្បុសជន សម្រាប់ព្យាបាលជម្ងឺរួចមក លោក លឹម គានហោ រដ្ឋមន្រ្តីក្រសួង ធនធានទឹកនិងឧតុនិយម បានឧត្ថមថវិកាដល់លោក ជ័យ ប៊ុនឈឿ ចំនួន ៥០០ ដុល្លារសម្រាប់ ព្យាបាលជម្ងឺ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97488-2015-08-20-04-53-22.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0839.jpg', 'Thursday, 20 August 2015 11:52', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1225', 'B030101', 'B030000', 'លោក ស សុខា ប្រតិកម្មនឹងរថយន្ត ពាក់ស្លាកលេខ នគរបាល 2-2089 ចតបញ្ច្រាសទិស លើគំនូសសញ្ញា អ្នកថ្មើរជើង', '<strong>(ភ្នំពេញ)៖</strong> លោក ស សុខា អ្នកតំណាងរាស្ត្រ វ័យក្មេង ប្រចាំខេត្តព្រៃវែង នៅថ្ងៃទី២០ ខែសីហានេះ បានបញ្ចេញ ការរិះគន់ តាមរយៈគេហទំព័រ Facebook របស់លោក ដោយមានបង្ហោះ រូបភាពរថយន្ត Land Cruiser មួយពាក់ ស្លាកលេខ នគរបាល 2-2089 និងបានសរសេរពាក្យពេជ្រ ដ៏មានអត្ថន័យមួយចំនួន ដោយរិះគន់ខ្លាំងទៅ លើ ម្ចាស់រថយន្តទំំនើបនោះ ដែលបានចតរថយន្ត បញ្ច្រាសទិស ហើយថែមទាំងចត លើកគំនូរសម្រាប់ថ្មើរជើង ថែមទៀត។', 'http://www.dap-news.com/2011-06-14-02-39-55/97489--2-2089-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-089.jpg', 'Thursday, 20 August 2015 11:51', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1226', 'B030102', 'B030000', 'អាមេរិក៖ ម្តាយ សម្លាប់កូនប្រុស ៣នាក់ ដោយសាររឿង ច្រណែនឈ្នានីស', '<strong>វ៉ាស៊ីនតោន៖</strong> ទីភ្នាក់ងារព័ត៌មាន ចិនស៊ិនហួចេញផ្សាយ នៅថ្ងៃទី២០ ខែសីហា ឆ្នាំ ២០១៥ បានឲ្យដឹងថាស្រ្តីម្នាក់ នៅរដ្ឋអូហៃអូ សហរដ្ឋអាមេរិក បានសារភាពថា បានសម្លាប់កូនប្រុស របស់នាង ៣នាក់ដែលនៅក្នុងនោះមាន ទារក២នាក់ និង ក្មេងប្រុសអាយុ ៤ឆ្នាំម្នាក់ កាលពីឆ្នាំ មុន ដោយសារតែស្វាមីរបស់នាង មិនយកចិត្តទុកដាក់ ជាមួយ នឹងកូនស្រី បង្កើតរបស់ពួកគេ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97479-2015-08-20-03-55-10.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-083.jpg', 'Thursday, 20 August 2015 10:50', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1227', 'B030102', 'B030000', 'ប៉ូលិសថៃ បង្ហាញមុខ ជនសង្ស័យពីរនាក់ទៀត បន្ទាប់ពីការបំផ្ទុះ គ្រាប់បែកនៅបាងកក', '<strong>បាងកក៖</strong> តាមការផ្សាយ របស់បាងកកប៉ុស្តិ៍ នៅព្រឹកថ្ងៃព្រហស្បតិ៍ ទី២០ ខែសីហា ឆ្នាំ២០១៥ នេះឲ្យដឹងថា ប៉ូលិសថៃទើបតែកំណត់ បានរូបថតជនសង្ស័យ ចំនួនពីររូបបន្ថែមទៀត ដែល អាចជាប់ពាក់ព័ន្ធនឹងការ បំផ្ទុះគ្រាប់បែក នៅទីសក្ការៈបូជា អេរ៉ាវ៉ាន កណ្តាលទីក្រុងបាងកក កាលពីថ្ងៃចន្ទ ទី១៧ ខែសីហា ដែលបាន បណ្តាលឲ្យ មនុស្សស្លាប់ ២០នាក់ និង របួស ១២៥នាក់នោះ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97475-2015-08-20-03-33-05.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-082.jpg', 'Thursday, 20 August 2015 10:30', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1228', 'B030102', 'B030000', 'រដ្ឋាភិបាលថៃ បង្ហាញមុខជនសង្ស័យ ដែលអាចជាអ្នក បំផ្ទុះគ្រាប់បែក នៅបាងកក (Video Inside)', '<strong>បាងកក៖</strong> ប៉ូលិសថៃ នៅថ្ងៃពុធ នេះបានបញ្ចេញនូវរូបថត បុរសជាជនសង្ស័យ ដែលគេជឿ ជាក់ថា ជាអ្នកដាក់គ្រាប់បែក និង បញ្ជាឲ្យផ្ទុះនៅទីសក្ការៈបូជា អេរ៉ាវ៉ាន ក្នុងសង្កាត់ រាជប្រាស៊ុង កណ្តាលទីក្រុង បាងកក កាលពីយប់ថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ ២០១៥។', 'http://www.dap-news.com/2011-06-14-02-40-26/97448--video-inside.html', 'http://s3-ap-southeast-1.amazonaws.com/images.freshnews/fn.photo/Aug2015/K-5764.jpg', 'Wednesday, 19 August 2015 16:35', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1229', 'B030102', 'B030000', 'សរុបព្រឹត្តការណ៍ វិវឌ្ឍថ្មីៗ ក្រោយការផ្ទុះ គ្រាប់បែកនៅ ទីក្រុងបាងកក', '<strong>បាងកក៖</strong> យោងតាមសារព័ត៌មាន បាងកកប៉ុស្តិ៍ ចេញផ្សាយ នៅថ្ងៃពុធ ទី១៩ ខែសីហា ឆ្នាំ ២០១៥ នេះឲ្យដឹងថា លោកនាយករដ្ឋមន្ត្រីថៃ ប្រាយុទ្ធ ចាន់អូឆា បានអំពាវនាវឲ្យជនណា ក៏ដោយ ដែលជាអ្នកបាន ប្រព្រឹត្តការ បំផ្ទុះគ្រាប់បែកនោះ ត្រូវបានតែប្រគល់ខ្លួនមកឲ្យប៉ូលិស ជាបន្ទាន់ ដើម្បីសុវត្ថិភាពរបស់ខ្លួនគេផ្ទាល់ បើមិន ដូច្នោះទេ ជនទាំងអស់នោះអាចនឹងត្រូវគេ សម្លាប់ក៏ថាបានដែរ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97446-2015-08-19-09-28-10.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-068.jpg', 'Wednesday, 19 August 2015 16:25', '0', '0', '0', 't', '2015-08-19', 'f');
INSERT INTO "tbnews" VALUES ('1230', 'B030302', 'B030000', 'តារាល្បីៗសិង្ហបុរី ក្នុងរឿង ពន្លកស្នេហ៍ ក្នុងភ្លើងសង្រ្គាម បានមកជួប អ្នកគាំទ្រនៅកម្ពុជា', '<strong>ភ្នំពេញ៖</strong> តារាល្បីៗ របស់ប្រទេសសិង្ហបុរី ដែលបានចូលរួមសម្ដែង ក្នុងខ្សែភាពយន្តថ្មី រឿងពន្លកស្នេហ៍ ក្នុងភ្លើង សង្រ្គាម<strong> </strong>បានមកជួបអ្នកគាំទ្រ នៅកម្ពុជានារសៀល ថ្ងៃទី១៥ ខែ សីហា ឆ្នាំ២០១៥។', 'http://www.dap-news.com/morokatmagazine/97255-2015-08-17-03-28-01.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-001.jpg', 'Monday, 17 August 2015 10:25', '0', '0', '0', 't', '2015-08-17', 'f');
