INSERT INTO "tbnews" VALUES ('1232', 'B010105', 'B010000', '​ម៉ូតូ​ចាស់​ៗស្ទក់​ៗ​គេ​ធ្វើ​អី​ចឹង​​ទេ', 'ម៉ូតូ​ដែល​មាន​អាយុ​កាល​យូរ​ឆ្នាំ​ ឬ ក៏​ការ​ជិះ​របស់​ប្រិយមិត្ត​មួយ​ចំនួន​មិន​បាន​ថែទាំ​អាច​ជួប​នឹង​ហានិភ័យ...', 'http://news.sabay.com.kh/article/456306', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/ddd6-285x170.jpg?ebb82d', '20 August, 2015', '0', '1', '32', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1233', 'B010105', 'B010000', 'បែក​ធ្លាយ​រូបរាង​ Blackberry ​ស៊េរី​ថ្មី​ មាន​អេក្រង់​កោង​ស្រដៀង​ S6 Edge', 'រូបភាព​ចំនួន​មួយ​សន្លឹក​​​ត្រូវ​បាន​​បង្ហោះ​លើ​បណ្ដាញ​សង្គម​ ត្រូវ​​គេ​សន្និដ្ឋាន​ថា​ ជា​រូបភាព​នៃ​​ទូរសព្ទ​ Blackberry​ ស៊េរី​ថ្មី​ ដែល​គ្រោង​នឹង​ប្រកាស​នា​ពេល​ខាង​មុខ​នេះ។ &nbsp; &nbsp; រូបភាព​​​មួយ​សន្លឹក​នោះ​ត្រូវ​បាន​បង្ហោះ​ចេញ​ពី​គណនី​បណ្ដាញ​សង្គម​ Twitter​ ដែល​មាន​ឈ្មោះ​@evleak​ ហើយ​នា​ពេល​កន្លង​មក​គណនី​នេះ​ក៏​ធ្លាប់​បាន​បង្ហោះ​នូវ​រូបភាព​បែក​ធ្លាយ​នៃ​ទូរសព្ទ​ជា​ច្រើន​ម៉ូដែល​រួច​មក​ហើយ។ ចំពោះ​រូបភាព​ Blackberry​ ដែល​គេ​បាន​បង្ហោះ​នេះ​មាន​ឈ្មោះ​ថា​ Venice ​ដើរ​ដោយ​ប្រព័ន្ធ​ប្រតិបត្តិការ​ Android​ របស់​ Google។ វា​គឺ​ជា​ទូរសព្ទ​ប្រភេទ​រុញ​មាន​មក​ជា​មួយ​នូវ​ Keyboard​ ដែល​មាន​ការ​រចនា​រូប​រាង​យ៉ាង​ស្រស់​ស្អាត​ ជា​ពិសេស​គឺ​គែម​នៃ​ទូរសព្ទ​មាន​សភាព​កោង​ស្រដៀង​គ្នា​ទៅ​នឹង​ Galaxy S6 Edge។ ​​ យោង​តាម​​វិបសាយ​ The Verge​ បាន​​ឲ្យ​ដឹង​ថា​ Blackberry Venice​ នេះ​គ្រោង​នឹង​វាយ​លុក​ចូល​ទីផ្សារ​សហរដ្ឋ​អាមេរិក​នា​ខែ​កញ្ញា​ខាង​មុខ​នេះ។ ទោះ​ជា​យ៉ាង​ណា​ គេ​មិន​ទាន់​ដឹង​ច្បាស់​អំពី​តម្លៃ​និង​លក្ខណៈ​សម្បត្តិ​នៃ​ទូរសព្ទ​នេះ​នៅ​ឡើយ​នោះ​ទេ៕​​​​ &nbsp; ​-ចុច​អាន៖ នៅ​​ចាំ​ទូរសព្ទ​​ទាំង​នេះ​ទេ​​ ថា​​តើ​ប្រិយមិត្ត​ធ្លាប់​ប្រើ​មួយ​ណា? &nbsp; ប្រែ​សម្រួល៖ ហៃ សុខ​ហេង​ ប្រភព​៖ theverge', 'http://news.sabay.com.kh/article/456456', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/featured108-285x170.jpg?ebb82d', '20 August, 2015', '0', '21', '32', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1234', 'B010105', 'B010000', 'ឆត្រ​ពិសេស​មិន​បាច់​យក​ដៃ​កាន់​ក៏​មាន​លំនឹង​ដែរ​', 'ឆត្រ​ដែល​មាន​ដង​កាន់​អក្សរ​ C​ នេះ​គឺ​មិន​ចាំ​បាច់​ឲ្យ​អ្នក​កាន់​វា​នោះ​ទេ​ ដោយ​គ្រាន់​តែ​សូក​ដៃ​ចូល​ប្រឡោះ​នោះ​ប៉ុណ្ណោះ...', 'http://news.sabay.com.kh/article/456372', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/1277-285x170.jpg?ebb82d', '20 August, 2015', '0', '0', '0', 't', '2015-08-20', 'f');
