INSERT INTO "tbnews" VALUES ('1239', 'B010302', 'B010000', 'លី តឺហួរ បដិសេធ​លុយ​រាប់​លាន​ដុល្លារ​ដើម្បី​​ទុក​ពេល​នៅ​ក្បែរ​កូន​ស្រី', 'តារា​សម្ដែង​និង​​ចម្រៀង​ហុងកុង Andy Lau​ បាន​បដិសេធ​ទឹក​ប្រាក់​ចំនួន ៥០លាន​ ពីកម្មវិធី The Voice of China ...', 'http://news.sabay.com.kh/article/456639', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/feat164-285x170.jpg?ebb82d', '20 August, 2015', '0', '0', '0', 't', '2015-08-20', 'f');
