INSERT INTO "tbnews" VALUES ('1245', 'B010505', 'B010000', 'អ្នក​ទេសចរ​ម៉ាឡេស៊ី​បាត់​បង់​សមាជិក​គ្រួសារ៥នាក់​​ក្រោយ​​​​ផ្ទុះ​គ្រាប់​បែក​នៅ​បាងកក​', 'ជា​ច្រើន​ម៉ោង​ក្រោយ​ពេល​ផ្ទុះ​ទើប​បុរស​វ័យ ៥៥​ឆ្នាំ​ដឹង​ថា មាន​តែ​កូន​ស្រី​ពពោះ​ក្នុង​ផ្ទៃ និង​លោក​ប៉ុណ្ណោះ​ដែល​នៅ​រស់រាន​មាន​ជីវិត...', 'http://news.sabay.com.kh/article/456609', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/neoh_3412522b1-285x170.jpg?ebb82d', '20 August, 2015', '0', '0', '0', 't', '2015-08-20', 'f');
