INSERT INTO "tbnews" VALUES ('1271', 'B020107', 'B020000', 'ម្នាស់​អាយុ​ពាន់​ឆ្នាំ​មាន​បារមី​សក្តិ​សិទ្ធិ​នៅ​លើ​ភ្នំ​ពិ​ស្ស៍', 'ខេត្ត​កំពង់់​ស្ពឺ ៖ ម្នាស់​ជា​ច្រើន​គុម្ព​ដុះ​លើ​ផ្ទាំង​ថ្ម រក្សា​ជីវិត​គង់​ពាន់​ឆ្នាំ​មក​ហើយ ខុស​ពី​ម្នាស់​ធម្មតា ។ អ្នកស្រុក​មាន​ជំនឿ​ថា​ម្នាស់​នោះ​មាន​ឫទ្ធិ​បារមី​អាច​ធ្វើ​ឱ្យ​មនុស្ស​ជួប​គ្រោះអាក្រក់​ប្រសិនបើ​ធ្វើ​ខុស​ត្រណម ។', 'http://kohsantepheapdaily.com.kh/article/127231.html', 'http://i.kspg.co/2015/8/6fba3928-5f14-4873-8cb0-ef1fb7f71131.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:០២', '0', '0', '0', 't', '2015-08-21', 'f');
