INSERT INTO "tbnews" VALUES ('1276', 'B020102', 'B020000', 'ជម្លោះក្នុង​វង់​​​ផឹកស៊ី​ បណ្តាល​ឱ្យ​បុរស​ម្នាក់​​ស្ទើរ​​ដាច់​ដៃ​ម្ខាង ​', 'ខេត្ត​ត្បូងឃ្មុំ​ ៖ ​ជម្លោះ​នៅ​​ក្នុង​វង់​ផឹកស៊ី​មួយ​កន្លែង ​រហូត​ឈានផ្ទុះ​អំពើ​ហិ​​ង្សា ​បណ្តាល​ឱ្យ​​បុរស​ម្នាក់ ​ឈ្មោះ ឡេ​ង ​ចាន់​សាង អាយុ​៣៣​ឆ្នាំ រង​របួស​​ដាច់​​​ដៃ​​ខាងឆ្វេង​ហូរ​ឈាម​​ស្រោច​ពេញ​ខ្លួន', 'http://kohsantepheapdaily.com.kh/article/127209.html', 'http://i.kspg.co/2015/8/b33e560a-2f6c-4563-8bc6-574a8984e9ad.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៤៧', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1277', 'B020102', 'B020000', 'ចេញពី​ក្លិប​ត្រូវ​ក្មេង​ទំនើង​​វាយ​ប៉ោង​មុខ​​', '​រាជ​ធានី​ភ្នំពេញ ៖ យុវជន​២​នាក់​​ជិះ​ម៉ូតូ​ មួយគ្រឿង ចេញពី​ក្លិប​ត្រូវ​ក្មេង​ទំនើង​២នាក់​វាយ​មួយ​ដៃ​ប៉ោងមុខ​ដោយ​មិនដឹង​ពី​​មូលហេតុ ហើយ​ក្មេង​ទំនើង​ក៏​រត់ឡើង​ផ្ទះ​គេ​ គេចខ្លួន​បាត់​ស្រមោល', 'http://kohsantepheapdaily.com.kh/article/127195.html', 'http://i.kspg.co/2015/8/d2dbfd1c-ae9e-484e-ad96-65eaf972913c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:០២', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1278', 'B020102', 'B020000', 'រថយន្ត​ដឹក​កូន​ត្រី​ឆ្ដោ​បើក​បំ​បុក​រថយន្ត​ប៉ូ​លិស​​​ធ្លាក់​ពីលើ​ស្ពាន​បាក់​បង្កាន់ដៃ​០៣​ផ្ទាំង ​តែ​ពុំ​មាន​អ្នក​ស្លាប់', 'ខេត្តពោធិ៍សាត់​ ៖​ រថយន្ត​ពីរ​គ្រឿង​ដែល​គេ​ដឹង​ថា ​ជា​រថយន្ត​សមត្ថកិច្ច​ និង​រថយន្ត​រកស៊ី​បទល្មើស​បាន​បើក​ប្រដេញ​គ្នា​ផ្អើល​ពេញ​ផ្លូវជាតិ​លេខ​៥​ ​ទីបំផុត​រថយន្ត​របស់​សមត្ថកិច្ច​ត្រូវ​រថយន្ត​រកស៊ី​បទល្មើស​បុក', 'http://kohsantepheapdaily.com.kh/article/127189.html', 'http://i.kspg.co/2015/8/868c9a83-d1ef-4e31-8285-30b6915e139c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:១៨', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1279', 'B020101', 'B020000', 'អភិបាលរង​ខេត្ត​សុំ​ឱ្យ​ក្រុមហ៊ុន​ដេញថ្លៃ​ម៉ៅ​ការ​ផ្លូវត្រូវ​តម្លៃ​ឱ្យ​ទាប ហើយ​មានគុណ​ភាព', 'ខេត្តបន្ទាយមានជ័យ ៖ លោក​អភិបាលរង​ខេត្ត​បន្ទាយ​បាន​លើក​ឡើង​នៅ​ក្នុង​ពិធី​ដេញថ្លៃ​ផ្លូវ​ក្នុង​សង្កាត់​​ចំនួន​៣ នៅ​សាលាក្រុង​សិរី​សោភ័ណ ​នា​ព្រឹក​ថ្ងៃ​ទី​២០ សីហា ២០១៥​ថា សុំ​ឱ្យ​ក្រុមហ៊ុន​ដេញថ្លៃ​ម៉ៅ​ការ​ធ្វើ​ផ្លូវត្រូវ​យក​តម្លៃ​ឱ្យ​ទាប ហើយ​មានគុណ​ភាព ។', 'http://kohsantepheapdaily.com.kh/article/127228.html', 'http://i.kspg.co/2015/8/55096d96-870c-416f-b484-cfaa49da68fe.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២០:៤២', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1280', 'B020101', 'B020000', 'ប៉ូលិស​ចរាចរណ៍​សហការ​ជាមួយ​ក្រុម​យុវជន​កាកបាទក្រហមកម្ពុជា​ខេត្ត​បន្ត​រឹត​បន្តឹង​ចរាចរណ៍​ក្នុង​ក្រុង​សៀមរាប', 'ខេត្តសៀមរាប ៖ ក្នុង​សកម្មភាព​ប្រតិបត្តិការ​ចុះ​ធ្វើការ​រឹត​បន្តឹង និង​ផ្សព្វផ្សាយ​ច្បាប់​ចរាចរណ៍​ផ្លូវគោក​ក្នុង​ខេត្តសៀមរាប​ដែល​ជា​តំបន់​ទេសចរណ៍​នេះ ត្រូវ​បាន​កម្លាំង​នគរបាល​ចរាចរណ៍​ផ្លូវគោក នៃ​ស្នងការដ្ឋាន​នគរបាល​ខេត្តសៀមរាប​ធ្វើ​ឡើងជា​ប្រចាំ', 'http://kohsantepheapdaily.com.kh/article/127226.html', 'http://i.kspg.co/2015/8/37c22701-7575-4b8a-b6c8-5074ab11042f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៩:៥៥', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1281', 'B020101', 'B020000', 'អភិបាលខេត្ត​ព្រមាន​មេ​ប៉ូលិស​អន្តោប្រវេសន៍​បញ្ឈប់​ការ​លួច​បើក​ច្រករបៀង​ខុស​ច្បាប់​ក្បែរ​ច្រក​ទ្វារ​អន្តរជាតិ ត្រូវ​ទប់ស្កាត់​រាល់​បទល្មើស​ឆ្លង​ដែន', 'ខេត្តបន្ទាយមានជ័យ ៖ លោក​អភិបាលខេត្ត​បាន​ព្រមាន​ដល់​មេ​ប៉ូលិស​អន្តោប្រវេសន៍​ឱ្យ​បញ្ឈប់​ការ​លួច​បើក​ច្រករបៀង​ខុសច្បាប់​ក្បែរ​ច្រក​ទ្វារ​អន្តរជាតិ​ប៉ោយ​ប៉ែ​ត ត្រូវ​មាន​វិធានការ​ទប់ស្កាត់​រាល់​បទ​ល្មើស​ឆ្លង​ដែន ។', 'http://kohsantepheapdaily.com.kh/article/127224.html', 'http://i.kspg.co/2015/8/d904d3a8-4fe9-4395-8a12-adfc281d8ddb.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៨:៥៧', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1282', 'B020101', 'B020000', 'ប្រធានសភា​ពាណិជ្ជកម្ម​និងឧស្សាហកម្មចិន​នៅហ្វីលីពីនស្វែងរក​ការវិនិយោគនៅកម្ពុជា', 'រាជធានីភ្នំពេញ ​​៖ ​នៅក្នុងឱកាសចូលជួប​សម្តែងការគួរសមនិងធ្វើសវនាការ​ជាមួយសម្តេចតេជោ ហ៊ុន សែន ​នារសៀលថ្ងៃទី២០សីហានេះ​ លោក Wong Cheunng Sha', 'http://kohsantepheapdaily.com.kh/article/127223.html', 'http://i.kspg.co/2015/8/2927f677-d9e1-4117-a306-f944545f701b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៨:១៥', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1283', 'B020101', 'B020000', 'សមត្ថកិច្ច​ចម្រុះបន្ត​បង្ក្រាប​ឈើប្រណីត​ មាន​ជន​ថ្លើម​ធំ​​បើក​​រថយន្ត​មក​រា​រាំង', 'ខេត្ត​ត្បូងឃ្មុំ​ ៖ ​កម្លាំង​សមត្ថកិច្ច​ចម្រុះ​ ​មាន​ នគរបាល ​រដ្ឋបាល​ព្រៃឈើ ​និង​ស្ថាប័ន​សាលា​ដំបូង​ខេត្ត ​បានបន្ត​ប្រតិបត្តិការ​ចុះ​បង្ក្រាប​បទល្មើស​ព្រៃឈើ​បាន​ចំនួន​៣​ករណី ​ក្នុង​នោះ​ មាន​​នៅ​ ស្រុក​មេមត់ ស្រុក​ពញាក្រែក និង​ក្រុង​សួង', 'http://kohsantepheapdaily.com.kh/article/127219.html', 'http://i.kspg.co/2015/8/95ed563d-017d-4977-9b2a-b14ca477f5a5.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:០៧', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1284', 'B020101', 'B020000', '​ធ្វើ​ដំណើរទៅ​​​លោតទឹក​សម្លាប់​ខ្លួន​ពីលើ​ស្ពាន​ជ្រោយចង្វារ តែមិន​បាន​សម្រេច', 'រាជធានី​ភ្នំពេញ ​៖ ​បុរស​មាន​ជំងឺ​សរសៃប្រសាទ​ម្នាក់​បាន​ធ្វើ​ដំណើរ​ទៅ​លោតទឹក​សម្លាប់​ខ្លួន​ពីលើ​ស្ពាន​ជ្រោយចង្វារ​ចាស់ ​កាលពី​វេលា​ម៉ោង​១​និង​៣០​នាទី​រសៀល​ថ្ងៃ​ទី​១៩​​សីហា​ ​២០១៥ ​តែ​មិន​បាន​សម្រេច​', 'http://kohsantepheapdaily.com.kh/article/127214.html', 'http://i.kspg.co/2015/8/9664670a-fe26-481e-bd43-e648b8a4f713.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:២០', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1285', 'B020101', 'B020000', 'កូន​ក្រែង​ឈាមជាង​មួយ​តោនបម្រុង​ឆ្លង​ដែន​ទៅ​វៀត​ណាម​ត្រូវ​សមត្ថ​កិច្ច​បង្ក្រាប​​​', 'ខេត្ត​ព្រះ​សីហនុ​ ៖ កូន​ក្រែង​ឈាម​(ងាវ)ចំនួន​២០​បាវទម្ងន់​ប្រមាណ​ជាង​មួយ​តោន​ត្រូវ​បាន​កំលាំង​នគរបាល​នាយកដ្ឋាន​ប្រឆាំង​បទល្មើស​សេដ្ឋកិច្ច​នៃ​ក្រសួងមហាផ្ទៃ​​បង្ក្រាប', 'http://kohsantepheapdaily.com.kh/article/127204.html', 'http://i.kspg.co/2015/8/a1cac777-942e-4dbf-b3ce-dbd051434526.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៣៣', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1286', 'B020101', 'B020000', 'លោក​ឧបនាយករដ្ឋមន្ត្រី ហោ ណាំ​ហុង ជួប​ជាមួយ​បេសកជន​នៃ​ក្រសួងការបរទេស​ចិន នា​រសៀល​នេះ', 'រាជធានី​ភ្នំពេញ ៖ លោក​ឧបនាយករដ្ឋមន្ត្រី ហោ ណាំ​ហុង រដ្ឋមន្ត្រី​ការ​បរទេស បាន​អនុញ្ញាត​ឱ្យ​លោក​បេសកជន​ទទួល​បន្ទុក​កិច្ចការ​អាស៊ី​នៃ​ក្រសួង​ការ​ប្រទេស​ចិន លោក SUN GUOXIANG ចូល​ជួប​សម្តែង​ការងា​គួរសម និង​ពិភាក្សា​នៅ​វេលា​ម៉ោង​០៣ និង​៣០​នាទី​រសៀល', 'http://kohsantepheapdaily.com.kh/article/127201.html', 'http://i.kspg.co/2015/8/3783d9c8-7813-4f26-b96b-7bb237ac4751.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:១៧', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1287', 'B020101', 'B020000', 'តាម​ហៅ​ក្មួយ​កំពុង​ផឹក​ឱ្យ​ទៅ​ជួយ​ភ្ជួរ​ស្រែ ត្រូវ​អ្នក​ផឹក​ជាមួយ​ក្មួយវាយ​ស្លាប់', 'ខេត្ត​កំពង់​ស្ពឺ ៖ មន្ត្រី​នគរបាល​ស្រុក​ភ្នំ​ស្រួច​បាន​បង្ហើប​ឱ្យដឹងថា​ ពេល​នេះ​សមត្ថ​កិ​ច្ច​កំពុង​តែ​ដុត​ដៃ​​​ដុត​ជើង​ក្នុង​ការ​តាម​ចាប់ខ្លួន​ជន​ដៃ​ដល់​ម្នាក់​ដែល​បាន​យក​ចបជីក​វាយ​​​អ្នក​ភូមិ​ជាមួយ​គ្នា​បណ្តាលឱ្យ​បែក​ក្បា​ល', 'http://kohsantepheapdaily.com.kh/article/127199.html', 'http://i.kspg.co/2015/8/ee5b0e6d-ee6d-48fa-b89b-aea6e91484df.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:០៦', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1288', 'B020101', 'B020000', 'ម៉ូតូ​២​គ្រឿង​ត្របាក់​គ្នា បណ្តាល​ឱ្យ​រង​របួសធ្ងន់​ម្នាក់', 'ខេត្តមណ្ឌលគិរី ៖ យុវជន​ម្នាក់​បាន​រង​របួសធ្ងន់ ក្រោយ​ម៉ូតូ​២​គ្រឿង​បុក​គ្នា​យ៉ាង​ពេញទំហឹង ហើយ​ត្រូវ​អ្នក​ឃើញ​ហេតុការណ៍​ជួយ បញ្ជូន​ទៅ​សង្គ្រោះ​មន្ទីរពេទ្យ​បង្អែក​ខេត្ត​ភ្លាម​ៗ ដែល​ហេតុការណ៍​នេះ​កើតឡើង​កាលពី​វេលា​ម៉ោង​០៨ និង​៣០​នាទី​យប់​ថ្ងៃ​ទី​១៩ សីហា ២០១៥ ស្ថិត​នៅ​តាម​បណ្តោយ​ផ្លូវជាតិ​លេខ​៧៦ ត្រង់​ចំណុច​ជិត​សឹក​រង​ក្រុង​សែន​មនោ​រម្យ ក្នុងភូមិ​ពូ​លុង សង្កាត់​រម្យ​នា ', 'http://kohsantepheapdaily.com.kh/article/127197.html', 'http://i.kspg.co/2015/8/d06138fd-4b27-47a8-9f11-acead0ff0a97.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:០៤', '0', '0', '0', 't', '2015-08-20', 'f');
