INSERT INTO "tbnews" VALUES ('1290', 'B020101', 'B020000', 'អគារ​រោងចក្រ​ប៊ី​យូ​ធី​ហ្វូ​ល​វី​ន​ដូត្រូវ​ក្រុម​កម្មករ​គប់​ដុំ​ថ្ម​រង​ការ​ខូចខាត​ បន្ទាប់​ពី​ការ​តវ៉ាគ្មាន​ដំណោះស្រាយ​', 'ខេត្តស្វាយរៀង​ ៖ ​អគារ​រោងចក្រ​ប៊ី​យូ​ធី​ហ្វូ​ល​វី​ន​ដូរ ​ផលិត​ស្លាប​ព្រឹល​(​ផលិត​វាំងនន​)​ចំនួន​៤​ត្រូវ​ក្រុម​កម្មក-​កម្ម​ការ​នី​ប្រមាណ​ជាង​៣០០​នាក់​ប្រើ​ហិង្សា​​គប់នឹង​ដុំ​ថ្ម​បែក​កញ្ចក់', 'http://kohsantepheapdaily.com.kh/article/127191.html', 'http://i.kspg.co/2015/8/90e9054f-1861-4732-95e5-c7225559f56c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៣៣', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1291', 'B020101', 'B020000', '​លោក ស ថាវី ដឹក​នាំ​មន្ត្រី​​ទៅ​ចូល​រួម​​​ពិធី​សម្ពោធ​ទីក្រុង​ថ្មី​របស់ថៃនៅ​​ច្រក​អន្តរជាតិ​​ជាំ​ស្រង៉ាំ', '​ខេត្ត​ឧត្តរមានជ័យ ៖​ លោក​ ស ថា​វី​ អភិបាលខេត្ត​​ ​បាន​ដឹកនាំ​មន្ត្រីរាជការ​គ្រប់​ស្ថា​ប័ន ​​ព្រម​ទាំង​​មេបញ្ជាការ​កង​កម្លាំងប្រដាប់អាវុធ​គ្រប់​ប្រភេទ ​អញ្ជើញ​ទៅ​ចូលរួម​ក្នុង​ពិធី​សម្ពោធសមិទ្ធផល​នានា និង​​ទីក្រុង​ថ្មី​​របស់​ព្រះរាជា​អាណាចក្រ​ថៃ​​ ', 'http://kohsantepheapdaily.com.kh/article/127188.html', 'http://i.kspg.co/2015/8/ddf1cd90-95a3-46ed-a813-bb34dfca9689.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:១៧', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1292', 'B020101', 'B020000', 'ប្រកាស​ផ្ទេរតំណែង និង​បំពាក់​ឋានន្តរស័ក្តិ​មន្ត្រី​នគ​បាល​ជាតិ​នៃ​ស្នងការ​ដ្នា​ន​នគ​បាល​ខេត្តស្វាយរៀង', 'ខេត្តស្វាយរៀង ៖ កាលពី​វេលា​ម៉ោង​០៨​ព្រឹក​ថ្ងៃ​ទី​២០ សីហា ២០១៥ នេះ មាន​រៀបចំ​ពិធី​ប្រកាស​ផ្ទេរ​ភារកិច្ច​តែងតាំង​មុខ​តំណែង និង​បំពាក់​ឋានន្តរស័ក្តិ​មន្ត្រី​នគ​បាល​ជាតិ​នៃ​ស្នងការដ្ឋាន​នគ​បាល​ខេត្តស្វាយរៀង ក្រោម​អធិបតីភាព​លោកនាយ​ឧត្តមសេនីយ៍​ផ្កាយ​មាស​៤ ចាន់ អ៊ា​ន អគ្គ​រង​នៃ​អគ្គស្នងការ​នគ​បាល​ជាតិ​, លោក​ម៉ែ​ន វិបុល អភិបាលរង​ខេត្ត ព្រមទាំង​មន្ត្រី​នគរបាល​ខេត្ត​-​ក្រុង​-​', 'http://kohsantepheapdaily.com.kh/article/127187.html', 'http://i.kspg.co/2015/8/ce6b5c2c-77ab-4004-a298-2f6bf2620a07.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:០៩', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1293', 'B020101', 'B020000', 'សម្តេចតេជោ​ ហ៊ុន សែន​​ ទទួល​ជួបសមាជិក៥០រូប​នៃសមាគម​សភាចិន​នៅហ្វីលីពីនរសៀលនេះ', 'រាជធានីភ្នំពេញ ៖​ បើគ្មានអ្វីប្រែប្រួលនោះទេ​ សម្តេច​អគ្គមហាសេនាបតីតេជោ​ ហ៊ុន សែន​ នឹងអនុញ្ញាតឲ្យ​សមាជិកចំនួន៥០រូប​នៃសមាគមសភាចិន​ក្នុងប្រទេសហ្វីលីពីន ', 'http://kohsantepheapdaily.com.kh/article/127184.html', 'http://i.kspg.co/2015/8/9f9420c8-bb22-40e3-9088-cacac14980ec.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៥៣', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1294', 'B020101', 'B020000', 'បញ្ជូន​ជនសង្ស័យ​៣​នាក់​ជាប់​ពាក់ព័ន្ធ​បទល្មើស​ផ្សេង​គ្នា​ទៅ​ពន្ធនាគារ​ខេត្ត', 'ខេត្ត​កំ​ពង​ចាម ៖ ក្រោម​កិច្ចសហការ​រវាង​កម្លាំង​កងរាជអាវុធហត្ថ​ខេត្តកំពង់ចាម ជាមួយ​និង​សមត្ថកិច្ច​តាម​បណ្តា​ខេត្តសៀមរាប និង​​ខេត្តបាត់ដំបង​តាម​ប្រមាញ់​ចាប់​ជនសង្ស័យ​ជា​បន្តបន្ទាប់​បាន​ចំនួន​៣​នាក់ ក្នុង​នោះ​មាន​១-​ជាប់​ករណី​ជួញដូរ និង​រក្សា​ទុក​នូវ​សារ​ធាតុ​ញៀន ២-​ទទួលផល​ចោរកម្ម និង​ឆបោក​ប្រាក់​ចំនួន​ជាង​១០​ម៉ឺន​ដុល្លារ ត្រូវ​បាន​បញ្ជូន​ទៅ​កាន់​ពន្ធនាគារ​ខេត្ត កាលពី​', 'http://kohsantepheapdaily.com.kh/article/127183.html', 'http://i.kspg.co/2015/8/53cb9305-41ed-4c70-a42b-49a390c62167.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៥០', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1295', 'B020101', 'B020000', 'កិច្ចប្រជុំ​បូក​សរុប​ស្តី​ពី​ការ​អនុវត្ត​គោលនយោបាយ​ភូមិ/ឃុំមាន​សុវត្ថិភាព', 'ខេត្តសៀមរាប​​ ៖ កិច្ចប្រជុំ​​​បូក​សរុប​មួយ ត្រូវ​បាន​រៀបចំ​ឡើង​នៅ​សាល​ប្រជុំ​ស្នងការដ្ឋាន​នគរបាល​ខេត្ត​ ​កាលពី​ព្រឹក​ថ្ងៃ​ទី​១៨​ សីហា​​ ​ក្រោម​កិច្ច​ដឹកនាំ​ដោយ លោក​ឩ​ត្តម​សេនីយ៍​ទោ សត ណា​ឌី ​ស្នងការ​នគរបាល​ខេត្ត​', 'http://kohsantepheapdaily.com.kh/article/127182.html', 'http://i.kspg.co/2015/8/72103e4c-3df6-4661-af96-f5a6c8ef8f2b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៤៦', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1296', 'B020103', 'B020000', 'គណបក្ស​​ហ្វ៊ុនស៊ិនប៉ិចថា​ ​ការផ្ទៀងផ្ទាត់ផែនទី​ព្រឹកមិញប្រព្រឹត្ត​ទៅដោយតម្លាភាព​និងគណនេយ្យភាព', 'រាជធានីភ្នំពេញ ៖​ គណបក្សហ្វ៊ុនស៊ិនប៉ិច​បានចេញសេចក្តីប្រកាស​ព័ត៌មាននៅរសៀលថ្ងៃទី២០​សីហានេះដោយបញ្ជាក់ថា ​ការផ្ទៀងផ្ទាត់ផែនទី​នំាមកដោយ​តំណាង​អង្គការ', 'http://kohsantepheapdaily.com.kh/article/127216.html', 'http://i.kspg.co/2015/8/45f83bcc-42aa-4227-9ea8-6f277626f7fb.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:៤៤', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1297', 'B030101', 'B030000', 'មន្រ្តីរាជការក្រសួង យុត្តិធម៌ ប្រកាសគាំទ្រ រាជរដ្ឋាភិបាល លើយន្តការដោះស្រាយ បញ្ហាព្រំដែន', 'null', 'http://www.dap-news.com/2011-06-14-02-39-55/97533-2015-08-20-17-37-37.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/SR-018.jpg', 'Friday, 21 August 2015 00:36', '1', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1298', 'B030101', 'B030000', 'ព្រះមហាក្សត្រ ទទួលស្គាល់ថា សន្ធិសញ្ញាកំណត់ ព្រំដែនឆ្នាំ១៩៨៥ មិនបានធ្វើឲ្យ បាត់បង់ទឹកដី', '<b>ភ្នំពេញ៖</b> ព្រះមហាក្សត្រខ្មែរ ព្រះករុណា ព្រះបាទ សម្តេច ព្រះបរមនាថ នរោត្តម សីហមុនី បានទទួលស្គាល់ថា សន្ធិសញ្ញាកំណត់ព្រំដែនឆ្នាំ១៩៨៥ រវាងរាជរដ្ឋាភិបាលកម្ពុជា និងរដ្ឋាភិបាលវៀតណាម គឺត្រឹមត្រូវ មិនបានធ្វើឲ្យ កម្ពុជា បាត់បង់ទឹកដីនោះឡើយ។ ព្រះអង្គ ក៏បានឡាយព្រះហស្ថលេខាដាក់ឲ្យប្រើប្រាស់ ច្បាប់ស្តីពីការអនុម័ត យល់ព្រមលើសន្ធិសញ្ញាបំពេញបន្ថែមរវាងព្រះរាជាណាចក្រកម្ពុជា និង ប្រទេសវៀតណាមផងដែរ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97531-2015-08-20-15-43-09.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/SR-017.jpg', 'Thursday, 20 August 2015 22:42', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1299', 'B030101', 'B030000', 'លោក សុខ ទូច ៖ មានតែផែនទី រាជរដ្ឋាភិបាលទេ អាចប្រើសម្រាប់ បោះបង្គោលព្រំដែន កម្ពុជា-វៀតណាម', '<b>ភ្នំពេញ៖</b> លោកបណ្ឌិត សុខ ទូច ប្រធានស្រាវជ្រាវព្រំដែនកម្ពុជា នៃរាជ បណ្ឌិត្យសភា បានចេញមកអះអាងថា មានតែផែនទីរបស់រាជរដ្ឋាភិបាលទេ ដែលអាចប្រើប្រាស់សម្រាប់បោះ បង្គោលព្រំដែនរវាងប្រទេសកម្ពុជា ជាមួយ ប្រទេសវៀតណាម គ្រាដែលផែនទីរបស់គណបក្សសង្រ្គោះជាតិ និង យកមកពីអង្គការសហប្រជាជាតិ នៅខ្វះ ចន្លោះមិនអាចប្រើជាការសម្រាប់បោះបង្គោលព្រំដែនជាមួយវៀតណាម នោះឡើយ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97530-2015-08-20-15-04-13.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/SR-013.jpg', 'Thursday, 20 August 2015 22:02', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1300', 'B030101', 'B030000', 'រថយន្តដឹកទំនិញ រអិលធ្លាក់ផ្លូវ ខ្លួនឯងសំណាង មិនមានគ្រោះថ្នាក់ ដល់មនុស្ស', '<strong>បាត់ដំបង៖</strong>&nbsp; រថយន្តធុនធំដឹកទំនិញ របស់ថៃ១គ្រឿងបើកបរ ពីស្រុកភ្នំព្រឹកឆ្លង មកស្រុកសំពៅលូន បានរអិលធ្លាក់ ផ្លូវខ្លួនឯងសំណាងល្អ មិនមានគ្រោះថ្នាក់ ដល់មនុស្សខណៈពេល មេឃកំពុងភ្លៀង។', 'http://www.dap-news.com/2011-06-14-02-39-55/97527-2015-08-20-13-45-18.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00525.jpg', 'Thursday, 20 August 2015 20:39', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1301', 'B030101', 'B030000', 'ចប់ការប្រកួត ពេញម៉ោង៖ កម្ពុជា ២-០ ប៊ូតាន់', '<strong>ភ្នំពេញ៖</strong> ក្រុមបាល់ទាត់ ជម្រើសជាតិកម្ពុជា បានយកឈ្នះ ក្រុមភ្ញៀវប៊ូតាន់ ដោយលទ្ធផលបច្ចេកទេស ២-០ នៅពហុ កីឡដ្ឋានជាតិអូឡាំពិកកម្ពុជា អំបាញ់មិញនេះ នៅពីមុខអ្នក ទស្សនាប្រមាណ ជាង៤ម៉ឺននាក់ នៃជំនួបមិត្តភាពអន្តរជាតិ លើកទី១ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97526-2015-08-20-13-22-45.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00523.jpg', 'Thursday, 20 August 2015 20:20', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1302', 'B030101', 'B030000', 'ក្រុមប្រឹក្សាសកម្មភាព ជនពិការ ចុះផ្សព្វផ្សាយការងារ ពិការភាព នៅខេត្តកណ្តាល', '<b>កណ្តាល៖</b> ក្រុមប្រឹក្សាសកម្មភាពជនពិការ បានបន្តបេសកកម្មផ្សព្វផ្សាយងារការពិការភាព ទៅដល់ខេត្តកណ្តាល&nbsp; ហើយនៅព្រឹកថ្ងៃទី២០ ខែសីហា ឆ្នាំ២០១៥នេះ ក្នុងបំណងទាញយក និងស្វែងរកនូវឧត្តមប្រយោជន៍ ជូនដល់ ជនពិការនៅក្នុងខេត្តកណ្តាល។', 'http://www.dap-news.com/2011-06-14-02-39-55/97523-2015-08-20-12-11-16.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/ST-170.jpg', 'Thursday, 20 August 2015 19:10', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1303', 'B030101', 'B030000', 'លោក សម រង្ស៊ី និងលោក កឹម សុខា ជួបថ្នាក់ដឹកនាំរដ្ឋ មួយចំនួន និងសមាជិកសភា នៅអូស្ត្រាលី', '<strong>ភ្នំពេញ៖</strong> លោក សម រង្ស៊ី និងលោក កឹម សុខា ថ្នាក់ដឹកនាំគណបក្សសង្រ្គោះជាតិ នៅថ្ងៃទី២០ ខែសីហា ឆ្នាំ២០១៥នេះ បានជួបសំណេះសំណាលជាមួយ ថ្នាក់ដឹករដ្ឋមួយចំនួន និងសមាជិកសភានៅក្នុងប្រទេសអូស្តាលី ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97522-2015-08-20-12-00-33.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0516.jpg', 'Thursday, 20 August 2015 18:59', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1304', 'B030101', 'B030000', 'សហព័ន្ធ ស្រូវអង្ករកម្ពុជា ស្នើសុំ អគ្គនាយកដ្ឋានគយ និងរដ្ឋាករកម្ពុជា អន្តរាគមន៍៣សំខាន់ៗ ដើម្បីគាំទ្រ វិស័យស្រូវអង្ករកម្ពុជា', '<strong>ភ្នំពេញ៖</strong> ក្រុមការងារ សហព័ន្ធស្រូវអង្ករកម្ពុជា ដែលដឹកនាំដោយប្រធានសហព័ន្ធលោក សុខ ពុទ្ធីវុធ នៅថ្ងៃទី២០ ខែសីហា ឆ្នាំ២០១៥ បានអញ្ជើញចូលរួម នៅក្នុងកិច្ចប្រជុំពិភាក្សាការងារមួយ ជាមួយថ្នាក់ដឹកនាំអគ្គនាយកដ្ឋានគយ និងរដ្ឋាករកម្ពុជា ដឹកនាំដោយលោក គុណ ញឹម អគ្គនាយក នៃអគ្គនាយកដ្ឋានគយនិងរដ្ឋាករកម្ពុជា។', 'http://www.dap-news.com/2011-06-14-02-39-55/97521-2015-08-20-11-53-05.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0513.jpg', 'Thursday, 20 August 2015 18:52', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1305', 'B030102', 'B030000', 'កូរ៉េខាងជើង ព្រមាន នៅតែបន្ត បាញ់ប្រហារ, បើសិនជា កូរ៉េខាងត្បួង មិនបញ្ឈប់ការចាក់ ផ្សាយសំឡេងឃោសនា នៅព្រំដែន', 'null', 'http://www.dap-news.com/2011-06-14-02-40-26/97524-2015-08-20-12-38-48.html', 'img/dapnews.png', 'Thursday, 20 August 2015 19:34', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1306', 'B030102', 'B030000', 'កូរ៉េខាងជើង និងខាងត្បូង ផ្ទុះសង្រ្គាម បាញ់ផ្លោងគ្រាប់ដាក់គ្នា ខណៈប្រជាជនត្រូវបានជម្លៀស', '<b>សេអ៊ូល៖</b> បន្ទាប់ពីការកំញើញកម្លាំងដាក់គ្នាអស់ជាច្រើនថ្ងៃនោះ នៅថ្ងៃនេះកម្លាំងយោធាកូរ៉េខាងជើង និង ខាង ត្បូង បានឈានដល់ការផ្ទុះសង្រ្គាម និងបាញ់ផ្លោងគ្រាប់ដាក់គ្នាជាច្រើនគ្រាប់ ហើយប្រជាជននៅតាមបន្ទាត់ព្រំ &nbsp;ដែន កន្លែងផ្ទុះជម្លោះ ក៏ត្រូវបានជម្លៀសចេញពីតំបន់បាញ់ផ្លោងគ្រាប់ផងដែរ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97520-2015-08-20-11-01-07.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/ST-164.jpg', 'Thursday, 20 August 2015 18:00', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1307', 'B030102', 'B030000', '២៤នាក់ស្លាប់ និងបាត់ខ្លួន ដោយសារព្យុះភ្លៀង នៅភាគនីរតី ប្រទេសចិន', '<strong>ប៉េកាំង៖</strong> យ៉ាងហោចណាស់ មនុស្ស១១នាក់ស្លាប់ និង១៣នាក់ផងទៀត បាត់ខ្លួន បន្ទាប់ពីព្យុះ ភ្លៀង យ៉ាងខ្លាំង បានវាយប្រហារ នៅខេត្ដ ស៊ីឈួន(Sichuan) ភាគនីរតីប្រទេសចិន ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97516-2015-08-20-09-55-00.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-114.jpg', 'Thursday, 20 August 2015 16:53', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1308', 'B030102', 'B030000', 'អ្នកប្រមាញ់ ដ៏សាហាវ នាសម័យបុរាណ ត្រូវបានរកឃើញ', '<strong>ស្កុតឡេន៖</strong> ក្រុមអ្នកបុរាណ លេខសាស្រ្ត បាននិយាយថា សំណល់ហ្វូស៊ីល មួយនៅក្នុង សារមន្ទីរ ស្កុតឡេន ដែលត្រូវបានរកឃើញ តាំងពីមួយសតវត្សរ៍មុន ត្រូវបានកំណត់ថា ជាអ្នកប្រមាញ់ នៅក្នុងទឹកយ៉ាងសាហាវ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97514-2015-08-20-09-32-36.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-113.jpg', 'Thursday, 20 August 2015 16:28', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1309', 'B030102', 'B030000', 'អូស្រ្តាលី-ឥណ្ឌូណេស៊ី នឹងរួមគ្នាធ្វើជាម្ចាស់ផ្ទះ នៃកិច្ចប្រជុំកំពូល ប្រឆាំងភេរវកម្ម', '<strong>កង់បេរ៉ា៖</strong> នៅព្រឹក ថ្ងៃព្រហស្បតិ៍នេះ ប្រទេសអូស្រ្តាលី និងឥណ្ឌូណេស៊ី បានព្រមព្រៀងថា នឹងរួមគ្នាធ្វើជា ម្ចាស់ផ្ទះក្នុងកិច្ចប្រជុំ កំពូលស្ដីពីការប្រយុទ្ធប្រឆាំង អំពើភេរវកម្មអន្ដរជាតិ ក្នុងគោលបំណង ការពារ ពីការ ផ្តល់មូលនិធិរបស់ អង្គការជ្រុលនិយម ទៅកាន់ក្រុមភេរវករ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97505-2015-08-20-08-30-49.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-098.jpg', 'Thursday, 20 August 2015 15:29', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1310', 'B030102', 'B030000', 'ការសិក្សា៖ កំដៅចេញ ពីទីក្រុង អាចប៉ះពាល់ដល់ សីតុណ្ហភាព ភពផែនដី', '<strong>ខូឡូរ៉ាដូ៖</strong> ក្រុមអ្នកស្រាវជ្រាវ ជនជាតិ អាមេរិក បាននិយាយថា ការប្រើប្រាស់ ថាមពលនៅ ក្នុង បណ្តាទីក្រុងទូទាំងពិភពលោក អាចនាំឲ្យមានផលប៉ះពាល់ ដល់អាកាសធាតុដោយនាំឲ្យ សីតុណ្ហភាព របស់ភពផែនដី កាន់តែក្តៅឡើងៗ បើទោះបីជានៅក្នុងរដូវរងាយ៉ាង ណាក៏ដោយ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97501-2015-08-20-07-34-48.html', 'http://www.nasa.gov/images/content/324350main_11_full.jpg', 'Thursday, 20 August 2015 14:34', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1311', 'B030102', 'B030000', 'រុស្ស៊ីសន្យា ផ្តល់ឧទ្ធម្ភាគចក្រ ប្រភេទ Mi-35M ៤គ្រឿង ទៅប៉ាគីស្ថាន', '<strong>អ៊ីស្លាម៉ាបាត៖</strong> យោងតាមប្រព័ន្ធ ផ្សព្វផ្សាយក្នុងស្រុកមួយ បានឲ្យដឹងថា ប្រទេសប៉ាគីស្ថាន និងរុស្ស៊ី បាន ចុះកិច្ចព្រមព្រៀងផ្ដល់ ឧទ្ធម្ភាគចក្រប្រយុទ្ធចំនួន ៤គ្រឿង ដើម្បីប្រើប្រាស់ នៅសមរភូមិ ក្នុងប្រទេស។', 'http://www.dap-news.com/2011-06-14-02-40-26/97482----mi-35m--.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-085.jpg', 'Thursday, 20 August 2015 11:24', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1312', 'B030302', 'B030000', 'ជម្រើសជាតិកម្ពុជា ប៉ះ ប៊ូតាន់ ជាប្រវត្តិសាស្រ្តល្ងាចនេះ នៅស្តាតអូឡាំពិក', '<strong>ភ្នំពេញ៖</strong> នេះជាជំនួបលើកទី១ប្រវត្តិសាស្រ្ត ដែលក្រុមបាល់ទាត់ជម្រើសជាតិកម្ពុជា បានជួបប្រកួតជាមួយនឹងក្រុមស្ថិត ក្នុងទ្វីបអាស៊ីដូចគ្នា ប៊ូតាន់ ក្នុងមិត្តភាពអន្តរជាតិនាម៉ោង ៦៖៣០នាទីល្ងាចនេះ នៅពហុកីឡដ្ឋានស្មៅសិប្បនិម្មិតអូ ឡាំពិកកម្ពុជា ។', 'http://www.dap-news.com/morokatmagazine/97491-2015-08-20-05-27-30.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-140/hour-01650.jpg', 'Thursday, 20 August 2015 12:22', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1313', 'B010105', 'B010000', 'តិច​អត់​ដឹង​ ហ្វេសប៊ុក​អាច​ឲ្យ​អ្នក​ Forward ​រូប​ភាព​បាន​យ៉ាង​ងាយ​?', 'ចុច​លើ​រូប​ដែល​គេ​ផ្ញើ​មក​នោះ​ នោះ​អ្នក​នឹង​ឃើញ​ពាក្យ​ Forward ​នៅ​ក្បែរ​ Download ​មិន​ខាន។ ចុច​លើ​ពាក្យ​ Forward ​នោះ...', 'http://news.sabay.com.kh/article/457134', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/featured111-285x170.jpg?ebb82d', '21 August, 2015', '0', '0', '0', 't', '2015-08-21', 'f');
