INSERT INTO "tbnews" VALUES ('1377', 'B020102', 'B020000', 'នារី​៣​នាក់ជិះ​ម៉ូតូ​តាម​ផ្លូវត្រូវ​ចោរឆក់​កាបូប​ដួល​បោក​ក្បាល​​រង​របួ​ស​ទាំង៣​នាក់', '​រាជ​ធានី​ភ្នំពេញ ៖​ ​នារី​៣​នាក់​​ជិះ​ម៉ូតូ​មួយ​គ្រឿង ត្រូវ​ចោរឆក់​កាបូបស្ពាយ​បណ្តាល​ឲ្យ​ដួល​ម៉ូតូ​របួសធ្ងន់​​​ស្រាល​​ទាំង​៣​នាក់ កាលពី​វេលា​ម៉ោង​៥​ភ្លឺ​​ថ្ងៃ​ទី​២១ ​សីហា ២០១៥ ​', 'http://kohsantepheapdaily.com.kh/article/127250.html', 'http://i.kspg.co/2015/8/ee110e4d-ab3b-4b12-b08e-10c151661b8c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣៩', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1378', 'B020102', 'B020000', 'ចូល​ទិញ​ថ្នាំពេទ្យ​ក្នុង​ឱសថស្ថានភ្លេច​ចាក់​សោទ្វារ​រថយន្ត​ ត្រូវ​ចោរ​ចូល​លួច​​កាបូប​លុយទៅបាត់', 'ខេត្តកំពង់ឆ្នាំង ​៖ ស្ត្រី​អ្នក​ទីក្រុង​ភ្នំពេញ​ម្នាក់​ត្រូវ​ចោរលួច​កាបូប​ក្នុង​រថយន្ត​បាត់​លុយ​ជាង​២​ពាន់​ដុល្លារ ខណៈ​ពេល​គាត់​ឈប់​រថយន្ត​ភ្លេច​ចាក់សោទ្វារ​រថយន្ត​', 'http://kohsantepheapdaily.com.kh/article/127243.html', 'http://i.kspg.co/2015/8/45b78283-66ec-4ce0-abc0-c2822b395691.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:១៦', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1379', 'B020102', 'B020000', 'ចោរ​​​​​ជិះ​ម៉ូតូ​យ៉ាង​កញ្ចាស់​ឆក់​ទូរស័ព្ទ​បាន​​សម្រេច តែធ្លាក់​ជង្ហុករលត់​​រត់​ចោល​ម៉ូតូ', '​រាជ​ធានីភ្នំពេញ ៖ ស្ត្រី​ជា​កម្មការិនី​កាត់ដេរ​ម្នាក់​ដើរ​តាម​ផ្លូវ​បណ្តើរ​ឆែក​មើល​ហ្វេ​សប៊ុក​​តាម​ទូរស័ព្ទ​បណ្តើរ​ ត្រូវ​ជន​​២​នាក់​ឆក់​ទូរស័ព្ទ​បាន​សម្រេច​​ហើយ​រត់ឡើង​ជិះ​ម៉ូតូ​សង់​កូរ៉េ​កញ្ចាស់​មួយ​គ្រឿង', 'http://kohsantepheapdaily.com.kh/article/127242.html', 'http://i.kspg.co/2015/8/ab1a8484-24a6-40f6-9af7-3b7f2efc55ac.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:១៥', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1380', 'B020102', 'B020000', 'ទំនាស់​ក្នុង​ពិធី​រាំ​ធុងបាស់​មង្គលការ​នៅ​ស្រុក​ថ្មពួក របួសធ្ងន់​ស្រាល​២​នាក់', 'ខេត្តបន្ទាយមានជ័យ ៖ យុវជន​២​នាក់​បាន​រង​របួសធ្ងន់ និង​ស្រាល ដោយសារ​តែ​ទំនាស់​គ្នា​ក្នុង​ពិធី​រាំ​ធុងបាស់​នៃ​ពិធី​មង្គលការ​មួយ​កាលពី​វេលា​ម៉ោង​៩ និង​៣០​នាទី​យប់​ថ្ងៃ​ទី​១៩ សីហា ២០១៥ នៅ​ភូមិ​គំរូ ឃុំ​គំរូ ស្រុក​ថ្មពួក ខេត្តបន្ទាយមានជ័យ ។', 'http://kohsantepheapdaily.com.kh/article/127229.html', 'http://i.kspg.co/2015/8/6bfb3267-2f13-4b2b-9fa9-863917e6b052.jpg?w=285&h=170&mode=carve', 'ថ្ងៃព្រហស្បត្ត៏ ទី២០ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២២:០២', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1381', 'B020101', 'B020000', 'មន្ទីរ​ឧស្សាហកម្ម​ខេត្ត​ព្រះ​សីហ​នុស្នើ​ទៅ​ក្រសួង​រៀបចំ​ដែនដី​បញ្ឈប់​ការ​ចេញ​ប្លង់​កម្មសិទ្ធិ និង​ការ​សាងសង់​ជុំវិញ​នៅ​បឹង​ព្រែក​ទប់', 'ខេត្ត​ព្រះ​សីហ​នុ ៖ ប្រធាន​មន្ទីរ​ឧស្សាហកម្ម និង​សិប្បកម្ម លោក​ប្រាក់ ប្រាកដ បាន​ចេញ​លិខិត​ជូនដំណឹង​ទៅ​កាន់​មន្ទីរ​រៀបចំ​ដែន​ដី​នគរូបនីយកម្ម និង​សំណង់ ដោយ​ស្នើរ​ឱ្យ​មន្ទីរ​ជំនាញ​បញ្ឈប់​នូវ​ការ​ចេញ​ប្លង់​កម្មសិទ្ធិ និង​ច្បាប់​សាងសង់​គ្រប់​ប្រភេទ​នៅ​លើ​ផ្ទៃដី​ជុំវិញ​បឹង​ព្រែក​ទប់ ក្នុង​គោលបំណង​ការពារ​បរិស្ថាន និង​ប្រភព​ទឹក​ស្អាត ដែល​ជា​ប្រភព​ផ្គត់ផ្គង់​ទឹក​ស្អា​តទី​២ បន្ទាប់​ពី​តំបន់​ក្បាល​', 'http://kohsantepheapdaily.com.kh/article/127276.html', 'http://i.kspg.co/2015/8/70925363-3f74-45b9-928f-76d929e554b0.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:២៣', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1382', 'B020101', 'B020000', '​​ថ្នាក់ដឹកនាំ​ខេត្ត​ជំរុញ​ឱ្យ​មន្ត្រី​ជំនាញ​ទប់ស្កាត់​សំណង់​គ្មាន​ច្បាប់​​ដែល​​សង់​រំលោភ​ចំណី​ផ្លូវ', 'ខេត្តបន្ទាយមានជ័យ​​ ៖ ថ្នាក់ដឹកនាំ​ខេត្ត​ជំរុញ​ឱ្យ​​មន្ត្រី​ជំនាញ​ទប់ស្កាត់​សំណង់​គ្មាន​ច្បាប់​​ដែល​​សង់​រំលោភ​ចំណី​ផ្លូវ ត្រូវ​បាន​លើក​ឡើង​ក្នុង​សិក្ខាសាលា​ស្តីពី​ការ​ផ្សព្វផ្សាយ​និង​អនុវត្តន៍​សារាចរ​០៣', 'http://kohsantepheapdaily.com.kh/article/127274.html', 'http://i.kspg.co/2015/8/c8ee9062-d85f-4a5f-aca8-0833e7355eaa.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:១៩', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1383', 'B020101', 'B020000', 'ប្រតិ​បត្តិ​ការ​បង្ក្រាប​គ្រឿង​ញៀននៅ​តំបន់​ត្រពាំង​ឈូក​បាន​បញ្ចប់​ហើយ', 'រាជ​ធានី​ភ្នំពេញ​ ៖ សេចក្តីរាយ​ការណ៍​បាន​ឱ្យ​ដឹង​ថា ប្រតិ​បត្តិ​ការ​បង្ក្រាប​គ្រឿង​ញៀន​ទ្រង់​ទ្រាយ​ធំ​នៅ​តំបន់​ត្រពាំង​ឈូកបាន​បញ្ចប់​ហើយ​នៅ​វេលា​ម៉ោង​២រសៀល​ថ្ងៃ​ទី ២១ សីហា ', 'http://kohsantepheapdaily.com.kh/article/127273.html', 'http://i.kspg.co/2015/8/2335c422-6a49-448c-a460-96c931346317.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:១១', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1384', 'B020101', 'B020000', 'ផឹកស៊ី​នៅ​កន្លែង​លក់​សាច់​ឆ្កែ ស្រវឹង​ដណ្តើម​ក្បាល​មីក្រូហ្វូន​គ្នា​ច្រៀង​ផ្ទុះ​ហិង្សា', 'រាជធានី​ភ្នំពេញ ៖ បុរស​៣​នាក់​ត្រូវ​កម្លាំង​ប៉ូលិស​ខណ្ឌដង្កោ​ធ្វើការ​ឃាត់ខ្លួន បន្ទាប់​ពី​ពួក​គេ​កាន់​កាំបិត​គ្រប់​ដៃ​ដេញ​កាប់សម្លាប់ យុវជន​ម្នាក់ ព្រោះតែ​រឿង​ទំនាស់​ដណ្ដើម​ក្បាល​មីក្រូហ្វូន​គ្នា​ច្រៀង​ខារ៉ាអូខេ​នៅ​កន្លែង​លក់​សាច់​ឆ្កែ​មួយ​កន្លែង ប៉ុន្តែ​ហេតុការណ៍​នោះ​មិន​បាន​បណ្ដាល​ឱ្យ​ជន​រង​គ្រោះ​រង​របួស​នោះ​ឡើយ ដោយ​បាន​ម្ចាស់ផ្ទះ​ជួយ​ឃាត់​ទាន់ ហើយ​ត្រូវ​កម្លាំង​ប៉ូលិស​ចាប់​ខ្លូ​ន​បាន​', 'http://kohsantepheapdaily.com.kh/article/127272.html', 'http://i.kspg.co/2015/8/2272440d-3a5f-405b-ac62-0835ee5ce122.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:០៧', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1385', 'B020101', 'B020000', 'មក​ដល់​ម៉ោងនេះ​សមត្ថ​កិច្ច​ឃាត់​ក្រុម​ប្រើ​ប្រាស់​ និង​ជួញ​ដូរ​គ្រឿង​ញៀន​នៅ​តំបន់​ត្រពាំង​ឈូក​​បាន​ជិត​១០០​នាក់​ហើយ', 'រាជ​ធានី​ភ្នំពេញ​ ៖ តាម​សេចក្តី​រាយការណ៍បាន​ឱ្យ​ដឹង​ថា មក​ដល់​ម៉ោង​នេះ នគរ​បាល​ប្រឆាំង​គ្រឿង​ញៀន រួម​ទាំង​សមត្ថ​កិច្ច​ និងអាជ្ញា​ធរ​ខណ្ឌ​សែ​នសុខ​ដែល​បាន​ចុះ​រដ្ឋ​បាល', 'http://kohsantepheapdaily.com.kh/article/127271.html', 'http://i.kspg.co/2015/8/1e850008-f3a4-4d9a-a6f8-e74034cbe96e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៤៨', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1386', 'B020101', 'B020000', '​រថយន្ត​​មន្ត្រី​​នគរ​បាល​ម្នាក់ ក្លាយ​ជា​ខ្មោច​រថយន្ត​​​នៅ​លើ​ផ្លូវជាតិ​លេខ​៣៣', '​ខេត្ត​កែប ៖ ​រថយន្តម៉ាក​​កាមរី​​ ពណ៌សម្បក​ឪឡឹក ពាក់​ស្លាក​លេខ​​បាត់​ដំបង 2A-0394 ​ស៊េរី​ឆ្នាំ​1993 ​អ្នក​បើក​បរ​​​​ឈ្មោះ ​ងិន ចិន្តា ​ភេទ​ប្រុស អាយុ​៣១​ឆ្នាំ ​​​ជា​មន្ត្រី​នគរ​បាល​ខេត្ត​​កែប', 'http://kohsantepheapdaily.com.kh/article/127270.html', 'http://i.kspg.co/2015/8/b68021d4-7634-43b5-abc7-5b05550d0e48.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១២:៥៧', '0', '0', '0', 't', '2015-08-21', 'f');
