INSERT INTO "tbnews" VALUES ('1399', 'B020101', 'B020000', 'ប៉ូលិស​មូលដ្ឋាន និង​ប៉ូលិស​អន្តោប្រវេសន៍​ដណ្តើម​ស្នាដៃ​គ្នា ក្រោយ​ចាប់​បាន​ចោរ​ម្នាក់', 'ខេត្តបន្ទាយមានជ័យ​ ៖ ប៉ូ​លិស​ប៉ុស្តិ៍​ស​ង្កាត់​ប៉ោយ​ប៉ែ​ត​ បានទាស់​គ្នា​​បែកផ្សែង​ជាមួយ​ប៉ូ​លិស​អន្តោប្រវេសន៍​រឿង​​កាង​ចោ​រលួច​ទូរ​ ស័ព្ទ​​​​ភ្ញៀវ​ស្ត្រី​ថៃ ​ដែល​​បាន​កើតឡើង​​កាលពី​​ថ្ងៃ​ទី​១៩ ​សីហា​ ២០១៥ ​​កន្លងមក។', 'http://kohsantepheapdaily.com.kh/article/127247.html', 'http://i.kspg.co/2015/8/48492174-e95d-41c8-9e71-59c040dc64a3.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:២៧', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1400', 'B020101', 'B020000', '​​នគរបាល​ជំនាញ​​គ្រប់គ្រង​អាវុធ​ជាតិ​ផ្ទុះចុះ​ពិនិត្យ​ការដ្ឋាន​ផលិត​ថ្ម​ចំនួន​៦​នៅ​ស្រុក​មង្គល​បូ​រី', 'ខេត្តបន្ទាយមានជ័យ ៖ ​បន្ទាប់​ពី​មាន​ករណី​លួច​ដាក់​​​គ្រាប់បែក​បំផ្ទុះបណ្តាល​ឲ្យ​មាន​មនុស្ស​ស្លាប់ ​និង​របួសជា​ច្រើន​នាក់នៅ​ចំ​កណ្តាល​​ទីក្រុង​បាងកក​ ប្រទេស​ថៃ កាលពី​យប់​ថ្ងៃ​ទី​១៧ ​សីហា​ ២០១៥​នេះ​មក', 'http://kohsantepheapdaily.com.kh/article/127240.html', 'http://i.kspg.co/2015/8/a087dfa2-49c7-44eb-b233-1283aad7d441.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:០២', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1401', 'B020101', 'B020000', 'ខណៈនេះ ​សម្តេចតេជោ ​ហ៊ុន សែន​ កំពុងដឹកនំាកិច្ចប្រជុំគណៈរដ្ឋមន្រ្តី', 'រាជធានីភ្នំពេញ ៖​ គណៈរដ្ឋមន្រ្តីបាន​បើកសម័យប្រជុំពេញអង្គ​ប្រចំាសបា្តហ៍របស់ខ្លួន​នៅវិមានសន្តិភាព​ ​ក្រោមអធិបតីសម្តេចតេជោ​ ​ហ៊ុន សែន ​នាយករដ្ឋមន្រ្តីនៃ​ព្រះរាជា', 'http://kohsantepheapdaily.com.kh/article/127238.html', 'http://i.kspg.co/2015/8/e71f6240-67aa-4b1e-a7b8-30c19b0ccbc3.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៥២', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1402', 'B020101', 'B020000', '​ដើរ​រើស​អេ​ត​ចាយ​អស់កម្លាំង​ពេក​ដេក​លក់​​តាម​ចិញ្ចើម​ផ្លូវ ផ្អើល​អ្នកដំណើរ​ស្មាន​តែ​ខ្យល់គរ​សន្លប់​', '​រាជធានី​ភ្នំពេញ​ ៖ បុរស​រើស​អេ​ត​ចាយ​ម្នាក់ដើរ​រើស​អេ​ត​ចាយ​តាម​ផ្លូវ​អស់កម្លាំង​ពេក ដេក​សម្រាក​តាម​ផ្លូវ​ដាស់​មិន​ក្រោក បង្ក​ឱ្យមាន​ការ​​ភ្ញាក់ផ្អើល​ដល់​អ្នក​ធ្វើ​ដំណើរ​តាម​ផ្លូវ​ចោមរោម​មើល​ស្មាន​តែ​ខ្យល់គរ​សន្លប់ ។', 'http://kohsantepheapdaily.com.kh/article/127236.html', 'http://i.kspg.co/2015/8/5a1fdb92-b315-4170-bffe-3fb0a4126b2f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៥១', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1403', 'B020101', 'B020000', 'ចាប់​បាន​ឃាតករ​សម្លាប់​បុរស​ជា​មន្ត្រី​មន្ទីរ​ធម្មការ​ខេត្ត​ហើយ កាលពី​រសៀល​ម្សិលមិញ', '​ខេត្តកំពង់ឆ្នាំង ៖ ជនសង្ស័យ​ជា​ឃាតករ ដែល​ធ្វើ​សកម្មភាព​រឹត​.​ក និង​វាយ​សម្លាប់​បុរស​ជា​មន្ត្រី​មន្ទីរ​ធម្មការ​ខេត្ត និង​ជា​គ្រូបង្រៀន​សាលា​ពុទ្ធិ​កប​ថម​សិក្សា​ម្នាក់ ដែល​ឆ្លៀតពេល​ទំនេរ​រត់​ម៉ូតូឌុប​នោះ ត្រូវ​បាន​សមត្ថកិច្ច​ធ្វើការ​ឃាត់ខ្លួន​បាន​ហើយ កាលពី​វេលា​ម៉ោង​២​រសៀល​ថ្ងៃ​ទី​២០ សីហា ២០១៥ ស្ថិត​នៅ​ចំណុច​វត្ត «​ជាង​ហ្លួង​» ក្នុង​ឃុំ​ពពេល ស្រុក​បរិបូរណ៍ ដើម្បី​ប្លន់​យក​ម៉ូតូ​មួយ​គ្រឿង ហើយ​', 'http://kohsantepheapdaily.com.kh/article/127235.html', 'http://i.kspg.co/2015/8/31c52841-1ad7-46b9-a20e-ca28489c6420.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៤៦', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1404', 'B020101', 'B020000', 'ពិធី​ដាំ​កូន​ឈើ​ក្នុង​ព្រៃ​សហគមន៍​ការពារ​ទេសភាព​ភូមិ​ត្រពាំង​ធ្លក ​', '​ខេត្តបន្ទាយ​មាន​ជ័យ ៖ មន្ទីរ​បរិស្ថាន​ខេត្ត​នា​ព្រឹក​ថ្ងៃ​ទី​២០​ សីហា​​ ​២០១៥​ បាន​រៀបចំ​ពិធី​ដាំ​កូន​ឈើ​ជា​មិត្តភាព​ដោយ​មានការ​ចូលរួម​ពី​ភាគី​ថៃ​និង​អង្គការ​ការពារ​អភិរក្ស​ធន​ធាន​ធម្មជាតិអន្តរ​ជាតិ ហៅ​កាត់ (IUCN) បាន​ផ្តួចផ្តើម​កម្មវិធី​ដាំ​កូន​ឈើ ', 'http://kohsantepheapdaily.com.kh/article/127234.html', 'http://i.kspg.co/2015/8/f2e7e9c1-80ee-435a-9c4b-b4cdb39a327a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៣៩', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1405', 'B020101', 'B020000', 'ព្រានព្រៃ​ដេញ​ប្រមាញ់​សត្វ​លើ​ភ្នំ​កែប ប្រទះ​ឃើញ​គ្រោងឆ្អឹង​សាកសព​មនុស្ស​ប្រុស', 'ខេត្ត​កែប ៖ ព្រានប្រមាញ់​សត្វព្រៃ​ម្នាក់ បាន​ប្រទះ​ឃើញ​គ្រោងឆ្អឹង​មនុស្ស​ម្នាក់​ដែល​រលួយ​សល់​សាច់​អស់​ទៅ​ហើយ កាលពី​ព្រឹក​ថ្ងៃ​ទី​១៦ សីហា នៅ​លើ​ភ្នំ​កែប ស្ថិត​ក្នុងភូមិ​សាស្ត្រ​សង្កាត់​កែប ក្រុងកែប ។ ', 'http://kohsantepheapdaily.com.kh/article/127232.html', 'http://i.kspg.co/2015/8/6344c4f2-0d34-4493-8165-0c42d676d8bf.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:១៥', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1406', 'B020401', 'B020000', 'តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃស​ប្តា​ហ៍​ទី​3​នៃ​ខែសីហា 2015', 'រាជធានី​ភ្នំពេញ ៖ តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ​ក្នុង​ស​ប្តា​ហ៍​ទី​៣ ខែសីហា ២០១៥ ដែល​កោះសន្ដិភាព ទទួល​បាន​ពី​ក្រសួងពាណិជ្ជកម្ម ឃើញ​ថា មុខទំនិញ សាច់​ជ្រូក   សាច់​គោ និង​បន្លែ មានការ​ប្រែប្រួល​តម្លៃ​តិចតួច ។', 'http://kohsantepheapdaily.com.kh/article/127265.html', 'http://i.kspg.co/2015/8/9bca0e20-7239-422c-9a59-1e60d6c38e37.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០៥', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1407', 'B020401', 'B020000', 'Donaco សម្រេច កិច្ចព្រមព្រៀង​ថ្មី', 'រាជធានី​ភ្នំពេញ ៖ ប្រតិបត្តិ​ករ​កាស៊ីណូ Donaco International Ltd ដែល​ចុះបញ្ជី​ក្នុង​ផ្សារ​ហ៊ុន​អូស្ត្រាលី​បាន​ចុះ​កិច្ចព្រមព្រៀង​ជាមួយ​ក្រុមហ៊ុន Heng Seng Group ដែល​ជា​ក្រុមហ៊ុន​នាំមុខ​គេ​មួយ​របស់​ម៉ា​កាវ ', 'http://kohsantepheapdaily.com.kh/article/127263.html', 'http://i.kspg.co/2015/8/4a100df6-294b-4207-b0e1-fade51c94d4a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០២', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1408', 'B020401', 'B020000', 'កូកាកូឡា​ចាប់ផ្តើម​សាងសង់​រោងចក្រ​ទី​2​នៅ​កម្ពុជា', 'រាជធានី​ភ្នំពេញ ៖ ក្រុមហ៊ុន​កូកាកូឡា​បាន​បើក​សម្ពោធ​ការដ្ឋាន​សាងសង់​រោងចក្រ​ទី​២​របស់​ខ្លួន ដែល​ត្រូវ​ចំណាយ​ទឹកប្រាក់​អស់​១០០​លាន​ដុល្លារ​ដើម្បី​ពង្រីក​ខ្សែ​សង្វា​ក់​ផលិតកម្ម និង​ដើម្បី​ឆ្លើយ​តប​នឹង​តម្រូវការ​ក្នុងស្រុក', 'http://kohsantepheapdaily.com.kh/article/127262.html', 'http://i.kspg.co/2015/8/25f6b7ac-dfbe-48bf-82da-f496fc77e46b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០០', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1409', 'B030101', 'B030000', 'EU ប្រកាសផ្តល់ថវិកា៨,៩លាន​ដុល្លារ​ ដល់​សាលា​ក្តី​ខ្មែរ​ក្រហម សម្រាប់​ឆ្នាំ ២០១៥​និង២០១៦', 'null', 'http://www.dap-news.com/2011-06-14-02-39-55/97581-eu----.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0845.jpg', 'Friday, 21 August 2015 14:15', '1', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1410', 'B030101', 'B030000', 'ប្រតិបត្ដិការចុះឆែកឆេរ នៅប្លុកបឹង ឈូក សមត្ថកិច្ចឃាត់ខ្លួន មនុស្ស៨៨នាក់', '<strong>ភ្នំពេញ ៖</strong> អស់រយៈពេលប៉ុន្មានម៉ោង ក្រោយបញ្ចប់ការចុះឆែកឆេរ នៅតាមបន្ទប់ ជួល ក្នុងប្លុកបឹងឈូក សង្កាត់ទឹកថ្លា ខណ្ឌ សែនសុខ នារសៀលថ្ងៃទី២១ ខែសីហា ឆ្នាំ ២០១៥នេះ មនុស្សចំនួន៨៨នាក់ក្នុងនោះ ស្រី ៩នាក់ត្រូវបានកម្លាំងសមត្ថកិច្ចឃាត់ខ្លួន យកទៅសួរនាំ ដើម្បីអនុវត្ដតាមនីតិវិធី តើ ជនណាខ្លះជាមុខសញ្ញាចែកចាយប្រើប្រាស់ គ្រឿងញៀននិងជនណាខ្លះ ដែលពាក់ព័ន្ធនឹង បទល្មើសផ្សេងទៀត ក្នុងមូលដ្ឋាន ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97580-2015-08-21-07-10-13.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00577.jpg', 'Friday, 21 August 2015 14:09', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1411', 'B030101', 'B030000', 'បញ្ជូនខ្លួន ជនសង្ស័យ ៣នាក់ ឡើងតុលាការ ពីបទប្រើអាវុធខុសច្បាប់', '<strong>កំពត៖</strong> ជនសង្ស័យ ០៣នាក់ ត្រូវបាននគរបាល នៃការិយាល័យកណ្តាលព្រហ្មទណ្ឌខេត្ត កសាងសំណុំរឿង បញ្ជូនខ្លួន រួមជាមួយវត្ថុតាង ទៅសាលាដំបូងខេត្ត នៅវេលាព្រឹក ថ្ងៃទី២១ ខែសីហា&nbsp; ឆ្នាំ២០១៥នេះ ពីបទប្រើប្រាស់អាវុធខុសច្បាប់ ចាត់ការទៅតាមផ្លូវច្បាប់។', 'http://www.dap-news.com/2011-06-14-02-39-55/97579-2015-08-21-07-06-44.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0534.jpg', 'Friday, 21 August 2015 14:05', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1412', 'B030101', 'B030000', 'ពិព័រណ៌ ឧស្សាហកម្ម កាត់ដេរ និងគ្រឿងយន្ដ បានសម្ពោធជាផ្លូវការ ហើយនៅព្រឹកនេះ', '<strong>ភ្នំពេញ៖</strong> ពិព័រណ៌ឧស្សាហកម្ម គ្រឿងយន្ដ សម្លៀកបំពាក់ និងវាយនភ័ណ្ឌកម្ពុជា ជាលើកទី៥ សម្រាប់ឆ្នាំ២០១៥ ជាមហាព្រឹតិ្ដការណ៍ អាជីវកម្មឈានមុខ ត្រូវបានចាប់ផ្តើមបើកឲ្យចូល ទស្សនាជាផ្លូវការពីថ្ងៃ ២១-២៤ ខែសីហា ឆ្នាំ២០១៥ នៅមជ្ឈមណ្ឌល មហាសន្និបាទ កោះពេជ្រ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97578-2015-08-21-07-05-17.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-125.jpg', 'Friday, 21 August 2015 14:00', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1413', 'B030101', 'B030000', 'លោកយាយកំសត់ គ្មានទីពឹង អាយុ១០៣ឆ្នាំ ទទួលអំណោយ ពីអនុសាខា កាកបាទក្រហម ខណ្ឌច្បារអំពៅ', '<strong>ភ្នំពេញ ៖</strong> លោកយាយ ចាស់ជរាអាយុ ១០៣ឆ្នាំ គ្មានទីពឹង រស់នៅសង្កាត់ ច្បារអំពៅទី២ នៅព្រឹកថ្ងៃទី ២១ ខែសីហា ឆ្នាំ២០១៥នេះ បានយកអំណាយជាគ្រឿង ឧបភោគបរិភោគ សម្ភារប្រើប្រាស់ជាច្រើនមុខទៀត និងថវិកា ជូនដល់លោកយាយ ដែលកំពុងខ្វះខាត និងមានជំងឺប្រចាំកាយ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97577-2015-08-21-06-57-24.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0529.jpg', 'Friday, 21 August 2015 13:54', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1414', 'B030101', 'B030000', 'បញ្ចប់ប្រតិបត្ដិការ ត្រួតពិនិត្យនៅការ ដ្ឋានសំណង់ ធនាគារអេស៊ីលីដា រកឃើញ កម្មករបរទេស ជាង១០០នាក់', '<strong>ភ្នំពេញ ៖</strong> ក្រោយបញ្ចប់ប្រតិបត្ដិការ ចុះ ត្រួតពិនិត្យនៅការដ្ឋានសំណង់របស់ធនាគារ អេស៊ីលីដា ដែលស្ថិតនៅតាមផ្លូវមុនីវង្ស សង្កាត់ស្រះចក ខណ្ឌដូនពេញ អស់រយៈពេល ប៉ុន្មានម៉ោងមកនេះ កម្មករបរទេសជាង ១០០នាក់ ត្រូវបានសមត្ថកិច្ចរកឃើញនិង សួរនាំរកលិខិតស្នាក់នៅ និងលិខិតឆ្លងដែន ជាលទ្ធផលជនជាតិវៀតណាមមានច្រើនជាង គេ ដែលត្រូវចាត់វិធានការតាមផ្លូវច្បាប់ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97576-2015-08-21-06-42-33.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00564.jpg', 'Friday, 21 August 2015 13:42', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1415', 'B030101', 'B030000', 'ព័ត៌មាន​វិទ្យុ​ដើម​អម្ពិល ម៉ោង ១២-១៣ រៀបចំ​ដោយ​លោក សយ សុភាព', 'null', 'http://www.dap-news.com/2011-06-14-02-39-55/97575-2015-08-21-06-42-17.html', 'img/dapnews.png', 'Friday, 21 August 2015 13:41', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1416', 'B030101', 'B030000', 'NGO និងអ្នក វិភាគសង្គម៖ បើផែនទីកម្ពុជា និង UN ដូចគ្នា គួរតែឈប់និយាយ ពាក្យថាក្លែងក្លាយ', '<strong>ភ្នំពេញ៖</strong> អង្គការសង្គមស៊ីវិល (NGO) និងអ្នកវិភាគសង្គម បានលើកឡើង ស្រដៀងគ្នាថា បើការត្រួតពិនិត្យ ផ្ទៀងផ្ទាត់ផែនទីរបស់ខ្មែរ និងផែនអង្គការ សហប្រជាជាតិ (UN) ដូចគ្នាហើយ ដូច្នេះក្រុមអង្គការ គណបក្ស នយោបាយ និងសាធារណៈជននានា គួរបញ្ឈប់ការនិយាយថា ផែនទីដែលរាជរដ្ឋាភិបាល ប្រើប្រាស់បោះបង្គោល ព្រំដែន ជាមួយវៀតណាម ជាផែនទីក្លែងក្លាយទៀតទៅ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97574-ngo-----un---.html', 'img/dapnews.png', 'Friday, 21 August 2015 13:37', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1417', 'B030101', 'B030000', 'សម្តេចក្រឡាហោម ស ខេង  ព្រមានចាត់វិធានការ ចំពោះមន្រ្តីរាជការ បើកប្រាក់ខែពីរកន្លែង', 'null', 'http://www.dap-news.com/2011-06-14-02-39-55/97573-2015-08-21-06-23-28.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0881.jpg', 'Friday, 21 August 2015 13:22', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1418', 'B030101', 'B030000', 'លោក សេង គន្ធ នាំយកថវិកា ៣៥៦០ដុល្លារ ទៅប្រគល់ជូនលោកគ្រូ អ្នកគ្រូ ដែលបញ្ចប់ បង្រៀនបំប៉ន ដល់សិស្សថ្នាក់ទី១២', '<strong>ភ្នំពេញ ៖</strong> លោក សេង គន្ធ អភិបាលរង ខណ្ឌពោធិ៍សែនជ័យ និងជាប្រធានសហភាព សហព័ន្ធ យុវជនកម្ពុជា ខណ្ឌពោធិ៏សែនជ័យ តំណាងឲ្យ លោក សាយ សំអាល់ ប្រធាន ស.ស.យ.ក រាជធានីភ្នំពេញ នៅព្រឹកថ្ងៃទី២១ ខែសីហា ឆ្នាំ២០១៥នេះ បានចុះសំណេះ សំណាល ជាមួយ លោកគ្រូ អ្នកគ្រូស្ម័គ្រចិត្ត និងសិស្សសានុសិស្ស សាលាជម្ពូវ័ន្ត និងសាលា អរុណវត្តី សរុប ៤៣៥នាក់ ដែលបានបញ្ចប់វគ្គ បំប៉នត្រៀមប្រឡង សញ្ញាបត្រមធ្យម សិក្សាទុតិយភូមិ ឆ្នាំ២០១៥ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97572-2015-08-21-05-40-05.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00558.jpg', 'Friday, 21 August 2015 12:39', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1419', 'B030102', 'B030000', 'ណាសា៖ ភពផែនដី នឹងមិនរងការ គំរាមកំហែងពី អាចម៍ផ្កាយ នៅខែកញ្ញា នោះទេ', '<strong>(វ៉ាស៊ីនតោន)៖</strong> យោងតាម ប្រភពព័ត៌មាន CNN ចេញផ្សាយនៅ ថ្ងៃសុក្រទី២១ ខែសីហា ឆ្នាំ២០១៥ បានឲ្យដឹងថា ភ្នាក់ងារអវកាស ណាសាបានប្រកាសថា “គ្មានការធ្លាក់អាចម៍ផ្កាយនៅ លើភពផែនដី នៅក្នុងខែកញ្ញា ខាងមុខនេះ ឡើយ។” ហេតុដូច្នេះហើយ សូមអ្នកទាំងអស់គ្នា ឈប់មានការព្រួយបារម្ភ ទៀតទៅ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97555-2015-08-21-04-05-35.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-117.jpg', 'Friday, 21 August 2015 11:01', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1420', 'B030102', 'B030000', 'ប៉ូលិសថៃ៖ យ៉ាងហោចណាស់ មនុស្ស ១០នាក់ អាចជាប់ពាក់ព័ន្ធ នឹងការបំផ្ទុះគ្រាប់បែក', '<strong>បាងកក៖</strong> យោងតាមការលើកឡើង របស់ប៉ូលិសថៃ បានឲ្យដឹងថា យ៉ាងហោចណាស់មាន សកម្មជនចំនួន ១០នាក់ ដែលអាចពាក់ព័ន្ធ នឹងករណីបំផ្ទុះគ្រាប់បែក អត្តឃាត នៅចំកណ្ដាល ទីក្រុងបាងកក កាលពីដើមសប្តាហ៍ នេះ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97553-2015-08-21-03-45-21.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-116.jpg', 'Friday, 21 August 2015 10:42', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1421', 'B030102', 'B030000', 'ក្រុមអ្នកស្រាវជ្រាវ រកឃើញអ្នកដែល មានភាពជាអ្នកដឹកនាំ ពីកំណើត', '<strong>ឡុងដ៍៖</strong> ក្រុមអ្នកស្រាវជ្រាវ ជនជាតិអង់គ្លេស បាននិយាយថា ពួកគេបានរកឃើញមនុស្ស មួយចំនួនដែលមាន ភាពជាអ្នកដឹកនាំ តាំងពីកំណើត តាមរយៈការពិនិត្យ DNA ដ៏ជាក់លាក់មួយ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97552-2015-08-21-03-40-28.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-115.jpg', 'Friday, 21 August 2015 10:37', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1422', 'B030102', 'B030000', 'កូរ៉េខាងជើង ធ្វើការថ្កោលទោស សមយុទ្ធយោធា រវាងអាមេរិក និង កូរ៉េខាងត្បូង ទៅយូអិន', '<strong>ញូយ៉ក៖</strong> ប្រេសិតកំពូល របស់កូរ៉េខាងជើង ប្រចាំនៅអង្គការសហប្រជាជាតិ បានធ្វើការថ្កោល ទោសចំពោះ សមយុទ្ធ យោធារួមគ្នា រវាងកូរ៉េខាងត្បូង និង សហរដ្ឋអាមេរិក ដោយចោទថា ប្រទេសទាំងពីរនេះ កំពុងតែបំផ្លាញ សន្តិសុខ និង សន្តិភាពក្នុងតំបន់ថែមទៀត។ នៅក្នុងលិខិត ទៅកាន់លោក បាន គីមូន ប្រេសិតកូរ៉េខាងជើង បានស្នើ ឲ្យអង្គការសហប្រជាជាតិ បើកកិច្ច ប្រជុំមួយជាបន្ទាន់ស្តីពីបញ្ហានេះ ព្រោះវាជាបញ្ហាសម្រាប់ អន្តរជាតិ ទាំងមូល។', 'http://www.dap-news.com/2011-06-14-02-40-26/97549-2015-08-21-03-31-03.html', 'http://www.northkoreatech.org/wp-content/uploads/2014/08/5812911.jpg', 'Friday, 21 August 2015 10:28', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1423', 'B030102', 'B030000', 'ចាប់ពីម៉ោង៥ ព្រឹកថ្ងៃសុក្រនេះ កូរ៉េខាងជើង ប្រកាសដាក់ប្រទេស ក្នុងពេលសង្គ្រាម', '<strong>ព្យុងយ៉ាង៖</strong> មេដឹកនាំកំពូលរបស់ប្រទេសកូរ៉េខាងជើង លោក គីម ចុងអ៊ុន បានប្រកាសដាក់ ប្រទេសរបស់ខ្លួនទៅក្នុងគ្រាមានសង្គ្រាម ហើយឲ្យកងទ័ពរបស់ខ្លួនទាំងអស់ ប្រុងជើងការ ខ្ពស់បំផុត ក្នុងការចេញប្រតិបត្តិការគ្រប់ ពេលវេលាចាប់ម៉ោង ៥ព្រឹកថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ ២០១៥ នេះតទៅ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97540--kim-jong-un-.html', 'http://www.marketoracle.co.uk/images/2010/Nov/north-korea-attack3.jpg', 'Friday, 21 August 2015 09:20', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1424', 'B030102', 'B030000', 'បុរស ២នាក់ ដែលប៉ូលិសថៃ សង្ស័យជាប់ពាក់ព័ន្ធ ករណីផ្ទុះគ្រាប់បែក បានចូលខ្លួនបំភ្លឺ នៅប៉ុស្ដិ៍ប៉ូលិស', '<strong>បាងកក៖</strong> យោងតាមការចុះផ្សាយរបស់ BBC ចេញផ្សាយនៅមុននេះបន្ដិចនេះបានឲ្យដឹងថា បុរស ២នាក់ ដែលត្រូវបានប៉ូលិសបាងកក សង្ស័យថាជាជនសង្ស័យ និងជាបក្ខពួករបស់បុរសអាវលឿង បានចូលខ្លួនទៅបំភ្លឺនៅស្ថានីយប៉ូលិស ដោយទទូចថាពួកគេគឺជាមគ្គុទេសទេសចរ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97528-2015-08-20-13-49-33.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00527.jpg', 'Thursday, 20 August 2015 20:48', '0', '0', '0', 't', '2015-08-20', 'f');
INSERT INTO "tbnews" VALUES ('1425', 'B010105', 'B010000', 'វីដេអូ៤នាទី​នឹង​ជួយ​​​ឲ្យ​​អ្នកចេះ​ប្ដូរ​ពណ៌​សក់​​បានលឿន​លើកម្មវិធី Photoshop', ' Adobe Photoshop គឺ​ជា​កម្មវិធី​ឌីស្សាញ​រូបភាព​ដ៏​ពេញ​និយម​មួយ​  ​ដូច្នេះ​ថ្ងៃ​នេះ​យើង​នឹង​លើក​យក​វិធីងាយៗ​​ក្នុង​ការ​ឌីស្សាញ​​ប្ដូរ​ពណ៌​សក់​​នៅ​លើ​កម្មវិធី​មួយ​នេះ....', 'http://news.sabay.com.kh/article/457776', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/chang-hair-color-285x170.jpg?ebb82d', '21 August, 2015', '1', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1426', 'B010105', 'B010000', 'ចុម!​​ Smartwatch​​ បក​ប្រែ​​ពាក្យ​សម្ដី​​​ទៅ​​អក្សរ​ជា​​ភាសា​​ផ្សេង​​បាន!', 'កន្លង​មក​ក្រុមហ៊ុន​ Google​ បាន​ដាក់​ដំណើរ​ការ​កម្មវិធី​ Google Translate​ របស់​ខ្លួន​នៅ​លើ​ Smartwatch ដែល​ដើរ​ដោយ​ប្រតិបត្តិការ​ Android។ ហើយ​ឥលូវ​នេះ​ ជា​មួយ​ការ​ Update ថ្មី​វា​មិន​អាច​​ត្រឹម​តែ​​​បក​ប្រែ​អក្សរ​បាន​ប៉ុណ្ណោះ​ទេ​ គឺ​ថែម​ទាំង​អាច​បក​ប្រែ​ការ​សន្ទនា​ដោយ​សំឡេង​បាន​ទៀត​ផង។ &nbsp; &nbsp; គោល​បំណង​នៃ​ការ​ Update​ ថ្មី​នេះ​គឺ​ដើម្បី​ឲ្យ​មនុស្ស​ពីរ​នាក់​ដែល​ប្រើ​ភាសា​ខុស​គ្នា​ធ្វើ​ការ​សន្ទនា​គ្នា​បាន​តាម​រយៈ​នាឡិកា​ដៃ​បាន​ដោយ​ងាយ​បំផុត។ ការ​បក​ប្រែ​​ពី​សំឡេង​ទៅ​ជា​អក្សរ​នេះ​អាច​ធ្វើ​ទៅ​បាន​តែ​ចំពោះ​ ៤៤​ភាសា​​ប៉ុណ្ណោះ​ក្នុង​ចំណោម​ ៩០​ភាសា ​ដែល​កម្មវិធី​អាច​បក​ជា​អក្សរ​បាន។ ហើយ​អ្វី​ដែល​ពិសេស​នោះ​គឺ​​កម្មវិធី​អាច​ធ្វើ​ការ​ចាប់​យក​នូវ​ភាសា​ដែល​អ្នក​បាន​ប្រើ​ថា​ជា​ភាសា​អ្វី ​រួច​បក​ប្រែ​វា​ទៅ​ជា​ភាសា​អង់គ្លេស​ ឬ​ភាសា​ផ្សេង​ទៀត។ កម្មវិធី​នេះ​គឺ​តម្រូវ​ឲ្យ​នាឡិកា​ដៃ​មាន​ប្រព័ន្ធ​អ៊ីនធឺណិត​ដើម្បី​អាច​ប្រើ​ប្រាស់​បាន។ &nbsp; &nbsp; &nbsp; &nbsp; ​​​​ នេះ​​អាច​​ជា​ការ​ Update ថ្មី​ដើម្បី​ត្រៀម​ខ្លួន​ប្រកួត​ប្រជែង​ជា​មួយ​នឹង​ Apple Watch​ ជំនាន់​ថ្មី​ដែល​គ្រោង​ប្រកាស​នា​ពេល​ខាង​មុខ​នេះ៕ ​ -ចុច​អាន៖ នេះ​ឬ​ជា​ទូរសព្ទ​ iPhone 6s​ ពណ៌​ស៊ីជម្ពូ? &nbsp; &nbsp; ប្រែ​សម្រួល៖ ហៃ សុខ​ហេង ប្រភព៖ theverge', 'http://news.sabay.com.kh/article/457875', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/moto-360-watch-face-650-80-285x170.jpg?ebb82d', '21 August, 2015', '1', '0', '0', 't', '2015-08-21', 'f');
