INSERT INTO "tbnews" VALUES ('1464', 'B020202', 'B020000', 'Marseille តែងតាំង​អតីត​កីឡាករ​ក្លិប Real Madrid ជា​គ្រូបង្វឹក​ជំនួស​លោក​ម៉ា​សេ​ឡូ ប៊ី​អេ​ល​សា ដែល​លាលែង', 'Marseille ជា​ក្លិប​ល្បី​ក្នុង​ក្របខ័ណ្ឌ​បាល់ទាត់​ឡឺ​សំ​ផ្ចូ​ណា​របស់​បារាំ​ង បាន​តែងតាំង​លោក​ម៉ៃ​ឃើ​ល អតីត​ខ្សែបម្រើ​ក្លិប Real Madrid ជា​គ្រូបង្វឹក​ថ្មី​របស់​ក្លិប​កាលពី​ថ្ងៃ​ទី​១៩ សីហា កន្លង​ទៅ​នេះ ។', 'http://kohsantepheapdaily.com.kh/article/127293.html', 'http://i.kspg.co/2015/8/2157b033-f6f5-4999-9386-3177755709f7.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:២៥', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1465', 'B020202', 'B020000', 'RFEF ដាក់​បម្រាម​4​ប្រកួត​លើ​កីឡាករ​ជឺ​រ៉ា​ដ ពី​កេ ដែល​ប្រើ​ពាក្យ​អសុរោះ​ដាក់​ជំនួយការ​អាជ្ញាកណ្តាល', 'សហព័ន្ធ​បាល់ទាត់​អេ​ស្ប៉ា​ញ (RFEF) បាន​ដាក់​បម្រាម​ដល់​កីឡាករ​ជឺ​រ៉ា​ដ ពី​កេ ខ្សែការពារ​ឆ្នើម​របស់​ក្លិប​អ្នកមាន​បុណ្យ Barcelona ក្នុង​ក្របខ័ណ្ឌ La Liga ដោយ​ហាម​ចូល​លេង​ចំនួន​៤​ប្រកួត ក្រោយ​ពី​សាមីខ្លួន​ទទួល', 'http://kohsantepheapdaily.com.kh/article/127290.html', 'http://i.kspg.co/2015/8/e089210c-4d70-4fc9-9dcf-af88cd1d4f7f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:១៩', '0', '0', '0', 't', '2015-08-21', 'f');
