INSERT INTO "tbnews" VALUES ('1467', 'B020201', 'B020000', 'កម្ពុជា​ឈ្នះ​ប៊ូ​តាន​2-0 ក្នុង​ជំនួប​មិត្តភាព​អន្តរជាតិ​មុន​ជួប​ក្រុម​សា​មូ​រ៉ៃ​ជ​ប៉ុ​ន', 'រាជធានី​ភ្នំពេញ ៖ ក្រុមបាល់ទាត់​ជម្រើសជាតិ​កម្ពុជា​បាន​យកឈ្នះ​ក្រុម​ជម្រើសជាតិប៊ូ​តាន​២-០ ក្នុង​ជំនួប​មិត្តភាព​អន្តរជាតិ​នៅ​លើ​ទីលាន​ពហុ​កីឡដ្ឋាន​ជាតិ កាលពី​យប់​ថ្ងៃ​ទី​២០ សីហា មុន​ពេល​ធ្វើ​ដំណើរ​ទៅ​ជួប​ក្រុម​សា​មូ​រ៉ៃ', 'http://kohsantepheapdaily.com.kh/article/127284.html', 'http://i.kspg.co/2015/8/7b572771-cddd-4052-9540-b78455e6279a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:០១', '1', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1468', 'B020201', 'B020000', 'បិទ​វគ្គ​សិក្សា​សាមគ្គី​អូ​ឡាំ​ពិ​ក​ស្តី​ពី​បច្ចេកទេស​គ្រូ​ជំនាញ​តេ​ក្វា​ន់​ដូ', 'រាជធានី​ភ្នំពេញ ៖ វគ្គ​សិក្សា​សាមគ្គី​អូ​ឡាំ​ពិ​ក​ស្ដី​ពី​បច្ចេកទេស​គ្រូ​ជំនាញ​តេ​ក្វា​ន់​ដូ​រយៈពេល​១០​ថ្ងៃ​បាន​បញ្ចប់​ជា​ស្ថាពរ​ហើយ​កាលពី​ថ្ងៃ​ទី​២០ សីហា បន្ទាប់​ពី​រៀបចំ​ធ្វើ​តាំងពី​ថ្ងៃ​ទី​១០ សីហា កន្លង​មក ។ ពិធី​បិទ​វគ្គ​ត្រូវ​បាន​ធ្វើ​ឡើង', 'http://kohsantepheapdaily.com.kh/article/127279.html', 'http://i.kspg.co/2015/8/a35c66c3-7b1b-4f0b-8402-45538a6bcc98.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៤០', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1469', 'B020201', 'B020000', 'ឃី​ម ឌី​ម៉ា និង​សេក គឹ​ម​ផៃ ប្រាថ្នា​កាច់​ចង្កូម​កីឡាករ​ថៃ', 'រាជធានី​ភ្នំពេញ ៖ នៅ​រសៀល​ថ្ងៃអាទិត្យ​ស្អែក កីឡាករ​កម្ពុជា​២​រូប​គឺ ឃី​ម ឌី​ម៉ា និង​សេក គឹ​ម​ផៃ ត្រូវ​ឡើង​ប្រកួត​ជាមួយ​កីឡាករ​ថៃ​នៅ​លើ​សង្វៀន​ទូរទស្សន៍​អាស៊ី​អាគ្នេយ៍​ដែល​គេ​រំពឹង​ថា​នឹង​មាន​អ្នក​ទស្សនា​កកកុញ ', 'http://kohsantepheapdaily.com.kh/article/127277.html', 'http://i.kspg.co/2015/8/12b3c019-7a82-4623-bdcb-35e7ecfbb031.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៣២', '10', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1470', 'B020301', 'B020000', 'នៅ​ច្រក​ទ្វារ​អន្តរជាតិ​ប៉ោយ​ប៉ែ​ត​ភ្ញៀវ​ទេស​ចរ​​ធ្លាក់​ចុះ​១៥% បន្ទាប់​​ពី​ផ្ទុះ​គ្រាប់បែក​​កណ្តាល​​ក្រុង​​​បាង​កក​', 'ខេត្តបន្ទាយមានជ័យ ៖ ក្រោយ​ពី​មាន​ករណី​ផ្ទុះ​គ្រាប់បែក​នៅ​កណ្តាល​ទីក្រុង​បាងកក ប្រទេស​ថៃ រួច​មក នៅ​ច្រក​ទ្វា​អន្តរជាតិ​ប៉ោយ​ប៉ែ​ត ចំនួន​ភ្ញៀវ​ទេសចរ​បរទេស​មានការ​ធ្លាក់​ចុះ​១៥% ក្នុង​រយៈពេល​៣​ថ្ងៃ ។', 'http://kohsantepheapdaily.com.kh/article/127299.html', 'http://i.kspg.co/2015/8/cee0c0d8-2316-4da5-8348-d6485045c05e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៥៨', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1471', 'B020102', 'B020000', 'ចោរឆក់​អស់លក្ខណ៍​២​នាក់ត្រូវ​សមត្ថកិច្ច​ក្របួច​បាន​ខណៈ​កំពុង​ធ្វើ​សកម្មភាព', 'រាជធានី​ភ្នំពេញ ៖ ជនសង្ស័យ​ឆក់​ខ្សែ​.​ក​២​នាក់​ត្រូវ​បាន​សមត្ថកិច្ច​នគរបាល​ព្រហ្មទណ្ឌ​កម្រិត​ស្រាល​ខណ្ឌ​៧​មក​រា​ធ្វើការ​ចាប់ខ្លួន​ភ្លាម​ៗ​ខណៈ​​ធ្វើ​សកម្ម​ភាព​ឆក់​ខ្សែ​.​ក​ជនបរទេស​ដែល​កំពុង​ជិះ​ម៉ូតូ​កង់​បី​មិន​បាន​សម្រេច​កាលពី​វេលា​ម៉ោង​៥​ល្ងាច', 'http://kohsantepheapdaily.com.kh/article/127316.html', 'http://i.kspg.co/2015/8/36a5dc29-c5ec-4361-a249-5b4ef378e877.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២១:៥០', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1472', 'B020102', 'B020000', 'ព័ទ្ធ​ចាប់​ជនសង្ស័យ​ជួញដូរ​គ្រឿងញៀន​បាន​២​នាក់ ដកហូតបាន​​ថ្នាំ​ញៀន​៥៨​កញ្ចប់​តូច​នៅ​អគារ​ប៊ូ​ឌិ​ញ', 'រាជធានី​ភ្នំពេញ ៖ សមត្ថកិច្ច​នគរបាល​ខណ្ឌចំការមន​បាន​ធ្វើការ​ឡោម​ព័ទ្ធ​ចាប់​បាន​ជនសង្ស័យ​២​នាក់​ពាក់ព័ន្ធ​ករណី​ជួញដូរ និង​ប្រើប្រាស់​គ្រឿង​ញៀន​នៅ​ចំណុច​ផ្ទះ​ជួល​លេខ​១៣ ជាន់​ទី​២ អគារ​ប៊ូ​ឌិ​ញ ស្ថិត​ក្នុង​សង្កាត់​ទន្លេបាសាក់ ខណ្ឌចំការមន ', 'http://kohsantepheapdaily.com.kh/article/127315.html', 'http://i.kspg.co/2015/8/04dc9f4a-5f59-4181-926b-a18e485fd2f4.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២១:២២', '0', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1473', 'B020102', 'B020000', 'ចាប់ខ្លួន​បុរស​ម្នាក់​ពី​បទ​ប៉ុនប៉ង​រំលោភ​ក្មេង​ស្រី​អាយុ​៧​ឆ្នាំ​នៅ​ចាក់​អង្រែ​ក្រោម', 'រាជធានី​ភ្នំពេញ ៖ បុរស​ម្នាក់​ត្រូវ​បាន​ប្រជាពលរដ្ឋ​ប្តឹង​សមត្ថកិច្ច​ឱ្យ​ចាប់ខ្លួន​ភ្លាម​ៗ​ខណៈ​ពេល​ធ្វើ​សកម្មភាព​ប៉ុនប៉ង​រំលោភ​ក្មេង​ស្រី​ម្នាក់ ។ ហេតុការណ៍​នេះ​បង្ក​ឱ្យ​មានការ​ភ្ញាក់ផ្អើល​កាលពី​ថ្ងៃ​ទី​២១ សីហា ២០១៥ ស្ថិត​នៅ​ភូមិ​ព្រែក​តាឡុ​ង​១', 'http://kohsantepheapdaily.com.kh/article/127313.html', 'http://i.kspg.co/2015/8/2c49587f-39cf-42a6-979a-cceb3acf0611.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២០:០០', '0', '0', '0', 't', '2015-08-21', 'f');
