INSERT INTO "tbnews" VALUES ('1475', 'B020102', 'B020000', '​ឆក់​ទៀត​ហើយ​ ​​​​ឆក់​​កាបូប​តែ​​មិន​បាន​សម្រេច​ ត្រូវ​សមត្ថកិច្ច​ចាប់វាយ​ខ្នោះ​', '​​​រាជ​ធានី​ភ្នំពេញ ៖ យុវ​​​ជន​សង្ស័យ​២នាក់ ត្រូវ​បាន​​សមត្ថកិច្ច​​​មូលដ្ឋាន​ចាប់​វាយ​ខ្នោះ បន្ទាប់​ពី​ជិះ​ម៉ូតូ​ធ្វើ​សកម្ម​​ភាព​ឆក់​កាបូប​ពី​នារី​ម្នាក់​​មិន​បាន​សម្រេច ​កាល​ពី​​​​រសៀល​ថ្ងៃទី​២១ សីហា', 'http://kohsantepheapdaily.com.kh/article/127297.html', 'http://i.kspg.co/2015/8/0b5cb311-6602-4238-acde-0a2d922206fd.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៤៦', '0', '0', '45', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1476', 'B020101', 'B020000', 'ក្រសួង​រ៉ែ និង​ថាមពល​ដឹកនាំនិស្សិត និងសាស្ត្រាចារ្យ​ចំនួន​៦៥នាក់ចុះទស្សនកិច្ច​សិក្សា​វារីអគ្គិសនី​ស្ទឹងតាតៃ-ស្ទឹងប្ញស្សីជ្រុំ', 'ខេត្តកោះកុង ៖ ដំណើរ​ទស្សនកិច្ច​សិក្សា​រយៈពេល​២​ថ្ងៃ ដែល​រៀបចំ​ឡើង​ដោយ​ក្រសួង​រ៉ែ និង​ថាមពល បាន​ដឹកនាំ​ក្រុម​និស្សិត និង​​សាស្ត្រាចារ្យ មក​ពី​វិទ្យាស្ថាន​ជាតិ​ពហុបច្ចេកទេស​កម្ពុជ​, ពហុបច្ចេកទេស​ព្រះ​កុសុមៈ​, សាកលវិទ្យាល័យ​ភូមិន្ទ​ភ្នំពេញ​, លោក​គ្រូ មក​ពី​ខេត្តពោធិ៍សាត់ រួម​និង​អ្នក​សារព័ត៌មាន ទូរទស្សន៍ សរុប​ចំនួន​៦៥​នាក់ កាលពី​ថ្ងៃ​ទី​២១ សីហា ២០១៥ ចូល​ទៅ​ដល់​ទំនប់​', 'http://kohsantepheapdaily.com.kh/article/127324.html', 'http://i.kspg.co/2015/8/ae4e4c79-13d9-4957-92d3-d9a783954cb2.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសៅរ៏ ទី២២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤១', '0', '0', '0', 't', '2015-08-22', 'f');
