INSERT INTO "tbnews" VALUES ('1478', 'B020101', 'B020000', 'នគរបាល​ខណ្ឌដង្កោ​ចុះ​ត្រួតពិនិត្យ​អាវុធ​ជាតិ​ផ្ទុះ ឃាត់​បាន​ជនសង្ស័យ​ប្រុស​-​ស្រី​៣​នាក់​រួម​ទាំង​ថ្នាំ​ញៀន', 'រាជធានី​ភ្នំពេញ ៖ សមត្ថកិច្ច​ចម្រុះ​នៃ​អធិការដ្ឋាន​នគរបាល​ខណ្ឌដង្កោ បាន​ធ្វើការ​ចុះ​ត្រួតពិនិត្យ​អាវុធ​ជាតិ​ផ្ទុះ​ទៅ​លើ​យានយន្ត​គ្រប់ ប្រភេទ កាលពី​វេលា​ម៉ោង​០៧​ល្ងាច​ថ្ងៃ​ទី​២១ សីហា ២០១៥ ស្ថិត​នៅ​ចំណុច​ផ្លូវ​លេខ​២១៧​មុខ​សាលា​មី​គី​វ៉េ ក្នុងភូមិ​សំបួរ សង្កាត់​ដង្កោ ខណ្ឌដង្កោ រាជធានី​ភ្នំពេញ ។ ', 'http://kohsantepheapdaily.com.kh/article/127321.html', 'http://i.kspg.co/2015/8/6572751b-8bd3-44b9-8210-792347c1c811.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសៅរ៏ ទី២២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:១៦', '0', '0', '0', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1479', 'B020101', 'B020000', 'ល្ងាច​មិ​ញ​! មានការ​ដោះ​លែង​អតីត​អធិការ​នគរបាល​ខណ្ឌមានជ័យ ដែល​ជាប់ចោទ​ពី​បទ​អំពី​ពុក​រលួយ', 'រាជធានី​ភ្នំពេញ ៖ សេចក្តីរាយការណ៍​បានឱ្យ​ដឹង​ថា លោក​ហ៊ី ណា​រិ​ន អតីត​អធិការ​នគរបាល​ខណ្ឌមានជ័យ ដែល​ត្រូវ​ចាប់ខ្លួន​ដោយ​អង្គភាព​ប្រឆាំង​អំពើ​ពុក​រលួយ ACU រឿង​ពុក​រលួយ​នោះ ត្រូវ​បាន​តុលាការ​ដោះ​លែងឱ្យ​មាន​សេរីភាព​វិញ​ហើយ កាលពី​វេលា​ម៉ោង​០៦​ល្ងាច​ថ្ងៃ​ទី​២១ សីហា ២០១៥ ចេញពី​ពន្ធនាគារ PJ ។', 'http://kohsantepheapdaily.com.kh/article/127320.html', 'http://i.kspg.co/2015/8/c3338d22-8081-42b1-8f3e-3f321bbc9b6c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសៅរ៏ ទី២២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៤៥', '0', '0', '5', 't', '2015-08-22', 'f');
