INSERT INTO "tbnews" VALUES ('1482', 'B020101', 'B020000', 'ក្រសួង​រ៉ែ និង​ថាមពល ចេញ​សេចក្តីថ្លែងការណ៍​គាំទ្រ​សម្តេច​តេ​ជោ ហ៊ុន សែន ក្នុង​ការ​ដោះស្រាយ​បញ្ហា​ព្រំដែន និង​ថ្កោលទោស​ជន​ដែល​ថា​ប្រើ​ផែនទី​ក្លែងក្លាយ', 'ក្រសួង​រ៉ែ និង​ថាមពល ចេញ​សេចក្តីថ្លែងការណ៍​គាំទ្រ​សម្តេច​តេ​ជោ ហ៊ុន សែន ក្នុង​ការ​ដោះស្រាយ​បញ្ហា​ព្រំដែន និង​ថ្កោលទោស​ជន​ដែល​ថា​ប្រើ​ផែនទី​ក្លែងក្លាយ', 'http://kohsantepheapdaily.com.kh/article/127311.html', 'http://i.kspg.co/2015/8/e579a6c8-6c68-4f8d-9854-4009db99715c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៩:៤១', '0', '0', '5', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1483', 'B020101', 'B020000', '​​តូចចិត្ត​នឹង​​ជំងឺ​រ៉ាំរ៉ៃ​ព្យា​បាល​​មិនជាចង​.​ក​បញ្ចប់​ជីវិត​​​ចោល​ប្រពន្ធនិងកូន​', '​​ខេត្ត​បន្ទាយ​មានជ័យ ៖ ​បុរស​ម្នាក់​ត្រូវ​​​គេ​ប្រទះ​ឃើញ​​ចង​.​កសម្លាប់​ខ្លួន​ ​នៅ​​​​ក្រោលគោ​ក្បែរ​ផ្ទះ​ខ្លួន​ឯង បង្ក​ឲ្យ​មានការ​ភ្ញាក់ផ្អើល​កាលពី​វេលា​ម៉ោង​៦​និង​៤០​នាទី​ព្រឹក​ថ្ងៃ​ទី​២១​​សីហា​ ​២០១៥ នៅ​ភូមិ​ទឹកថ្លា ', 'http://kohsantepheapdaily.com.kh/article/127309.html', 'http://i.kspg.co/2015/8/ac73907a-9ad6-423a-90a9-0b9f911904b3.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:៥២', '0', '0', '45', 't', '2015-08-21', 'f');
