INSERT INTO "tbnews" VALUES ('1488', 'B020101', 'B020000', 'ព្រះ​រាជ​អាជ្ញា​ឃុំ​ខ្លួន​បុរស​ម្នាក់​ពាក់​ព័ន្ធ​រឿង​គ្រោះ​ថ្នាក់​ចរាចរណ៍ ពល​រដ្ឋ​​ជាង​​៥០​នាក់លើក​​គ្នា​តវ៉ា​សុំ​​ដោះ​លែង', 'ខេត្ត​តាកែវ ៖ ក្រុម​ប្រជាពលរដ្ឋ​ជាង​៥០​នាក់​ដែល​មាន​ទីលំនៅ​ក្នុង​សង្កាត់​បា​រា​យណ៍ ក្រុង​ដូន​កែវ កាល​ពី​ព្រឹក​ថ្ងៃ​ទី២០ សីហា ​២០១៥ ​បាន​លើកគ្នា​មក​កាន់​សាលាខេត្ត​តាកែវ​ដើម្បី​ស្នើ​ឱ្យ​អភិបាល', 'http://kohsantepheapdaily.com.kh/article/127304.html', 'http://i.kspg.co/2015/8/fa55874f-4106-4077-a696-13e8f55b634f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:៣១', '0', '0', '0', 't', '2015-08-21', 'f');
