INSERT INTO "tbnews" VALUES ('1492', 'B020101', 'B020000', 'អគារ​ប៉ុស្តិ៍​រដ្ឋបាល​អូរ​គ្រៀងចាប់ផ្តើម​សង់​វិញហើយ ​បន្ទាប់​ពី​រាវរក​គ្រាប់​ និង​កម្ទេច​ចោល', 'ខេត្តក្រចេះ ​៖ ​ទីតាំង​អគារ​ប៉ុស្តិ៍​នគរបាល​រដ្ឋបាល​អូរ​គ្រៀង​ក្នុងស្រុក​សំបូរ ត្រូវ​បាន​ចាប់ដំណើរការ​សាងសង់​ធម្មតា​វិញហើយ កាល​ពីព្រឹក​ថ្ងៃ​ទី​២១ សីហា បន្ទាប់​ពី​ផ្អាក​ការ​សាងសង់​រយៈពេល​៥​ថ្ងៃ ', 'http://kohsantepheapdaily.com.kh/article/127300.html', 'http://i.kspg.co/2015/8/6c96ca0d-6b97-4afe-8a35-34344e9e6a43.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:០២', '0', '0', '4', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1493', 'B020101', 'B020000', 'បង្ក្រាប​គ្រឿង​ញៀន​ចាប់​ខ្លួន​មនុស្ស​៤នាក់ និង​ថ្នាំ​ញៀន​២​កញ្ចប់​ធំនៅ​ស្រុក​ទ្រាំង', 'ខេត្ត​តាកែវ​​ ៖ ជន​សង្ស័យ​ប្រើ​ប្រាស់​ថ្នាំ​ញៀន​២នាក់ និង​អ្នកលក់-​ផ្តល់​ទីតាំង​​​​​​ប្រើ​ប្រាស់​​២នាក់​ទៀត​ត្រូវ​ប៉ូលិស​ស្រុក​ទ្រាំង និង​ប៉ូលិស​ការិយាល័យ​ប្រឆាំង​ការ​ជួញ​ដូរ​គ្រឿង​ញៀន​ខេត្ត​សហការ​គ្នា​ចាប់ខ្លួន​', 'http://kohsantepheapdaily.com.kh/article/127298.html', 'http://i.kspg.co/2015/8/9a00eec9-023a-42d7-be79-da146faa822f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៤៧', '1', '0', '5', 't', '2015-08-21', 'f');
