INSERT INTO "tbnews" VALUES ('1496', 'B020101', 'B020000', 'ផឹក​ស៊ី​មាត់​ឡូ​ឡា​រំខាន​អ្នក​ជិតខាង​ចេញ​មក​ប្រាប់​ផ្ទុះ​ហិង្សារបួស​ម្នាក់', 'ខេត្ត​កណ្តាល ៖ ស្ត្រី​ម្នាក់​អង្គុយ​ផឹក​ស៊ី​ជាមួយ​បុរស​ម្នាក់​នៅ​ពេល​រាត្រី​ ហើយ​ស្រែក​​ឡូឡា​រំខាន​ដល់​អ្នក​ជិតខាង​ដេក​ពួន​មិន​បានធ្វើ​ឱ្យ​​បុរស​ម្នាក់​ដើរទៅ​ប្រាប់​កុំ​ឲ​មាត់​ឮ​ពេក', 'http://kohsantepheapdaily.com.kh/article/127288.html', 'http://i.kspg.co/2015/8/2a9e1c25-8143-4a24-a42b-3d69db8e53bd.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:១៥', '0', '1', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1497', 'B020101', 'B020000', '​អាជ្ញា​ធរ​ខេត្ត​​ ៖ ​​​ជូន​ដំណឹង​ដល់​ប្រជា​នេសាទ​ ត្រូវ​ប្រុង​ប្រយ័ត្ន​​​ពីឥទ្ធិ​ពល​ខ្យល់​ព្យុះពីថ្ងៃ​ទី​២២​ដល់​២៥ ​សីហា', 'ខេត្ត​ព្រះសីហនុ ៖ ​នៅ​ព្រឹក​ថ្ងៃទី​២១ សីហា ​នេះ មន្ត្រី​​សមត្ថកិច្ច​​ពាក់​ព័ន្ធ​ខេត្តព្រះសីហនុ បាន​ជូន​ដំណឹង​ដល់​ប្រជា​នេសាទ និង​​អ្នក​​ដើរ​សមុទ្រ​ទាំង​អស់​ ​ត្រូវ​បង្កើន​​ការ​ប្រុង​ប្រយ័ត្ន​ពី​ឥទ្ធិពល​ខ្យល់​ព្យុះ​ដែល​នឹង​​បក់​​​មក​ប៉ះ​លើ​ដែន​សមុទ្រ​កម្ពុជា', 'http://kohsantepheapdaily.com.kh/article/127287.html', 'http://i.kspg.co/2015/8/7de5b364-e92d-48c5-9918-087284b6f0ad.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:១១', '12', '32', '5', 't', '2015-08-21', 'f');
