INSERT INTO "tbnews" VALUES ('1499', 'B020101', 'B020000', 'ឃាត់​ខ្លួន​យុវ​ជន​ម្នាក់ល្បួង​ក្មេង​ស្រី​វ័យ១៥ឆ្នាំ​ផឹក​ស្រា​ស្រវឹងនាំ​​ទៅ​រំលោភ', 'ខេត្តស្ទឹងត្រែង ៖ យុវជន​ម្នាក់​ត្រូវ​កម្លាំង​អធិការដ្ឋាន​នគរបាល​ក្រុង​ស្ទឹង​ត្រែង​ចាប់ខ្លួន​ក្រោយ​ល្បួង​ក្មេង​ស្រី​វ័យ​១៥​ឆ្នាំ​ម្នាក់​យក​ទៅ​រំលោភ​ ខណៈ​នាង​ស្រវឹងស្រា​ជោក​ជាំ​ កាលពី​ថ្ងៃ​ទី​១៩​ សីហា ​២០១៥', 'http://kohsantepheapdaily.com.kh/article/127283.html', 'http://i.kspg.co/2015/8/76f13b93-3949-4116-8f3d-2df1a512d36b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៥៨', '21', '32', '4', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1500', 'B020101', 'B020000', 'រាជធានី​ភ្នំពេញ និង​ទីក្រុង​គី​តា​គ្យូ​ស្ស៊ូ ប្រទេស​ជប៉ុន គ្រោង​ចងសម្ព័ន្ធ​មេត្រីភាព​នា​ពេល​ខាង​មុខ​នេះ', 'រាជធានី​ភ្នំពេញ ៖ ក្រោម​ការ​ផ្តួចផ្តើម​របស់​សម្តេច​អគ្គមហាសេនាបតី​តេ​ជោ ហ៊ុន សែន នាយក​រដ្ឋមន្ត្រី​នៃ​ព្រះរាជ​ណា​ចក្រ​កម្ពុជា រាជធានី​ភ្នំពេញ និង​ទីក្រុង​គី​តា​គ្យូ​ស្ស៊ូ ខេត្ត​ហ្វូ​៊​គុ​អូ​កាប្រទេស​ជប៉ុន គ្រោង​ចងសម្ព័ន្ធ​មេត្រីភាព ដើម្បី​ពង្រឹង​និង​ពង្រីក​វិស័យ​គម​នាគមន៍ ឧស្សាហ៍កម្ម សេដ្ឋកិច្ច បរិស្ថាន និង​ការ​អប់រំ នា​ពេល​ឆាប់​ៗ​ខាង​មុខ​នេះ ។', 'http://kohsantepheapdaily.com.kh/article/127281.html', 'http://i.kspg.co/2015/8/ba838034-9d6d-41c5-95d9-68d07a37bf0e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសុក្រ ទី២១ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៥០', '0', '2', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1501', 'B020103', 'B020000', 'SMS​ជូន​លោក សម រ​ង្ស៊ី ខ្ញុំ​សង្ឃឹមថា ឯ​.​ឧ​.​នឹង​អប់រំ​ផ្ទៃក្នុង​បក្ស​កុំឱ្យ​ធ្វើ​សកម្មភាព​ទុច្ចរិត​ទៀត', 'រាជធានី​ភ្នំពេញ ៖ ខណៈ​ពេល​លោក​សម រ​ង្ស៊ី និង​លោក កឹ​ម សុខា មេដឹកនាំ​គណបក្ស​សង្គ្រោះ​ជាតិ កំពុង​ធ្វើ​ទស្សនកិច្ច​នៅ​ប្រទេស​អូស្ត្រាលី ហើយ​ការ​ផ្ទៀងផ្ទាត់​ផែនទី​បាន​បញ្ចប់​ជា​ស្ថាពរ​ដែរ​នោះ សម្តេច​តេ​ជោ​នាយក​រដ្ឋមន្ត្រី ហ៊ុន សែន បាន​ផ្ញើ​សារ (SMS)', 'http://kohsantepheapdaily.com.kh/article/127323.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃសៅរ៏ ទី២២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៣០', '2', '2', '5', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1502', 'B030101', 'B030000', 'ស្លាប់ម្នាក់ រងរបួសធ្ងន់ស្រាល ១៥នាក់ ក្នុងគ្រោះថ្នាក់ចរាចរណ៍ ទូទាំងប្រទេស ថ្ងៃទី២១ ខែសីហាម្សិលមិញ', '<strong style="line-height: 2;">ភ្នំពេញ៖</strong><span style="line-height: 2;"> យ៉ាងហោចណាស់ មានមនុស្សម្នាក់ បានស្លាប់ ៨នាក់រងរបួសធ្ងន់ និង៧នាក់ផ្សេងទៀត រងរបួសស្រាល ក្នុងហេតុការណ៍ គ្រោះថ្នាក់ចរាចរណ៍ ទូទាំងប្រទេសថ្ងៃទី២១ ខែសីហា ឆ្នាំ២០១៥ម្សិលមិញ នេះបើតាមការចុះផ្សាយ របស់គេហទំព័រ អគ្គស្នងការនគរបាលជាតិ។</span>', 'http://www.dap-news.com/2011-06-14-02-39-55/97613-2015-08-22-02-41-28.html', 'http://111.92.240.170:81/dap-news/dap-images/2012/November/id-011/Hong-0261.jpg', 'Saturday, 22 August 2015 09:37', '12', '2', '0', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1503', 'B030101', 'B030000', 'មន្រ្តី កម្លាំងប្រដាប់អាវុធ និងប្រជាពលរដ្ឋ ខេត្តឧត្តរមានជ័យ ចេញមកគាំទ្រយន្តការ ដោះស្រាយបញ្ហាព្រំដែន របស់សម្តេចតេជោ', '<b>ភ្នំពេញ៖</b> បន្ទាប់ពីការផ្ទៀតផ្ទាត់ផែនទីរបស់រាជរដ្ឋាភិបាល ប្រើសម្រាប់បោះបង្គោលព្រំដែនជាមួយវៀតណាម និង ផែនទីរបស់ខ្មែរយកពីអង្គការសហប្រជាជាតិ រកឃើញថា ខ្សែបន្ទាត់ព្រំដែនដូចគ្នា មិនដូចការចោទប្រកាន់របស់ ក្រុមតំណាងរាស្រ្ត និងសកម្មជនគណបក្សសង្រ្គោះជាតិនោះ លោក ស ថាវី អភិបាលខេត្តបន្ទាយមានជ័យ មន្រ្តី រាជការ កម្លាំងប្រដាប់អាវុធ និងប្រជាពលរដ្ឋ បានចេញមកគាំ ទ្រ ព្រមទាំងពេញចិត្តពេញថ្លើមយ៉ាង ទៅលើយន្ត ការដោះស្រាយបញ្ហាព្រំដែន ដើម្បីនាំមកស្ថេរភាពនយោបាយ និងការឯកភាពជាតិ របស់ប្រមុខរាជរដ្ឋាភិបាល កម្ពុជា សម្តេចតេជោ ហ៊ុន សែន។', 'http://www.dap-news.com/2011-06-14-02-39-55/97612-2015-08-21-23-53-41.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/SR-021.jpg', 'Saturday, 22 August 2015 06:53', '1', '2', '5', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1504', 'B030101', 'B030000', 'ចោរឆក់បានកាបូប បានសំរេចពីនារីម្នាក់ តែរត់មិនរួច ត្រូវប្រជាពលរដ្ឋ វាយឡើងទ្រោមខ្លួន', '<strong>ភ្នំពេញៈ</strong>កាលពីវេលាម៉ោង១និង៣០នាទី ថ្ងៃទី២១ ខែសីហា ឆ្នាំ២០១៥<strong> </strong>តាមបណ្តោយផ្លូវ៣៧១ &nbsp;ក្នុងសង្កាត់ទឹកថ្លា ខណ្ឌ សែនសុខ មានករណីឆក់កាបូបបានសម្រេចពីនារីម្នាក់ តែជាអកុសល បន្ទាប់ពីឆក់បាន ហើយជិះម៉ូតូគេច មិនរួចត្រូវបាន ប្រជាពលរដ្ឋ ចាប់បាននិងវាយ ជិតស្លាប់៕', 'http://www.dap-news.com/2011-06-14-02-39-55/97611-2015-08-21-16-05-26.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00659.jpg', 'Friday, 21 August 2015 23:04', '1', '3', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1505', 'B030101', 'B030000', 'ប្រធាននាយកដ្ឋាន នៅរដ្ឋលេខាធិការដ្ឋាន អាកាសចរណ៏សីុវិល បើករថយន្ត ទាំងស្រវឹង បុកគេតាមផ្លូវ', '<strong>ភ្នំពេញៈ</strong> កាលពីវេលាម៉ោង៩:៤០នាទីយប់ ថ្ងៃទី២១ ខែសីហា ឆ្នាំ២០១៥ មុខទីស្តីការគណរដ្ឋមន្រ្តី ស្ថិតក្នុងខណ្ឌ៧មករា រាជធានីភ្នំពេញ មានបុរសចំណាស់ស្រវឹងម្នាក់ឈ្មោះ ថុង និច បុក្កលិកនៅ រដ្ឋលេខាធិការដ្ឋានអាកាសចរណ៌សុីវិល តួនាទី ជាប្រធាន នាយកដ្ឋាន បានបើកេថយន្ត 4Runner ពណ៌ខ្មៅ ស្លាកលេខភ្នុំពេញ ២R-៣១៩៦ ក្នុងទិសដៅពីកើតទៅលិច លុះបើកមកដល់ មុខនាយកដ្ឋានផ្លូវដែក ទល់មុខទីស្តីការគណរដ្ឋមន្ត្រី បានបុករថយន្ត Hilux ViGo ពណ៌ក្រហម ស្លាកលេខរដ្ឋ ២៥-២-០៣៧៤ ក្នុងទិសដៅច្រាស់គ្នា បណ្តាលអោយខូចខាតខាងមុខដាច់កង់ខាងឆ្វេង បន្ទាប់ពីបុក ហើយរថយន្ត ដែលបើកដោយ បុរសស្រវឹងនិយាយមិនស្តាប់គ្នា នោះបានហោះឡើង ទៅលើសួនច្បារមុខ ទីស្តីការ គណរដ្ឋមន្ត្រី ទៀតតែសំណាងល្អម្ចាស់ទាំងសងខាង ពុំមានរងរបួសនោះទេ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97610-2015-08-21-15-36-21.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00646.jpg', 'Friday, 21 August 2015 22:28', '21', '3', '5', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1506', 'B030101', 'B030000', 'កម្លាំងនគរបាល  ចរាចររាជធានីភ្នំពេញ បន្តយុទ្ធនាការ ចុះឆែកអាវុធ ជាតិផ្ទុះ និងពង្រឹងច្បាប់ ចរាចរតាមដងផ្លូវ', '<b>ភ្នំពេញៈ </b>កម្លាំងនគរបាលចរាចរជើងគោក នៃស្នងការដ្ឋាន រាជធានីភ្នំពេញ នៅយប់ថ្ងៃទី២១ ខែសីហា ឆ្នាំ២០១៥នេះ បានបន្តប្រតិបត្តិការរបស់ខ្លួនចុះ ត្រួតពិនិត្យអាវុធជាតិផ្ទុះ និងចាប់អ្នកជិះម៉ូតូអត់ពាក់មួក និងកញ្ចក់ នៅតាមដងផ្លូវ សាធារណៈ &nbsp;ក្នុងគោលបំណង ដើម្បីចូលរួម កាត់បន្ថយអាត្រាគ្រោះថ្នាក់ចរាចរ ដែលកំពុងកើនឡើងគួរអោយព្រួយ បារម្ភ និងដើម្បីធ្វើប្រជាពលរដ្ឋ ធ្វើដំណើរនៅតាមដងផ្លូវ ចូលរួមទាំងអស់គ្នាក្នុង ការអនុវត្តច្បាប់ចរាចរ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97609-2015-08-21-14-58-25.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00641.jpg', 'Friday, 21 August 2015 21:56', '0', '3', '5', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1507', 'B030101', 'B030000', 'សភាអាស៊ី សម្រេចរួមគ្នា ដាំដើមឈើ ដើម្បីការពារ បម្រែបម្រួល អាកាសធាតុ', '<strong>ភ្នំពេញ៖</strong> នៅក្នុងសម័យប្រជុំសភា តំបន់អាស៊ី (APA) ចាប់ពីថ្ងៃទី១៧-២១ ខែសីហា ឆ្នាំ២០១៥ នៅទីក្រុងហ្សាកាតា ប្រទេស ឥណ្ឌូនេស៊ី ដែលមានសមាជិកចូលរួម ២១ប្រទេសនោះ បានសម្រេចរួមគ្នាដាំដើមឈើ ដើម្បីការពារការប្រែប្រួលអាកា<wbr>ស ធាតុនៅក្នុងពិភពលោក ។</wbr>', 'http://www.dap-news.com/2011-06-14-02-39-55/97608-2015-08-21-13-41-11.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00640.jpg', 'Friday, 21 August 2015 20:37', '2', '0', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1508', 'B030101', 'B030000', 'លោក ស ថាវី ប្រកាសផ្តល់រង្វាន់ ម៉ូតូស្កូបពីសម្រាប់ សិស្សជាប់និទ្ទេស A', '<strong>ឧត្តរមានជ័យ៖</strong> អភិបាលខេត្តឧត្តរមានជ័យ លោក ស ថាវី បានប្រកាសផ្តល់ម៉ូតូ ស្កូបពីស៊េរីឆ្នាំ២០១៥ មួយគ្រឿង សម្រាប់សិស្សរូប ណាទទួលបាននិទ្ទេស A នៅក្នុងការប្រឡងមធ្យមសិក្សាទុតិយភូមិ បាក់ឌុប ដែលនឹងចាប់ផ្តើម ឡើងនៅថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥សប្តាហ៍ក្រោយនេះ។ លោកអភិបាលខេត្ត ក៏បានស្នើសុំម្ចាសផ្ទះសំណាក់ នៅក្នុងខេត្តទាំងអស់ កុំឲ្យដំឡើងថ្នៃឈ្នួល សម្រាប់មកស្នាក់នៅ ដើម្បីប្រលងបាកឌុបនេះ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97607--a-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00639.jpg', 'Friday, 21 August 2015 20:10', '2', '32', '4', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1509', 'B030101', 'B030000', 'ក្រសួងរ៉ែ និងថាមពល គាំទ្រសម្តេច ហ៊ុន សែន ក្នុងការដោះស្រាយ បញ្ហាព្រំដែន ហើយ ថ្កោលទោស ដល់អ្នក ដែលចោទថា ប្រើផែនទីក្លែងក្លាយ', '<strong>ភ្នំពេញ៖</strong> ថ្នាក់ដឹកនាំ មន្រ្តីរាជការ នៃក្រសួងរ៉ែ និងថាមពល បានសម្តែងការគាំទ្រ យ៉ាងឱឡារិក ចំពោះ រាជរដ្ឋាភិបាលកម្ពុជា ដឹកនាំដោយ សម្តេចនាយករដ្ឋមន្រ្តី ហ៊ុន សែន ដែលបានខំបូជា អស់កម្លាំងកាយ ចិត្ត ក្នុងការការពារទឹកដី ជាពិសេសការដោះស្រាយ បញ្ហាព្រំដែន ជាមួយប្រទេសជិតខាង ហើយបាន ធ្វើការថ្កោលទោស ដល់ជនអគតិមួយចំនួនតូចដែលបាន ធ្វើការប្រឌិត និងចោទប្រកាន់មួលបង្កាច់ មកលើរាជរដ្ឋាភិបាល ចំពោះរឿងនេះ ដើម្បីតែបម្រើនយោបាយប្រជាភិថុតរបស់ខ្លួន។', 'http://www.dap-news.com/2011-06-14-02-39-55/97606-2015-08-21-11-45-28.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-010/chanroth015-aug-150.jpg', 'Friday, 21 August 2015 18:44', '2', '2', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1510', 'B030101', 'B030000', 'ក្រុមសហភាព សហព័ន្ធយុវជនកម្ពុជា ខេត្តព្រៃវែង ចុះជួយធ្វើ ផ្ទះជូនប្រជាពលរដ្ឋ នៅស្រុកពារាំង', '<strong>ព្រៃវែង ៖</strong> ក្រុមសហភាព សហព័ន្ធយុវជនកម្ពុជា ខេត្តព្រៃវែង ដឹកនាំដោយលោក សែម គន្ធា តំណាងលោក ស សុខា ប្រធានសហភាព សហព័ន្ធយុវជន ខេត្តព្រៃវែង កាលពីព្រឹកថ្ងៃទី២១ ខែសីហា ឆ្នាំ២០១៥ បានចុះជួយធ្វើផ្ទះ របស់ប្រជាពលរដ្ឋ មួយខ្នង ស្ថិតនៅ ភូមិព្រៃខ្លា ឃុំរាប ស្រុកពារាំង ទំហំ៤គុណ៥ម៉ែត្រ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97605-2015-08-21-11-39-28.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00624.jpg', 'Friday, 21 August 2015 18:39', '2', '0', '4', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1511', 'B030101', 'B030000', 'លោក ហោ ណាំហុង ស្នើឲ្យក្រុង គីតាគ្យូស្ស៊ូ ជួយផ្គត់ផ្គង់ទឹកស្អាត នៅភ្នំពេញ', '<strong>ភ្នំពេញ៖</strong> លោកឧបនាយករដ្ឋមន្រ្តី ហោ ណាំហុង រដ្ឋមន្រ្តីការបរទេសខ្មែរ បានស្នើឲ្យលោកស្រី សាវរីយ៍ កុយស៊ី នាយកដ្ឋានអន្តរជាតិ របស់ទីក្រុងគីតាគ្យូស្ស៊ូ នៃខេត្តហ៊្វូគុអូការ ចុះអនុស្សារណៈជាមួយគ្នា ដើម្បីជួយផ្គត់ផ្គង់ទឹកស្អាតនៅក្រុងភ្នំពេញ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97604-2015-08-21-11-21-02.html', 'img/dapnews.png', 'Friday, 21 August 2015 18:20', '2', '23', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1512', 'B030102', 'B030000', 'ផ្កាយរណប អាមេរិក ថតរូបភាព ព្យុះទីហ្វុង២ ស្ទួន នៅមហាសមុទ្រ ប៉ាស៊ីហ្វិច', '<strong>(វ៉ាស៊ីនតោន)៖</strong> យោងតាមប្រភព ព័ត៌មាន CNN ចេញផ្សាយនាថ្ងៃសុក្រ ទី ២១ ខែសីហា ឆ្នាំ ២០១៥ បានឲ្យដឹងថា ផ្កាយរណបវិទ្យាសាស្រ្ត បានប្រទះឃើញព្យុះ ទីហ្វុង២ ស្ទួន កំពុងតែ វិលវល់នៅមហាសមុទ្រ បាស៊ីហ្វិច ដែល ព្យុះទីហ្វុងមួយឈ្មោះ ហ្គូនី នឹងធ្វើការបក់ផាត់លើ ប្រទេសហ្វីលីពីន កោះតៃវ៉ាន់ និង ភាគខាងត្បូង ប្រទេសជប៉ុន។', 'http://www.dap-news.com/2011-06-14-02-40-26/97585-2015-08-21-07-43-05.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-131.jpg', 'Friday, 21 August 2015 14:41', '3', '0', '4', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1513', 'B030102', 'B030000', 'ប៉ូលិសថៃ តាមស្វែងរក ជនសង្ស័យ ម្នាក់ទៀតជាស្រ្តី', '<strong>បាងកក៖</strong> ប៉ូលិសថៃ កំពុងស្វែងរកស្រ្តីម្នាក់ ដែលពាក់អាវយឺត ពណ៌ខ្មៅ ដែលសង្ស័យថា មានជាប់ទាក់ទង ទៅនឹង ការចូលរួមបំផ្ទុះគ្រាប់បែក នៅកណ្តាលទីក្រុងបាងកក ថ្មីៗនេះ ។ នេះបើតាមការឲ្យដឹង ពីអ្នកនាំពាក្យ ប៉ូលិស ជាន់ខ្ពស់របស់ប្រទេសថៃ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97584-2015-08-21-07-34-25.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-130.jpg', 'Friday, 21 August 2015 14:31', '2', '23', '0', 't', '2015-08-21', 'f');
INSERT INTO "tbnews" VALUES ('1514', 'B030102', 'B030000', 'ជប៉ុន ជំរុញកូរ៉េខាងជើង ឲ្យចៀសវាង ពីការបង្ករឿង ខណៈកូរ៉េខាងត្បូង គំរាមតបវិញ', '<strong>តូក្យូ៖</strong> នៅព្រឹកថ្ងៃសុក្រនេះ ស្របពេលដែល ប្រទេសកូរ៉េខាងជើង ប្រកាសដាក់ ប្រទេសរបស់ខ្លួនក្នុងពេល មាន សង្គ្រាមនោះ ភាគីជប៉ុន បានជំរុញឲ្យកូរ៉េខាងជើង ចៀសវាងពីការបង្ករឿង ដោយផ្ទុះអាវុធទៀត ស្របពេលដែល កម្តៅកំពុងកើនឡើង នៅឧបទ្វីបកូរ៉េ។ ជាមួយគ្នានេះដែរ កូរ៉េខាងត្បូង ក៏បានគំរាមតបតទៅ វិញដែរ នៅពេល ទទួល ការវាយប្រហារបែបនេះ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97557-2015-08-21-04-15-07.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-119.jpg', 'Friday, 21 August 2015 11:13', '0', '2', '4', 't', '2015-08-21', 'f');
