INSERT INTO "tbnews" VALUES ('1546', 'B020202', 'B020000', 'ផ្ទុះ​នៅ​ធៀន​ជី​ន​អ្នក​ស្លាប់​ឡើង​ដល់​121​នាក់', 'ក្នុង​សន្និសីទ​សារព័ត៌មាន​មួយ អាជ្ញាធរ​រាយការណ៍​កាលពី​ថ្ងៃ​ទី​២២ សីហា​ថា តុល្យការ​ចំនួន​អ្នក​ស្លាប់​ដោយសារ​ការ​ផ្ទុះ​យ៉ាង​ខ្លាំង​ពីរ​ក្នុង​ឃ្លាំង​ស្តុក​ក្រុង​ធៀន​ជី​ង ពេល​នេះ​ឡើង​ដល់​១២១​នាក់​ក្នុង​នោះ​មាន​អ្នក​ពន្លត់ភ្លើង​៦៧​នាក់', 'http://kohsantepheapdaily.com.kh/article/127396.html', 'http://i.kspg.co/2015/8/3026e58f-3ad3-4ddf-95c4-6ae33e16630a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៤៤', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1547', 'B020202', 'B020000', 'គ្រូបង្វឹក​បិសាច​ក្រហម Man Utd មិន​ទិញ​ខ្សែប្រយុទ្ធ​បន្ថែម និង​ពេញចិត្ត​លើ​ទម្រង់​លេង​របស់​កីឡាករ', 'លោក​ល្វី វ៉ា​ន់​ហ្គា​ល់ គ្រូបង្វឹក​ក្លិប​បិសាចក្រហម Man Utd បាន​គូសបញ្ជាក់​ថា ក្លិប​របស់​លោក​មិន​ចាំបាច់​ទិញ​ខ្សែប្រយុទ្ធ​បន្ថែម​ទេ ទោះបី​ក្លិប​ទើបតែ​លេង​ស្មើ​ជាមួយ​ក្លិប Newcastle 0-0 នៅ​ក្នុង​ទឹកដី ', 'http://kohsantepheapdaily.com.kh/article/127389.html', 'http://i.kspg.co/2015/8/7dffa374-81f6-4bee-800f-9c43984a290e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:២៧', '0', '0', '0', 't', '2015-08-23', 'f');
