INSERT INTO "tbnews" VALUES ('1550', 'B020202', 'B020000', 'បាល់ទាត់​អេ​ស្ប៉ា​ញ​ព្រី​មេ​រ៉ា​លី​ហ្គា​បើក​រដូវ​កាល​ថ្មី​សម្រាប់​ចុង​ស​ប្តា​ហ៍​នេះ', 'បាល់ទាត់​អេ​ស្ប៉ា​ញ​ព្រី​មេ​រ៉ា​លី​ហ្គា បើក​រដូវ​កាល​ថ្មី​សម្រាប់​រាត្រី​ចុង​ស​ប្ដា​ហ៍​នេះ​មាន 8 ប្រកួត​។  អ្នកមាន​បុណ្យ Barcelona សង្ឃឹម​ស្ដារ​មុខមាត់​ពេល​ប៉ះ Bilbao ក្រៅ​ផ្ទះ ខណៈ Real M, Espanyol,Atletico, និង Real Betis  ជា​ជម្រើស​ល្អ ។', 'http://kohsantepheapdaily.com.kh/article/127362.html', 'http://i.kspg.co/2015/8/39073d61-57e9-4d60-9c57-77e0f283a92c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:១៧', '0', '0', '0', 't', '2015-08-23', 'f');
