INSERT INTO "tbnews" VALUES ('1565', 'B020102', 'B020000', 'ឃាតករ​សម្លាប់​មន្ត្រី​ធម្មការ ប្លន់​យក​ម៉ូតូ​ជា​ឪពុក​-​កូន រួមគំនិត​គ្នា ទីបំផុត​ដេក​គុក', 'ខេត្តកំពង់ឆ្នាំង ៖ សមត្ថកិច្ច​នគរបាល ខេត្ត នៅ​ព្រឹក​ថ្ងៃ​ទី​២១ សីហា បាន​បង្ហាញ​ជនសង្ស័យ​២​នាក់​ជា​ឪពុក​និង​កូន ដែល​បានរួមគំនិត​គ្នា​វាយ​សម្លាប់​ប្លន់​យក​ម៉ូតូ​មួយ​គ្រឿង​ពី​ជន​រង​គ្រោះជា​មន្ត្រី​មន្ទីរ​ធម្មការ​និង​សាសនា  ខេត្ត និង​ជា​គ្រូបង្រៀន​នៅ​សាលា​ពុទ្ធិកបឋមសិក្សា​ខេត្តកំពង់ឆ្នាំង', 'http://kohsantepheapdaily.com.kh/article/127340.html', 'http://i.kspg.co/2015/8/4482bf2b-65b2-49ab-839d-8f22ba255767.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសៅរ៏ ទី២២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៣០', '0', '0', '0', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1566', 'B020102', 'B020000', 'ហាម​ចេញ​ប្លង់​កម្មសិទ្ធិ​នៅ​តំបន់​ជុំវិញ​ព្រែក​ទប់', 'ខេត្ត​ព្រះ​សីហ​នុ ៖ ប្រធាន​មន្ទីរ​ឧស្សាហកម្ម និង​សិប្បកម្ម លោក ប្រាក់ ប្រាកដ បាន​ចេញ​លិខិត​ជូនដំណឹង​មួយ ទៅ​កាន់​មន្ទីរ​រៀបចំ​ដែនដី​នគរ​រូប​នី​យ៍​កម្ម​និង​សំណង់ ដោយ​ស្នើ​ឱ្យ​មន្ទីរ​ជំនាញ​នេះ​បញ្ឈប់​នូវ​ការ​ចេញ​ប្លង់​កម្មសិទ្ធិ និងច្បាប់​សាងសង់​គ្រប់​ប្រភេទ', 'http://kohsantepheapdaily.com.kh/article/127333.html', 'http://i.kspg.co/2015/8/3de863f1-e283-4764-89b3-78ad27344dd1.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសៅរ៏ ទី២២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៥២', '0', '0', '0', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1567', 'B020102', 'B020000', 'មាន​លោក​ផ្កាយ​2​និង​លោក​ធំ សុំ​ដាក់​ល្បែងស៊ីសង​ទូ​ហ្គេ​ម​បាញ់​ត្រី​ត្រូវ​បដិសេធ', 'រាជធានី​ភ្នំពេញ ៖ នៅ​សាលា​ខណ្ឌ​៧​មក​រា កាលពី​ព្រឹក​ថ្ងៃ​ទី​២១ សីហា មាន​បើក​កិច្ចប្រជុំ​គណៈ​បញ្ជាការ​ឯកភាព​ខណ្ឌ លើ​របៀបវារៈ​ចំនួន​៥​ចំណុច ក្នុង​ទិស​ដៅ​អនុវត្ត​សម្រាប់​ខែសីហា​នេះ​។ កិច្ចប្រជុំ​បាន​ធ្វើ​ឡើង​ក្រោម​អធិបតីភាព​លោក លី​ម សុភា អភិបាល​ខណ្ឌ', 'http://kohsantepheapdaily.com.kh/article/127327.html', 'http://i.kspg.co/2015/8/71e1fe4d-3045-4d55-af94-67d33ade7b0f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃសៅរ៏ ទី២២ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:០៧', '0', '0', '0', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1568', 'B020101', 'B020000', 'ការប្រឡង​សញ្ញា​ប័ត្រមធ្យមសិក្សា​ទុតិយភូមិ​បាន​ចាប់ផ្តើម​ហើយព្រឹក​នេះ', 'រាជធានី​ភ្នំពេញ ៖ សម័យ​ប្រឡង​សញ្ញា​ប័ត្រមធ្យមសិក្សា​ទុតិយភូមិ ឬ​បាក់​ឌុ​ប​ចំណេះ​ទូទៅ និង​បំពេញវិជ្ជា​ប្រចាំឆ្នាំ​២០១៤-២០១៥ បាន​ចាប់ផ្តើម​ហើយ នៅ​ព្រឹក​ថ្ងៃ​ទី​២៤​ សីហា ​​២០១៥', 'http://kohsantepheapdaily.com.kh/article/127430.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:០៦', '2', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1569', 'B020101', 'B020000', 'ខណៈ​ក្រុង​សែន​មនោ​រម្យ​ទាំងមូល​កំពុង​គ្របដណ្តប់​ដោយ​អ័ព្ទ​ផ្សែង', 'ខេត្តមណ្ឌលគិរី ៖ ខណៈ​ពេល​នេះ ក្រុង​សែន​មនោ​រម្យ ខេត្តមណ្ឌលគិរី​ទាំងមូល​កំពុង​គ្របដណ្តប់​ដោយ​អ័ព្ទ​ផ្សែង ដែល​មិន​អាច​មើលផ្លូវ​ឃើញ ទោះបី​ជិះ​ម៉ូតូ​-​ឡាន​បើកភ្លើង​ភ្លឺ​យ៉ាងណា​ក៏​ដោយ ។', 'http://kohsantepheapdaily.com.kh/article/127423.html', 'http://i.kspg.co/2015/8/cab58739-0108-4f52-a7d2-aa3738b13bcc.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២១:៤១', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1570', 'B020101', 'B020000', 'ភ្លៀង​ខ្លាំង​កាលពី​រសៀល​ថ្ងៃនេះ​បណ្តាល​ឱ្យ​បាក់​ដើម​ត្រ​សេក​១​ដើម​នៅ​ក្រោយ​ក្រសួង​អភិវឌ្ឍន៍ជនបទ', 'រាជធានី​ភ្នំពេញ ៖ ក្នុង​ពេល​ភ្លៀង​ខ្លាំង​នា​រសៀល​ថ្ងៃ​ទី​២៣ ខែមេសា ឆ្នាំ​២០១៥​នេះ មាន​ករណី​ដើម​ត្រ​សេក​​១​ដើម​បាន​ដួលរ​លំនៅ​វិ​ថី​ឆេ​កូ កាច់ជ្រុង​ខាងក្រោយ​ក្រសួង​អភិវឌ្ឍន៍ជនបទ ។ បច្ចុប្បន្ន​មន្ទីរ​សាធារណការ​រាជធានី​កំពុង​កាប់​ និង​ដឹក​ចេញ', 'http://kohsantepheapdaily.com.kh/article/127418.html', 'http://i.kspg.co/2015/8/ac7fa691-c711-4424-a96b-cc56b37f96eb.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៩:០៦', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1571', 'B020101', 'B020000', 'រថយន្ត​កូរ៉េ​ដឹកឈើ​១​គ្រឿង​ត្រូវ​ម្ចាស់​កាច់​ស្លាក​លេខ​ចោល ខណៈ​បើក​រត់​រហូត​ជាប់ផុង​ទៅ​មុខ​លែង​រួច', 'ខេត្តកំពង់ធំ ៖ រថយន្ត​កូរ៉េ​ដឹកឈើ​មួយ​គ្រឿង​ត្រូវ​បាន​ព្រះរាជអាជ្ញា​អម​សាលាដំបូង​ខេត្តកំពង់ធំ​បញ្ជា​ឱ្យ​លោក ភោគ ចិ​ត្រា នាយក​សង្កាត់​រដ្ឋបាល​ព្រៃឈើ​សាលា​វិស័យ​ធ្វើការ​ឃាត់​ទុក រង់ចាំ​កម្លាំង​មាន​សមត្ថកិច្ច​ចម្រុះ​ទៅ​ដល់ ។', 'http://kohsantepheapdaily.com.kh/article/127417.html', 'http://i.kspg.co/2015/8/e67e05a3-fed2-45db-8822-71a699e736e9.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៨:៤៩', '1', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1572', 'B020101', 'B020000', 'បេក្ខជន​ប្រលង​ភាគច្រើន​បង្ហាញ​ពី​ជំហរ​ជោគជ័យ ​ខណៈ​មណ្ឌល​ប្រលង​រឹត​បន្ដឹង​កាន់តែ​ខ្លាំង', 'ខេត្តបាត់ដំបង ​៖ សម័យ​ប្រលង​មធ្យមសិក្សា​ទុតិយភូមិ ត្រូវ​គេ​មើលឃើញ​ថា បាន​និង​កំពុង​រឹត​បន្ដឹង​កាន់តែ​ខ្លាំង​ឡើង​ៗ ក្នុងរ​យៈពេល​២​ឆ្នាំ​ចុង​ក្រោយ​នេះ ប៉ុន្តែ​តាម​រយៈ​ការ​ធ្វើ​បទ​សម្ភាស​ន៍​ជាមួយ​កោះសន្តិភាព', 'http://kohsantepheapdaily.com.kh/article/127416.html', 'http://i.kspg.co/2015/8/71cff2a0-f6ae-454f-a3f8-4bbf6b2ed596.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:៣៩', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1573', 'B020101', 'B020000', 'ឃុំខ្លួន​យុវជន​ម្នាក់​ដាក់​ពន្ធនាគារ​បណ្តោះអាសន្ន​ពី​បទ​ញុះញង់​ឲ្យ​មាន​ឧក្រិដ្ឋកម្មផ្តើម​ចេញពីសម្តី​', 'រាជ​ធានី​ភ្នំពេញ​ ៖ សាលាដំបូង​រាជធានី​ភ្នំ​ពេញ កាលពី​ថ្ងៃ​ទី​២២ សីហា បាន​សម្រេច​ចេញ​ដីកា​ឃុំខ្លួន​យុវជនម្នាក់​ដាក់​ពន្ធនាគារ​ជា​បណ្តោះអាសន្នពី​បទ​ញុះញង់​ឲ្យ​មាន​ឧក្រិដ្ឋកម្ម​ ', 'http://kohsantepheapdaily.com.kh/article/127415.html', 'http://i.kspg.co/2015/8/7f204e24-b286-4178-a1c6-90284fee3217.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:២៨', '1', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1574', 'B020101', 'B020000', '​ភ្លៀង​ធ្លាក់​ខ្លាំង​រយៈពេល​៤០នាទី​​ ​ភ្នំពេញ​ពុំ​​​ទាន់​​​​ឃើញ​មាន​តំបន់ណា​មួយ​​លិច​​ទឹក​​ឡើយ​', '​រាជ​ធានី​ភ្នំពេញ ៖ ​តាម​​សេចក្តីរាយការណ៍​ពី​អ្នក​ឆ្លើយ​ឆ្លងព័ត៌មាន​ បាន​ឱ្យ​ដឹង​ថា នៅ​រសៀល​ម៉ោង​២​និង​១០​នាទី​​​ថ្ងៃទី​២៣ សីហា នេះ ​​​មាន​​ភ្លៀង​ធ្លាក់​​​មួយ​មេ​ធំ​រយៈ​ពេល​៤០នាទី ​​គេ​សង្កេត​ឃើញ​ថា ', 'http://kohsantepheapdaily.com.kh/article/127414.html', 'http://i.kspg.co/2015/8/386ef5dc-431a-4b8f-9d3d-9bf35fb130da.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:២១', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1575', 'B020101', 'B020000', '​ពិធី​បុណ្យ​ឆ្លង​ផ្លូវ​មួយ​ខ្សែ​នៅ​ភូមិ​ត្រពាំង​ឈូក​', 'រាជ​ធានី​ភ្នំពេញ​ ៖ សេចក្តី​រាយការណ៍​បាន​ឱ្យ​ដឹង​ថា នៅ​វេលា​ម៉ោង​៤​រសៀល ថ្ងៃ​ទី​២៣ សីហា នេះ លោក​ហេង ស៊ី​ថុល អភិ​បាល​រង​ខណ្ឌ​សែន​សុខ​ បាន​អញ្ជើញ​ជា​អធិបតី​ក្នុង​ពិធី​បុណ្យ​ឆ្លង​ផ្លូវ​មួយ​ខ្សែ', 'http://kohsantepheapdaily.com.kh/article/127411.html', 'http://i.kspg.co/2015/8/15692167-e330-4c30-ad75-67945731e924.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:៥៩', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1576', 'B020101', 'B020000', 'មន្ត្រី​ប៉េ​អឹម​ម្នាក់​​​​ចង​.​កសម្លាប់​ខ្លួន ដោយ​សារ​តូចចិត្ត​​នឹង​​ជំងឺ​​ប្រចាំ​កាយ', 'ខេត្តក្រចេះ​ ៖ បុរស​ម្នាក់​ដែល​គេ​ស្គាល់​ថា​ ជា​មន្ត្រី​ប៉េ​អឹម​ផ្ទៃ​ប្រទេសក្រុម​កងចល័ត ​​ដោយ​សារ​តែ​​មាន​ជំងឺ​​ប្រចាំ​កាយ​​រ៉ាំរ៉ៃ​ជា​ច្រើន​ឆ្នាំ​​​ព្យាបាល​មិនជា​ បាន​សម្រេចចិត្ត​​​ធ្វើ​អត្ត​ឃាត​​ដោយ​ចង.​ក​សម្លាប់​ខ្លួន ', 'http://kohsantepheapdaily.com.kh/article/127410.html', 'http://i.kspg.co/2015/8/5ac5181a-40a7-4e51-a9b9-2aaa3cfc4479.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:៥៤', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1577', 'B020101', 'B020000', 'ករណីភ្លើងឆេះកុដិ​នៅក្នុងវត្តកោះបានរលត់ទំាងស្រុង​ហើយនៅម៉ោង៤និង៣០នាទី​រសៀលនេះ', 'រាជធានីភ្នំពេញ ៖​ បើតាមអ្នកឆ្លើយឆ្លងព័ត៌មាន​កោះសន្តិភាពបានឲ្យដឹង​នៅប៉ុន្មាននាទីមុននេះថា ​ភ្លើងដែលឆេះកុដិ​នៅក្នុងវត្តកោះនៅសង្កាត់បឹងរំាង ​ខណ្ឌដូនពេញបានរលត់​', 'http://kohsantepheapdaily.com.kh/article/127408.html', 'http://i.kspg.co/2015/8/49d76a0c-21fb-4f59-825e-a4ac686ffb6b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:៣៩', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1578', 'B020101', 'B020000', 'ខណៈនេះ​ មានករណី​ភ្លើងឆេះផ្ទះនៅក្បែរវត្តកោះ', 'រាជធានីភ្នំពេញ ​៖ បើតាមអ្នកឆ្លើយឆ្លង​ព័ត៌មានកោះសន្តិភាព​បានឲ្យដឹងជំហានដំបូង​នៅប៉ុន្មាននាទីមុននេះថា ​លំនៅដ្ឋានមួយខ្នង​ ស្ថិតនៅក្បែរវត្តកោះ​​បានត្រូវភ្លើង​កំពុង​ឆាបឆេះមិនទាន់​ដឹងមូលហេតុ ។ មកដល់ម៉ោង៤និង២៧នាទីរសៀលថ្ងៃទី២៣សីហានេះ រថយន្តពន្លត់អគ្គិភ័យ៣​គ្រឿងបានទៅដល់គោលដៅ​និងកំពុងបាញ់ពន្លត់ ៕', 'http://kohsantepheapdaily.com.kh/article/127407.html', 'http://i.kspg.co/2015/8/d2e363ef-69dc-4121-a73b-d1635967a719.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:២៩', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1579', 'B020101', 'B020000', 'លោក នុ​ត ពុធ​ដា​រ៉ា សំណេះសំណាលជាមួយ​កម្មករកម្មការិនី​​​ជិត​៤០០នាក់​​ នៅ​សង្កាត់​ដង្កោ', '​​រាជ​ធានីភ្នំពេញ​ ៖ លោក នុ​ត ពុធ​ដា​រ៉ា អភិបាល​ខណ្ឌដង្កោ បាន​ចុះ​សំណេះសំណាល​ជាមួយ​កម្មករ​កម្មការិនី ដែល​ស្នាក់​នៅ​តាម​បន្ទប់​ជួល នៅ​ក្នុងភូមិ​សំបួរ និង​ភូមិ​ម័​ល សង្កាត់​ដង្កោ', 'http://kohsantepheapdaily.com.kh/article/127405.html', 'http://i.kspg.co/2015/8/ddef698f-02c0-4ae6-b00b-51f711eb0f2f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:២៦', '1', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1580', 'B020101', 'B020000', 'យុវសិស្សនៅ​កំពង់ឆ្នាំង សម្រុកទៅ​បន់ស្រន់​លោកយាយ​ទេពឱ្យ​ជួយ​ប្រឡង​ជាប់', 'ខេត្តកំពង់ឆ្នាំង ៖ ខណៈ​​ការ​ប្រឡង​សញ្ញាប​ត្រ​មធ្យម​សិក្សា​ទុ​តិ​យ​ភូមិ (​​ប្រឡងបាក់​ឌុ​ប) ​នឹង​ចាប់ផ្តើម​ប្រព្រឹត្ត​ទៅ​នៅ​ព្រឹកថ្ងៃ​ទី​២៤ ​សីហា ​​២០១៤​ ស្អែក​​នេះ នៅ​​ក្នុង​​​វត្ត​ទេពធី​តា​រាម ស្ថិត​ក្នុងភូមិ​ផ្សារឆ្នាំង', 'http://kohsantepheapdaily.com.kh/article/127404.html', 'http://i.kspg.co/2015/8/5620c703-92b2-4797-9224-dbccb43bc879.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:០៣', '1', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1581', 'B020101', 'B020000', 'សមត្ថ​កិច្ច​ចម្រុះ​ចុះ​យុទ្ធ​នា​ការ​បិទ​ទី​តាំង​សិប្ប​កម្ម​ឈើ​អត់​ច្បាប់​មួយ​កន្លែងនៅ​ស្រុក​ពេជ្រាដា', 'ខេត្តមណ្ឌលគិរី ៖ ​នៅ​រសៀល​ថ្ងៃ​ទី​២៣ សីហា ២០១៥ នេះ ​មន្ត្រី​ផ្នែក​រដ្ឋបាល​ព្រៃឈើ​សែន​មនោ​រម្យ​ និង​ស្រុក​ពេ​ជ្រា​ដា ​រួម​និង​កំលាំង​ចំរុះ​ដឹកនាំ​ដោយ​លោកនាយ​រង​ផ្នែក ហេង ចិ​ន្ត្រា​', 'http://kohsantepheapdaily.com.kh/article/127402.html', 'http://i.kspg.co/2015/8/e49d98fd-9c86-4125-8223-34e2607cbe4c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៥៨', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1582', 'B020101', 'B020000', 'ឮថាស្រីម៉ីល្បីខាងដឹកជញ្ជូន​ឈើប្រណីតមិនខ្លាចសមត្ថកិច្ច​រតនគិរី ​ព្រោះមានមន្រ្តីតុលាការ​នៅពីក្រោយ', 'ខេត្តរតនគិរី ៖​ទីបំផុតទៅឈើប្រណីត​ប្រភេទធ្នង់រាប់រយកំណាត់​ដែលសមត្ថកិច្ចរតនគិរីបង្រ្កាប​នៅក្នុងផ្ទះមេឈ្មួញឈើ​ជនជាតិវៀតណាមឈ្មោះបេ ​នៅក្នុងភូមិជ្រៃ ​សង្កាត់​', 'http://kohsantepheapdaily.com.kh/article/127398.html', 'http://i.kspg.co/2015/8/39bbe1cf-4328-4f9a-a2a0-f160eb116ef9.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៤៩', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1583', 'B020101', 'B020000', '​បង្ក្រាប​បទល្មើស​នេសាទ​​ទ្រង់ទ្រាយ​ធំនៅ​តំបន់​បឹង​ទន្លេសាប​នៃ​ក្រុង​សៀមរាប', 'ខេត្តសៀមរាប ៖ សមត្ថកិច្ច​ចម្រុះ​រួម​មានប៉ូលិស​​ ​ប៉េ​អឹម ​បរិស្ថាន ដឹកនាំ​បញ្ជា​ផ្ទាល់​ដោយ​លោក​គឹ​ម ឆៃ​ហៀ​ង អភិបាលរង​ខេត្តបានឱ្យ​កម្លាំង​សមត្ថកិច្ច​ចម្រុះ​ដឹកនាំ​ដោយ​លោក​វរ​ស​នី​យ៍ត្រី ខោ ស៊ុន​ភិ​រម្យ', 'http://kohsantepheapdaily.com.kh/article/127395.html', 'http://i.kspg.co/2015/8/dcf4f60d-e667-4e9d-b454-df984114f818.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៤៣', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1584', 'B020101', 'B020000', 'ពលរដ្ឋ​ខ្មែរ​៦នាក់ត្រឡប់ពី​កាប់​ឈើ​គ្រ​ញួ​ងនៅ​ថៃ​​ត្រូវ​ប៉ូ​លិសការពារ​ព្រំដែន​គោក​កម្ពុជា-ថៃ​ឃា​ត៉​ខ្លួន', 'ខេត្ត​ឧត្តរមានជ័យ ៖ ប៉ូ​លិ​ស​ការពារ​ព្រំដែន​គោក​កម្ពុជា-ថៃ​ក្នុង​កងវរសេនាតូច​លេខ​៩០៥​បាន​​ឃាត់ខ្លួន​ពលរដ្ឋ​ខ្មែរ​៦​នាក់​ដែលឆ្លង​ដែន​​ខុសច្បាប់​ចូល​ទៅ​ក្នុង​ទឹកដី​ថៃ​រុករកកាប់​ឈើ​គ្រ​ញួ​ង', 'http://kohsantepheapdaily.com.kh/article/127391.html', 'http://i.kspg.co/2015/8/4cf0c1fc-05ab-4c0f-b547-7229cb6afe2f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៣៥', '0', '0', '0', 't', '2015-08-23', 'f');
