INSERT INTO "tbnews" VALUES ('1592', 'B020101', 'B020000', '​​យុទ្ធនាការ​ចាក់​ថ្នាំ​វ៉ាក់សាំង​​​បង្ការ​ជំងឺសារ​ទឹក​​ដល់​​គោក្របី​ នៅ​ស្រុក​ព្រះនេត្រព្រះ', 'ខេត្តបន្ទាយមានជ័យ​​ ៖ មន្ត្រី​ពេទ្យសត្វ​នៅ​រដូវភ្លៀង​​​នេះ​បាន​ចុះ​យុទ្ធនាការ​ចាក់​ថ្នាំ​វ៉ាក់សាំង​បង្កា​រ​ជំងឺសារ​ទឹក​សត្វ​គោ​និង​ក្របី​បាន​ចំនួន​១៩៥​ក្បាល ​កាលពី​​ថ្ងៃ​ទី​២២ ​សីហា​២០១៥ ​នៅ​ភូមិ​​ទឹក​ជោ ​ឃុំ​ទឹកជោ​ ស្រុក​ព្រះនេត្រព្រះ​ ​។​', 'http://kohsantepheapdaily.com.kh/article/127373.html', 'http://i.kspg.co/2015/8/65b8e0ec-4ee5-40d5-9b45-d2a14b031e4d.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១២:៤១', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1593', 'B020401', 'B020000', 'វិស័យ​អចលនទ្រព្យ​កម្ពុជា នៅ​តែ​រក្សា​កំណើន​មួយ​ល្អ​ក្នុង​ឆមាស​ទី​1​ឆ្នាំ​នេះ', 'រាជធានី​ភ្នំពេញ ៖ ក្រុម​អ្នកជំនាញ​លើ​វិស័យ​អចលនទ្រព្យ​នៅ​កម្ពុជា បាន​ឲ្យ​ដឹង​ថា​ស្ថានភាព​វិនិយោគ​លើ​វិស័យ​អចលនទ្រព្យ​នៅ​ឆ្នាំ​២០១៥​នេះ មិន​មានការ​ប្រែប្រួល​ខ្លាំង​ណាស់ណា​ទេ បើ​ធៀប​នឹង​ឆ្នាំ​២០១៤​កន្លង​មក', 'http://kohsantepheapdaily.com.kh/article/127370.html', 'http://i.kspg.co/2015/8/83e800b5-ab88-469e-9c63-91cd9d2506a0.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០៧', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1594', 'B020401', 'B020000', 'ចេ​ង ខេ​ង ៖ ពី​អ្នកកាសែត​ក្លាយជា​សេដ្ឋី​អចលនទ្រព្យ​ឈានមុខ​គេ​នៅ​កម្ពុជា', 'រាជធានី​ភ្នំពេញ ៖ ស្ថិត​ក្នុង​ការិយាល័យ​បំពាក់​ទៅ​ដោយ​ឧបករណ៍​ទំនើប ម៉ាស៊ីនត្រជាក់ ប្រកបដោយ​ផាសុកភាព​នៃ​ក្រុមហ៊ុន​អចលនទ្រព្យ​ដ៏​ធំ​មួយ​នៅ​កម្ពុជា គឺ​ក្រុមហ៊ុន​អចលនទ្រព្យ ស៊ី​ភី​អិ​ល ខេ​ម​បូ​ឌា បុរស​ដែល​មាន​មាឌ​មាំ ', 'http://kohsantepheapdaily.com.kh/article/127367.html', 'http://i.kspg.co/2015/8/5b420bbc-cbef-406b-b4ed-7d25db70bad2.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០២', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1595', 'B030101', 'B030000', 'គ្រូបង្រៀននៅវិទ្យាល័យ វត្តអង្គរជ័យ ធ្លាក់ខ្លួនក្លាយជា អ្នកជួញដូរថ្នាំញៀន', '<b>កំពត៖</b> លោកគ្រូ ព្រំ ចាន់ថុល អាយុ ៣៥ឆ្នាំ បង្រៀន នៅវិទ្យាល័យវត្តអង្គរជ័យ បានធ្លាក់ខ្លួនក្លាយជាអ្នកជួញដូរ ថ្នាំញៀន ត្រូវបានកម្លាំងនគរបាលឃាត់ខ្លួន កាលពីយប់ថ្ងៃទី២១ ខែសីហា ហើយក៏ត្រូវបានកម្លាំងនគរ បាលការិយាល័យប្រឆាំងគ្រឿងញៀនខេត្តកំពត បានបញ្ជូនខ្លួនទៅកាន់សាលាដំបូងខេត្ត ដើម្បីចាត់ការតាមច្បាប់ នាព្រឹកថ្ងៃអាទិត្យ ទី២៣ ខែសីហា នេះផងដែរ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97711-2015-08-23-11-46-13.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/SR-068.jpg', 'Sunday, 23 August 2015 18:44', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1596', 'B030101', 'B030000', 'បិទសិប្បកម្មកែច្នៃ ឈើខុសច្បាប់មួយកន្លែង នៅស្រុកពេជ្រាដា រឹបអូសឈើ ជាច្រើនម៉ែត្រគូប', '<b>មណ្ឌលគិរី៖</b> មន្រ្តីរដ្ឋបាលព្រៃឈើសែនមនោរម្យ និងស្រុកពេជ្រាដា រួមនិងកម្លាំងសមត្ថកិច្ចចម្រុះ បានចុះបិទ សិប្បកម្មកែច្នៃឈើអត់ច្បាប់មួយកន្លែងនៅស្រុកពេជ្រាដា ដោយរឹបអូសឈើកែច្នៃជាច្រើនម៉ែត្រគូប។', 'http://www.dap-news.com/2011-06-14-02-39-55/97710-2015-08-23-11-31-34.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/SR-064.jpg', 'Sunday, 23 August 2015 18:30', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1597', 'B030101', 'B030000', 'លោក អ៊ុំ សុផល ដឹក​នាំ​សហការី ជួប​សំ​ណេះ​សំ​ណាល និង​ចែក​អំ​ណោយ ជូន​សមា​ជិក​បក្ស', '<strong>បន្ទាយមានជ័យ៖</strong> លោក អ៊ុំ សុផល សមាជិកអចិន្ត្រយ៍បក្សក្រុងប៉ោយប៉ែត បានដឹកនាំ សមាជិក ចំនួន៤១នាក់ នៅរសៀលថ្ងៃទី២៣ ខែសីហា ឆ្នាំ២០១៥នេះ ចុះសំណេះសំណាល ជាមួយក្រុមការងារបក្សនៅភូមិអូរនាង សង្កាត់ផ្សារកណ្តាល ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97709-2015-08-23-11-22-59.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0922.jpg', 'Sunday, 23 August 2015 18:19', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1598', 'B030101', 'B030000', 'រថយន្តដឹកទំនិញ​ចុះ​ចំណោទ​ ស្ពានតាតៃ​ក្រឡាប់​ ស្លាប់​និង​របួស​ ៣នាក់ នៅ​ស្រុក​កោះកុង​', '<strong>កោះកុង៖</strong> នៅ រសៀល វេលាម៉ោង ៣.០០ ថ្ងៃទី២៣ ខែសីហា ឆ្នាំ២០១៥នេះ មានរថយន្តដឹកទំនិញខ្នាតធំ (យីុឌុប) បានចុះចំណោទស្ពានតាតៃ ឃុុតាតៃ ស្រុកកោះកុង ខេត្តកោះក៧ង ក្រឡាប់ដោយខ្លួនឯង បណ្តាលអោយស្លាប ២ នាក់ នៅជាប់កាប៊ីន របួសធ្ងន់១នាក់ ប្រុស ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97708-2015-08-23-10-53-04.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0917.jpg', 'Sunday, 23 August 2015 17:52', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1599', 'B030101', 'B030000', 'អគ្គិភ័យឆេះកុដិក្នុង​វត្ត​កោះ ខូច​ខាត​ម៉ូតូ១គ្រឿង​និងសម្ភារៈ​មួយ​ចំនួន​', '<strong>ភ្នំពេញ៖</strong> ភ្នាក់ងារបានរាយការណ៍ឲ្យដឹងថា នៅវេលាម៉ោងប្រមាណ ១៦៖១៥នាទីថ្ងៃទី២៣ ខែសីហា ឆ្នាំ២០១៥នេះ ចំនុចកុដិលេខ២៣ ផ្លូវ៦៣ កែង១៧៨ ក្រុម៣៧ ភូមិលេខ៩ សង្កាត់បឹងរាំង ត្រូវបានអគ្គិភ័យឆាបឆេះ ប៉ុន្តែត្រូវបានរថយន្តពន្លត់អគ្គិភ័យបាញ់ពន្លត់ទាំងស្រុងនៅម៉ោងប្រមាណ ១៦៖៤០នាទី។', 'http://www.dap-news.com/2011-06-14-02-39-55/97707-2015-08-23-10-52-57.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-099/v_0212.jpg', 'Sunday, 23 August 2015 17:49', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1600', 'B030101', 'B030000', 'មានអ្វីប្លែក​នៅលើ​ Facebook របស់​នាយករដ្ឋ​មន្រ្តីខ្មែរ ?​', '<strong>ភ្នំពេញ៖</strong> Facebook របស់នាយករដ្ឋមន្រ្តីកម្ពុជា កំពុងទាញការចាប់អារម្មណ៌ជាច្រើនពីមហាជន តាមរយៈការបង្ហោះនូវព័ត៌មាន និងរូបភាពប្លែកៗ រួមទាំងរូបថតអនុស្សាវរីយ៍ផ្អែមល្ហែម (ភក្តីស្នេហ៌) របស់សម្តេច និងជំទាវទីមួយផងដែរ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97705---facebook--.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-099/v_0206.jpg', 'Sunday, 23 August 2015 16:49', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1601', 'B030101', 'B030000', 'លោក ស ថាវី ប្រាប់ប្រជាពលរដ្ឋ ដែលចេញចូល ប្រទេសថៃ ឲ្យមានការ ប្រុងប្រយ័ត្នខ្ពស់', '<strong>ឧត្តរមានជ័យ៖</strong> អភិបាលខេត្តឧត្តរមានជ័យ លោក ស ថាវី នៅថ្ងៃនេះ បានអំពាវនាវដល់ប្រជាពលរដ្ឋខ្មែរ ដែល ឧស្សាហ៍ចេញចូលទៅប្រទេសថៃ ជាពិសេសប្រជាពលរដ្ឋទៅស្វែងរកការងារធ្វើក្នុងប្រទេសថៃ ត្រូវមានការប្រុង ប្រយ័ត្នឲ្យបានខ្ពស់ ដើម្បីសុវត្ថិភាពផ្ទាល់ខ្លួន។ ការអំពាវនាវរបស់លោកអភិបាលខេត្ត បានធ្វើឡើងក្រោយពេល ដែលប្រទេសថៃ បានកើតមានការបំផ្ទុះគ្រាប់កណ្តាលទីក្រុងបាងកក កាលពីថ្ងៃចន្ទ ទី១៧ ខែសីហា ឆ្នាំ២០១៥ កន្លងទៅនេះ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97704-2015-08-23-09-25-39.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/SR-056.jpg', 'Sunday, 23 August 2015 16:24', '1', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1602', 'B030101', 'B030000', 'អ្នកនាំ​ពាក្យ​ក្រសួង​ អប់​រំពឹងថា ​នឹង​គ្មាន​បាតុភាព​អសកម្ម​ កើតឡើង​ក្នុង​ពេល​បាក់ឌុប​ (មានសំឡេង)', '<strong>ភ្នំពេញ៖</strong> លោក រស់ សាលីន អ្នកនាំពាក្យក្រសួងអប់រំ យុវជននិងកីឡា បានសម្តែងសង្ឃឹមទុកជាមុនថា ក្នុងការប្រឡងសញ្ញាបត្រទុតិយភូមិ ឬបាក់ឌុបរយៈពេល២ថ្ងៃ ចាប់ពីថ្ងៃទី២៤-២៥ ពិតជាគ្មានភាព អសកម្មណាមួយកើត មានឡើងឡើយ ព្រោះក្រសួងបានសហការ ជាមួយក្រសួងមហាផ្ទៃ និងភាគីពាក់ព័ន្ធ ដើម្បីដាក់កំលាំង ការពារសណ្តាប់ធ្នាប់ទូទៅ នៅតាមមណ្ឌលទូទាំងប្រទេស។', 'http://www.dap-news.com/2011-06-14-02-39-55/97703-2015-08-23-08-53-26.html', 'img/dapnews.png', 'Sunday, 23 August 2015 15:51', '1', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1603', 'B030101', 'B030000', 'លោក អ៊ុំ ម៉ារ៉ា អំពាវនាវ​អោយ​មន្រ្តី​បក្ស​ បន្ត​រក្សា​សន្តិភាព ​​ស្ថេរភាព​​ ​នយោបាយ និង​សណ្តាប់​ធ្នាប់ សង្គម​តាម​មូលដ្ឋាន​', '<strong>ព្រះវិហារ :</strong> លោក អ៊ុំ ម៉ារ៉ា ប្រធានគណបក្ស ប្រជាជនកម្ពុុជា និងជាអភិបាល ខេត្តព្រះវិហារ បានថ្លែងអំពាវនាវ ដល់បណ្តាថ្នាក់ដឹកនាំ និងមន្រ្តី គណបក្សគ្រប់លំដាប់ថ្នាក់ ត្រូវបន្តចូលរួមថែរក្សា សន្តិភាព ស្ថេរភាព&nbsp; នយោបាយ និង សន្តិសុខសណ្តាប់ធ្នាប់សង្គម តាមមូលដ្ឋាន ជូនប្រជាពលរដ្ឋ រស់នៅប្រកបដោយផាសុកភាព និងសុភមង្គល ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97702-2015-08-23-07-26-31.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-099/v_0200.jpg', 'Sunday, 23 August 2015 14:25', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1604', 'B030101', 'B030000', 'អ្នកបើក រថយន្តស្ទើរភ្លើង បុករះរថយន្ត២គ្រឿង និងម៉ូតូ២គ្រឿងទៀត ខូចខាតយ៉ាងដំណំ តែមិនបង្កឲ្យមាន មនុស្សស្លាប់', '<strong>បន្ទាយមានជ័យ៖</strong> នេះសំណាង១០០គ្រោះថ្នាក់ ដែលគ្មានមនុស្សស្លាប់ និងរងរបួសនៅក្នុងទីប្រជុំជន ដោយសារតែស្រ្តី វ័យចំណាស់ម្នាក់ បានបើករថយន្តស្លន់ ជាន់ហ្គាស្មានថាហ្វ្រាំងទៅបុករះផ្ទួនៗ លើរថយន្ត២គ្រឿង និងម៉ូតូ២គ្រឿងខូចខាតយ៉ាងដំណំ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97701-2015-08-23-07-16-24.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-010/chanroth015-aug-174.jpg', 'Sunday, 23 August 2015 14:15', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1605', 'B030102', 'B030000', 'អាស៊ាន នៅតែសន្យាថា សមាហរណកម្មសេដ្ឋកិច្ចអាស៊ាន នៅតែអនុវត្តឲ្យបានដដែល', '<strong>កូឡាឡាំពួរ៖</strong> កាលពីថ្ងៃសៅរ៍ម្សិលមិញនេះ មានកិច្ចប្រជុំថ្នាក់រដ្ឋមន្ត្រីក្រសួងសេដ្ឋកិច្ច និងពាណិជ្ជកម្មដែល មានសមាសភាពថ្នាក់ដឹកនាំ មកពីបណ្តាប្រទេសសមាជិក អាស៊ានទាំងអស់ចូលរួម កម្មវិធីនេះបានរៀបចំឡើងនៅទីក្រុងកូឡាឡាំពួរ ប្រទេសម៉ាឡេស៊ី ។ នៅក្នុងកិច្ចប្រជុំនេះបានបញ្ជាក់អំពីរបៀបវារៈសំខាន់នោះ គឺថា "នៅតែរក្សាដដែល" សម្រាប់ការអនុវត្តន៍ទៅលើការធ្វើសមាហរណកម្មសេដ្ឋកិច្ចអាស៊ាន ទោះបីជាស្ថានភាពសេដ្ឋកិច្ចនៅក្នុងតំបន់ ស្ថិតក្នុងស្ថានភាព ប្រឈមមុខប៉ុន្មានខែមកនេះក្តី ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97685-2015-08-23-04-38-14.html', 'img/dapnews.png', 'Sunday, 23 August 2015 11:35', '3', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1606', 'B030102', 'B030000', 'ធ្លាក់​ឧទ្្ធម្ភាគចក្រ នៅ​ភាគខាងត្បូង​នៃ​អង់គ្លេស ​ស្លាប់​០៧​នាក់​', '<strong>ឡុងដ៏៖</strong> ទីភ្នាក់ងារព័ត៌មានចិនស៊ិនហួ ចុះផ្សាយនៅថ្ងៃទី២៣ ខែសីហា ឆ្នាំ២០១៥ថាកាលពីថ្ងៃសៅរ៍ម្សិលមិញនេះមានករណី ធ្លាក់យន្តហោះ នៅភាគខាងត្បូង ប្រទេសអង់គ្លេស ខណៈដែលមានការបង្ហាញអំពីការហោះហើរមួយនៅភាគខាងលិចក្រុង Sussex បណ្តាលឲ្យស្លាប់០៧នាក់ និងរបួសមួយចំនួនទៀត ។ តាមការអះអាងពីមន្ត្រីបានឲ្យដឹងយ៉ាងដូច្នេះ ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97680-2015-08-23-04-09-00.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-099/v_0133.jpg', 'Sunday, 23 August 2015 11:05', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1607', 'B030102', 'B030000', 'កិច្ចពិភាក្សាគ្នា​រវាង​កូរ៉េ​ទាំងពីរ​ ត្រូវ​បាន​បញ្ចប់ ប៉ុន្តែ​លទ្ធផល​នៅ​មិនទាន់​ដឹង​', '<strong>សេអ៊ូល៖</strong> យោងតាមសារព័ត៌មាន យ៉ុនហាប់ ចេញផ្សាយឲ្យដឹងថា ជំនួបពិភាក្សារវាង មន្ត្រីកំពូលៗ របស់កូរ៉េទាំងពីរ បានបញ្ចប់ហើយ ប៉ុន្តែលទ្ធផលនៃកិច្ចចរចា នៅមិនទាន់ដឹងនោះទេ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97667-2015-08-22-14-02-05.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-099/v_0103.jpg', 'Saturday, 22 August 2015 20:59', '0', '0', '0', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1608', 'B030102', 'B030000', 'មេទ័ព សហរដ្ឋអាម៉េរិក ប្រកាសប្ដេជ្ញា ការពារកូរ៉េខាងត្បូង', '<strong>(កូរ៉េខាងត្បូង)៖</strong> យោងតាមព័ត៌មាន AFP ចេញផ្សាយមុននេះបន្តិច បានឲ្យដឹងថា មេបញ្ជាការកងទ័ព សហរដ្ឋអាម៉េរិក បានប្រកាស ប្ដេជ្ញាការពារ កូរ៉េខាងត្បូង លើបញ្ហាសង្គ្រាម ជាមួយកូរ៉េខាងជើង ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97654-2015-08-22-08-09-40.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-165.jpg', 'Saturday, 22 August 2015 15:07', '0', '0', '0', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1609', 'B030102', 'B030000', 'យន្តហោះចម្បាំង កូរ៉េខាងត្បូង និងអាមេរិក ហោះពេញមេឃ នៅព្រឹកថ្ងៃនេះ, មិនខ្លាចការគំរាម របស់កូរ៉េខាងជើង', '<strong>(សេអ៊ូល)៖</strong> មេបញ្ជាការជាន់ខ្ពស់ កូរ៉េខាងត្បូង បានឲ្យដឹងនៅព្រឹក ថ្ងៃសៅរ៍នេះថា យន្តហោះចម្បាំង របស់ កូរ៉េខាងត្បូង និងសហរដ្ឋអាមេរិក ចំនួន ៨គ្រឿង បានហោះបង្ហាញនៅលើផ្ទៃមេឃ ក្នុងប្រទេសកូរ៉េខាងត្បូង ដើម្បីបង្ហាញសញ្ញាថា កូរ៉េខាងត្បូង មិនខ្លាចការគំរាម របស់កូរ៉េខាងជើងឡើយ នេះបើយោងតាមការ ចុះផ្សាយ របស់ ទីភ្នាក់ងារព័ត៌មានយ៉ុនហាប់ ចេញផ្សាយនៅថ្ងៃនេះ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97653-2015-08-22-08-06-50.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-162.jpg', 'Saturday, 22 August 2015 15:05', '0', '0', '0', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1610', 'B030102', 'B030000', 'មន្ត្រីជាន់ខ្ពស់ របស់កូរ៉េទាំងពីរ នឹងជួបពិភាក្សាគ្នា នៅម៉ោង៤ល្ងាច ថ្ងៃសៅរ៍នេះ ដើម្បីដោះស្រាយ ជម្លោះឧបទ្វីបកូរ៉េ', '<strong>(សេអ៊ូល)៖</strong> ការិយាល័យ ប្រធានាធិបតីកូរ៉េខាងត្បួង បានឲ្យដឹងថា នៅមុននេះបន្ដិចថា មន្ត្រីជាន់ខ្ពស់របស់ ប្រទេសកូរ៉េទាំងពីរ នឹងជួបពិភាក្សាគ្នានៅភូមិ Panmunjom ដែលជាទីកន្លែងសន្ដិភាពមួយ នៅតាមព្រំដែន ដោយ កូរ៉េទាំងពីរទុកវាជាតុចរចាពិភាក្សាគ្នាលើបញ្ហាសង្គ្រាម នៅឧបទ្វីបកូរ៉េ នេះបើយោងតាមការចុះផ្សាយរបស់ BBC ចេញផ្សាយនៅមុននេះបន្ដិច។', 'http://www.dap-news.com/2011-06-14-02-40-26/97652-2015-08-22-07-45-41.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-161.jpg', 'Saturday, 22 August 2015 14:41', '0', '0', '0', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1611', 'B030102', 'B030000', 'ឯកសារ​ស្ថានទូត​កូរ៉េ​ខាងជើង​៖ សង្គ្រាម​អាច​នឹង​កើតឡើង​រវាង​ កូរ៉េ​ខាង​ជើង​ និង​កូរ៉េ​ខាង​ត្បូង ?​', '<strong>កូរ៉ខាងជើង៖</strong> ឯកសារមួយច្បាប់ចំនួន២ទំព័រ ដែលត្រូវបានយកចេញពីស្ថានទូតកូរ៉េខាងជើង នៅទីក្រុងភ្នំពេញ ដោយមន្រ្តីជាន់ខ្ពស់រាជរដ្ឋាភិបាលមួយរូប បានផ្តល់ជូន មជ្ឈមណ្ឌលព័ត៌មានដើមអម្ពិល នៅថ្ងៃត្រង់ទី២២ ខែសីហា ឆ្នាំ២០១៥នេះថា <strong>កូរ៉េខាងជើង បានដាក់ឱសានវាទចំនួន ៤៨ម៉ោង ចាប់ពីម៉ោង ១៧៖០០ល្ងាចថ្ងៃទី២០ ខែសីហា ឆ្នាំ២០១៥ ទៅកាន់កូរ៉េខាងត្បួង ឲ្យបញ្ឈប់ជាបន្ទាន់នូវប្រកាសផ្សព្វផ្សាយឃោសនា ដែលអាចបង្កជាសង្គ្រាមផ្លូវចិត្ត និងប៉ះពាល់ដល់អារម្មណ៍កងទ័ពនៅតាមព្រំដែន ព្រមទាំងឲ្យដកសម្ភារបំពងសំឡេងទាំងនោះពីព្រំដែនភ្លាមៗ</strong>។<br />ដោយគិតមកដល់ម៉ោងប្រមាណ ១៖៣០នាទីរសៀលថ្ងៃទី២២ ខែសីហានេះ នៅខ្វះតែប៉ុន្មានម៉ោងទៀតប៉ុណ្ណោះ ដល់ពេលកំណត់ហើយ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97649-2015-08-22-07-13-39.html', 'img/dapnews.png', 'Saturday, 22 August 2015 14:11', '2', '0', '0', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1612', 'B030102', 'B030000', 'ពីងពាងអវកាស ទទួលបានផ្ទះថ្មី នៅក្នុងសារមន្ទីរ រដ្ឋវ៉ាស៊ីនតោន', '<strong>វ៉ាស៊ីនតោន៖</strong> ពីងពាងមួយក្បាល ដែលទទួលបានការទាក់ទាញចំណាប់អារម្មណ៍ ជាច្រើន នៅក្នុងសារមន្ទីរនៃរដ្ឋវ៉ាស៊ីនតោន គឺមិនមែនជាប្រភេទសត្វពីងពាងធម្មតានោះទេ ផ្ទុយទៅវិញវាគឺជាពីងពាងអវកាស ដែលបានត្រលប់មកពីលំហអាកាសវិញ កាលពីពេលថ្មីនេះ បន្ទាប់ពីបានរស់នៅទីនោះអស់រយៈ១០០ថ្ងៃកន្លងមក។', 'http://www.dap-news.com/2011-06-14-02-40-26/97623-2015-08-22-03-33-50.html', 'http://public.media.smithsonianmag.com/legacy_blog/Spiders.jpg', 'Saturday, 22 August 2015 10:32', '0', '0', '0', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1613', 'B030302', 'B030000', 'គ្រូបង្វឹកប៊ូតាន់៖ ក្រុមជម្រើសជាតិកម្ពុជា មិនគួរទទួលបាន លទ្ធផលត្រឹមតែ ២គ្រាប់ឡើយ (Video Inside)', '<strong>ភ្នំពេញ៖</strong> លោក Norio Tsukitate គ្រូបង្វឹកក្រុម ជម្រើសជាតិប៊ូតាន់ បានលើកឡើងថា ក្រុមបាល់ទាត់ជម្រើសជាតិ កម្ពុជា គួរតែរកគ្រាប់បាល់បានច្រើនលើសពី២គ្រាប់ នៅក្នុងជំនួបមិត្តភាពលក្ខណៈអន្ដរជាតិ កាលពីល្ងាចថ្ងៃទី២០ ខែ សីហា ឆ្នាំ២០១៥ ម្សិលមិញ ខណៈកម្ពុជា បានលត់ក្រុមភ្ញៀវប៊ូតាន់ ក្នុងលទ្ធផលបច្ចេកទេស ២-០ នៅលើទីលានពហុ កីឡដ្ឋានជាតិ ។', 'http://www.dap-news.com/morokatmagazine/97535--video-inside.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-140/hour-01695.jpg', 'Friday, 21 August 2015 08:13', '0', '0', '0', 't', '2015-08-21', 'f');
