INSERT INTO "tbnews" VALUES ('1645', 'B010201', 'B010000', 'អ្នក​គាំទ្រ Man Utd ​ខក​ចិត្ត​ខ្លាំង​ ព្រោះ​បាន​ត្រឹម​ស្មើ Newcastle', 'អ្នក​គាំទ្រ Manchester United ​ទាំង​អស់​មាន​ទំនុក​ចិត្ត​១០០%​ទៅ​ហើយ​ក្នុង​ការ​យក​៣​ពិន្ទុ​ពី​ក្រុម​ Newcastle United ​ប៉ុន្តែ​ចុង​ក្រោយ​បាន​ត្រឹម​តែ​ខក​ចិត្ត​ទៅ​វិញ ព្រោះ​ថា​ទ័ព​ស្រួច​របស់​បិសាច​ក្រហម...', 'http://news.sabay.com.kh/article/458745', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/Untitled-162-285x170.jpg?ebb82d', '23 August, 2015', '0', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1646', 'B010201', 'B010000', 'Neymar ​ឈ្នះ​ Ronaldo ​និង Messi ​នៅ​លើ​បណ្ដាញ Instagram', '​នៅ​លើ Instagram ​វិញ នា​ពេល​បច្ចុប្បន្ន​នេះ​គឺ​មិនមែន​ជា​កំពូល​កីឡាករ​២​នាក់​នេះ​ទេ ដែល​ត្រូវ​បាន​គេ​ចូល​ចិត្ត​ច្រើន​នោះ ពោល​គឺ​បែរ​ជា​ Neymar ​ឯ​ណោះ​វិញ​ទេ ដែល​មាន​អ្នក​គាំទ្រ​ចុច Follow...', 'http://news.sabay.com.kh/article/458697', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/Untitled-160-285x170.jpg?ebb82d', '23 August, 2015', '2', '0', '0', 't', '2015-08-23', 'f');
INSERT INTO "tbnews" VALUES ('1647', 'B010201', 'B010000', 'U-19 កម្ពុជា បាន​១​ពិន្ទុ បន្ទាប់​ពី​ស្មើ​ម្ចាស់​ផ្ទះ U-19 ឡាវ ០-០', 'ក្រុមបាល់ទាត់យុវជនជម្រើសជាតិកម្ពុជាអាយុក្រោម១៩ឆ្នាំ(U19)​ទទួល​បាន​១​ពិន្ទុ បន្ទាប់​ពី​​បញ្ចប់​ការ​ប្រកួត​​ជា​មួយ​ក្រុម​ម្ចាស់​ផ្ទះ​ U-19 ឡាវ​ដោយ​លទ្ធផល​០-០ នៃ​ពាន​រង្វាន់ U19 AFF Championship 2015។ &nbsp; សម្រាប់​ការ​ប្រកួត​ទាំង​ពីរ​តង់ ក្រុម​ទាំង​ពីរ​មាន​ឱកាស​គ្រប់​គ្រង​បាល់​បាន​ប្រហាក់​ប្រហែល​គ្នា និង​មាន​ឱកាស​​ស៊ុត​បាល់​នៅ​ចំពោះ​មុខ​ទី​ក៏​មិន​សូវ​ខុស​គ្នា​ប៉ុន្មាន​ឡើយ។ ក្រុម​ម្ចាស់​ផ្ទះ​មាន​ឱកាស​​ច្រើន​​ទាំង​ពីរ​តង់ ប៉ុន្តែ​អ្នក​ចាំ​ទី​កម្ពុជា អ៊ុំ សេរីរ័ត្ន ជួយ​សង្គ្រោះ​បាន​ច្រើន ខណៈ​ខ្សែ​ការពារ​កម្ពុជា​ហាក់​ធ្វេស​ប្រហែស​តិច​តូច។ &nbsp; ទោះ​បី​ជា​យ៉ាងណា ក្រុម​កម្ពុជា​មាន​ឱកាស​ស៊ុត​បាល់​ចំគោល​ដៅ​គឺ​ទី​របស់​ឡាវ​បាន​​មួយ​លើក​ជា​ពីរ​លើក​ដែរ តែ​អ្នក​ចាំ​ទី​របស់​ឡាវ​ទះ​បាល់​ចេញ​ជា​ច្រើន​ និង​គ្រប់​គ្រង​បាល់​បាន​ល្អ​ជាង​តង់​ទី​មួយ៕ &nbsp; ការ​ប្រកួត​រវាង​កម្ពុជា និង ឡាវ (រូបថត៖ Sophoeun Vanna)  &nbsp; ជម្រើស​​ក្រុម​ទាំង​ពីរ (រូបថត៖ PNN) &nbsp; អត្ថបទ៖ ជាន ចាន់រ៉ា', 'http://news.sabay.com.kh/article/458664', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/laosef-285x170.jpg?ebb82d', '22 August, 2015', '1', '0', '0', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1653', 'B020105', 'u0000004', '១២ចំណុច​​បរិយាយ​ពី​ iWatch​ កាន់​តែ​​មាន​ពន្លឺ​!', 'នាឡិកាដៃ​ឆ្លាតវៃ (SmartWatch) ចេញ​ព័ត៌មាន​ជា​ច្រើន​រយៈពេល​ច្រើន​ឆ្នាំ​មក​នេះ តែ​រូបរាង និង​លក្ខណៈ​សម្បត្តិ​ពិតប្រាកដ​​ពុំ​ត្រូវ Apple ប្រកាស​ផ្លូវការ​នៅ​ឡើយ​​ទេ។', '', 'img/1404708173MainImage.jpg', '8/24/15 4:55 PM', '1', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1654', 'B010105', 'u0000004', 'ក្រុម​ហ៊ុន YouTube​ ​​​ចែចូវ​ទិញ​ក្រុម​ហ៊ុន Twitch ក្នុង​តម្លៃ​ជាង​១​ពាន់​លាន​ដុល្លារ', 'ក្រុម​ហ៊ុន​​ YouTube ​​កំពុង​តាម​ចែចូវ​​​​​ទិញ​ក្រុម​ហ៊ុន​ Twitch ​ដែល​ផលិត​ហ្គេម​វីដេអូ​ដ៏​ល្បី​ឈ្មោះ​ក្នុង​តម្លៃ​ជាង​ ​​១​ពាន់​លាន​ដុល្លារ​។', '', 'img/1400551114YouTubetoacquireTwitch.jpg', '8/24/15 5:01 PM', '6', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1655', 'B010105', 'u0000004', 'ការងារ​​ជំនាញ​សុវត្ថិភាព​​​​អ៊ីនធឺណិត​កំពុង​ត្រូវ​ប៉ាន់​នៅ​អាមេរិក​​ ខណៈ​សកម្មភាព​ hacker ​កាន់​តែ​​ផុលផុស', 'ក្រុមហ៊ុន​ធំៗ​បំផុត​នៅ​អាមេរិក​កំពុង​តែ​ត្រូវការ​ជ្រើស​រើស​អ្នក​ជំនាញ​ខាង​សន្តិសុខ​​អ៊ីនធឺណិត​​ដែល​គេ​ហៅ​ថា​ cybersecurity ​ឱ្យ​​មក​​កាន់​​​​​មុខ​តំណែង​ខ្ពស់ៗ ​​សម្រាប់​​ការពារ​ប្រព័ន្ធ​សុវត្ថិភាព​​ក្រុមហ៊ុន​របស់​ខ្លួន​ខណៈ​ដែល​សកម្មភាព​របស់​ក្រុម​ចោរ​លួច​ព័ត៌មាន​តាម​អ៊ីនធឺណិត​កាន់​តែ​ផុលផុស​ឡើងៗ​។', '', 'img/1401502950images2.jpg', '8/24/15 5:07 PM', '5', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1656', 'B010105', 'u0000004', 'ម៉ាស៊ីន​ព្រីន​ IBM 3D បោះ​ពុម្ព​ក្រប​ទស្សនាវដ្ដី​តូច​បំផុត​ក្នុង​ពិភព​លោក', 'ដោយ​ឆ្លង​កាត់​​ប្រព័ន្ធ​បច្ចេកវិទ្យា​តូច​មួយ​ និង​ការ​បោះ​ពុម្ព​តាម​ប្រព័ន្ធ ​​3Dនោះ​ ទើប​គេ​ហៅ​ប្រព័ន្ធ​នេះ​ជា​ម៉ាស៊ីន​ព្រីន​ 3D ដ៏​តូច​បំផុត​របស់​ក្រុម​ហ៊ុន IBM ដែល​ឥឡូវនេះ​មាន​លក្ខណៈ​ពិសេស​សម្រាប់​បោះ​ពុម្ព​ក្រប​ទស្សនាវដ្ដី​តូច​បំផុត​នៅ​ក្នុង​ពិភព​លោក​។', '', 'img/ibm_smallest_magazine.jpg', '8/25/15 8:43 AM', '5', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1657', 'B030401', 'u0000004', '៨ចំណុច​ដែល​អ្នក​ត្រូវ​ដឹង​ពី​សេដ្ឋកិច្ច​ឥណ្ឌា', 'ប្រិយមិត្ត​ធ្លាប់​ដឹង​ថា ឥណ្ឌា​​គឺ​​ជា​មហាអំណាច​មួយ​ដែល​មាន​​ប្រជាជន​​ច្រើន​ជាងគេ​បំផុត​លំដាប់​ទី២​បន្ទាប់​ពី​ចិន​ ពោល​គឺ​មាន​ចំនួន​ប្រមាណ​ជាង ​១,២ ពាន់​លាន​នាក់។ ក្នុង​រយៈ​ពេល​ប៉ុន្មាន​ឆ្នាំ​ចុងក្រោយ​នេះ ទន្ទឹម​នឹង​ការ​លូតលាស់​ទៅ​មុខ​យ៉ាង​​លឿន​របស់​​សេដ្ឋកិច្ច​ចិន គេ​ក៏​មិន​ត្រូវ​មើល​រំលង​​ពី​​មហាយក្ស​ឥណ្ឌា​នោះ​ឡើយ ត្បិតអី​មហាអំណាច​​អាស៊ី​ខាងត្បូង​​នេះ​ ​ក៏​មាន​សេដ្ឋកិច្ច​​​ខ្លាំងក្លា​មិន​ចាញ់​ចិន​ប៉ុន្មាន​នោះ​ដែរ។ ជុំវិញ​ប្រធានបទ​នេះ ​TheBNEWS ​សូម​​ណែនាំ​ប្រិយមិត្ត​ឲ្យ​បាន​ជ្រាប​ខ្លះៗ​ពី​​ស្ថានភាព​សេដ្ឋកិច្ច​​របស់​រដ្ឋាភិបាល​ទីក្រុង​ញូដេលី ​ដែល​​មាន​ដូច​ខាងក្រោម៖', '', 'img/14046948977TajMahal_2200779b.jpg', '8/24/15 5:13 PM', '3', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1658', 'B020505', 'u0000004', 'ក្រុមហ៊ុន​នៅ​ចិន​ជួល​ស្រី​ស្អាត​ដើម្បី​លើក​ទឹក​ចិត្ត​បុគ្គលិក​ប្រុស', 'ការងារ​ក្នុង​ក្រុមហ៊ុន​ពេល​ខ្លះ​អាច​ធ្វើ​ឲ្យ​ធុញ​ទ្រាន់​ស្ទើរ​ទ្រាំ​មិន​បាន ព្រោះ​ការ​គិត​ច្រើន​បង្កើន​ភាព​ស្មុគស្មាញ​ក្នុង​ខួរ​ក្បាល​។ ដើម្បី​ដោះ​ស្រាយ​ស្ថានភាព​នេះ ក្រុមហ៊ុន​បច្ចេកវិទ្យា​ជា​ច្រើន​ក្នុង​ប្រទេស​ចិន បាន​បែក​គំនិត​ជួល​ស្រី​ស្អាត មក​ជជែក​លេង វាយ​កូន​ឃ្លី​លើ​តុ និង​ទិញ​អាហារ​ពេល​ព្រឹក ដើម្បី​លើក​ទឹក​ចិត្ត​បុគ្គលិក​ប្រុស​ឲ្យ​ខំ​ធ្វើការ​ដោយ​គ្មាន​ភាព​ធុញ​ទ្រាន់។', '', 'img/1327.jpg', '8/24/15 5:16 PM', '2', '0', '0', 't', '2015-08-24', 'f');
