INSERT INTO "tbnews" VALUES ('1690', 'B020102', 'B020000', 'អគ្គី​ភ័យ​ឆេះ​រោងចក្រ​មួយ​កន្លែង​នៅ​ក្រោយ​សាលា​ខណ្ឌ​សែន​សុខ', 'រាជធានី​ភ្នំពេញ ៖ ខណៈ​ពេល​នេះ​អគ្គី​ភ័យ​កំពុង​ឆេះ​រោងចក្រ​មួយ​កន្លែង​នៅ​ក្រោយ​សាលា​ខណ្ឌ​សែន​សុខ ស្ថិត​ក្នុង​សង្កាត់​ភ្នំពេញ​ថ្មី ​ដែល​រថយន្ត​ពន្លត់​អគ្គីភ័យ​កំពុង​ចេញ​ទៅ​អន្តរាគមន៍ ។', 'http://kohsantepheapdaily.com.kh/article/127516.html', 'http://i.kspg.co/2015/8/ccad9665-220d-4eae-a535-b65817c86c9e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៨:៥៧', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1691', 'B020102', 'B020000', 'ចាប់ខ្លួន​ជនសង្ស័យ​ម្នាក់ ខណៈ​កំពុង​ចែកចាយ​នូវ​សារធាតុ​ញៀន', 'ខេត្ត​ព្រះ​សីហ​នុ ៖ ជនសង្ស័យ​ម្នាក់​ក្នុង​ចំណោម​បក្ខពួក​២​នាក់​ផ្សេង​ទៀត​ត្រូវ​បាន​ប៉ូលិស​ស្រុក​ព្រៃ​នប់​ធ្វើការ​បង្ក្រាប និង​ឃាត់ខ្លួន​បាន ខណៈ​ពេល​ដែល​ជនសង្ស័យ និង​បក្ខពួក​បាន​និង​កំពុង​ចែកចាយ​នូវ​សារធាតុ​ញៀន ។', 'http://kohsantepheapdaily.com.kh/article/127515.html', 'http://i.kspg.co/2015/8/60cc4ae9-ee33-4db5-b3c2-a4214e7c66b7.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៨:៤៩', '0', '0', '0', 't', '2015-08-24', 'f');
