INSERT INTO "tbnews" VALUES ('1693', 'B020102', 'B020000', 'សិស្ស​វិទ្យាល័យ​សន្ធរ​ម៉ុ​ក​ម្នាក់​ក្រឡាប់​ទូក​លង់ទឹក​ស្លាប់ ខណៈ​ទៅ​លេង​បឹង​ក្រោយ​ស្នងការ​នគរបាល​រាជធានី​ភ្នំពេញ', 'រាជធានី​ភ្នំពេញ ៖ ក្មេង​ប្រុស​ម្នាក់​បាន​ក្រឡាប់​ទូក​លង់ទឹក​ស្លាប់ និង​ម្នាក់​ទៀត​ត្រូវ​បាន​ប្រជាពលរដ្ឋ​ជួយ​សង្គ្រោះ​ទាន់ ខណៈ​ពេល​ដែល​នាំ​គ្នា​ទៅ​លេង​ទឹក​បឹង​មួយ​កន្លែង​ស្ថិត​នៅ​ខាងលិច​ស្នង​ការ​ដ្ឋាន​​រាជធានី​ភ្នំពេញ', 'http://kohsantepheapdaily.com.kh/article/127511.html', 'http://i.kspg.co/2015/8/dbc09429-4cdd-4358-8711-2e018827e2ed.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:៥៥', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1694', 'B020102', 'B020000', 'ប្រកែកគ្នាបន្តិបន្តួច​ លោកកូនចិញ្ចឹម​ទាញពូថៅកាប់ក្បាល​ អារ.ក​និងកាត់លិង្គឪពុកស្លាប់', 'ខេត្តពោធិសាត់៖ ​បុរសចំណាស់ម្នាក់បាន​ស្លាប់យ៉ាងអាណោចអាធ័ម​ដោយសារកូនចិញ្ចឹមទាញ​ពូថៅកាប់ក្បាល ​អារ.កនិង​កាត់លិង្គ ​ព្រោះតែរឿង​ឈ្លោះប្រកែកគ្នា​បន្តិបន្តួច ។ ', 'http://kohsantepheapdaily.com.kh/article/127478.html', 'http://i.kspg.co/2015/8/b332f563-a8e7-4c81-add6-d2d214f8f448.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៥៥', '0', '0', '0', 't', '2015-08-24', 'f');
