INSERT INTO "tbnews" VALUES ('1696', 'B020102', 'B020000', 'នារី​​​ម្នាក់ស្រវឹង​ស្រា​ដេក​លើ​សួន​ច្បារ​​​ត្រូវ​ក្រុម​ចោរច្របាច់​.​កប្លន់​យក​ទូរស័ព្ទ​ និង​កាបូប​លុយទៅបាត់', 'រាជ​ធានីភ្នំពេញ​ ៖ នារី​បំរើ​ការងារ​ខារ៉ាអូខេ​ម្នាក់​ដោយសារ​តែ​ឈ្លោះ​ប្រ​កែ​កជាមួយ​បុរស​ជា​សង្សាររឿង​លួច​មាន​ស្រី​ថ្មី​បាន​ផឹកស្រា​​រហូត​ស្រវឹង​រួច​មក​ដេក​នៅ​លើ​សួនច្បារ', 'http://kohsantepheapdaily.com.kh/article/127467.html', 'http://i.kspg.co/2015/8/271eb792-0f4d-4f61-90f4-7f40caa380b3.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:១៣', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1697', 'B020102', 'B020000', '​និស្សិត​ពេទ្យ​​ម្នាក់​​ចេញ​ពី​យាម​ប្រញាប់​​​ទៅ​ដឹក​ប្អូន​ទៅប្រឡង​បាក់​ឌុប​ ​​​​​​ត្រូវ​ចោរ​ឆក់​កាបូប​​ដួល​បោក​ក្បាល​ស្លាប់​', '​រាជ​ធានីភ្នំពេញ ៖ យុវនិស្សិត​ពេទ្យ​ម្នាក់​ចេញពី​យាម​ប្រញាប់​ទៅ​ដឹក​ប្អូន​ទៅ​ប្រឡង​បាក់​ឌុ​ប ​ត្រូវ​ចោរឆក់​កាបូប​បណ្តាល​ឲ្យ​ដួល​ម៉ូតូ​បោក​ក្បាល​ស្លាប់​ភ្លាម​ៗ​នៅ​កន្លែង​កើតហេតុ​ កាលពី​វេលា​ម៉ោង​៥​និង​៣០​នាទី​ភ្លឺ​ថ្ងៃ​ទី​២៤ ​សីហា ', 'http://kohsantepheapdaily.com.kh/article/127437.html', 'http://i.kspg.co/2015/8/6898d8c3-f2be-4d3e-be63-e26d4b95ea56.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:២៤', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1698', 'B020101', 'B020000', 'ស្អែក​ម៉ោង​៩​ព្រឹក ក្រសួងមហាផ្ទៃ​នឹង​ធ្វើ​សន្និសីទកាសែត​ស្តី​ពី​លទ្ធផល​បង្ក្រាប​គ្រឿងញៀន​កាលពី​ថ្ងៃ​ទី​២១​សីហា', 'រាជធានី​ភ្នំពេញ ៖ នៅ​វេលា​ម៉ោង​៩​ព្រឹក ថ្ងៃ​ទី​២៥ សីហា ២០១៥ ស្អែក​នេះ នគរបាល​មន្ទីរ​ប្រឆាំង​គ្រឿងញៀន ក្រសួងមហាផ្ទៃ នឹង​ធ្វើ​សន្និសីទ​សារព័ត៌មាន ស្តី​ពី​លទ្ធផល​នៃ​កិច្ច​បង្ក្រាប​គ្រឿងញៀន​ជា​លក្ខណៈ​ទ្រង់ទ្រាយ​ធំ​មួយ', 'http://kohsantepheapdaily.com.kh/article/127526.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២២:៥៥', '0', '0', '0', 't', '2015-08-24', 'f');
