INSERT INTO "tbnews" VALUES ('1701', 'B020101', 'B020000', 'សមត្ថ​កិច្ច​ខណ្ឌ​៧​មករា​ម្នាក់​ត្រូវ​ស្ត្រី​ជា​ម្ចាស់ភោជនីយ​ដ្ឋាន​មេគង្គវាយ​របួស​ស្រាល​', 'រាជ​ធានីភ្នំពេញ ​៖ មន្ត្រី​សមត្ថកិច្ច​ខណ្ឌ​៧​មក​រាម្នាក់​ដែលមក​ការពារ​នៅ​មណ្ឌល​ប្រលង​នៃ​វិទ្យាល័យ​បាក់​ទូក ​ប្រចាំ​នៅ​គោលដៅ​ផ្លូវ​១៦៤ កែង​ផ្លូវ​១៦៩​ ត្រូវ​បាន​ស្ត្រី​ម្នាក់​ជា​ម្ចាស់​ហាងភោជនីយដ្ឋាន មេគង្គ​', 'http://kohsantepheapdaily.com.kh/article/127494.html', 'http://i.kspg.co/2015/8/6eb39956-37a1-4723-9ffb-2181784c8ce2.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៥៦', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1702', 'B020101', 'B020000', 'នគរបាល​សេដ្ឋកិច្ច​​និងគយ​​ចល័ត ឃាត់​រថយន្ត​យី​ឌុ​ប​​ដឹក​ស្រា​ជិត​១.០០០​កេស', 'ខេត្ត​ត្បូងឃ្មុំ ៖ រថយន្ត​១​គ្រឿង​ម៉ាក​ហ៊ី​យ៉ា​ន់​ដាយ ពាក់​ស្លាក​លេខ​ភ្នំពេញ 3C-3293 បាន​ដឹក​ស្រា​ប្រភេទ​វី​ស្គី និង​ស្រាក្រហម​ចំនួន​៩៧០​កេស បម្រុង​ឆ្លង​ដែន​ចូល​ទៅ​ប្រទេស​វៀតណាម ត្រូវ​បាន​លោក​ឧត្តមសេនីយ៍ត្រី ម៉ៅ ពៅ ស្នងការ​នគរបាល​ខេត្ត', 'http://kohsantepheapdaily.com.kh/article/127492.html', 'http://i.kspg.co/2015/8/c940d3f1-d47b-44d3-8066-126cfbda28f5.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៤៨', '0', '0', '0', 't', '2015-08-24', 'f');
