INSERT INTO "tbnews" VALUES ('1712', 'B020101', 'B020000', 'ព្រះ​ចៅ​អធិការ​​វត្ត​ព្រះ​ពុទ្ធ​មណ្ឌល​គិរី​​សំណូម​ពរ​ដល់​​ពុទ្ធ​បរិ​​ស័ទ​​ជួយ​​ជា​ថវិកា ​សំភារ​​​សាង​សង់​ព្រះ​​វិហារ​​ថ្មី ​', 'ខេត្ត​មណ្ឌល​គិរី ៖ ព្រះ​ចៅអធិការ​វត្ត​ព្រះពុទ្ធ​មណ្ឌលគិរី បានសំណូមពរ​ដល់​ពុទ្ធ​បរិ​ស័ទ​ទាំង​ក្នុង និង​ក្រៅប្រទេសជួយ​ជាថវិកា សំភារក្នុង​ការ​សាងសង់​ព្រះ​វិហារ​ថ្មី ។', 'http://kohsantepheapdaily.com.kh/article/127471.html', 'http://i.kspg.co/2015/8/e3b39ad1-6b4f-4c87-9be0-3a0942a2800e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:២៧', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1713', 'B020101', 'B020000', '​​ប្រឡង​សញ្ញាបត្រ​​មធ្យមសិក្សា​​ទុតិយភូមិនៅមណ្ឌលខេត្ត​ស្ទឹង​ត្រែង​​​មាន​សភាព​ស្ងប់​ស្ងាត់ ខណៈ​​អ្នក​សង្កេត​ការណ៍​​ស្ម័គ្រចិត្ត​មាន​​​៤១​​នាក់', 'ខេត្តស្ទឹងត្រែង​ ៖ បេក្ខជន​​ប្រលង​សញ្ញាបត្រ​​មធ្យមសិក្សា​​ទុតិយភូមិ​ចំនួន​៦២៧​​នាក់ បាន​រុះ​រាន់​តាំង​ពី​ព្រឹកព្រ​លឹ​ម មក​​ដល់​មណ្ឌល​ប្រឡ​ង​​របស់​ខ្លួន​នៅ​ថ្ងៃ​ទី​២៤ ​សីហា ២០១៥ ជា​ថ្ងៃ​ទី​១​នៃ​ការ​ប្រលង​ ។ គេ​សង្កេត​​ឃើញ​បេក្ខជន​ចូល​តាម​បន្ទប់​ប្រឡង​​រៀង​ៗ​ខ្លួន មាន​​សភាព​​ស្ងប់ស្ងាត់​ល្អ ក្នុង​បរិវេណ​មណ្ឌល​​ប្រឡង និង​នៅ​ក្នុង​បន្ទប់​​ប្រឡង ខណៈ​ពេល​​មាន​អ្នក​សង្កេតការណ៍​​ស្ម័គ្រចិត្ត​ឈរ​', 'http://kohsantepheapdaily.com.kh/article/127466.html', 'http://i.kspg.co/2015/8/aa7a48b3-7d06-4e25-9a04-2c83e715e508.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:១២', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1714', 'B020101', 'B020000', 'បុរស​នេសាទ​ម្នាក់​ប៉ុន​ប៉ង​ចាប់​អ្នក​គ្រូ​រំលោភ​ ត្រូវ​សមត្ថកិច្ច​ចាប់​វាយ​ខ្នោះ', 'ខេត្ត​ព្រះ​សីហនុ ៖ ​ជន​សង្ស័យ​ជាប់​ចោទ​ពី​បទ​ប៉ុន​ប៉ង​រំលោភ​សេព​សន្ថវៈ​ម្នាក់ ត្រូវ​​បាន​នគរ​បាល​​ស្រុក​ព្រៃ​នប់ សហការ​ជា​មួយ​​កងទ័ព​ជើង​ទឹក​មូល​ដ្ឋាន​រាមចាប់​​ឃាត់​ខ្លួន​​ ', 'http://kohsantepheapdaily.com.kh/article/127465.html', 'http://i.kspg.co/2015/8/9024126b-9038-4bfc-a1cc-8477e1145287.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៥៣', '1', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1715', 'B020101', 'B020000', 'ប្រទះ​ឃើញបុរស​វ័យ​ចំណាស់​ម្នាក់​ចង.ក​សម្លាប់ខ្លួន​លើដើម​ស្វាយ​ចន្ទី​​ខាងកើត​ផ្សារ​ក្រុងបាន​លុង', 'ខេត្តរតនគិរី ៖ បុរស​វ័យ​ចំណាស់​ម្នាក់​បាន​ចង.ក​សម្លាប់​ខ្លួន​នៅ​ដើម​ស្វាយចន្ទី​ក្បែរ​មាត់ជ្រោះ​ខាងកើត​ផ្សារ​ក្រុង​បាន​លុង​ត្រូវ​បាន​​ប្រជា​ពលរដ្ឋប្រទះ​ឃើញ​ធ្វើ​ឱ្យ​មានការ​ភ្ញាក់​ផ្អើល​កាល​ពី​វេលា​​ម៉ោង​៦​ព្រឹក', 'http://kohsantepheapdaily.com.kh/article/127464.html', 'http://i.kspg.co/2015/8/fcc05ca3-4785-4e04-b0a0-a6d18d94816f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៤១', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1716', 'B020101', 'B020000', '​​ឃាត់ខ្លួន​យុវជន​ម្នាក់​​​បន្លំ​​​ខ្លួន​ចូល​ប្រឡង​បាក់​ឌុប​ជំនួស​​បង​ប្រុស​ នៅ​​មណ្ឌល​វត្ត​កោះ', '​រាជ​ធានី​ភ្នំពេញ ៖ ​ខ​ណៈ​អង្គភាព​ប្រឆាំង​អំពើ​ពុក​រលួយ​ដាក់​ពង្រាយ​​កម្លាំងនៅតាម​មណ្ឌល​ប្រឡង​បាក់​ឌុ​ប​ទូ​ទាំង​ប្រទេស​​ ​​ស្រាប់​​តែ​នៅ​​វេលា​ម៉ោង​​១០​និង​៣០​នាទី​ព្រឹក​ថ្ងៃ​ទី​២៤​​ សីហា ', 'http://kohsantepheapdaily.com.kh/article/127463.html', 'http://i.kspg.co/2015/8/d3f40047-a20d-46d4-8ee9-23809875e149.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:០៩', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1717', 'B020101', 'B020000', '​លោក អ៊ុត សំអន ​អភិ​បាល​ខេត្ត​កំពង់​ធំ ​ ​អញ្ជើញ​​ក្នុង​ពិធី​បើក​សម័យ​​​ប្រឡង​​បាក់​ឌុ​ប​​ឆ្នាំ២០១៥​', 'ខេត្តកំពង់ធំ​ ៖ ​​លោក អ៊ុត សំ​អន អភិបាល​ខេត្ត​​ និង​លោក​ សំ ង៉ា ​ប្រធាន​គណៈមេប្រយោគ​ទូ​ទាំង​ប្រទេស​នៅ​ព្រឹក​ថ្ងៃ​ទី​២៤ ​សីហា ​បាន​អញ្ជើញចូលរួម​ជា​អធិបតី​ភាព​ក្នុង​ការ​បើក​សម័យ​ប្រឡង​​សញ្ញា​បត្រ​មធ្យម​សិក្សា​​ទុតិយភូមិ', 'http://kohsantepheapdaily.com.kh/article/127462.html', 'http://i.kspg.co/2015/8/9fad7f8f-5beb-4a7d-91eb-7ac712736c83.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១២:៥៣', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1718', 'B020101', 'B020000', 'ការផ្ទៀងផ្ទាត់ផែនទី​ ៖ សម្តេចតេជោ​ ហ៊ុន សែន ​ផ្ញើសារលិខិត​ថ្លែងអំណរគុណ​លោក បាន គីមូន', 'រាជធានីភ្នំពេញ ៖ ​សម្តេចអគ្គមហាសេនាបតីតេ​ ហ៊ុន សែន ​បានថ្លែងអំណរគុណ​ជាថ្មីម្តងទៀត​ ជូនចំពោះលោកអគ្គលេខាធិការ​អង្គការសហប្រជាជាតិ​ បាន គីមូន​ ដែល​', 'http://kohsantepheapdaily.com.kh/article/127461.html', 'http://i.kspg.co/2015/8/8da38d0a-986c-4e1f-864a-83a01799527f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១២:២៣', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1719', 'B020101', 'B020000', 'ប្រឡងបាក់ឌុប ​៖ សម្តេចតេជោ ហ៊ុន សែន ​ស្នើឲ្យបេក្ខជន​តំាងចិត្តប្រឡង​ដោយស្មារតីទទួលខុសត្រូវខ្ពស់', 'រាជធានីភ្នំពេញ​ ៖ សម្តេច​អគ្គមហាសេនាបតីតេជោ​ ​ហ៊ុន សែន ​បានស្នើដល់សិស្សានុសិស្ស​តាំងចិត្តប្រឡងដោយ​ស្មារតីទទួលខុសត្រូវខ្ពស់​ និង​បានជូនពរឲ្យជាប់​​ជា', 'http://kohsantepheapdaily.com.kh/article/127459.html', 'http://i.kspg.co/2015/8/82a78e01-d4ba-47e1-8db5-1ea0045e3b66.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:៥៥', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1720', 'B020101', 'B020000', 'កញ្ញា​សាសា ដីកាកោះហៅឱ្យ​ចូល​ថ្ងៃ​ទី២៧ សីហា​', 'ខេត្ត​បាត់ដំបង ៖ តាមដីកាកោះ​ហៅ​របស់​​តំណាង​​អយ្យ​ការ​អម​សាលា​ដំបូង​​ខេត្ត​បាត់ដំបង តាមព័ត៌មាន​​ច្បាស់​ការណ៍​បញ្ជាក់ថា ​បានឱ្យ​កញ្ញា​ សាសា ចូល​ខ្លួនទៅបំភ្លឺ​​នៅ​ថ្ងៃទី​២៧ សីហា នេះ ជាកំហិត ​លើ​សំណុំរឿង​​គ្រោះថ្នាក់​ចរាចរណ៍​​ កាលពីយប់​ថ្ងៃទី​២ សីហា នៅ​កណ្តាល​ក្រុង​បាត់ដំបង បណ្តាល​ឱ្យ​បុរស​ម្នាក់​​របួស​ប្រកាច់ប្រកិន ។', 'http://kohsantepheapdaily.com.kh/article/127458.html', 'http://i.kspg.co/2015/8/df468144-bb9d-43f9-9f71-122c52096c6b.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:១១', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1721', 'B020101', 'B020000', 'ខណៈនេះ​ ចៅក្រមកំពុងសួរជនជាតិចិន២នាក់​និង​ខ្មែរពីរនាក់​ពាក់ព័ន្ឋជួញដូរគ្រឿងញៀន​​ជិត៥៥គីឡូក្រាម', 'រាជធានីភ្នំពេញ ៖​ ជនជាតិចិន២នាក់និងខ្មែរ២នាក់​ ដែលពាក់ព័ន្ធនឹងការជួញដូរ​គ្រឿងញៀនជិត៥៥គីឡូក្រាម ​កាលពីពាក់កណ្តាលខែ​មិថុនានៅខេត្តស្ទឹងត្រែង​កន្លងទៅ​ បាន​ត្រូវចៅក្រមស៊ើបសួរ​សាលាដំបូង​រាជធានី​ភ្នំពេញ​', 'http://kohsantepheapdaily.com.kh/article/127454.html', 'http://i.kspg.co/2015/8/288b5091-e536-45dc-97a5-63cfc8d20dd2.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៥២', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1722', 'B020101', 'B020000', 'ភាគី​ថៃ​បញ្ជូន​ពលករ​កាប់​ឈើ​គ្រញូង​ត្រឡប់​មក​ស្រុក​វិញជា​បន្តបន្ទាប់​នៅ​ចុងខែ​សីហា​នេះ', 'ខេត្តបន្ទាយមានជ័យ ៖ ពលករ​ចាញ់បោក​មេខ្យល់​កាប់​ឈើ​គ្រញូង​លើ​ទឹកដី​ថៃ​មួយ​រយៈ​ចុង​ក្រោយ​ក្នុង​ខែសីហា ឆ្នាំ​២០១៥ ត្រូវ​បាន​​ភាគី​ថៃ​ធ្វើការ​បញ្ជូន​ត្រឡប់​តាម​ច្រក​ទ្វារ​អន្តរជាតិ​ប៉ោយ​ប៉ែ​ត​វិញ​ជា​បន្តបន្ទាប់ ។', 'http://kohsantepheapdaily.com.kh/article/127453.html', 'http://i.kspg.co/2015/8/7dbe251e-5b2c-4633-8cfe-6717cdb1b9bb.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៥១', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1723', 'B020401', 'B020000', 'ការ​ប្រែប្រួល​អាកាសធាតុ​នឹង​ធ្វើ​អោយ​សេដ្ឋកិច្ច​កម្ពុជា​ថមថយ​កំណើន​ធ្ងន់ធ្ងរ', 'រាជធានី​ភ្នំពេញ ៖ ប្រទេស​កម្ពុជា នឹង​ខាតបង់​ទំហំ​សេដ្ឋកិច្ច​១,៥​ភាគរយ​ជា​រៀង​រាល់​ឆ្នាំ គិត​ត្រឹម​ឆ្នាំ​២០៣០ ដោយ​បញ្ហា​បម្រែបម្រួល​អាកាសធាតុ​ធ្ងន់ធ្ងរ​ពី​មួយ​ឆ្នាំទៅ​មួយ​ឆ្នាំ និង​ទិ​ន្នា​ការ​កើនឡើង​ដែល​អាច​ប៉ះពាល់​ដល់​សេដ្ឋកិច្ច​', 'http://kohsantepheapdaily.com.kh/article/127457.html', 'http://i.kspg.co/2015/8/415091f3-2bf6-4664-a4d3-e432b8290e68.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០៨', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1724', 'B020401', 'B020000', 'ការ​ចូលរួម​ពី​វិស័យ​ឯកជន​ក្នុង​សង្គម​នឹង​ជួយ​សេដ្ឋកិច្ច​អោយ​មាន​កំណើន​រឹង​មាំ', 'រាជធានី​ភ្នំពេញ ៖ រាជរដ្ឋាភិបាល​កម្ពុជា​បាន​ជំរុញ​ឲ្យ​វិស័យ​ឯកជន​ក្នុងស្រុក​ធ្វើ​សកម្មភាព​វិនិយោគ​ឲ្យ​កាន់តែ​ច្រើន​ឡើង​បន្ថែម​ទៀត​ដើម្បី​កាត់​បន្ថយ​ការ​ពឹងពាក់​ពី​បរទេស ខណៈ​សក្តានុពល​សេដ្ឋកិច្ច​ក្នុងស្រុក​កំពុង​រីក​ចម្រើន ។  ', 'http://kohsantepheapdaily.com.kh/article/127456.html', 'http://i.kspg.co/2015/8/7cb3205c-bf5d-4f21-9edc-14ccb1acd717.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០៥', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1725', 'B020104', 'B020000', 'បទ​ពិសោធន៍​នៃ​ការ​ចិញ្ចឹម​មាន់​ឱ្យ​ឆាប់​ធំធាត់​និង​មិន​ងាប់', 'ជា​ទូទៅ កសិករ​ធ្លាប់​ចិញ្ចឹម​មាន់​តាម​ទម្លាប់​ដោយ​លែងឱ្យ​ដើរ​ស៊ីចំណី​គ្រប់​ទីកន្លែង ដែល​ធ្វើ​ឱ្យ​មាន់​មិន​បាន​ទទួល​ការ​ថែបំប៉ន និង​ឆាប់​ឆ្លង​ជំងឺ ។ ដោយឡែក បងប្រុស សាយ ច​ន្ថា គាត់​បាន​ចិញ្ចឹម​មាន់​តាម​គោលការណ៍​ធម្មជាតិ ', 'http://kohsantepheapdaily.com.kh/article/127512.html', 'http://i.kspg.co/2015/8/528f960a-cf9e-436e-a530-01c059dfae64.jpg?w=285&h=170&mode=carve', 'ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៧:២៨', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1726', 'B030101', 'B030000', 'មន្រ្តីក្រសួងទំនាក់ទំនង រដ្ឋសភាព្រឹទ្ធសភា និងអធិការកិច្ច ចូលគោរពវិញ្ញាណក្ខន្ធ សពលោកស្រី ចែម សាវ៉ៃ', '<b>ភ្នំពេញ៖</b> លោក កង ណែម រដ្ឋលេខាធិការក្រសួងទំនាក់ ទំនងរដ្ឋសភា ព្រឹទ្ធសភា និងអធិការកិច្ច តំណាងលោក ស្រីឧបនាយករដ្ឋមន្ត្រី ម៉ែន សំអន រដ្ឋមន្រ្តីក្រសួងទំនាក់ទំនងរដ្ឋសភា ព្រឹទ្ធសភា និងអធិការកិច្ច នៅរសៀលថ្ងៃទី ២៤ ខែសីហា ឆ្នាំ២០១៥នេះ បានដឹកនាំគណប្រតិភូក្រសួងទៅគោរពវិញ្ញាណក្ខន្ធសពលោកស្រី ចែម សាវ៉ៃ អ្នក តំណាងរាស្រ្តគណបក្សប្រជាជនកម្ពុជាមណ្ឌលកំពង់ចាម នៅគេហដ្ឋានសពស្ថិតនៅផ្ទះលេខ៦២ ផ្លូវណតប៊្រីត ភូមិស្លែងរលើង សង្កាត់ទឹកថ្លា ខណ្ឌសែនសុខ រាជធានីភ្នំពេញ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97796-2015-08-24-23-50-45.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/SR-077.jpg', 'Tuesday, 25 August 2015 06:49', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1727', 'B030101', 'B030000', 'សមត្ថកិច្ច ចាប់ចោរពីរនាក់ ក្រោយធ្វើ សកម្មភាព ឆក់កាបូប មិនបានសម្រេច', '<strong>ភ្នំពេញៈ</strong> នៅថ្ងៃ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ វេលាម៉ោង១៧និង១០នាទី&nbsp; កម្លាំងមានសមត្ថកិច្ច បានឃាត់ខ្លួនជនសង្ស័យ ក្រោយធ្វើសកម្មភាព ឆក់កាបូបពីស្ត្រីម្នាក់ មិនបានសំរេច នៅចំណុចមុខផ្ទះលេខ1D ផ្លូវ70 ភូមិ១៩សង្កាត់ស្រះចក ខណ្ឌដូនពេញ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97795-2015-08-24-14-54-46.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00788.jpg', 'Monday, 24 August 2015 21:41', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1728', 'B030101', 'B030000', 'អគ្គិភ័យ ដែលឆាបឆេះ ធនាគារ UCB បានរលត់ ទៅវិញហើយ', '<strong>ភ្នំពេញ៖</strong> បន្ទាប់ពីការចេញ ប្រតិបត្តិការអន្តរាគមន៍ ញាប់ដៃញាប់ជើងពី កម្លាំងសមត្ថកិច្ចពន្លត់អគ្គិភ័យ នៅម៉ោង ៩យប់ថ្ងៃនេះ អគ្គិភ័យ ដែលឆាបឆេះធនា គារសហពាណិជ្ជ UCB ត្រូវបានបាញ់ទឹកពន្លត់ទៅវិញទាំងស្រុងហើយ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97794--ucb-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00785.jpg', 'Monday, 24 August 2015 21:26', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1729', 'B030101', 'B030000', 'ការងារពិការភាព ត្រូវបានផ្សព្វផ្សាយ និងបញ្ជ្រាបដល់មន្រ្តី ក្នុងខេត្តកំពង់ឆ្នាំង', '<b>ភ្នំពេញ៖</b> យុទ្ធនាការស្វែងរកការយកចិត្តទុកដាក់ពីមន្រ្តី តាមបណ្តាក្រសួង និងខេត្តនានា ទៅលើសុខទុក្ខ និង ជីវ &nbsp;&nbsp;ភាពប្រចាំថ្ងៃរបស់ជនពិការ ត្រូវបានធ្វើដំណើរទៅដល់ខេត្តកំពង់ឆ្នាំងហើយ នៅថ្ងៃទី២៤ ខែសីហា ឆ្នាំ២០១៥។ &nbsp;&nbsp;អភិបាលខេត្តកំពង់ឆ្នាំង លោក ឈូរ ច័ន្ទឌឿន ដែលមានតួនាទីជាប្រធានកិត្តិយសក្រុមប្រឹក្សាសកម្មភាពជនពិការ &nbsp;&nbsp;ខេត្តកំពង់ឆ្នាំង បានក្រើនរំលឹកដល់មន្រ្តីក្នុងខេត្តទាំងអស់ បង្កើនការយកចិត្តទុកដាក់ឲ្យបានខ្ពស់លើជនពិការ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97792-2015-08-24-13-49-07.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/ST-223.jpg', 'Monday, 24 August 2015 20:48', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1730', 'B030101', 'B030000', 'សមត្ថកិច្ច និងចោរប្លន់ ផ្ទុះអាវុធដាក់គ្នា កណ្តាលក្រុង ប៉ោយប៉ែត ស្លាប់ចោរប្លន់ម្នាក់', 'null', 'http://www.dap-news.com/2011-06-14-02-39-55/97793-2015-08-24-13-51-06.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00767.jpg', 'Monday, 24 August 2015 20:45', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1731', 'B030101', 'B030000', 'អគ្គិភ័យឆាបឆេះ រោងចក្រមួយកន្លែង យ៉ាងសន្ធោសន្ធៅ (វីដេអូ)', '<b>ភ្នំពេញ៖</b> អគ្គិភ័យឆាបឆេះ រោងចក្រមួយកន្លែងយ៉ាងសន្ធោសន្ធៅ នៅខាងក្រោយ សាលាខណ្ឌសែនសុខ ស្ថិតក្នុងសង្កាត់ភ្នំពេញថ្មី។ នៅខណៈ ពេលនេះរថយន្ដ ពន្លត់អគ្គិភ័យ បាននឹងកំពុង ចេញអន្តរាគមន៍។', 'http://www.dap-news.com/2011-06-14-02-39-55/97791-2015-08-24-13-24-17.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00753.jpg', 'Monday, 24 August 2015 20:23', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1732', 'B030101', 'B030000', 'អគ្គិភ័យ ឆាបឆេះ បន្ទប់ជាន់ទី២ នៃធនាគារ សហពាណិជ្ជ UCB', '<b>ភ្នំពេញ៖</b> មានការភ្ញាក់ផ្អើលឆេះធនាគារ សហពាណិជ្ជ (UCB) ជាន់ទី២, រថយន្តពន្លត់ អគ្គិភ័យចំនួន៤គ្រឿង កំពុងអន្តរាគមន៍។&nbsp;', 'http://www.dap-news.com/2011-06-14-02-39-55/97790--ucb.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00750.jpg', 'Monday, 24 August 2015 20:12', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1733', 'B030101', 'B030000', 'ក្រុមហ៊ុន SEATEL គ្រោងនឹងចំណាយទុន ៤០០លានដុល្លារ ផ្គត់ផ្គង់សេវាកម្ម អ៊ិនធឺណែត និងទូរគមនាគមន៍ចល័ត នៅកម្ពុជា', '<strong>ភ្នំពេញៈ</strong> ក្រុមហ៊ុន&nbsp; South East Asia Telecom( Cambodia) Co,Ltd ដែលជា ក្រុមហ៊ុនផ្គត់ផ្គង់សេវាកម្មអ៊ិនធឺណែត និង ទូរគមនាគមន៍ចល័តល្អបំផុត នៅកម្ពុជាបាន បើកសម្ពោធជាផ្លូវការ នៅថ្ងៃទី២៣ ខែ សីហា ឆ្នាំ២០១៥ នាមជ្ឈមណ្ឌលសន្និសីទ កោះពេជ្រ ដែលនឹងគ្រោងចំណាយទុន៤០០ លានដុល្លារ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97789--seatel-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0629.jpg', 'Monday, 24 August 2015 19:09', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1734', 'B030101', 'B030000', 'អគ្គិភ័យ កំពុងឆាបឆេះ  រោងចក្រមួយកន្លែង យ៉ាងសន្ធោរសន្ធៅ ខាងក្រោយ សាលាខណ្ឌ សែនសុខ', '<strong>ភ្នំពេញ៖</strong> អគ្គិភ័យកំពុងឆាបឆេះ រោងចក្រមួយកន្លែង យ៉ាងសន្ធោរសន្ធៅ នៅខាងក្រោយ សាលាខណ្ឌសែនសុខ ស្ថិតក្នុងសង្កាត់ ភ្នំពេញថ្មី រាជធានីភ្នំពេញ ។ នៅខណៈពេលនេះរថយន្ដពន្លត់អគ្គិភ័យ បាននឹងកំពុងចេញ អន្តរាគមន៍៕', 'http://www.dap-news.com/2011-06-14-02-39-55/97788-2015-08-24-12-07-13.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-010/chanroth015-aug-198.jpg', 'Monday, 24 August 2015 19:06', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1735', 'B030101', 'B030000', 'លោក ឱម យ៉ិនទៀង ៖ ACU អនុញ្ញាតឱ្យ យុវជន ដែលចូលប្រឡង ជំនួសបងប្រុស ទៅផ្ទះវិញ ក្រោយធ្វើកិច្ចសន្យា', '<strong>ភ្នំពេញ៖</strong> លោកទេសរដ្ឋមន្រ្តី ឱម យ៉ិនទៀង ប្រធានអង្គភាព ប្រឆាំងអំពើពុករលួយ (ACU) បានប្រាប់ មជ្ឈមណ្ឌលព័ត៌មានដើមអម្ពិល នៅល្ងាចថ្ងៃទី២៤ ខែសីហា ឆ្នាំ២០១៥នេះថា ACUបានអនុញ្ញាត ឱ្យយុវជនម្នាក់ ដែលចូលប្រឡងជំនួសបងប្រុសរបស់ខ្លួនត្រឡប់ទៅផ្ទះវិញហើយ បន្ទាប់ពីមានកិច្ច សន្យាមួយចំនួន ដើម្បីយុវសិស្សអាយុទើប១៧ឆ្នាំរូបនេះ បញ្ឈប់ការប្រព្រឹត្តបែបនេះទៅថ្ងៃក្រោយ ទៀត។', 'http://www.dap-news.com/2011-06-14-02-39-55/97787--acu-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-010/chanroth015-aug-197.jpg', 'Monday, 24 August 2015 18:55', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1736', 'B030102', 'B030000', 'ការប្រែប្រួល អាកាសធាតុ នៅតែបន្ត គំរាមកំហែង ឆ្នេរសមុទ្រអាមេរិក', '<strong>វ៉ាស៊ីនតោន៖</strong> ក្រុមអ្នកស្រាវជ្រាវ បាននិយាយថា ការប្រែប្រួលអាកាសធាតុ នឹងនៅតែបន្ត គំរាមកំហែងដល់ ប្រព័ន្ធ ធម្មជាតិ និងសេដ្ឋកិច្ច នៃបណ្តារដ្ឋ តាមឆ្នេរ និង ឈូងសមុទ្រ ទាំងឡាយរបស់សហរដ្ឋអាមេរិក។', 'http://www.dap-news.com/2011-06-14-02-40-26/97765-2015-08-24-08-09-02.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-192.jpg', 'Monday, 24 August 2015 15:04', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1737', 'B030102', 'B030000', 'Apple រកឃើញបញ្ហា ព្រិលរបស់ កាមេរ៉ាទូរស័ព្ទ iPhone 6 plus', '<strong>ប៉េកាំង៖</strong> ទីភ្នាក់ងារព័ត៌មាន ចិនស៊ិនហួ ចេញថ្ងៃចន្ទ ទី ២៤ ខែសីហា ឆ្នាំ ២០១៥បានឲ្យដឹងថា ក្រុមហ៊ុនទូរស័ព្ទ Smartphone Apple បានរកឃើញបញ្ហាមួយ ជាមួយនឹងកាមេរ៉ា របស់ទូរស័ព្ទ ដៃ iPhone 6 Plus មួយចំនួនដែល មានលក្ខណៈព្រិល នៅពេលថតរូប។', 'http://www.dap-news.com/2011-06-14-02-40-26/97763-apple----iphone-6-plus.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-191.jpg', 'Monday, 24 August 2015 15:01', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1738', 'B030102', 'B030000', 'យ៉ុនហាប់៖ កូរ៉េខាងជើង កំពុង​ដាក់​ពង្រាយ​នាវា​ពោង​ដឹក​យោធា យ៉ាង​សម្បើម', '<strong>សេអ៊ូល៖</strong> ប្រទេសកូរ៉េខាងជើង បានធ្វើការដាក់ពង្រាយនូវ នាវាពោងដឹកកងកម្លាំងពិសេស ប្រកបដោយ សក្តានុពល ទៅដាក់តាមខ្សែត្រៀមជួរមុខ បន្ទាប់ពីរដ្ឋកុម្មុយនិស្តមួយ នេះបាន អះអាងថា ខ្លួនត្រូវតែបង្កើននូវកងកម្លាំងយោធាដើម្បីត្រៀម ទប់ទល់នឹងស្ថានការណ៍តានតឹង ផ្នែកក្នុងឧបទ្វីបនាពេលបច្ចុប្បន្ននេះ។ នេះបើតាមទីភ្នាក់ងារព័ត៌មាន យ៉ុនហាប់ ដកស្រង់ ចេញពី ប្រភពមន្ត្រីយោធាកូរ៉េខាងត្បូង ដែលសុំមិនបញ្ចេញឈ្មោះ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97760-2015-08-24-07-29-05.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-010/chanroth015-aug-192.jpg', 'Monday, 24 August 2015 14:27', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1739', 'B030102', 'B030000', 'ព្រះពុទ្ធរូបបុរាណ មួយអង្គ ត្រូវបានឆ្លាក់ ចេញពី អាចម៍ផ្កាយ', '<strong>អាល្លឺម៉ង់៖</strong> ក្រុមអ្នកវិទ្យាសាស្រ្តជនជាតិអាល្លឺម៉ង់ ដែលបានធ្វើការវិនិច្ឆ័យ ទៅលើព្រះពុទ្ធរូប បុរាណ មួយអង្គដែលត្រូវបានរកឃើញ ដោយការធ្វើបេសកកម្ម Nazi ទៅកាន់តំបន់ទីបេ នៅក្នុងឆ្នាំ ១៩៣៨ និយាយថា រូបចំលាក់ព្រះពុទ្ធមួយអង្គនេះ ត្រូវបានឆ្លាក់ចេញ ពីទម្រង់ដ៏ កម្រ មួយរបស់អាចម៍ផ្កាយ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97738-2015-08-24-04-42-11.html', 'http://en.es-static.us/upl/2012/10/meteorite_buddha_statue.jpg', 'Monday, 24 August 2015 11:39', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1740', 'B030102', 'B030000', 'កិច្ចប្រជុំចរចាគ្នា រវាងកូរ៉េទាំងពីរ ធ្វើឡើងមួយយប់ ទល់ភ្លឺដើម្បី បន្ធូរស្ថានការណ៍', '<strong>សេអ៊ូល៖</strong> កិច្ចប្រជុំចរចា កម្រិតខ្ពស់រវាង កូរ៉េខាងជើង និង ខាងត្បូង បានធ្វើឡើងចាប់ពី រសៀល ថ្ងៃអាទិត្យ អូសបន្លាយមកដល់ ព្រឹកថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ នេះ ដើម្បីស្វែងរក ភាពប្រសព្វគ្នាមួយ ក្នុងការបន្ធូរ បន្ថយស្ថានការណ៍ តានតឹង កន្លងមក។ នេះបើតាមការ ផ្សាយពីទីភ្នាក់ងារព័ត៌មាន ជប៉ុន ក្យូដូ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97728-2015-08-24-03-44-32.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-178.jpg', 'Monday, 24 August 2015 10:41', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1741', 'B030102', 'B030000', 'ផ្ទុះឃ្លាំងស្តុក ទំនិញ យោធាអាមេរិក នៅជប៉ុន មិនមានអ្នករបួស', '<strong>យូកូហាម៉ា៖</strong> សម្លេងផ្ទុះដ៏កក្រើក បានលាន់ឮទាំងកណ្តាលយប់ ថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ នេះ នៅឯ មូលដ្ឋាន ទ័ពអាមេរិក ក្នុងទីក្រុង សាហ្គាមីហារ៉ា នៃខេត្តកាណាហ្គាវ៉ា ប្រទេសជប៉ុន ប៉ុន្តែមិនទាន់មានប្រភព ណាមួយ រាយការណ៍ថា មានអ្នករបួស បាត់បង់ជីវិត និង ខូចខាត អ្វីជារួមនោះឡើយ។ នេះបើតាមការផ្សាយ ពីទីភ្នាក់ងារព័ត៌មានជប៉ុន ក្យូដូ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97726-2015-08-24-03-19-29.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-177.jpg', 'Monday, 24 August 2015 10:17', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1742', 'B030102', 'B030000', 'ប្រជាពលរដ្ឋ កូរ៉េខាងជើង មួយលាននាក់ ស្ម័គ្រចិត្តចូល ធ្វើសង្គ្រាមប្រឆាំង នឹងកូរ៉េខាងត្បូង', '<strong>កូរ៉េខាងជើង៖</strong> ប្រជាពលរដ្ឋកូរ៉េខាងជើង មួយលាននាក់ បានស្ម័គ្រចិត្ត ការការពារប្រទេស ក្នុងករណីដែលមាន ការផ្ទុះ សង្គ្រាមជាមួយកូរ៉េខាងត្បូង នេះបើយោងតាម សេចក្តីរាយការណ៍ របស់សារព័ត៌មាន ក្នុងស្រុកដកស្រង់ ដោយ សារព័ត៌មាន Sky News ចេញផ្សាយនៅរាត្រីថ្ងៃទី២៣ ខែសីហា ឆ្នាំ២០១៥នេះ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97725-2015-08-24-03-17-00.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-176.jpg', 'Monday, 24 August 2015 10:15', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1743', 'B030102', 'B030000', 'ហ្វាង់ឡង់ ចោទយន្ត ហោះអាមេរិក ថាហោះរំលោភ ដែនអាកាសខ្លួន', '<strong>ហែលស៊ីងគី៖</strong> រដ្ឋាភិបាលហ្វាំងឡង់ បានចេញសេចក្តីថ្លែងការណ៍មួយ ដោយលើកឡើងពី ការហោះរំលោភដែនអាកាសរបស់ខ្លួន ពីសំណាក់យន្តហោះចម្បាំងពីរគ្រឿង របស់សហរដ្ឋ អាមេរិក កាលពីថ្ងៃអាទិត្យ ទី២៣ ខែសីហា ឆ្នាំ ២០១៥។ នេះបើតាមការផ្សាយពីទីភ្នាក់ងារ ព័ត៌មានចិនស៊ិនហួ នៅថ្ងៃចន្ទ នេះ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97720-2015-08-24-02-41-21.html', 'https://upload.wikimedia.org/wikipedia/commons/b/bc/C-17_test_sortie.jpg', 'Monday, 24 August 2015 09:39', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1744', 'B030401', 'B030000', 'តូយ៉ូតា បង្អួតរថយន្ត Hilux Revo ជំនាន់ថ្មីទី៨ ទាក់ទាញ ចិត្តអតិថិជន', '<strong>ភ្នំពេញ៖</strong> រថយន្តភិកអាប់ Hilux Revo ជំនាន់ថ្មីចុងក្រោយ ភ្លឺរលោងចាំងប្រិចៗ ជាមួយនឹងបច្ចេកវិទ្យាទំនើប ពោរពេញដោយ រូបរាងស្រស់សង្ហា គ្មានគូប្រៀប សាកសមនឹងមនុស្សសម័យថ្មី ត្រូវបានក្រុមហ៊ុន តូយ៉ូតា (ខេមបូឌា) ដាក់តាំងបង្ហាញ ដល់ទស្សនិកជន ចាប់ពីថ្ងៃទី១៥ ដល់ថ្ងៃទី១៦ ខែសីហា ឆ្នាំ២០១៥ នាមជ្ឈមណ្ឌលកម្សាន្ត កោះពេជ្រ រាជធានីភ្នំពេញ។', 'http://www.dap-news.com/2011-06-14-02-43-39/97293--hilux-revo-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0425.jpg', 'Monday, 17 August 2015 14:37', '0', '0', '0', 't', '2015-08-17', 'f');
INSERT INTO "tbnews" VALUES ('1745', 'B010105', 'B010000', 'ទស្សនា​វីដេអូ​នេះ​ហើយ​បាន​ដឹង​ថា​ Range Rover ខ្លាំង​ដល់​កម្រិត​ណា​', 'តាម​រយៈ​វីដេអូ​ គេ​ឃើញ​ថា​ Range Rover Sport មួយ​នេះ​មាន​សមត្ថភាព​អស្ចារ្យ​មិន​គួរ​ឲ្យ​ជឿ​ មិន​ថា​បើក​លើ...', 'http://news.sabay.com.kh/article/460815', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/4511-285x170.jpg?ebb82d', '25 August, 2015', '2', '0', '0', 't', '2015-08-25', 'f');
