INSERT INTO "tbnews" VALUES ('1747', 'B010105', 'B010000', 'ក្រុមហ៊ុន​បច្ចេកវិទ្យា​ធំៗបួន​ដែល​កាន់​កាប់​ដោយ​​ CEO ​ជនជាតិ​ឥណ្ឌា', 'ក្រៅ​ពី​លោក​ Sundar Pichai ​ដែល​ជា​នាយក​ប្រតិបត្តិ​ថ្មី​របស់​ Google ​ក៏​នៅ​មាន​ក្រុមហ៊ុន​បច្ចេកវិទ្យា​ធំៗ​ចំនួន​បី​ទៀត​ដែល​កាន់​ដោយ​នាយក​ប្រតិបត្តិ​...', 'http://news.sabay.com.kh/article/460620', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/Untitled-172-285x170.jpg?ebb82d', '24 August, 2015', '1', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1748', 'B010105', 'B010000', 'វិធី​បិទ​​ឬ​​បើក​​​​iPhone​,​ iPad និង​ iPod​ ពេល​ខូច​ប៊ូតុង​ Power', 'ការ​ខូច​ប៊ូតុង​ Power​ គឺ​ជា​បញ្ហា​មួយ​ដែល​​ធ្វើ​ឲ្យ​អ្នក​ប្រើ​ប្រាស់​ iDevice​ មួយ​ចំនួន​​តែង​មាន​ការ​ក្រពុល​មុខ​ជា​ខ្លាំង ​ដោយ​សារ​តែ​ធ្វើ​ឲ្យ​​ពិបាក​ទាំង​ការ​បិទ ​និង​ការ​បើក​ iDevice។ ប៉ុន្តែ​អ្នក​​នឹង​ដោះ​ស្រាយ​បញ្ហា​នេះ​បាន​ ដោយ​មិន​ចាំ​បាច់​ចំណាយ​ប្រាក់​ជួស​ជុល​វា​នោះ​ទេ​ គឺ​គ្រាន់​តែ​​អនុវត្ត​តាម​ជំហាន​ដូច​ខាង​ក្រោម​នេះ៖ &nbsp; &nbsp; ១. ចូល​ទៅ​កាន់​ Settings → General → Accessibility &nbsp; ២. បន្ទាប់​មក​ស្វែង​រក​ទីតាំង​ Interaction/ Physical &amp; Motor​ រួច​ចុច​លើ​ពាក្យ​ AssistiveTouch​ ហើយ​បើក​ដំណើរ​ការ​មុខ​ងារ​ AssistiveTouch​ នោះ​ឡើង​ &nbsp; &nbsp; ៣. នៅ​ពេល​រួច​រាល់ ​វា​នឹង​​មាន​បង្ហាញ​ប្រអប់​ពណ៌​ប្រផេះ​មួយ​ (AssistiveTouch)​ នៅ​លើ​អេក្រង់​ទូរសព្ទ​អ្នក ​បន្ទាប់​មក​ចុច​លើ​ប្រអប់​នោះ​ &nbsp; ៤. រួច​ចុច​លើ​ពាក្យ​ Device​ បន្ទាប់​មក​ចុច​លើ​ពាក្យ​ Lock Screen​ ឲ្យ​យូរ​រហូត​ដល់​បង្ហាញ​ផ្ទាំង​បិទ​iDevice​ ចេញ​មក​ នោះ​អ្នក​នឹង​អាច​ធ្វើ​ការ​បិទ​ iDevice ​របស់​អ្នក​បាន​ហើយ &nbsp; ដើម្បី​បើក​ iDevice [&hellip;]', 'http://news.sabay.com.kh/article/460668', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/featured125-285x170.jpg?ebb82d', '24 August, 2015', '0', '0', '0', 't', '2015-08-24', 'f');
