INSERT INTO "tbnews" VALUES ('1783', 'B010201', 'B010000', 'ឯកឧត្តម ខៀវ សាម៉េត របស់​កម្ពុជា ជាប់​ឆ្នោត​ជា​អនុ​ប្រធាន​សហព័ន្ធ​បាល់ទាត់​អាស៊ាន ដល់​ឆ្នាំ ២០១៩', 'ឯកឧត្តម ខៀវ សាម៉េត អនុ​ប្រធាន​សហព័ន្ធ​កីឡា​បាល់ទាត់​កម្ពុជា (FFC) ត្រូវ​បាន​ប្រទេស​ជា​សមាជិក​តំបន់​​អាស៊ីភាគអាគ្នេយ៍​បោះ​ឆ្នោត​ឱ្យ​ធ្វើ​ជា​អនុ​ប្រធាន...', 'http://news.sabay.com.kh/article/460368', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/featured124-285x170.jpg?ebb82d', '24 August, 2015', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1784', 'B010201', 'B010000', 'មើល​ក្មេង​ៗ​ដើរ​លក់​ចំណី​នៅ​ពេល​ការ​​ប្រកួត​ផ្លូវ​ការ​នៅ​អូឡាំពិក​', 'ក្មេងៗ​មួយ​ចំនួន​បាន​ដើរ​លក់​ចំណី​អាហារ​នៅ​ពហុ​កីឡដ្ឋាន​ជាតិ​នៅ​ពេល​ការ​ប្រកួត​បាល់ទាត់​មិត្តភាព​អន្តរជាតិ​ផ្លូវ​ការ​រវាង...', 'http://news.sabay.com.kh/article/460290', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/featurededchild-285x170.jpg?ebb82d', '24 August, 2015', '2', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1785', 'B020202', 'B020000', 'ម៉ា​រី​យូ បា​ឡូ​តេ​លី ចូល​លេង​ឱ្យ​ក្លិប Milan ក្នុង​ល័ក្ខខ័ណ្ឌ​ខ្ចី​ជើង', 'កីឡាករ​ខ្សែប្រយុទ្ធ​ក្លិប Liverpool អាយុ​២៥​ឆ្នាំ ម៉ា​រី​យូ បា​ឡូ​តេ​លី នឹង​ត្រឡប់​ទៅ​លេង​ឱ្យ​ក្លិប​ក្របខ័ណ្ឌ​អុ​ី​តា​លី Milan វិញ ក្រោយ​ពី​កិច្ចព្រមព្រៀង​លេង​ខ្ចី​ជើង​រយៈពេល​វែង​មួយ​ត្រូវ​បាន​ធ្វើ​ឡើង​រវាង​ក្លិប​ទាំង​ពីរ​កាលពី​ថ្ងៃ​ចន្ទ​ទី​២៤​សីហា ។ ', 'http://kohsantepheapdaily.com.kh/article/127596.html', 'http://i.kspg.co/2015/8/a6114132-b023-4024-867b-975349c1d14d.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៦:១០', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1786', 'B020202', 'B020000', 'កីឡាករ​ដែល​ទទួល​ចំណូល​ពី​ក្រុមហ៊ុន​ឧបត្ថម្ភ​ខ្ពស់​នៅ​ឆ្នាំ​2015', '«Forbes» ជា​ទស្សនាវដ្ដី​ដ៏​ល្បាញ​ល្បី​របស់​អា​មេ​រិ​កបាន​រៀប​ចំណាត់ថ្នាក់​ប្រាក់​ចំណូល​របស់​កីឡាករ​នៅ​លើ​ពិភពលោក​តាម​រយៈ​ប្រាក់​ឧបត្ថម្ភ​ផ្សេង​ៗ​ពី​ក្រុមហ៊ុន​ពាណិជ្ជ​កម្ម​នៅ​ក្នុង​ឆ្នាំ​២០១៥ ឃើញ​ថា កីឡាករ​វាយ​កូន​បាល់ រូ​ជឺ ហ្វេ​ដឺ​រ័​រ ', 'http://kohsantepheapdaily.com.kh/article/127586.html', 'http://i.kspg.co/2015/8/a493e5df-461c-437e-82d7-470f64113ca7.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:២១', '0', '0', '0', 't', '2015-08-25', 'f');
