INSERT INTO "tbnews" VALUES ('1790', 'B020201', 'B020000', 'កីឡាករ​៨​រូប​ថ្មី ប្រជែង​យក​ខ្សែក្រវាត់​K-Cement  ក្នុង​រដូវ​កាល​ទី​២', 'រាជ​ធានី​ភ្នំពេញ​ ៖ បន្ទាប់​ពី​ស្វែងរក​ឃើញ​ម្ចាស់​ខ្សែក្រវាត់​ជើងឯក K-Cement  រដូវ​កាល​ទី​១​ក្នុង​ប្រភេទ​ទម្ងន់​៦០,៥​គីឡូក្រាម​បាន​ទទួល​ជោគជ័យហើយ ស្ថានីយទូរទស្សន៍​អាស៊ី​អា​គ្នេ​យ៏ និង​ក្រុមហ៊ុន​', 'http://kohsantepheapdaily.com.kh/article/127580.html', 'http://i.kspg.co/2015/8/040186a2-3109-4dac-9a0e-367bcef4138d.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:០៩', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1791', 'B020302', 'B020000', 'តារា​ចម្រៀង  HyunA ប្រកាស​ឱ្យ​អ្នក​គាំទ្រ​ឱប​តាម​​ចិត្ត បើសិន​ជា​បទ​ចម្រៀង​របស់​នាង​ឈរ​លំដាប់​លេខ​១', 'សមាជិក​ក្រុម​ចម្រៀង​កូរ៉េ 4minutes វ័យ ២៣ ឆ្នាំ​នាង HyunA បាន​និយាយ​ទៅ​កាន់​អ្នកគាំទ្រ​របស់​នាង​ថា «»​នាង​ខ្ញុំ​នឹង​ឱ្យ​អ្នក​ទាំងអស់​គ្នា​ឱប​តាម​ចិត្ត បើសិនជា​បទ​ចម្រៀង​របស់ខ្ញុំ​ស្ថិត​នៅ​លំដាប់​លេខ​១ ក្នុង​តារាង Music Chart» ។ ', 'http://kohsantepheapdaily.com.kh/article/127557.html', 'http://i.kspg.co/2015/8/fc6f8ff1-69f1-4c85-8951-74e0c44fd700.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៣៤', '0', '0', '0', 't', '2015-08-25', 'f');
