INSERT INTO "tbnews" VALUES ('1794', 'B020505', 'B020000', 'វិធីសាស្ដ្រ​ជួយ​ធ្វើ​ឱ្យ​ដុះ​សក់​ឡើង​វិញ​សម្រាប់​បុរស​ដែល​មាន​បញ្ហា​ជ្រុះ​សក់', 'ការ​ជ្រុះ​សក់​មិនយូរ​ប៉ុន្មាន​ច្បាស់​ជា​ត្រូវ​ទំពែក​បើ​សាមីខ្លួន​មិន​រក​វិធីសាស្ត្រ​ទប់ស្កាត់​ឱ្យ​បាន​ទាន់​ពេលវេលា​ទេ​នោះ​ក៏​អាច​រាប់​ថា​ជា​រឿង​គួរ​ឱ្យ​ខកចិត្ត​មួយ​សម្រាប់​ទាំង​បុរស​និង​ស្ត្រី ។ ជម្រើស​នៃ​ការ​ធ្វើ​ឱ្យ​ប្រសើរ​ឡើង​វិញ', 'http://kohsantepheapdaily.com.kh/article/127537.html', 'http://i.kspg.co/2015/8/70037ff6-8868-44df-a500-12682c396f7e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:២១', '0', '0', '0', 't', '2015-08-25', 'f');
