INSERT INTO "tbnews" VALUES ('1796', 'B020102', 'B020000', 'សមត្ថកិច្ច​ចាប់ខ្លួន​ជនសង្ស័យ​២​នាក់​ពី​បទ​ទទួលផល​ចោរកម្ម និង​ក្លែងបន្លំ​ឯកសារ', 'ខេត្តស្វាយរៀង ៖ ប្រុស​ស្រី​ពីរ​នាក់​ត្រូវ​បាន​សមត្ថកិច្ច​ចាប់ខ្លួន​ពី​បទ​ក្លែងបន្លំ​ឯក​សាធារណៈ មូលហេតុ​ទិញ​ម៉ូតូ​ចោរលួច​កាច់ក​យក​មក​លក់​ឱ្យ រួច​យក​ប័ណ្ណ​សំគាល់​យានយន្ត​(​កាត​គ្រី​) មក​កោស​លុប ហើយ​ស្គេ​ន​លេខ​តួ​-​លេខ​ម៉ាស៊ីន', 'http://kohsantepheapdaily.com.kh/article/127606.html', 'http://i.kspg.co/2015/8/5c740323-a269-4c48-a2e5-9ae65743f268.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២២:២៩', '0', '0', '0', 't', '2015-08-25', 'f');
