INSERT INTO "tbnews" VALUES ('1798', 'B020102', 'B020000', 'ដេញ​ប៉ផាប់​ចាប់​ពភីង និង​បាញ់​គំរាម​១​គ្រាប់ ទើប​សមត្ថកិច្ច​ចាប់ចោរ​កាច់​សោ​.​ក​ម៉ូតូ​បាន​មា្ន​ក់​ភ្លាម​ៗ', 'ខេត្តសៀមរាប ៖ នគរបាល​ស្រុក​ប្រាសាទបាគង​បាន​ដេញ​ចាប់ចោរ​កាច់​សោ​.​ក​ម៉ូតូ​ប៉ផាប់​ពភីង ខណៈ​ដែល​ជនសង្ស័យ​បាន​ធ្វើ​សកម្មភាព​កាច់​សោ​.​ក​ម៉ូតូ​នៅ​មុខ​ផ្ទះ​ជន​រង​គ្រោះ ត្រូវ​បាន​ម្ចាស់​ម៉ូតូ និង​ពលរដ្ឋ​ឃើញ', 'http://kohsantepheapdaily.com.kh/article/127602.html', 'http://i.kspg.co/2015/8/487ed377-d169-4ca6-9b6e-a3d97133be1f.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ២០:៤៦', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1799', 'B020102', 'B020000', 'កម្មករ​សំណង់​ម្នាក់​ធ្លាក់​ពី​ជាន់​ទី​៨​នៃ​សំណង់​សណ្ឋាគារ​នៅ​ក្រុង​កំពង់ចាម ស្លាប់​តាម​ផ្លូវ​បញ្ជូន​ទៅ​មន្ទីរពេទ្យ', 'ខេត្តកំពង់ចាម ៖ កម្មករ​ម្នាក់​ដែល​ធ្វើ​សំណង់​សណ្ឋាគារ​ជាង​១០​ជាន់​របស់​ឧកញ៉ា​ម្នាក់​បាន​ធ្លាក់​ពី​ជាន់​ទី​៨​បណ្តាល​ឱ្យ​រង​របួសធ្ងន់ ហើយ​ត្រូវ​បាន​គេ​ដឹក​តាម​រថយន្ត​សង្គ្រោះ​បន្ទាន់​របស់​គ្លីនិក​ឯកជន​មួយ​ទៅ​សង្គ្រោះ​នៅ​មន្ទីរពេទ្យ​កាល់​ម៉ែ​ត', 'http://kohsantepheapdaily.com.kh/article/127601.html', 'http://i.kspg.co/2015/8/8238b1a2-b5a4-4b9e-bb61-7c70cddfb588.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៩:០០', '0', '0', '0', 't', '2015-08-25', 'f');
