INSERT INTO "tbnews" VALUES ('1802', 'B020102', 'B020000', 'ឆក់​ទូរស័ព្ទ​បាន​ហើយ ​​ត្រូវ​ម្ចាស់​តាម​ទាន់​ផ្តូ​ល​ម៉ូតូ​រត់គេច​ខ្លួន', 'អ្នកបើកបរ​រថយន្ត​តាក់ស៊ី​​ម្នាក់​កំពុង​​ចុច​ទូរស័ព្ទ​ឆែក​អ៊ី​ន​ធើ​រ​ណេ​ត ​ត្រូវ​ជន​​៣​នាក់​ឆក់ទូរស័ព្ទ​បាន​​សម្រេច​ ​ក៏​​​បើករថយន្ត​ដេញ​តាម​កៀរ​ផ្តួល​ជនសង្ស័យ​​ទាំង​៣នាក់​នោះភ័យស្លន់​ស្លោផ្តួល​ម៉ូតូ​​រួច​រត់គេច​ខ្លួន​បាត់​​​ ', 'http://kohsantepheapdaily.com.kh/article/127545.html', 'http://i.kspg.co/2015/8/a8d482af-41c1-433f-b3aa-32de33dad26a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៩:៤៩', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1803', 'B020101', 'B020000', 'អាកាសធាតុ​នៅខេត្ត​ព្រះ​សីហ​នុ​ព្រឹក​ថ្ងៃ​ទី​២៦ សីហា ​នេះ ផ្ទៃមេឃ​​នៅ​តែ​បន្តអួអាប់ដដែល​', 'ខេត្ត​ព្រះ​សីហ​នុ ៖ តាម​សេចក្តី​រាយការណ៍​ពី​អ្នក​យក​ព័ត៌មាន​​កោះ​សន្តិភាព​ បាន​ឱ្យ​ដឹង​ថា អាកាសធាតុ​​ទូ​ទាំង​ខេត្ត​​ព្រះសីហនុ​នៅ​ព្រឹក​ថ្ងៃទី​២៦ សីហា ​នេះ ផ្ទៃមេឃ​​​នៅ​តែ​បន្ត​អួអាប់ និង​មាន​ពពក​ខ្មៅ​ងងឹត​​​', 'http://kohsantepheapdaily.com.kh/article/127612.html', 'http://i.kspg.co/2015/8/f798ef1b-a3d0-419c-914f-71764a972af1.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី២៦ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៤៦', '1', '0', '0', 't', '2015-08-26', 'f');
INSERT INTO "tbnews" VALUES ('1804', 'B020101', 'B020000', 'រាជរដ្ឋាភិបាល​ប្រកាស​ផ្សព្វផ្សាយ ​និង​ដាក់​ឱ្យ​អនុវត្តន៍​​គោលនយោបាយ​អភិវឌ្ឍន៍​​ឧស្សាហកម្ម​កម្ពុជា​ ២០១៥-២០២៥ ​', 'រាជធានី​ភ្នំពេញ ៖ សម្តេច​អគ្គមហាសេនាបតី​តេ​ជោ ហ៊ុន សែន បាន​អញ្ជើញ​ជា​អធិបតី​ក្នុង​ពិធី​ប្រកាស​ផ្សព្វផ្សាយ និង​ដាក់​ឱ្យ​អនុវត្ត​គោលនយោបាយ​អភិវឌ្ឍន៍​ឧស្សាហកម្ម ២០១៥​-​២០២៥ នៅ​វិមាន​សន្តិភាព ទីស្តីការ​នាយក​រដ្ឋមន្ត្រី នា​វេលា​ព្រឹក​ថ្ងៃ​ទី​២៦ សីហា ២០១៥ ។', 'http://kohsantepheapdaily.com.kh/article/127611.html', 'http://i.kspg.co/2015/8/7c192e6c-89d2-4215-b73d-2a6976daa4c6.jpg?w=285&h=170&mode=carve', 'ថ្ងៃពុធ ទី២៦ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ០៨:៤២', '1', '0', '0', 't', '2015-08-26', 'f');
