INSERT INTO "tbnews" VALUES ('1811', 'B020101', 'B020000', '​​​បញ្ជូន​ជនសង្ស័យ​ជួញដូរ​ និង​ប្រើប្រាស់់​គ្រឿងញៀន​​​៤​នាក់​ទៅ​​សាលា​ដំបូង​ខេត្ត​', '​ខេត្ត​​បន្ទាយ​មាន​ជ័យ ៖ កម្លាំង​នគរបាល​ការិយាល័យ​ប្រឆាំង​គ្រឿងញៀន​ខេត្ត​ បាន​បង្ហាញ​ជនសង្ស័យ​ជួញដូរ ​និង​ប្រើប្រាស់​គ្រឿងញៀន​ចំនួន​៤​នាក់ ព្រមទាំង​រៀបចំ​សំណុំ​រឿង​​បញ្ជូន​ទៅ​សាលាដំបូង​ខេត្ត​ នៅ​ថ្ងៃ​ទី​២៥ ​សីហា​', 'http://kohsantepheapdaily.com.kh/article/127595.html', 'http://i.kspg.co/2015/8/7405ab4e-8b20-467a-a809-7e2ea0f54e6e.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៥០', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1812', 'B020101', 'B020000', 'ខណៈ​នេះ​! ក្រុង​បាត់ដំបង​គ្របដណ្តប់​ដោយ​អ័ព្ទ និង​ភ្លៀង​តិច​ៗ​រយៈពេល​ជិត​៣​ម៉ោង', 'ខេត្តបាត់ដំបង ៖ ខណៈ​ពេល​នេះ នៅ​ក្រុង​បាត់ដំបង​មេឃ​កំពុង​គ្របដណ្តប់​ដោយ​អ័​ព្វ​អ៊ួ​រយ៉ា​ង​ខ្លាំង និង​មាន​ភ្លៀង​បន្ត​ធ្លាក់​តិច​ៗ​រហូត​មក គិត​ចាប់តាំងពី​វេលា​ម៉ោង​០១​រសៀល​ថ្ងៃ​ទី​២៥ សីហា ២០១៥ មក ​។ ', 'http://kohsantepheapdaily.com.kh/article/127594.html', 'http://i.kspg.co/2015/8/8d210199-3f04-4d7b-a677-279793d027ad.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៥:៤៣', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1813', 'B020101', 'B020000', 'ពលរដ្ឋ​ច្រើន​នាក់​រស់នៅ​ចំណុច​សម្បុកចាប ប្រតិកម្ម​នឹង​គម្រោង​ប្តូរ​លំនៅ​ឋាន', 'ខេត្ត​ប៉ៃលិន ៖ ថ្មី​ៗ​កន្លង​ទៅ​នេះ អាជ្ញាធរ​ខេត្ត បាន​ចាត់តាំង​សមាសភាព​គណៈកម្មការ​មួយ ចុះ​ស្រង់​ទិន្នន័យ​ចំនួន​ខ្នង​ផ្ទះ​ប្រជា​ពល​រដ្ឋ​ដែល​រស់នៅ​ជាក់ស្តែង​(​ចំណុច​សម្បុកចាប​) ក្នុងភូមិ​ផ្សារ​ព្រំ ឃុំ​ស្ទឹង​កាច់  ស្រុក​សាលា​ក្រៅ ដើម្បី​បែងចែក​ដី​ឡូត៍​ជូន​ពួក​គាត់​ប្រហែល​២០០​ទៅ​៣០០​គ្រួសារ​ឱ្យទៅ​រស់នៅ​ចំណុច​លូ​ដាច់ ជាប់​បណ្តោយ​ផ្លូវជាតិ​លេខ​១០​ចាស់ ចម្ងាយ​ពី​ផ្លូវជាតិ​លេខ​៥៧ ជាង​', 'http://kohsantepheapdaily.com.kh/article/127589.html', 'http://i.kspg.co/2015/8/f9c66141-be20-4e84-b4f8-be73e99e5bf6.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៤:៣៥', '0', '0', '0', 't', '2015-08-25', 'f');
