INSERT INTO "tbnews" VALUES ('1820', 'B020101', 'B020000', 'សិស្ស​ស្រី​ម្នាក់​ខ្យល់គ​​ពេល​កំពុង​ប្រឡង នៅ​មណ្ឌល​វត្ត​កោះ', 'រាជធានី​ភ្នំពេញ​ ៖ នៅ​វេលា​ម៉ោង៨​និង​១៥​នាទី​ព្រឹក​ថ្ងៃ​ទី២៥​ សីហា ​​នេះ និង​ជា​ថ្ងៃ​ទី​២នៃ​ការ​ប្រឡង​​សញ្ញាបត្រមធ្យមសិក្សាទុតិយភូមិ នៅ​មណ្ឌល​វិទ្យាល័យ​វត្ត​កោះ​ខាងជើង​មាន​ករណី​សិស្ស​ស្រី​​ខ្យល់គ​ម្នាក់​ឈ្មោះ​ ផេ​ត មក​រា', 'http://kohsantepheapdaily.com.kh/article/127576.html', 'http://i.kspg.co/2015/8/8e79a27c-1988-4091-8e50-35478f73a44a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៥៣', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1821', 'B020101', 'B020000', 'ដោះលែង​​អ្នកលេង​​​ និង​មេការ​​​ល្បែង​អន​ឡាញ​ តែ​បញ្ជូន​សំណុំរឿង​ និង​វត្ថុ​តាង​​ទៅ​តុលា​ការ', 'ខេត្តបាត់ដំបង ​៖ ក្រុម​​អ្នក​ប្រ​ព្រឹ​ត្ត​ល្បែងស៊ីសង និង​អ្នក​ទ​ទូល​ខុស​ត្រូវ​ទីតាំង​​ប្រ​ព្រិ​ត្ត​ល្បែងស៊ីសង​តាម​អន​ឡាញ​​ សរុប​ចំនួន​១២​នាក់ ត្រូវ​​សមត្ថកិច្ច​ដោះ​លែង​ឱ្យ​​ត្រឡប់​ទៅ​ផ្ទះ​វិញ​ នៅ​រសៀល​ថ្ងៃ​ទី​២៥​ សីហា​ នេះ បន្ទាប់​​ពី​ធ្វើការ​អប់រំ និង​ធ្វើ​កិច្ច​សន្យា​ឱ្យ​ប​ញ្ឈ​ប់​សកម្ម​ភាព​ល្មើស​ច្បាប់ ​។', 'http://kohsantepheapdaily.com.kh/article/127574.html', 'http://i.kspg.co/2015/8/ac0b0341-53ee-475b-b4cd-355e572b071a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៣៦', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1822', 'B020101', 'B020000', '​អាកាស​ធាតុ​​​​ក្រុង​សែន​មនោរម្យ​ ​​រហូត​​មក​ដល់​​ម៉ោង​​​​ជិត​១​​រសៀល​​​នេះ​រលឹម​​​​នៅ​តែ​បន្ត​​ឥតឈប់​', 'ខេត្តមណ្ឌលគិរី ៖ បើ​តាម​អ្នកឆ្លើយឆ្លង​ព័ត៌មាន​​បាន​​​​ឲ្យ​ដឹងថា ​​តាំងពី​ព្រឹក​ព្រលឹមរហូត​​មក​ដល់​​ម៉ោង​​​​ជិត​១​​រសៀល​​ថ្ងៃ​ទី​២៥​ សីហា ​នេះ ​​​​​​រលឹម​នៅ​តែ​បន្ត​​ឥតឈប់ ​នៅ​​ក្រុង​សែន​មនោរម្យ និង​នៅ​តាម​បណ្តា​ស្រុក​មួយ​ចំនួន​ទៀត', 'http://kohsantepheapdaily.com.kh/article/127573.html', 'http://i.kspg.co/2015/8/35bebb46-a0d3-412a-8eb6-50473f34e60a.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:១៤', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1823', 'B020101', 'B020000', '​នៅ​ម៉ោងជាង១២​ថ្ងៃ​ត្រង់នេះ អគ្គិភ័យ​ឆេះ​ផ្ទះ​ប្រជាពលរដ្ឋ​នៅ​គល់​ស្ពាន​ច្បារ​អំពៅ​បាន​រលត់​ហើយ', 'រាជធានី​ភ្នំពេញ ៖ ខណៈ​ភ្លើង​កំពុង​ឆេះ​គេ​សង្កេត​ឃើញ​រថយន្ត​ជាង​២០​គ្រឿង​​ក្នុង​​រាជ​ធានី​ភ្្នំពេញ​​​​បាន​​ចេញ​ទៅ​ជួយ​អន្តរាគមន៍​បាញ់​​ទឹកពន្លត់ មក​ដល់​ម៉ោង​​​ជាង​១២​ថ្ងៃ​ត្រង់​ ​អគ្គិភ័យដែល​បំផ្លាញ​ផ្ទះ​ប្រជាពលរដ្ឋ​នៅ​គល់​ស្ពាន​ច្បារ​អំពៅ បាន​រលត់​ទាំងស្រុង​ហើយ', 'http://kohsantepheapdaily.com.kh/article/127572.html', 'http://i.kspg.co/2015/8/4d40aa26-346d-458e-a9bd-c037d562d90c.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១២:៣៦', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1824', 'B020101', 'B020000', 'ម៉ោង១១និង៣៥នាទីថ្ងៃត្រង់នេះ​ ​ភ្លើងឆេះផ្ទះប្រជាពលរដ្ឋ​នៅគល់ស្ពានច្បារអំពៅ​បានរលត់ហើយ', 'រាជធានីភ្នំពេញ ៖​ ​មកដល់ម៉ោង១១និង៣៥នាទីថ្ងៃត្រង់នេះ​ ​ភ្លើងដែលបំផ្លាញផ្ទះ​ប្រជាពលរដ្ឋ​នៅ​គល់ស្ពានច្បារអំពៅ ​បានរលត់ទំាងស្រុងហើយ ​ប៉ុន្តែមិនទាន់​ដឹងពីមូលហេតុ​', 'http://kohsantepheapdaily.com.kh/article/127571.html', 'http://i.kspg.co/2015/8/33094801-4db0-4b44-892c-3c18957a09fa.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:៤២', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1825', 'B020101', 'B020000', 'នៅម៉ោងជាង១១ថ្ងៃត្រង់នេះ ​ចរាចរណ៍កំពុងកកស្ទះខ្លំាង​នៅលើស្ពានច្បារអំពៅ', 'រាជធានីភ្នំពេញ ៖ ​មកដល់ម៉ោងជាង១១ថ្ងៃត្រង់នេះ​ ចរាចរណ៍កំពុងកកស្ទះខ្លំាង​នៅលើស្ពានច្បារអំពៅ​ ដោយសារអ្នកធ្វើដំណើរ​ឈរនិង​ថតរូបអគ្គិភ័យ​ដែលឆេះផ្ទះ​', 'http://kohsantepheapdaily.com.kh/article/127570.html', 'http://i.kspg.co/2015/8/2f0a2bdc-4572-48fd-9f8f-66609a57f432.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:៣៦', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1826', 'B020101', 'B020000', 'បេក្ខជន​ប្រឡង​សញ្ញាបត្រ​មធ្យមសិក្សា​ទុតិយភូមិ​ឆ្នាំ​នេះ​ចំនួន​៨៨.៤៨៨​នាក់ បាន​ប្រឡង​តាម​មណ្ឌល​ចំនួន​១៥០', 'រាជធានី​ភ្នំពេញ ៖ នៅ​ថ្ងៃ​ទី​២៤ សីហា ជា​ថ្ងៃ​នៃ​ការ​ចាប់ផ្តើម​ប្រឡង​សញ្ញាបត្រ​មធ្យមសិក្សា​ទុតិយភូមិ (​បាក់​ឌុ​ប​) និង​បំពេញវិជ្ជា​ឆ្នាំ​សិក្សា​២០១៤-២០១៥ ដែល​បាន​ចាប់ផ្តើម​តាម​មណ្ឌល​ទូ​ទាំង​ប្រទេស បុ​៉​ន្តែ​អ្វី​ដែល​គួរ​ឱ្យ​កត់សម្គាល់​នោះ', 'http://kohsantepheapdaily.com.kh/article/127569.html', 'http://i.kspg.co/2015/8/046b487d-4fcd-4dcf-95d5-081fe2c7a959.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:២៩', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1827', 'B020101', 'B020000', 'លោក​អ៊ិ​ត សំ​ហេង អនុញ្ញាត​ឱ្យ​លោក​ប្រធាន​កម្មវិធី​មូលនិធិ​អាស៊ី​ប្រចាំ​កម្ពុជា ចូល​ជួប​ពិភាក្សា​ការងារ', 'រាជធានី​ភ្នំពេញ ៖ លោក​អ៊ិ​ត សំ​ហេង រដ្ឋមន្ត្រីក្រសួង​ការងារ និង​បណ្តុះបណ្តាល​វិជ្ជាជីវៈ នា​វេលា​ម៉ោង​១០ និង​៣០​នាទី​ព្រឹក​ថ្ងៃ​ទី​២៥ សីហា ២០១៥ នេះ បាន​អនុញ្ញាត​ឱ្យ​លោក​ស៊ី​ឡាស់ អ៊ី​វ័​រ​រ៉េ​ត ប្រធាន​កម្មវិធី​មូលនិធិ​អាស៊ី​ប្រចាំ​កម្ពុជា ចូល​ជួប​សម្តែង​ការ​គួរ​សម និង​ពិភាក្សា​ការងារ នៅ​ទីស្តីការក្រសួង ។', 'http://kohsantepheapdaily.com.kh/article/127568.html', 'http://i.kspg.co/2015/8/4224cd7d-7c21-41f6-95cb-48f5963daaa2.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:១៨', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1828', 'B020401', 'B020000', 'គ្រោះ​រាំង​ស្ងួត​ឆ្នាំ​នេះ​ធ្វើ​អោយ​មានការ​ប៉ះពាល់​ដល់​ជីវភាព​ប្រជា​កសិករ', 'រាជធានី​ភ្នំពេញ ៖ សម្រាប់​លោក បាន ភារ​ម្យ ក៏​ដូច​ជា​កសិករ​ផ្សេង​ៗ​ទៀត​នៅ​ក្នុង​ប្រទេស​កម្ពុជា ការ​ធ្វើស្រែ​របស់​គាត់​ស្ទើរតែ​ជាង ៩០​ភាគរយ​គឺ​ពឹងផ្អែក​ទៅ​លើ​ទឹកភ្លៀង ។ តែ​ភាពរាំងស្ងួត​ដែល​បាន​អូស​បន្លាយ​រយៈពេល​យូរ ', 'http://kohsantepheapdaily.com.kh/article/127565.html', 'http://i.kspg.co/2015/8/db1839f7-5251-473a-8794-88375f903265.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០៩', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1829', 'B020401', 'B020000', 'តំបន់ឆ្នេរ​កម្ពុជា​ចាប់ផ្តើម​មានការ​ចាប់អារម្មណ៍​ពី​ភ្ញៀវ​ដែល​ធ្វើ​ដំណើរ​តាម​នាវា', 'ខេត្ត​ព្រះ​សីហ​នុ ៖ កំណើន​ភ្ញៀវ​ទេសចរ​តាម​នាវា​មក​កាន់​ខេត្ត​ព្រះ​សីហ​នុ​ក្នុង​រយៈពេល​៧​ខែ​ឆ្នាំ​២០១៥​នេះ កើនឡើង​ជាង​២០​ភាគរយ បើ​ធៀប​នឹង​រយៈពេល​ដូច​គ្នា​កាលពី​ឆ្នាំ​២០១៤ ។ នេះ​បើ​យោង​តាម​ទិន្នន័យ​ពី​មន្ទី​រ', 'http://kohsantepheapdaily.com.kh/article/127564.html', 'http://i.kspg.co/2015/8/3d3de92b-cd17-4b35-92c8-0e1c3218afd6.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១១:០៦', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1830', 'B020103', 'B020000', 'បំបាក់​អ្នក​វាយប្រហារ ការ​ផ្ទៀងផ្ទាត់​ផែនទី​បង្ហាញ​ភាព​ហ្មត់ចត់​និង​ការ​ទទួលខុសត្រូវ​របស់​រាជរដ្ឋាភិបាល', 'រាជធានី​ភ្នំពេញ ៖ ក្នុង​សារលិខិត​ជូន​លោក​អគ្គលេខាធិការ​នៃ​អង្គការសហប្រជាជាតិ​លោក​បាន គី​មូ​ន សម្តេច​តេ​ជោ​នាយក​រដ្ឋមន្ត្រី ហ៊ុន សែន បាន​លើក​ឡើង​ថា ការ​ផ្ទៀងផ្ទាត់​ផែនទី​ពិតជា​បង្ហាញ​ឲ្យ​ឃើញ​ជាក់ច្បាស់ អំពី​ភាព​ហ្មត់ចត់', 'http://kohsantepheapdaily.com.kh/article/127559.html', 'http://i.kspg.co/2015/8/3affbc43-a5c5-416a-a685-cff9130051bb.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១០:៤៥', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1831', 'B020104', 'B020000', 'ដំណាំ​ស្វាយកែវ​រមៀត​អាច​ប្រមូល​ផល​បាន​ចាប់ពី​3​ឆ្នាំ​ឡើង​ទៅ', 'ស្វាយកែវ​រមៀត ជា​ដំណាំ​ឈើ​ហូប​ផ្លែ ដែល​មាន​អាយុ​ច្រើន​ឆ្នាំ គេ​អាច​ប្រមូល​ផល​ចាប់ពី​អាយុ​៣​ឆ្នាំ​ឡើង​ទៅ ។ អ្នក​មីង វ៉ា​ន់ យ៉េ​ត រស់នៅ​ភូមិ​បន្ទាយស្រី ឃុំ​ខ្នារ​សណ្តាយ ស្រុក​បន្ទាយស្រី ខេត្តសៀមរាប', 'http://kohsantepheapdaily.com.kh/article/127575.html', 'http://i.kspg.co/2015/8/78ad9ce8-31bf-46d5-bcae-650ba522fce5.jpg?w=285&h=170&mode=carve', 'ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ ម៉ោង ១៣:៤៨', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1832', 'B030101', 'B030000', 'មន្រ្តីយោធាមួយរូប ដកកាំភ្លើង K54 ប៉ងបាញ់សម្លាប់ លោក តូច ផន អធិការរង នគរបាលខណ្ឌ ពោធិ៍សែនជ័យ', '<strong>(ភ្នំពេញ)៖</strong> មន្រ្តីនគរបាល ខណ្ឌពោធិ៍សែនជ័យ បានរាយការណ៍ឲ្យ ដឹងនៅមុននេះបន្តិចថា នៅម៉ោង១១៖១០នាទី ថ្ងៃទី២៥ ខែសីហា ឆ្នាំ២០១៥នេះ ២០១៥ នៅមុខវត្តវិហារឃួន ក្នុងសង្កាត់ចោមចៅ ខណ្ឌពោធិ៍សែនជ័យ មន្រ្តីយោធាមួយរូបឈ្មោះ សោម សុផាត អាយុ៤០ឆ្នាំ ជាអតីតមន្រ្តីយោធា នៅអង្គភាពកងពលតូចលេខ ង៧០ ដោយបច្ចុប្បន្ន បានប្តូរទៅផ្នែកទៅផ្នែក គីមីសាស្រ្ត បានដកកាំភ្លើងខ្លី ប្រភេទ K54 ពណ៌ស បានបាញ់សំដៅប៉ងសម្លាប់មកលើ លោក តូច ផន អធិការរង នគរបាលខណ្ឌ ទទួលបន្ទុកផ្នែកព្រហ្មទណ្ឌ ចំនួន១គ្រាប់ ខណៈលោកអធិការរងបាន និងកំពុងដឹកនាំកម្លាំងល្បាត ចាប់ម៉ូតូអត់ស្លាកលេខ ត្រង់កន្លែង ដែលមន្រ្តីយោធាយាម ស្ថិតនៅមុខវត្តវិហារឃួន។', 'http://www.dap-news.com/2011-06-14-02-39-55/97869---k54-------.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00832.jpg', 'Tuesday, 25 August 2015 23:43', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1833', 'B030101', 'B030000', 'សមត្ថកិច្ច ចាប់ខ្លួន បុរសល្មោភកាម រំលោភ ក្មេងស្រី ជនជាតិភ្នង អាយុ១២ឆ្នាំ នៅស្រុកកែវសីម៉ា បានហើយ', 'null', 'http://www.dap-news.com/2011-06-14-02-39-55/97868-2015-08-25-16-42-52.html', 'null', 'Tuesday, 25 August 2015 23:35', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1834', 'B030101', 'B030000', 'ម៉ូតូស្កុបពី​ ប្រលះរថយន្ដយីឌុប បណ្ដាលឲ្យមនុស្ស ស្លាប់២នាក់ និងកន្លែងកេីតហេតុ', '<strong>ក្រចេះ</strong> ហេតុការណ៍គ្រោះថ្នាក់ចរាចរណ៍ មួយបានកេីតឡេីង បណ្តាលដោយម៉ូតូ ស្កុបពីបេីកបុក រថយន្ដយីឌុបពី ក្រោយកាលពីរវេលាម៉ោង៧ និង៣០នាទីថ្ងៃទី២៥ ខែសីហា ឆ្នាំ២០១៥ នៅលេីផ្លូវជាតិលេខ៧៦ (ស្នួលមណ្ឌលគរី) ត្រង់គីឡូម៉ែត្រ លេខ៨៨ភូមិជេីងឃ្លេ ឃុំ២ធ្នូ ស្រុកស្នួល ខេត្តក្រចេះ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97867-2015-08-25-15-09-54.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00830.jpg', 'Tuesday, 25 August 2015 22:02', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1835', 'B030101', 'B030000', 'យុវជនម្នាក់  ធ្លាក់ពីលើផ្ទះ ជាន់ទី៣ ស្លាប់ភ្លាមៗ នៅទឹកដី ខណ្ឌ៧មករា', 'null', 'http://www.dap-news.com/2011-06-14-02-39-55/97866-2015-08-25-13-38-46.html', 'null', 'Tuesday, 25 August 2015 20:34', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1836', 'B030101', 'B030000', 'ក្រសួងពាណិជ្ជកម្ម ចេញញត្តិគាំទ្រ យន្តការដោះស្រាយ បញ្ហាព្រំដែន របស់សម្តេចតេជោ', '<b>ភ្នំពេញ៖</b> បន្ទាប់ពីការផ្ទៀតផ្ទាត់ផែនទីរបស់រាជរដ្ឋាភិបាល ប្រើសម្រាប់បោះបង្គោលព្រំដែនជាមួយវៀតណាម និង ផែនទីរបស់ខ្មែរយកពីអង្គការសហប្រជាជាតិរកឃើញថា ខ្សែបន្ទាត់ព្រំដែនដូចគ្នា ក្រសួងពាណិជ្ជកម្ម បានចេញ ញត្តិមកគាំទ្រ ព្រមទាំងបង្ហាញពេញចិត្តពេញថ្លើមយ៉ាងខ្លាំង ទៅលើយន្តការដោះស្រាយបញ្ហាព្រំដែន ដើម្បីនាំមក នូវស្ថេរភាពនយោបាយ និងការឯកភាពជាតិ របស់ប្រមុខរាជរដ្ឋាភិបាលកម្ពុជា សម្តេចតេជោ ហ៊ុន សែន។', 'http://www.dap-news.com/2011-06-14-02-39-55/97865-2015-08-25-13-21-08.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/SR-088.jpg', 'Tuesday, 25 August 2015 20:20', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1837', 'B030101', 'B030000', 'ក្រសួងសេដ្ឋកិច្ច បង្ហាញពីលទ្ធផល នៃកិច្ចប្រជុំ ក្រុមការងារ ផ្នែកនីតិកម្ម ពន្ធដារ និង អភិបាលកិច្ច នៃវេទិការាជ រដ្ឋាភិបាល ផ្នែកឯកជន', '<strong>ភ្នំពេញ៖</strong> កាលពីព្រឹកថ្ងៃទី២៥ ខែសីហា ឆ្នាំ២០១៥ នៅទីស្តីការក្រសួង សេដ្ឋកិច្ច និងហិរញ្ញវត្ថុ មានរៀបចំកិច្ចប្រជុំ «ក្រុមឃ» នៃវេទិការាជរដ្ឋាភិបាល-ផ្នែកឯកជន ធ្វើឡើងដោយ លោកបណ្ឌិត អូន ព័ន្ធមុនីរ័ត្ន រដ្ឋមន្រ្តី ក្រសួងសេដ្ឋកិច្ច និងហិរញ្ញវត្ថុ និងជាប្រធានក្រុម ឃ និងលោក Bretton G. Scaironi សហប្រធានក្រុម ឃ ខាង ផ្នែកឯកជន និង មានការចូលរួមពីសំណាក់ លោក លោក លោក លោកស្រីជាសហសេវិកទាំងផ្នែករាជ រដ្ឋាភិបាល និងទាំងផ្នែកឯកជន ព្រមទាំងមានការចូលរួមពី តំណាងសភាពាណិជ្ជកម្ម កម្ពុជាផងដែរ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97864-2015-08-25-11-52-04.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-030/K-6032.jpg', 'Tuesday, 25 August 2015 18:51', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1838', 'B030101', 'B030000', 'ក្រសួងទេសចរណ៍ ផ្សព្វផ្សាយ លើស្តង់ដារអប្បរមា សម្រាប់ការគ្រប់គ្រង និងអភិវឌ្ឍ «ទេសចរណ៍ ផ្សងព្រេង កំសាន្តក្នុងព្រៃ» នៅរមណីយដ្ឋាន ឋានសួគ៌បូកគោ', '<strong>កំពត៖</strong> ក្រសួងទេសចរណ៍ បានផ្សព្វផ្សាយ និងពិគ្រោះយោបល់ លើស្តង់ដារអប្បរមា សម្រាប់ការគ្រប់គ្រង និងអភិវឌ្ឍ “ទេសចរណ៍ផ្សងព្រេងកំសាន្តក្នុងព្រៃ” (Minimum Standard for Management and Development of Jungle Trekking Adventure Tourism) ជាមួយបុគ្គលិកជំនាញ គ្រប់គ្រងតំបន់ទេសចរណ៍ ផ្សងព្រេងកំសាន្តក្នុងព្រៃ នៅរមណីយដ្ឋានឋានសួគ៌បូកគោ និងមន្ត្រីពាក់ព័ន្ធផ្នែកទេសចរណ៍ក្នុងតំបន់ ក្រោមការដឹកនាំ និងណែនាំ ពីលោក ថោង ខុន រដ្ឋមន្ត្រីក្រសួងទេសចរណ៍ កាលពីរសៀលថ្ងៃទី២៥ ខែសីហា ឆ្នាំ២០១៥ ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97863--l-r-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-014/that-0669.jpg', 'Tuesday, 25 August 2015 18:50', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1839', 'B030101', 'B030000', 'ប្រធាន​មណ្ឌល​ប្រឡង៖ ផ្តល់​ពិន្ទុ​សូន្យ ដល់​សិស្សស្រី​ដែលឈឺ​ពោះ​សម្រាល​កូន មិន​បាន​ប្រឡង វិញ្ញាសារ​ចុងក្រោយ', '<span style="line-height: 2;"><strong>កោះកុង៖</strong> លោក ញ៉ែម សំអឿន ប្រធានមណ្ឌលអនុវិទ្យាល័យជាស៊ីមស្មាច់មានជ័យ ខេត្តកោះកុង បានថ្លែងប្រាប់មជ្ឈមណ្ឌលព័ត៌មានដើមអម្ពិល នៅមុននេះបន្តិចថា សិស្សស្រី (សិស្សបំពេញវិជ្ជា) ដែលឈឺពោះឆ្លងទន្លេសម្រាលកូន ដែលមិនបានចូលប្រឡងវិញ្ញាសារចុងក្រោយ នៃការប្រឡងបាក់ឌុប ត្រូវបានប្រធានមណ្ឌលរូបនោះបញ្ជាក់ថា ការមិនបានប្រឡងមុខវិជ្ជានេះ នឹងបានពិន្ទុស្មើសូន្យ ។</span>', 'http://www.dap-news.com/2011-06-14-02-39-55/97862-2015-08-25-11-42-52.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-030/K-6028.jpg', 'Tuesday, 25 August 2015 18:39', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1840', 'B030101', 'B030000', 'នគរបាល ខេត្តព្រះសីហនុ ចាប់មុខសញ្ញា លួចម៉ូតូម្នាក់ និងទទួលផលចោរកម្ម', '<strong>ព្រះសីហនុ ៖</strong> កម្លាំងការិយាល័យ នគរបាល ព្រហ្មទណ្ឌកម្រិតធ្ងន់ សហការជាមួយ នគរបាល ប៉ុស្តិ៍សង្កាត់៤ នៃអធិការ ក្រុងព្រះសីហនុ នៅម៉ោង១០និង៣០នាទី ព្រឹកថ្ងៃទី២៥ ខែសីហា ឆ្នាំ២០១៥ បានស្រាវជ្រាវ និងចាប់បាន មុខសញ្ញាសង្ស័យ ពីបទទទួលផលចោរកម្ម (លួចម៉ូតូ) ។', 'http://www.dap-news.com/2011-06-14-02-39-55/97861-2015-08-25-11-38-13.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-067/CH-00828.jpg', 'Tuesday, 25 August 2015 18:37', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1841', 'B030101', 'B030000', 'សកម្មជន CNRP អ៊ុក ពេជ្រសំណាង ត្រូវតុលាការចោទប្រកាន់ ៤បទល្មើសបន្ថែមទៀត', '<b>ភ្នំពេញ៖</b> សកម្មជនគណបក្សសង្រ្គោះជាតិ (CNRP) លោក អ៊ុក ពេជ្រសំណាង ត្រូវបានចៅក្រមសាលាដំបូងរាជ ធានីភ្នំពេញ សម្រេចចោទប្រកាន់ចំនួន ៤បទល្មើសបន្ថែមទៀត ពាក់ព័ន្ធអំពើហិង្សានៅភូមិគ្រឹះរបស់សម្តេចតេជោ &nbsp;ហ៊ុន សែន នាយករដ្ឋមន្រ្តី នៃកម្ពុជា កាលពីថ្ងៃទី២០ ខែតុលា ឆ្នាំ២០១៤កន្លងទៅ។ សំណុំរឿងហិង្សានេះ នឹង ត្រូវប្រកាសនៅថ្ងៃទី១០ ខែកញ្ញា ឆ្នាំ២០១៥។', 'http://www.dap-news.com/2011-06-14-02-39-55/97860--cnrp-.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/id-009/ST-262.jpg', 'Tuesday, 25 August 2015 18:32', '1', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1842', 'B030102', 'B030000', 'អាមេរិកប្រកាស ពីវីធានការថ្មី ពីការប្រើប្រាស់ ថាមពលកែច្នៃ ឡើងវិញ', '<strong>វ៉ាស៊ីនតោន៖</strong> ទីភ្នាក់ងារព័ត៌មាន ចិនស៊ិនហួ ចេញផ្សាយនាថ្ងៃទី ២៥ខែសីហា ឆ្នាំ ២០១៥ បានឲ្យដឹងថា រដ្ឋាភិបាល អាមេរិក បានប្រកាសកាលពីថ្ងៃចន្ទ អំពីវិធានការថ្មី ដើម្បីប្រើប្រាស់ ថាមពលកែច្នៃឡើងវិញ និង បង្កើនប្រសិទ្ធភាពថាមពលនៅក្នុងផ្ទះសម្បែង។ ', 'http://www.dap-news.com/2011-06-14-02-40-26/97839-2015-08-25-07-44-38.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-216.jpg', 'Tuesday, 25 August 2015 14:42', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1843', 'B030102', 'B030000', 'សភាសិង្ហបុរី ត្រូវសម្រេច រំលាយមុនការ បោះឆ្នោត', '<strong>សិង្ហបុរី៖</strong> លោកប្រធានាធិបតី សិង្ហបុរី ថូនី តាន់ កេង យ៉ាម ក្រោយពីបាន ទទួលការប្រឹក្សាពី សំណាក់លោក រដ្ឋមន្ត្រី លី ស៊ៀនឡុង រួចមក នៅថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ ២០១៥ នេះ លោកក៏បានសម្រេចចិត្ត ធ្វើការរំលាយ សភានីតិកាលទី ១២ របស់ប្រទេសដីកោះមួយនេះ តែម្តង ចំពោះមុខការបោះឆ្នោត សកល ដែលនឹងប្រព្រឹត្តទៅ នៅរយៈពេលបីខែទៀត។', 'http://www.dap-news.com/2011-06-14-02-40-26/97838-2015-08-25-07-38-39.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-215.jpg', 'Tuesday, 25 August 2015 14:36', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1844', 'B030102', 'B030000', 'ព្យុះទីហ្វុងហ្គូនី កំពុង​ត្បុល​ទៅជប៉ុន ក្រោយ​ឆក់​យកជីវិត​ប្រជាជន​ហ្វីលីពីន ១៥នាក់', '<strong>តូក្យូ៖</strong> សារព័ត៌មាន CNN ចេញនៅថ្ងៃអង្គារ ទី ២៥ ខែសីហា ឆ្នាំ ២០១៥ បានឲ្យដឹងថា ព្យុះទីហ្វុងហ្គូនី បានបោកបក់ទៅកាន់កោះ អូគីណាវ៉ា ភាគខាងត្បូងប្រទេសជប៉ុន ដែលមាន ល្បឿនខ្យល់បក់ និងភ្លៀងធ្លាក់ខ្លាំង បន្ទាប់ពីព្យុះដ៏សាហាវនេះ ឆក់យក ជីវិតមនុស្សអស់ ១៥ នាក់ និង ជាច្រើននាក់ទៀតបាត់ខ្លូននៅភាគខាងជើង ប្រទេសហ្វីលីពីន ដោយនៅក្នុងនោះ រួមមាន ក្រុមអ្នកយករ៉ែចំនួន ១២នាក់ ដែលត្រូវបានកប់ក្នុងល្បាប់ភក់នៅតំបន់ភ្នំ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97813-2015-08-25-03-34-09.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0992.jpg', 'Tuesday, 25 August 2015 10:30', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1845', 'B030102', 'B030000', 'អាថ៌កំបាំង ត្រូវ​បាន​ទម្លាយ អំ​ពី​មហន្ត​រាយ​ផែន​ទឹកកក និង​កំពស់​ទឹក​សមុទ្រ', '<strong>សិង្ហបុរី៖</strong> ក្រុមអ្នកវិទ្យាសាស្រ្តជនជាតិអាមេរិកបានប្រើប្រាស់ប្រព័ន្ធផ្កាយរណប ដើម្បីសិក្សា និង កំណត់រូបរាងឲ្យបានច្បាស់ជាងមុននៃ ការកើនឡើងកំពស់ទឹកសមុទ្រ ដែល បានមក ពីការរលាយផែនទឹកកក និង កាតឹបទឹកកក។', 'http://www.dap-news.com/2011-06-14-02-40-26/97807-2015-08-25-02-59-54.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-0999.jpg', 'Tuesday, 25 August 2015 09:56', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1846', 'B030102', 'B030000', 'លោក បាន គីមូន ស្វាគមន៍កិច្ច ព្រមព្រៀង សម្រេចចេញដោយ កូរ៉េទាំងពីរ', 'យូអិន ៖ លោកអគ្គលេខាធិការ នៃអង្គការសហប្រជាជាតិ បាន គីមូន កាលពីថ្ងៃចន្ទ ទី២៤ ខែសីហា ឆ្នាំ២០១៥ បានប្រកាស ស្វាគមន៍ចំពោះកិច្ចព្រមព្រៀង ដែលប្រទេសកូរ៉េខាងជើង និង កូរ៉េខាងត្បូង បានសម្រេចចេញ បន្ទាប់ពីកិច្ចពិភាក្សារយៈពេល ៤៣ម៉ោង ជាប់ៗគ្នា ស្ថិតក្នុង ន័យបន្ធូរបន្ថយ កុំឲ្យស្ថានការណ៍កាន់តែតាន តឹងជាងនេះទៀត។', 'http://www.dap-news.com/2011-06-14-02-40-26/97804-2015-08-25-02-38-10.html', 'http://teimun.org/wp-content/uploads/2014/07/B6D7152D-09A6-4698-BF1B-C77280823036_mw1024_s_n.jpg', 'Tuesday, 25 August 2015 09:37', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1847', 'B030102', 'B030000', 'មូលហេតុនៃការ ផ្ទុះឃ្លាំងស្តុកទំនិញ គីមីនៅចិន មិនទាន់ត្រូវ បានរកឃើញ', '<strong>ធានជិន ៖</strong> រហូតមកដល់ថ្ងៃអង្គារ ទី២៥ ខែសីហា ឆ្នាំ២០១៥ នេះ អាជ្ញាធរទីក្រុងធានជិន ភាគខាងជើង ប្រទេសចិន នៅមិនទាន់បានរក ឃើញមូលហេតុ ឲ្យបានពិតប្រាកដ ចំពោះ ឧប្បដិហេតុ ផ្ទុះឃ្លាំងស្តុក ទំនិញ កំពង់ផែនោះឡើយ ក៏ព្រោះតែសាក្សីជាមនុស្ស និង វត្ថុតាង ផ្សេងៗ ទំនងជាបានឆេះខ្ទេចខ្ទី នៅក្នុងអគ្គីភ័យ និង ការផ្ទុះដ៏កក្រើក កាលពីដើមខែនោះបាត់ទៅហើយ។', 'http://www.dap-news.com/2011-06-14-02-40-26/97801-2015-08-25-02-09-07.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/AUGUST/id-011/Hong-0967.jpg', 'Tuesday, 25 August 2015 09:08', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1848', 'B030102', 'B030000', 'រដ្ឋាភិបាលថៃ ធានាជាសាធារណៈ អំពីសុវត្ថិភាព ទៅកាន់ជាតិ និង អន្តរជាតិ', '<strong>បាងកក៖</strong> ទីភ្នាក់ងារព័ត៌មាន ចិនស៊ិនហួ ចេញផ្សាយថ្ងៃចន្ទ ទី ២៤ខែសីហា ឆ្នាំ ២០១៥ នេះ បានឲ្យដឹងថា រដ្ឋាភិបាលថៃ បានថ្លែងសារមួយ យ៉ាងមានទំនុកចិត្តដើម្បីធានា សុវត្ថិភាព របស់ប្រជាពលរដ្ឋនិងសហគមន៍ អន្តរជាតិក្នុងប្រទេសថៃ បន្ទាប់ពីការផ្ទុះគ្រាប់បែក រយៈ ពេលមួយសប្តាហ៍កន្លងទៅនេះ បណ្តាលឲ្យមនុស្សស្លាប់ ២០នាក់ និង របួស ១២៥នាក់។', 'http://www.dap-news.com/2011-06-14-02-40-26/97771-2015-08-24-08-32-27.html', 'http://image.dap-news.com:81/dap-news/dap-images/2015/August/ID-068/Lay-193.jpg', 'Monday, 24 August 2015 15:28', '0', '0', '0', 't', '2015-08-24', 'f');
INSERT INTO "tbnews" VALUES ('1849', 'B030302', 'B030000', 'រឿង«អណ្ដូងមរណៈ» របស់​ផលិត​​កម្ម LD មានឈុត​ឆាក​ភ័យ​រន្ធត់​ច្រើន ជាង​រឿង​មុនៗ', '<strong>ភ្នំពេញ៖</strong> ផលិតកម្ម LD Picture បានដាក់បញ្ចាំងខ្សែភាពយន្ត ដែលជាស្នាដៃទី១៣ របស់ខ្លួន មានចំណងជើងថា «អណ្ដូងមរណៈ» នៅរោងភាពយន្ត Platimun ទូលគោក កាលពីល្ងាចថ្ងៃទី២៤ ខែសីហា ឆ្នាំ២០១៥នេះ ក្រោមការរំពឹងខ្ពស់ នូវភាពជោគជ័យ ពីចាងហ្វាងផលិតកម្ម លោក លៀក លីដា ក្នុងការទទួលបានការគាំទ្រ ដូចបណ្ដារឿងមុនៗ ដែលតែងតែមានអ្នក ចូលទស្សនាច្រើនសន្ធឹកសន្ធាប់ ពេលផលិតកម្មរបស់លោក បញ្ចាំងម្ដងៗ។ ពិធីចាក់បញ្ចាំង ជាផ្លូវការ រឿង«អណ្ដូងមរណៈ» នេះ មានការចូលរួមពីសំណាក់ តារាសម្ដែង តារាកំប្លែងល្បីៗ និងតួអង្គសំខាន់ៗក្នុងរឿង ព្រមទាំងទស្សនិកជន ដែលទន្ទឹងរង់ចាំទស្សនា ជាច្រើននាក់។', 'http://www.dap-news.com/morokatmagazine/97824-lr--ld---.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-141/MrMSP-1013A.jpg', 'Tuesday, 25 August 2015 11:33', '0', '0', '0', 't', '2015-08-25', 'f');
INSERT INTO "tbnews" VALUES ('1850', 'B030302', 'B030000', 'U-19 កម្ពុជា ចាប់ផ្តើមចេញដំណើរ​ ដោយប៉ះម្ចាស់ផ្ទះឡាវ នៅម៉ោង ៧យប់នេះ', '<strong>ភ្នំពេញ៖</strong> ក្រុមបាល់ទាត់ឈុតតូចជម្រើសជាតិ យុវជនអាយុក្រោម ១៩ឆ្នាំកម្ពុជា នឹងត្រូវធ្វើការប្រកួតបើកឆាកប្រចាំពូល A សន្សំពិន្ទុ ដោយត្រូវជួបម្ចាស់ផ្ទះ U-19 របស់ឡាវ នាម៉ោង ៧៖០០នាទីយប់នេះ (ម៉ោងនៅកម្ពុជា) នៃព្រឹត្តិការណ៍ ពានរង្វាន់យុវជនប្រចាំតំបន់អាស៊ាន AFF U-19 Youth Championship លើកទី១២ ឆ្នាំ២០១៥ ។', 'http://www.dap-news.com/morokatmagazine/97627-u-19-----.html', 'http://image.lookingtoday.com:81/lookingtoday/looking-images/news/2015/August/ID-140/hour-01772.jpg', 'Saturday, 22 August 2015 10:52', '0', '0', '0', 't', '2015-08-22', 'f');
INSERT INTO "tbnews" VALUES ('1851', 'B010105', 'B010000', 'តិច​​ច្រឡំ​​ដោតប៊ិច​​របស់​​ Galaxy Note 5 ​បញ្ច្រាស់​​ ដក់​អត់​ចេញ​ទេ​ណា​​', 'មិន​ទាន់​បាន​ប៉ុន្មាន​ផង​នៃ​ការ​ប្រកាស​កំពូល​ Smartphone របស់​ក្រុមហ៊ុន​ Samsung គឺ​ Galaxy Note 5 ​​​ឥឡូវ​នេះ​ស្រាប់​តែ​មាន​ព័ត៌មាន​ថ្មី​មួយ​បាន​បង្ហាញ​ថា​កំពូល...', 'http://news.sabay.com.kh/article/462234', 'http://cdn.sabay.com/cdn/news.sabay.com.kh/wp-content/uploads/2015/08/Feature-617x324-video102-285x170.jpg?ebb82d', '26 August, 2015', '0', '0', '0', 't', '2015-08-26', 'f');
