INSERT INTO "tbnews" VALUES ('1898', 'B020503', 'B030000', 'ប្រយោជន៍៤យ៉ាង​​​របស់​តន្ត្រីល្អ​សម្រាប់​​សុខភាព​ ដែល​អ្នក​​​មិន​គួរ​មើល​​រម្លង!', '​មិន​ថា​​​​​មនុស្ស​ធំ​ទេ ​​សូម្បី​តែ​ទារក​​​ក៏​ត្រូវការ​ស្តាប់​​​តន្ត្រី​​ដែរ។ នោះ​គឺ​​​ជា​សម្លេង​បំពេ​របស់​ម្តាយ មុន​ពេល​ដេក។ ដូច្នេះ​ តន្ត្រី​ពិត​ជា​មាន​សារៈសំខាន់​​ សម្រាប់​មនុស្ស​គ្រប់រូប។ អ្វី​ដែល​ពិសេស​នោះ គឺ​អ្នកស្រាវជ្រាវ​មួយ​ចំនួន​បង្ហាញ​ថា តន្ត្រី​ពិត​ជា អាច​ផ្តល់​ផលប្រយោជន៍​យ៉ាង​ធំធេង សម្រាប់​សុខភាព​របស់​អ្នក។', '', 'img/listeningtomusic.jpg', '8/26/15 11:08 AM', '0', '0', '0', 'f', '2015-08-26', 't');
INSERT INTO "tbnews" VALUES ('1899', 'B020105', 'u0000004', 'ឡាន​ហោះ​អាល្លឺម៉ង់ចេញ​លក់​​​តម្លៃ​ជាង ៣៩ម៉ឺន​ដុល្លារ​ក្រោយ​ធ្វើតេស្ត​ច្រើន​ឆ្នាំ', 'រយៈពេល ៥០ឆ្នាំ​ហើយ​ដែល​មនុស្ស​កើត​គំនិត​ចង់​បាន​រថយន្ត​មាន​សមត្ថភាព​មិន​ត្រឹមតែ​បើកបរ​លើ​ផ្លូវគោក តែ​ថែមទាំង​ចង់​ឱ្យ​អាច​ហោះ​បាន​ពេល​ស្ទះ​ផ្លូវ​ម្ដងៗ។ យ៉ាងណា ពី​ពេល​នេះ​ទៅ សុបិន​គឺ​បាន​ក្លាយ​ជា​ការ​ពិត​ហើយ ដោយ​ក្រុមហ៊ុន​អាល្លឺម៉ង់ C.Klok ចាប់​ដាក់​លក់​រថយន្ត​អាច​ហោះ​បាន​ឈ្មោះ Pal-V One លើ​ទីផ្សារ​ក្នុង​តម្លៃ ៣៩៥.៩៩៩ដុល្លារ​អាមេរិក ក្រោយ​ពី​បាន​ធ្វើតេស្ត​ជា​ច្រើន​ឆ្នាំ​មក​នេះ។ ', '', 'img/1405332379MainImage.jpg', '8/26/15 11:32 AM', '3', '0', '0', 't', '2015-08-26', 'f');
INSERT INTO "tbnewsdetail" VALUES ('85', '1653', '<p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><img src="data:image/jpeg;base64," style="width: 900px;"><br></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;">នាឡិកាដៃ​ឆ្លាតវៃ (SmartWatch) ចេញ​ព័ត៌មាន​ជា​ច្រើន​រយៈពេល​ច្រើន​ឆ្នាំ​មក​នេះ តែ​រូបរាង និង​លក្ខណៈ​សម្បត្តិ​ពិតប្រាកដ​​ពុំ​ត្រូវ Apple ប្រកាស​ផ្លូវការ​នៅ​ឡើយ​​ទេ។ ថ្ងៃ​នេះ​អាច​ជា​ការ​​លាត​ត្រដាង​គួរ​ឱ្យ​ទុក​ចិត្ត​បាន​​នៅ​​លើ​បណ្ដាញ​ព័ត៌មាន Business Insider រៀបរាប់ ១២​ចំណុច​ទាក់ទង​នាឡិកាដៃ​របស់ Apple ដូច​ខាង​ក្រោម៖</p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="padding: 0px; margin: 0px; list-style: none; border: 0px; font-style: inherit; font-weight: 700; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">១) តើ​នាឡិកា​នឹង​មាន​ឈ្មោះ​អ្វី? សាធារណជន​សុទ្ធតែ​នាំគ្នា​ហៅ​ថា “iWatch”។</span>&nbsp;ក្នុង​នោះ Apple ក៏​បាន​ចុះ​បញ្ជី​កម្មសិទ្ធិ​ពាណិជ្ជ​សញ្ញា iWatch នៅ​លើ​បណ្ដា​ប្រទេស​ចំនួន​៥ រួច​មក​ហើយ។</p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="padding: 0px; margin: 0px; list-style: none; border: 0px; font-style: inherit; font-weight: 700; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">២) គេ​រំពឹង​ថា នាឡិកា​ដំបូង​របស់ Apple នឹង​ដំណើរការ​ដោយ​ប្រព័ន្ធ​ប្រតិបត្តិការ iOS 8។</span></p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="padding: 0px; margin: 0px; list-style: none; border: 0px; font-style: inherit; font-weight: 700; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">៣) iWatch នឹង​មាន​មុខ​ជ្រុង អេក្រង់ ២,៥អ៊ីញ៖</span>&nbsp;មកដល់​ពេល​នេះ ពុំ​មាន​ចម្លើយ​ច្បាស់លាស់​អំពី​រូបរាង iWatvh របស់ Apple នៅឡើយ។ ទីភ្នាក់ងារ​ព័ត៌មាន Reuters បាន​រាយការណ៍​ថា៖ «ក្រុមហ៊ុន Apple នឹង​បង្ហាញ​នាឡិកា​ឆ្លាតវៃ ដែល​មាន​អេក្រង់ ២,៥អ៊ីញ ហើយ​មាន​មុខ​ជ្រុង​រាង​ចតុកោណ»។ ប៉ុន្តែ​លោក Brain Blair នៅ​ក្រុមហ៊ុន Rosenblatt Securities ប្រចាំ​តំបន់​អាស៊ី​បាន​និយាយ​ថា iWatch នឹង​មាន​មុខ​មូល​ទៅ​វិញ។</p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="padding: 0px; margin: 0px; list-style: none; border: 0px; font-style: inherit; font-weight: 700; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">៤) នាឡិកា​ឆ្លាតវៃ​នា​ពេល​បច្ចុប្បន្ន ត្រូវ​បាន​ផលិត​តែ​មួយ​ម៉ូដែល ហើយ​ទំហំ​ដូចៗ​គ្នា។</span>&nbsp;ចំណែក iWatch វិញ​ត្រូវ​គេ​រាយការណ៍​ថា នឹង​ចេញ​ពីរ​ម៉ូដែល និង​ពីរ​ទំហំ​សម្រាប់ បុរស​នារី។</p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="padding: 0px; margin: 0px; list-style: none; border: 0px; font-style: inherit; font-weight: 700; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">៥) iWatch នឹង​ទំនើប​សម្បូរ​បែប ភ្ជាប់​មក​ជាមួយ​មុខងារ​អត្ថប្រយោជន៍​ដល់​សុខភាព អាច​ប្រើ​សម្រាប់​រាប់​ជំហាន​ជើង វាស់​កម្រិត​ជាតិទឹក ញើស និង​ចង្វាក់​បេះដូង។</span>&nbsp;នាឡិកា​ក៏​នឹង​អាច​ភ្ជាប់​ជាមួយ iPhone ដើម្បី​ងាយស្រួល​ក្រើនរំលឹក ព្រមទាំង​ការ​ធ្វើ​បច្ចុប្បន្នភាព​ផ្សេងៗ​មក​លើ​អេក្រង់​នាឡិកា​បាន។</p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="padding: 0px; margin: 0px; list-style: none; border: 0px; font-style: inherit; font-weight: 700; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">៦) នាឡិកាឆ្លាតវៃ​នឹង​ភ្ជាប់​ជាមួយ​ឧបករណ៍​ចាប់​សញ្ញា សម្រាប់​តាមដាន​ចលនា​របស់​អ្នក។</span>&nbsp;ព្រឹត្តិបត្រ​សារព័ត៌មាន WSJ ឱ្យ​ដឹង​ថា iWatch «នឹង​បំពាក់ sensors ចំនួន១០ ក្នុង​នោះ​មាន​សម្រាប់​ត្រួត​ពិនិត្យ​សុខភាព និង​កាយសម្បទា»។</p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="padding: 0px; margin: 0px; list-style: none; border: 0px; font-style: inherit; font-weight: 700; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">៧) Apple ត្រូវ​គេ​រាយការណ៍​ថា ឥឡូវ​នេះ​មាន​អត្តពលិក​ដូច​ជា​លោក Kobe Bryant ជាដើម​សម្រាប់​ធ្វើតេស្ត​សាកល្បង​ទៅ​លើ​មុខងារ​សម្បទា។</span></p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="padding: 0px; margin: 0px; list-style: none; border: 0px; font-style: inherit; font-weight: 700; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">៨) iWatch នឹង​មាន​ការ​ផលិត​ដោយ​ប្រិតប្រៀង​យកចិត្តទុកដាក់</span>&nbsp;ដើម្បី​វាយលុក​ទីផ្សារ​ឱ្យ​បាន​ក្នុង​ពេល​ខាង​មុខ ព្រោះ​ក្រុមហ៊ុន​ទើបតែ​បាន​ជួល​បុគ្គលិក​ផ្នែក​ទីផ្សារ​ដែល​ជា​អតីត​ប្រធាន​ផ្នែក​លក់​របស់