INSERT INTO "tbnewsdetail" VALUES ('86', '1654', '<p><img src="data:image/jpeg;base64," style="width: 480px;"><br></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;">ក្រុម​ហ៊ុន​​ YouTube ​​កំពុង​តាម​ចែចូវ​​​​​ទិញ​ក្រុម​ហ៊ុន​ Twitch ​ដែល​ផលិត​ហ្គេម​វីដេអូ​ដ៏​ល្បី​ឈ្មោះ​ក្នុង​តម្លៃ​ជាង​ ​​១​ពាន់​លាន​ដុល្លារ​។<br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;"><br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">​<br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;"><br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">ក្រុម​ហ៊ុន​ Twitch ​​​ជា​ក្រុមហ៊ុន​ផ្ដល់​សេវាកម្ម​​​​​កម្សាន្ត​វីដេអូ​ហ្គេម​​ដែល​អនុញ្ញាត​ឲ្យ​លេង​ហ្គេម​​​តាម​ Xbox ឬក៏ Playstation 4 ​អាច​​ចូល​ទៅ​លេង​កម្មវិធី​ហ្គេម​រាប់​ពាន់​ផ្សេងៗ​គ្នា​​ដោយ​ផ្ទាល់​នៅ​លើ​បណ្ដាញ​គេហទំព័រ​របស់​ខ្លួន​។ ក្នុង​ពេល​ថ្មីៗ​នេះ​ មនុស្ស​ជាង​ ​១លាន​នាក់​បាន​នាំ​គ្នា​បង្កើត​​ជា​ក្រុម​​​ដើម្បី​លេង​ហ្គេម Pokemon។ ​<br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;"><br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">​<br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;"><br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">ទោះ​ជា​ YouTube ​​ជា​ក្រុមហ៊ុន​​ផ្ដល់​​ប្រព័ន្ធ​​​ចែក​ចាយ​​​​វីដេអូ​តាម​អ៊ីនធឺណិត​ដ៏​ធំ​បំផុត​ក៏​ដោយ ​​ក៏​​បច្ចេកវិទ្យា​របស់​​ YouTube ​​នៅ​មិន​ទាន់​ពេញ​និយម​ដូច​ក្រុម​ហ៊ុន Twitch នៅ​ឡើយ​ទេ​ (video streaming) ​​ ដូច្នោះ​ហើយ​បាន​ជា​ YouTube ​​​តាម​អន្ទង​ចិត្ត​​ទិញ​ក្រុមហ៊ុន​ខាង​លើ​នេះ។​ ​នេះ​បើ​យោង​តាម​ការ​អះអាង​របស់​ក្រុម​អ្នក​វិភាគ។ ​​<br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;"><br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">​​​<br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;"><br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">ទី​ភ្នាក់​ងារ​ព័ត៌មាន​ Variety បាន​រាយ​ការណ៍​ថា YouTube កំពុង​តែ​រៀប​ចំ​ឲ្យ​ក្រុម​​និយតករ​អាមេរិក​ពិនិត្យ​​​​ការ​ទិញ​ក្រុមហ៊ុន​ថ្មី​នេះ​ ​​ដើម្បី​បញ្ជាក់​ថា ខ្លួន​មិន​បង្កើត​បញ្ហា​ប្រឆាំង​ការ​ប្រកួត​ប្រជែង​នៅ​ក្នុង​ទី​ផ្សារ​វីដេអូ​តាម​អ៊ីនធឺណិត។ ​មិន​ទាន់​មាន​​​ចេញ​របាយការណ៍​ជា​ផ្លូវ​ការ​នៅ​ឡើយ​ទេ​ ​​​តែ​​គេ​រំពឹង​ថា​ក្រុមហ៊ុន​ YouTube ​នឹង​ប្រកាស​នៅ​ពេល​ឆាប់​ៗ​នេះ។ ​</p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;">​​បើ​សិន​ក្រុមហ៊ុន​ YouTubeចែចូវ​បាន​សម្រេច​មែន​ វា​នឹង​ក្លាយ​ជា​ការ​ទិញ​​ក្នុង​ទំហំ​ទឹក​ប្រាក់​ដ៏​ច្រើន​​បំផុត​នៅ​ក្នុង​ប្រវត្តិ​សាស្ត្រ​របស់​ក្រុម​ហ៊ុន​នេះ​ ហើយ​នឹង​ធ្វើ​ឲ្យ​​​​ YouTube ​​និង​ក្រុម​ហ៊ុន Google ប្រើ​គេហទំព័រ​ដែល​កំពុង​មាន​សក្ដានុពល​​តាម​អ៊ីនធឺណិត​​​ទាក់ទាញ​អ្នក​ប្រើ​ប្រាស់​បាន​កាន់​តែ​ច្រើន​​ទៀត៕</p><div><br></div><p><br></p>');
INSERT INTO "tbnewsdetail" VALUES ('87', '1655', '<p><img src="data:image/jpeg;base64," style="width: 683px; height: 425.926388888889px;"><br></p><p><span style="font-family: Hanuman, Arial, serif; font-size: 18px; line-height: normal;">ក្រុមហ៊ុន​ធំៗ​បំផុត​នៅ​អាមេរិក​កំពុង​តែ​ត្រូវការ​ជ្រើស​រើស​អ្នក​ជំនាញ​ខាង​សន្តិសុខ​​អ៊ីនធឺណិត​​ដែល​គេ​ហៅ​ថា​ cybersecurity ​ឱ្យ​​មក​​កាន់​​​​​មុខ​តំណែង​ខ្ពស់ៗ ​​សម្រាប់​​ការពារ​ប្រព័ន្ធ​សុវត្ថិភាព​​ក្រុមហ៊ុន​របស់​ខ្លួន​ខណៈ​ដែល​សកម្មភាព​របស់​ក្រុម​ចោរ​លួច​ព័ត៌មាន​តាម​អ៊ីនធឺណិត​កាន់​តែ​ផុលផុស​ឡើងៗ​។</span><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="font-family: Hanuman, Arial, serif; font-size: 18px; line-height: normal;">​</span><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="font-family: Hanuman, Arial, serif; font-size: 18px; line-height: normal;">នេះ​ជា​សញ្ញា​បង្ហាញ​ថា​ ​ក្រុមហ៊ុន​​​សាជីវកម្ម​​នៅ​អាមេរិក ​(corporate America ) ខ្វល់ខ្វាយ​កាន់​តែ​ខ្លាំង​ឡើងៗ​ចំពោះ​គ្រោះថ្នាក់​តាម​ប្រព័ន្ធ​អ៊ីនធឺណិត។</span><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="font-family: Hanuman, Arial, serif; font-size: 18px; line-height: normal;">​</span><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="font-family: Hanuman, Arial, serif; font-size: 18px; line-height: normal;">ក្រុមហ៊ុន​ដែល​កំពុង​ជ្រើស​រើស​បុគ្គលិក​ផ្នែក មន្ត្រី​ជាន់ខ្ពស់​ខាង​សន្តិសុខ​បច្ចេកវិទ្យា ​រួមមាន​ JPMorgan Chase &amp; Co, PepsiCo Inc, Cardinal Health Inc, Deere &amp; Co and The United Services Automobile Association (USAA) ​​ក្នុង​ចំណោម​បណ្ដាញ​ក្រុមហ៊ុន​យក្ស​នៅ​អាមេរិក​ធំ​ៗ ទាំង​ ៥០០។</span><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="font-family: Hanuman, Arial, serif; font-size: 18px; line-height: normal;">​</span><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="font-family: Hanuman, Arial, serif; font-size: 18px; line-height: normal;">​អ្នក​ដែល​មាន​ចំណេះ​ជំនាញ​ពិតប្រាកដ​ខាង​វិស័យ​សុវត្ថិភាព​ ​អាច​ទទួល​បាន​បៀវត្ស​​ខ្ពស់​ បើ​ធៀប​នឹង​ជំនាញ​ផ្សេង​ៗ​ទៀត។</span><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="font-family: Hanuman, Arial, serif; font-size: 18px; line-height: normal;">​​</span><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><span style="font-family: Hanuman, Arial, serif; font-size: 18px; line-height: normal;">កន្លង​ទៅ ក្រុម​ហ៊ុន​ធំៗ​បាន​ជួល​មន្ត្រី​ជំនាញ​ព័ត៌មាន​វិទ្យា​ជាន់ខ្ពស់ ដោយ​ផ្តល់​កម្រៃ​ចាប់ពី​​ ​៥០​​ម៉ឺន ទៅ​ ៧០​​ម៉ឺន​ដុល្លារ​ក្នុង​មួយ​ឆ្នាំ នេះ​បើ​យោង​តាម​លោក​ Matt Comyns ដែល​ជា​សហប្រធាន​នៃ​ក្រុម​ប្រតិបត្តិការ​ផ្នែក​សន្តិសុខ​​អ៊ីនធឺណិត​សាកល​របស់​ក្រុមហ៊ុន Russell Reynolds Associates៕​</span><br></p><div><span style="font-family: Hanuman, Arial, serif; font-size: 18px; line-height: normal;"><br></span></div>');
INSERT INTO "tbnewsdetail" VALUES ('88', '1656', '<p><img src="data:image/jpeg;base64," style="width: 646px; height: 323px;"><br></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;">ដោយ​ឆ្លង​កាត់​​ប្រព័ន្ធ​បច្ចេកវិទ្យា​តូច​មួយ​ និង​ការ​បោះ​ពុម្ព​តាម​ប្រព័ន្ធ ​​3Dនោះ​ ទើប​គេ​ហៅ​ប្រព័ន្ធ​នេះ​ជា​ម៉ាស៊ីន​ព្រីន​ 3D ដ៏​តូច​បំផុត​របស់​ក្រុម​ហ៊ុន IBM ដែល​ឥឡូវនេះ​មាន​លក្ខណៈ​ពិសេស​សម្រាប់​បោះ​ពុម្ព​ក្រប​ទស្សនាវដ្ដី​តូច​បំផុត​នៅ​ក្នុង​ពិភព​លោក​។</p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;">​ក្រុម​ហ៊ុន​ IBM និង​ទស្សនាវដ្ដី​ National Geographic Kids បាន​​បង្ហាញ​ក្រប​​ទស្សនាវដ្ដី​នៅ​​​ក្នុង​​កម្មវិធី​​​វិទ្យា​សាស្ត្រ​ ​និង​ស្ថាបត្យកម្ម​អាមេរិក​នៅ​ក្រុង​វ៉ាស៊ីនតោន​ឌី​ស៊ី​ជា​ទី​ដែល​អង្គ​ការ​កំណត់​ត្រា​ពិភពលោក​​ the Guinness Book of World Records បាន​អះ​អាង​ជា​ផ្លូវ​ការ​ថា​ ​ជា​ក្រប​ទស្សនាវដ្ដី​តូច​បំផុត​ក្នុង​ពិភព​លោក​។<br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;"><br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">​<br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;"><br style="padding: 0px; margin: 0px; list-style: none; color: rgb(255, 255, 255); border: 0px; font-style: inherit; font-weight: inherit; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased;">​​​ក្រប​ទស្សនាវដ្ដី​ NatGeo Kids នេះ​គឺ​ពិត​ជា​មិន​អាច​មើល​ឃើញ​ដោយ​ភ្នែក​ទទេ​​បាន​ឡើយ​។ លោក​វេជ្ជ​បណ្ឌិត​ Colin Rawlings ជា​អ្នក​រូប​វិទ្យា​នៅ​កម្ម​វិធី​ស្រាវ​ជ្រាវ​ IBM Research​ ​បាន​ថ្លែង​ថា​ សូម្បី​តែ​មើល​តាម​កែវយឹត​ ​គឺ​គ្រាន់​តែ​អាច​មើល​ឃើញ​ព្រិលៗ​ប៉ុណ្ណោះ​។ ដើម្បី​មើល​ឲ្យ​ឃើញ​ច្បាស់​ ​អ្នក​អាន​ទាល់​តែ​​​ប្រើ​​កែវយឹត​អេឡេច​ត្រុង។</p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;">​​ក្រប​​​តូច​​បំផុត​របស់​ទស្សនាវដ្ដី​ខាង​លើ​ ​​​គឺ​ជា​ការ​បង្ហាញ​គួរ​ឲ្យ​អស់​សំណើច​មួយ​ពី​សមត្ថ​ភាព​របស់​ម៉ាស៊ីន​ព្រីន​ Micro 3D។​ លោក Rawlings បាន​និយាយ​ថា ម៉ាស៊ីន​ដែល​ឥឡូវ​កំពុង​ត្រូវ​បាន​ប្រើ​សម្រាប់​ពាណិជ្ជ​កម្ម​នៅ​សាកល​វិទ្យាល័យ​ McGill ប្រទេស​កាណាដា​នោះ​ជា​ឧបករណ៍​ល្អ​ឥត​ខ្ចោះ​មួយ​សម្រាប់​ការ​ធ្វើ​ជា​គំរូ​​ដ៏​រហ័ស​មួយ។</p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;">លោក​បាន​ថ្លែង​ថា៖«​ក្រុម​អ្នក​វិទ្យា​សាស្ត្រ​មាន​កំហុស​ជា​ច្រើន​​ដែល​មិន​អាច​ដាក់​ឲ្យ​ធ្វើ​ជា​គម្រូ​អ្វី​មួយ​បាន​ភ្លាមៗ​ ហើយ​ពិត​ជា​មាន​សារៈ​សំខាន់​ ហើយ​នោះ​ជា​អ្វី​ដែល​រឿង​នេះ​អនុញ្ញាត​ឲ្យ​អ្នក​ធ្វើ​»។ ម៉ាស៊ីន​ព្រីន​នេះ​​ផ្ដល់​ភាព​ជាក់​លាក់​សម្រាប់​ការ​ស្រាវ​ជ្រាវ​ ប្រតិ​បត្តិ​ការ​ពាណិជ្ជ​កម្ម ​និង​ឱកាស​ផ្សាយ​ពាណិជ្ជ​កម្ម​ដូច​ជា​ក្រប​ទស្សនា​វដ្ដី​តូច​នេះ​អ៊ីចឹង​។​</p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;">ទោះ​ជា​យ៉ាង​ណា ​ម៉ាស៊ីន​ព្រី​នេះ​នឹង​ទាន់​ទៅ​ដល់​ផ្ទះ​អ្នក​ក្នុង​ពេល​ឆាប់ៗ​នេះ​ទេ​។ ខណៈ​ម៉ាស៊ីន​ព្រី​នេះ​តាំង​នៅ​លើ​តុ​ វា​មាន​លក្ខណៈ​ប៉ុន​ទូ​ក្លាសេ​និង​មាន​តម្លៃ​កន្លះ​លាន​ដុល្លារ៕</p>');
