INSERT INTO "tbnewsdetail" VALUES ('96', '1891', '<p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src="data:image/jpeg;base64," style="width: 794px; height: 397px;"><br></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;">បច្ចុប្បន្ន​ស្ត្រេស ​គឺ​ជា​បញ្ហា​ទូទៅ​ដែល​​កំពុង​កើត​មាន​ស្ទើរ​គ្រប់​គ្នា​ តែ​អ្នក​ខ្លះ​អាច​​មាន​កម្រិត​តិច​​ដែល​​អាច​បន្ធូ​បាន​តាម​រយៈ​ការ​សម្រាក​ លំហែកាយ ហាត់ប្រាណ​​ លេង​កីឡា​តាម​ចំណូល​ចិត្ត​ខ្លួន​ៗរៀង​ខ្លួន ​ខណៈ​ខ្លះ​ទៀត ​អាច​​​ធ្ងន់​​ធ្ងរ​ដោយ​​​រង​សម្ពាធ​ផ្សេងៗ​ពី​មជ្ឈដ្ឋាន​ជុំវិញ​ខ្លួន ដូច​ជា​រឿង​គ្រួសារ​ ឬ​​ការងារ​ជា​ដើម។</p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;">នេះ​នឹង​បណ្ដាល​ជា​ជំងឺ​ផ្លូវចិត្ត ដែល​នាំ​ឱ្យ​ប៉ះពាល់​សុខភាព​ទៅ​អនាគត ប្រសិន​អ្នក​មិន​បាន​ពិគ្រោះ ឬ​ព្យាបាល​ជា​មួយ​ពេទ្យ​ជំនាញ​បាន​ត្រឹមត្រូវ។ យ៉ាងណា ឧបករណ៍​ថ្មី​ដែល​មាន​ឈ្មោះ Spire នេះ​នឹង​ចូល​ខ្លួន​មក​ធ្វើ​ជា​ជំនាញការ សម្រាប់​ក្រើនរំលឹក​អ្នក​ដោយ​ការ​វាស់​ចង្វាក់​ដង្ហើម ដឹង​ពី​កម្រិត​នៃ​ភាព​តានតឹង​របស់​អ្នក​បាន។</p><p><br style="padding: 0px; margin: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; color: rgb(255, 255, 255); border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"></p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;">Spire គឺ​ជា​អ្នក​តាមដាន​ដ៏​មាន​សមត្ថភាព ក្នុង​ការ​វាស់​ចលនា​របស់​រាងកាយ​អ្នក ទីតាំង និង​លំនាំ​ដក​ដង្ហើម។ បន្ទាប់​ពី​នោះ ឧបករណ៍​នេះ​នឹង​បង្ហាញ​សារ​លើ​ស្មាតហ្វូន​ដែល​ភ្ជាប់​ជាមួយ​វា ដាស់តឿន​ប្រាប់​អំពី​ផ្លូវចិត្ត​របស់​អ្នក តើ​មាន​ភាព​តានតឹង​កម្រិត​ណា? វា​ថែមទាំង​ចាប់​បាន ​​និង​ដឹង​នូវ​ចំនួន​ជំហាន​ជើង ដើរ រត់​របស់​អ្នក ដឹង​អំពី​ភាព​ញឹកញាប់​ដែល​អ្នក​អង្គុយ​ចុះ ឈរ និង​គេង។ សូម​ទស្សនា​វីដេអូ​ខាង​ក្រោម៖</p><p style="padding: 0px; margin-bottom: 0px; font-family: Hanuman, Arial, serif; font-size: 18px; list-style: none; border: 0px; outline: 0px; vertical-align: baseline; -webkit-font-smoothing: antialiased; line-height: normal;"><iframe src="//www.youtube.com/embed/DY4OW8O7mNc" width="640" height="360" frameborder="0"></iframe><br></p>');
