INSERT INTO "tbnews" VALUES ('41', 'cl000002', 'u0000003', 'ម៉ោងជិត៥ល្ងាចនេះ ​អគ្គិភ័យបំផ្លាញ​ផ្ទះស្នងការ​​នគរបាលខេត្តកំពង់ធំ', 'ខេត្តកំពង់ធំ ៖​ ផ្ទះមួយកន្លែងដែលគេ​ស្គាល់ថាជាកម្មសិទ្ឋិ​របស់លោកឧត្តមសេនីយ៍ត្រី ​ជូ សំអាន​ ស្នងការនគរបាល​ខេត្តកំពង់ធំ ​និងជួលឲ្យទៅក្រុមជំនុំ​', 'http://kohsantepheapdaily.com.kh/article/116181.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('42', 'cl000002', 'u0000003', '​​រថយន្ត​ស្ទូច​បុកក្លោ​ង​ទ្វា​រការិយាល័យ​កណ្តាល​សណ្តាប់ធ្នាប់របស់​ស្នងការ​នគរបាល​ខេត្តកំពង់​ឆ្នាំងបាក់​រាប​ដល់​ដី', 'ខេត្តកំពង់ឆ្នាំង ៖ ក្លោ​ង​ទ្វារ​ការិយាល័យ​កណ្តាល​សណ្តាប់ធ្នាប់របស់​ស្នងការដ្ឋាន​នគរបាល​ខេត្តកំពង់ឆ្នាំងធ្វើ​ពី​បេតុង ដែល​មាន​វ័យ​ចំណាស់​បន្តិចត្រូវ​រថយន្ត​ស្ទូច​មួយ​គ្រឿង​', 'http://kohsantepheapdaily.com.kh/article/116179.html', 'http://i.kspg.co/2015/3/c8243b00-ea80-48c7-a854-7273f2745114.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('43', 'cl000002', 'u0000003', 'សហភាព​ការងារ​កម្ពុជា​ស្នើ​សុំ​រាជរដ្ឋាភិបាល​ដោះស្រាយ​៧​ចំណុច​នៅ​ថ្ងៃ​ប្រារព្ធ​ទិវា​នារី​អន្តរជាតិ​៨​មីនា​នៅ​ទីលាន​ប្រជាធិបតេយ្យ', 'រាជធានី​ភ្នំពេញ ៖ សមាជិក សមាជិកា សហភាព​ការងារ​កម្ពុជា ប្រមាណ​ជា​២០០​នាក់​ដឹកនាំ​ដោយ​លោក​អាត់ ធន់ ប្រធាន​សហភាព​ការងារ​កម្ពុជា បាន​រៀបចំ​ទិវា​សិទ្ធិ​នារី​អន្តរជាតិ​ខួប​លើក​ទី​១០៤ ថ្ងៃ​ទី​០៨ មីនា', 'http://kohsantepheapdaily.com.kh/article/116178.html', 'http://i.kspg.co/2015/3/e2bad0ba-849a-43ba-8ebe-0728404fd493.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('44', 'cl000002', 'u0000003', 'ក្រុម​សកម្មជន​ដីធ្លី​​មក​ពី​ជាង​៤០​សហគមន៍​បាន​ធ្វើ​ក្ដារ​មឈូស​ដង្ហែ​​​ទៅ​ពន្ធ​នាគារ​ព្រៃ.សទាមទារ​ឲ្យ​អាជ្ញាធរ​ដោះ​លែង​ស្ត្រី​១១​នាក់​​កំពុង​ជាប់​ឃុំ', '​ធានី​​ភ្នំពេញ​ ៖ ក្រុម​សកម្មជន​ដីធ្លីដែល​មក​ពី​ជាង​៤០​សហគមន៍ រួម​និ​ងង្រុ​ម​អង្គការ​សង្គម​ស៊ី​វិល​នានា សរុប​ប្រមាណ​ជិត​១​ពាន់​នាក់ បាន​រៀបចំ​ធ្វើ​ក្ដារ​មឈូស ព្រមទាំង​', 'http://kohsantepheapdaily.com.kh/article/116176.html', 'http://i.kspg.co/2015/3/84a559f7-a002-45d6-ba1a-d211204d000e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('45', 'cl000002', 'u0000003', 'ខណ្ឌ​ជ្រោយចង្វារ​នាំ​អំណោយ​សម្តេច​កិត្តិ​ព្រឹទ្ធ​បណ្ឌិតប៊ុន ​រ៉ា​នី​ហ៊ុន សែន​ ចែក​ជូន​​​ប្រជាពលរដ្ឋ​ក្រីក្រ​៧០​គ្រួសារ​នៅ​កោះដាច់', 'រាជធានី​ភ្នំពេញ​៖​ អនុ​សាខា​កាកបាទក្រហមកម្ពុជា​ខណ្ឌ​ជ្រោយចង្វារ កាលពី​ព្រឹក​ថ្ងៃ​ទី​៨ ​មិ​នា ​​២០១៥​ បាន​នាំ​អំណោយ​ជា​គ្រឿង​ឧបភោគ បរិភោគ​ និង​ថវិកា​របស់​សម្តេច​កិត្តិ​', 'http://kohsantepheapdaily.com.kh/article/116173.html', 'http://i.kspg.co/2015/3/dcd358dc-09cd-4187-9861-7fea7d0928d6.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('46', 'cl000002', 'u0000003', 'សម្តេច​តេជោ ហ៊ុន សែន ៖ អំពើ​ហិង្សា​ ការ​រំលោភ​សេព​សន្ថវៈ នៅ​តែ​ជា​កង្វល់​ចំពោះ​មុខ', 'រាជ​ធានី​ភ្នំពេញ ៖ សម្តេច​តេជោ​នាយក​រដ្ឋ​មន្ត្រី​មាន​ប្រសាសន៍​ថា បញ្ហា​អំពើ​ហិង្សា​ក្នុង​គ្រួសារ​ ការ​រំលោភ​សេព​សន្ថវៈ អំពើ​ជួញ​ដូរ និង​អំពើ​ធ្វើ​អាជីវ​កម្ម​ផ្លូវ​ភេទ​ពី​ស្ត្រី និង​កុមារ បញ្ហា​គ្រឿង​ញៀន​ជា​ដើម ', 'http://kohsantepheapdaily.com.kh/article/116171.html', 'http://i.kspg.co/2015/3/1b0a2162-f456-4501-b9f0-81d75f75900e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('47', 'cl000002', 'u0000003', 'លោកប៉ា សុ​ជាតិ​វង្ស​ ដឹកនាំ​មន្ត្រីរាជការ​ក្រោម​ឱ​វាទ​ចុះ​ជួយស្ទូងស្រូវ និង​ប្រក់​ដំបូលផ្ទះ​ជូន​ប្រជាពលរដ្ឋ​នៅ​ខណ្ឌដង្កោ​', 'រាជធានី​ភ្នំពេញ ៖ ​ស្រប​ពេល​ដែល​គ្រប់​ស្ថាប័ន​ជាតិ និង​ឯកជនក៏​ដូច​គ្រឹះស្ថាន​នានា​កំពុង​ចូលរួម​អបអរសាទរ​ទិវា​នារី​អន្តរជាតិ​៨ ​មី​នា​ ខួប​លើក​ទី​១០៤​នោះ អាជ្ញាធរ​រាជធានី​', 'http://kohsantepheapdaily.com.kh/article/116168.html', 'http://i.kspg.co/2015/3/8bcb1357-55fd-485d-b632-cb980fc5636f.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('48', 'cl000002', 'u0000003', 'លួច​ចាក់​សាំង​​ដុត​រថយន្ត​អ្នកកាសែត​នៅ​ស្រុក​ស្នួល ប៉ូលិស​សន្និដ្ឋាន​ថា ជា​រឿង​គំនុំ', 'ខេត្តក្រចេះ ​៖ នគរបាល​ស្រុក​ស្នួលកំពុង​តែ​បើក​ការ​ស៊ើបអង្កេត​លើ​ករណី​លួច​ដុតរថយន្ត​បា​ឡែ​ន​មួយ​គ្រឿង​របស់​អ្នកកាសែត​ម្នាក់ដែល​ចត​ទុក​ចោល​នៅ​ខាង​មុខ​ផ្ទះ​', 'http://kohsantepheapdaily.com.kh/article/116164.html', 'http://i.kspg.co/2015/3/5e9686d4-3c31-48c2-be48-bcb9f87f67e0.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('49', 'cl000002', 'u0000003', 'ថៃ​បញ្ជូន​ព្រះសង្ឃ​អត់​ច្បាប់​២០​អង្គ​តាម​ច្រក​ទ្វារ​អន្តរជាតិ​ប៉ោយ​ប៉ែ​ត', 'ខេត្ត​បន្ទាយ​មាន​ជ័យ​ ៖ ​ព្រះសង្ឃ​ខ្មែរ​ចំនួន​២០​អង្គ​ដែល​និមន្ត​ចេញពី​កម្ពុជា​ទៅ​ប្រទេស​ថៃ​តាម​ច្រករបៀង​នានា​ត្រូវ​បាន​ភាគី​ថៃ​បញ្ជូន​ត្រឡប់​មក​កម្ពុជា​វិញ​តាម​ច្រក​ទ្វារ​អន្តរជាតិ​', 'http://kohsantepheapdaily.com.kh/article/116162.html', 'http://i.kspg.co/2015/3/ea09d3cd-6328-41e2-8fb5-7f1a45277f19.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('50', 'cl000002', 'u0000003', 'អ្នកកាសែត​ជំនាន់​ថ្មី ចូល​រោងការ ​', 'រាជ​ធានីភ្នំពេញ​ ៖ លោក ទុ​យ ម៉ែ​ន ជា​អ្នកកាសែត​ក្នុង​យុគសម័យ​ថ្មី កំពុង​ប្រឡូក​វិជ្ជាជីវៈ​អ្នក​សារព័ត៌មាន បាន​សម្រេចចិត្តរៀប​អាពាហ៍ពិពាហ៍​ជាមួយកញ្ញា ម៉​ម ស្រី​នុ​ច ជា​', 'http://kohsantepheapdaily.com.kh/article/116160.html', 'http://i.kspg.co/2015/3/786ecc12-44bf-4ae1-8780-88a655846989.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('51', 'cl000002', 'u0000003', 'ក្រុម​ចោរបាញ់​ប្លន់​យក​គ្រឿងអលង្កា​រ៥​គីឡូក្រាមនៅ​ក្រុង​សួង​ សមត្ថកិច្ច​ស្គាល់​អត្តសញ្ញាណ​ហើយ', 'ខេត្ត​ត្បូងឃ្មុំ ​៖ រហូត​មកដល់​ល្ងាច​ថ្ងៃ​ទី​៧ មីនា ២០១៥ តាម​រយៈ​ការ​ស្រាវជ្រាវរបស់​សមត្ថកិច្ច​បាន​ស្គាល់​អត្ត​សញ្ញាណក្រុម​ចោរប្លន់​ប្រដាប់​អាវុធ​ចំនួន​២​នាក់ដែល​បាន​ធ្វើ​សកម្ម', 'http://kohsantepheapdaily.com.kh/article/116156.html', 'http://i.kspg.co/2015/3/c17fd870-b8b1-4679-a963-5156963e7278.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('52', 'cl000002', 'u0000003', 'រាជ​រដ្ឋា​ភិបាល​ផ្តល់​ប្រាក់​ឧបត្ថម្ភ​៤​ម៉ឺន​រៀល​ដល់​មន្ត្រី​រាជ​ការ និង​កង​កម្លាំង​ប្រដាប់​អាវុធ​ក្នុង​ឱកាស​បុណ្យ​ចូល​ឆ្នាំ​ខ្មែរ​', 'រាជ​រដ្ឋា​ភិបាល​ផ្តល់​ប្រាក់​ឧបត្ថម្ភ​៤​ម៉ឺន​រៀល​ដល់​មន្ត្រី​រាជ​ការ និង​កង​កម្លាំង​ប្រដាប់​អាវុធ​ក្នុង​ឱកាស​បុណ្យ​ចូល​ឆ្នាំ​ខ្មែរ​', 'http://kohsantepheapdaily.com.kh/article/116150.html', 'http://i.kspg.co/2015/3/0f2a2160-5da9-426f-8272-7ba05f82dc23.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('53', 'cl000002', 'u0000003', 'សមត្ថកិច្ច​ស្រុក​លើក​ដែក​ឃាត់​ខ្លួន​បុរស​ម្នាក់​ពី​បទ​ប្រើ​ប្រាស់​ស្លាក​លេខ​នគរបាល​ក្លែង​ក្លាយ', 'ខេត្ត​កណ្តាល ៖ កម្លាំង​សមត្ថកិច្ច​នគរបាល​ស្រុក​លើក​ដែក​សហការ​ជាមួយ​ប៉ូលិស​ប៉ុស្តិ៍​ឃុំ​ព្រែក​ទន្លាប់​ស្ទាក់​ចាប់​រថយន្ត​ស៊េរី​ទំនើប​១​គ្រឿង​ដោយ​សង្ស័យ​ប្រើ​ប្រាស់​ផ្លាក​លេខ​រថយន្ត​ ន.ប ក្លែង​ក្លាយ ។ ហេតុ​ការណ៍​ស្ទាក់​ចាប់​រថយន្ត​ប្រើ​ស្លាក​លេខ​ក្លែង​ក្លាយ', 'http://kohsantepheapdaily.com.kh/article/116149.html', 'http://i.kspg.co/2015/3/7bcc7981-fb47-40fd-a78e-bb2ff0e6c428.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('54', 'cl000002', 'u0000003', 'ព័ទ្ធ​ចាប់​​អ្នក​ចែកចាយ​និង​ប្រើប្រាស់​ថ្នាំ​ញៀន​បាន​២​នាក់​បញ្ជូន​ទៅ​តុលាការ', 'ខេត្តកំពង់ឆ្នាំង​ ៖ ជនសង្ស័យ​ជា​អ្នក​ចែកចាយ​ និង​ប្រើប្រាស់​ថ្នាំ​ញៀន​២​នាក់​ត្រូវ​បាន​សមត្ថកិច្ច​ឡោមព័ទ្ធ​ចាប់ខ្លួន​ និង​ដកហូត​បាន​ថ្នាំ​ញៀន​១​កញ្ចប់​តូច​ជាប់​នៅ​ក្នុង​ខ្លួន​ កាល​ពី​វេលា​ម៉ោង​៣​ និង​២៥​នាទី​ល្ងាច ថ្ងៃ​ទី​០៧ ខែ​មីនា ឆ្នាំ​២០១៥', 'http://kohsantepheapdaily.com.kh/article/116147.html', 'http://i.kspg.co/2015/3/fa63e66d-3239-4a17-b7d2-7b4869b133c6.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('55', 'cl000002', 'u0000003', 'តំណាង​សមាគម​ឧត្តម​សិក្សា​កោតសរសើរ​ចំពោះ​វឌ្ឍនាការ​របស់​អគ្គនាយកដ្ឋាន​ពន្ធដារ', 'រាជ​ធានី​ភ្នំពេញ ៖ ​តំណាង​សមាគម​គ្រឹះស្ថានឧត្តមសិក្សា​កម្ពុជា បាន​សម្តែង​នូវ​ការ​កោតសរសើរ​ចំពោះ​វឌ្ឍនាការ​របស់​អគ្គនាយកដ្ឋាន​ពន្ធដារក្នុង​រយៈ​ថ្មី​ៗ​ចុង​ក្រោយនេះ ', 'http://kohsantepheapdaily.com.kh/article/116146.html', 'http://i.kspg.co/2015/3/3cc5c7a3-896f-41b3-95e3-4f6101dfc69f.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('56', 'cl000002', 'u0000003', 'សម្តេច​សង្ឃនាយក​ភូមា​ប្រគេន​ព្រះ​ទន្ត​ព្រះ​ពុ​ទ្ធ​ជូន​សម្តេចព្រះ​វណ្ណ​រតន៍​ណ​យ ច្រឹ​ក​ ​', 'រាជ​ធានី​ភ្នំពេញ ៖ រដ្ឋាភិបាល​ប្រទេស​ភូមាបាន​ប្រគេន​នូវ​គោរមងា​ដល់​ព្រះ​វ័ន​រតន៍​ណ​យ ច្រឹ​ក​ ព្រះសង្ឃ​នាយករង​ទី​៣​នៃ​ព្រះរាជាណាចក្រ​កម្ពុជា ដែល​ព្រះ​អង្គជា​ថ្នាក់ដឹកនាំ​', 'http://kohsantepheapdaily.com.kh/article/116144.html', 'http://i.kspg.co/2015/3/201cda4b-2c07-4a44-9a8d-eecbe9341459.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('57', 'cl000002', 'u0000003', 'សន្និសីទ​ពិភពលោក​លើក​ទី​3 ស្តី​ពី​ការ​កាត់​បន្ថយ​ហានិភ័យគ្រោះ​មហន្តរាយ នឹង​ប្រព្រឹត្ត​ទៅ​នៅ​ទីក្រុង​សេន​ដាយ ប្រទេស​ជប៉ុន', 'រាជធានី​ភ្នំពេញ ៖ សន្និសីទ​ពិភពលោក​លើក​ទី​៣​របស់​អង្គការសហប្រជាជាតិ ស្តី​ពី​ការ​កាត់​បន្ថយ​ហានិភ័យ​គ្រោះ​មហន្តរាយ នឹង​ប្រព្រឹត្ត​ទៅ​ចាប់ពី​ថ្ងៃ​ទី​៩ ដល់​ថ្ងៃ​ទី​១៩ មីនា ឆ្នាំ​២០១៥ នៅ​ទីក្រុង​សេន​ដាយ', 'http://kohsantepheapdaily.com.kh/article/116142.html', 'http://i.kspg.co/2015/3/98991a93-c76b-459a-8185-6732c0f4ab26.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('58', 'cl000002', 'u0000003', 'ព្រះ​មហាក្សត្រ​ខ្មែរ​លើក​លែង​ទោស​ទណ្ឌិត​ជា​ស្ត្រី​២២​នាក់​', 'ព្រះ​មហាក្សត្រ​ខ្មែរ​លើក​លែង​ទោស​ទណ្ឌិត​ជា​ស្ត្រី​២២​នាក់​', 'http://kohsantepheapdaily.com.kh/article/116141.html', 'http://i.kspg.co/2015/3/6a29686a-4b11-4f5c-afdd-b3b7cf9878ed.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('59', 'cl000002', 'u0000003', 'ពង្រីក​កំណាត់​វិថី​សម្តេច​សុធារស សាលា​ក្រុង​ដាក់កំណាត់​ផ្លូវ​៣​ខ្សែ ជា​ផ្លូវ​ឯក​ទេស', 'ពង្រីក​កំណាត់​វិថី​សម្តេច​សុធារស សាលា​ក្រុង​ដាក់កំណាត់​ផ្លូវ​៣​ខ្សែ ជា​ផ្លូវ​ឯក​ទេស', 'http://kohsantepheapdaily.com.kh/article/116138.html', 'http://i.kspg.co/2015/3/eba18a60-6f88-4222-bec2-954efab11784.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('60', 'cl000002', 'u0000003', 'ពលរ​ដ្ឋទីទ័ល​ក្រ​៤៩​គ្រួសារ​ទទួល​អំណោយ​ពី​អនុ​សាខា​កាកបាទក្រហមកម្ពុជា​ខណ្ឌដូនពេញ', 'រាជធានីភ្នំពេញ​ ៖ អំណោយ​របស់​កាកបាទក្រហម​ដែល​បាន​យកមក​ចែក​ជូន​បងប្អូន​ប្រជាពលរដ្ឋ​ក្រី​ក្រ​នៅ​ពេល​នេះ គឺ​ទទួល​បាន​ពី​ការ​បរិច្ចាគ​ពី​សំណាក់​សប្បុរសជន​នានា ដែល​បាន​ចូលរួម​វិភាគទាន​ចូល​ក្នុង​កាកបាទក្រហមកម្ពុជា', 'http://kohsantepheapdaily.com.kh/article/116127.html', 'http://i.kspg.co/2015/3/281f30f4-e1a3-45df-9c20-e27c5bfde8bb.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('61', 'cl000002', 'u0000003', 'ទិវា​នារី អន្តរជាតិ​៨​មីនា​លើក​ទី​១០៤ ឆ្នាំ​២០១៥​នៅ​ក្នុង​ពន្ធនាគារ​ខេត្ត', 'ខេត្តសៀមរាក̡ߖ  នា​ព្រឹក​ថ្ងៃ​ទី​០៧ ខែ​មិ​នា ឆ្នាំ​២០១៥ នៅ​ក្នុង​ពន្ធនាគារ​ខេត្ត​មាន​រៀបចំ​ធ្វើ​ពិធី​ទិវា​នារី​អន្តរជាតិ ៨​មីនា​លើក​ទី​១០៤ ឆ្នាំ​២០១៥ ដោយ​ប្រព្រឹត្ត​ទៅ​ក្រោម​អធិបតីភាព​លោក ឆែម សា​វុ​ធ ប្រធាន​ពន្ធនាគារ​ខេត្តសៀមរាប និង​មានការ​ចូលរួម​ពី​ថ្នាក់ដឹកនាំ​ក្នុង​ពន្ធនាគារ', 'http://kohsantepheapdaily.com.kh/article/116124.html', 'http://i.kspg.co/2015/3/6d59ece2-c72e-486c-ae08-67e2a9641c51.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('62', 'cl000002', 'u0000003', 'ខឹងសុំលុុយ​ប្រពន្ធ​ទិញ​ម៉ូតូ​មិនបាន​ចង.ក​សម្លាប់ខ្លួន', 'ខេត្តសៀមរាប ៖ បុរស​បាន​គិត​ខ្លី​ចង​​ក​.​សម្លាប់​ខ្លួន​និង​ខ្សែ​អង្រឹង​ទៅ​នឹង​ដើម​ស្វាយចន្ទី​ក្រោយ​ផ្ទះ​ជួល​របស់​ខ្លួន ​ចោល​ប្រពន្ធ​កំពុង​ពរ​ពោះ​​៦​ខែ បន្ទាប់ពី​មាន​​​ទំនាស់​ជាមួយ​ប្រពន្ធ​បន្តិចបន្តួច​កាលពី​យប់​រឿង​សុំ​លុយ​ទិញ​ម៉ូតូ​​ជិះ​ ។  ', 'http://kohsantepheapdaily.com.kh/article/116118.html', 'http://i.kspg.co/2015/3/41f3fae2-3b48-445d-a6d6-32c8f110f330.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('63', 'cl000002', 'u0000003', 'បញ្ចុះ​បឋមសិលា​សាងសង់​អាគារ​ថ្មី ​នៅ​សាកលវិទ្យា​ល័យ​ស្វាយ​រៀង', 'ខេត្តស្វាយរៀង ៖ ពិធី​បញ្ចុះ​បឋមសិលា​សាងសង់​អាគារ​ថ្មី ​៣​ជាន់​១៨​បន្ទប់​ នៅ​សាកលវិទ្យា​ល័យស្វាយ​រៀង លោក​ជំទាវ​បណ្ឌិត​ម៉ែ​ន​ សំ​អន ​អញ្ជើញ​ជា​អធិបតី​ ក្នុង​ពិធី​បញ្ចុះ​បឋម​សីលា​សាងសង់​អាគារ​ថ្មី​ នៃ​សកលវិទ្យា​ល័យ​ស្វាយ​រៀង កាលពី​ម៉ោង​៨​ព្រឹក​ថ្ងៃ​ទី​៧ ​មី​នា ​២០១៥ ​នៅ​សកលវិទ្យា​ល័​យ​ស្វាយ​', 'http://kohsantepheapdaily.com.kh/article/116115.html', 'http://i.kspg.co/2015/3/08b46aec-d77c-4e48-8381-75db9599b001.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('64', 'cl000002', 'u0000003', 'រយៈពេល​២​ថ្ងៃ​កម្លាំងអាវុធហត្ថ​រាជធានី​ភ្នំពេញ​​ឃាត់ខ្លួន​ជនសង្ស័យ​​១៥​នាក់​ពាក់ព័ន្ធ​បទល្មើស​​​៥​ផ្សេង​​គ្នា​', 'រាជធានីភ្នំពេញ ៖ ដោយ​មានការ​ណែនាំ​ចង្អុល​ប​ង្ហាញ​ពី​លោក​នាយ​ឧត្តមសេនីយ៍ សៅ សុខា អគ្គមេបញ្ជាការ​រង​កង​យោធ​ពល​ខេមរ​ភូមិន្ទ និង​ជា​មេបញ្ជាការ​កង​រាជអាវុធហត្ថ​លើ​ផ្ទៃ​ប្រទេស ព្រមទាំង​ក្រោម​​ការ​ដឹក​នាំ​បញ្ជា​ផ្ទាល់​ពីលោកឧត្តមសេនីយ៍ទោ រ័​ត្ន ស្រ៊ា​ង', 'http://kohsantepheapdaily.com.kh/article/116113.html', 'http://i.kspg.co/2015/3/30dcd7ff-df8a-4784-9ba6-8cea12ee74fb.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('65', 'cl000002', 'u0000003', 'ឆក់​កាបូប​ចំ​ថ្ងៃ​សិទ្ធិ​នារី​ព្រះ​មិន​ជួយ​ នគរបាល​ក្រសួង​ក្របួច​បាន​មក​វិញ', 'រាជ​ធានី​ភ្នំពេញ​ ៖ ជនសង្ស័យ​ជា​ចោរឆក់​កាបូប២​នាក់ ត្រូវ​​​សមត្ថ​កិច្ច​នគរបាល​ការិយាល័យ​ព្រហ្មទណ្ឌ​កម្រិត​ស្រាលនៃ​នាយកដ្ឋាន​ព្រហ្មទណ្ឌក្រសួងមហាផ្ទៃ ឃាត់ខ្លួន​កាលពី​', 'http://kohsantepheapdaily.com.kh/article/116167.html', 'http://i.kspg.co/2015/3/4a9c9229-5428-463c-837d-67b4b33faf43.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('66', 'cl000002', 'u0000003', 'និស្សិត​អ៊ិ​ស្លា​ម​ម្នាក់​តូចចិត្ត​អត់​ទីជម្រក និង​សាលា​ផ្តាច់​អាហា​រូបករណ៍​ប៉ង​​លោតទឹក​សម្លាប់​ខ្លួន​ពីលើ​ស្ពាន​ជ្រោយចង្វារចាស់', 'រាជធានី​ភ្នំពេញ​ ៖​ និស្សិត​នារី​ជនជាតិ​ខ្មែរ​អ៊ិ​ស្លា​ម​ម្នាក់​បាន​អះអាង​ថា ដោយសារ​តូចចិត្ត​ខ្លួនឯង​ដែល​វិទ្យាស្ថាន​ផ្តាច់​អាហារូបករណ៍​ និង​អត់​ទីជម្រក​​ក៏​បាន​ជិះ​ម៉ូតូ​ទៅ​លោតទឹក​សម្លាប់​ខ្លួន​ពីលើ​ស្ពាន​ជ្រោយចង្វារ ​តែ​ត្រូវ​បាន​កម្លាំង​នគរបាល​ឃាត់​ទាន់​ ។', 'http://kohsantepheapdaily.com.kh/article/116155.html', 'http://i.kspg.co/2015/3/7e8ee0c4-2250-475b-b109-9ab474747a50.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('67', 'cl000002', 'u0000003', 'កម្មករ​សំណង់​ម្នាក់​ត្រូវ​​ខ្សែភ្លើង​ឆក់​ស្លាប់', 'ខេត្ត​បន្ទាយ​មាន​ជ័យ​ ៖​ កម្មករ​សំណង់​ម្នាក់​ក្រោយ​ពី​សម្រាក​​ការងារ​លាងដៃ​រួច​​ក៏​ប្រមូល​​​ខ្សែភ្លើង​រៀបចំ​ទុកដាក់​សង្ស័យ​ប៉ះ​ចំ​កន្លែង​ដាច់​ ម្យ៉ាង​ដៃ​នៅ​សើម​ឆក់​ស្លាប់​នៅ​ក្នុង​បន្ទប់ទឹក​កន្លែងធ្វើការ​ ។', 'http://kohsantepheapdaily.com.kh/article/116143.html', 'http://i.kspg.co/2015/3/fcd6e7be-3b39-44d3-af6b-05d7e958b49e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('68', 'cl000002', 'u0000003', 'ធ្វើខូបកំណើតក្នុងក្លឹបឌឺដងគ្នា​​បង្ក​ជា​ហិង្សា​ត្រូវឃាត់ខ្លួន', 'រាជធានី​ភ្នំពេញ ៖ ទៅ​ធ្វើ​ខួបកំណើត​នៅ​ក្នុង​ក្លឹ​ប​ហើយ​រាំ​ជាន់​ជើង​គ្នា​បង្ក​ជា​ហិង្សា​វាយតប់​ត្រូវ​សមត្ថ​កិច្ច​ឃាត់ខ្លួន​ភាគី​ទាំង​សងខាង​យក​​ទៅ​សាកសួរ បង្ក​ឲ្យ​មានការ​ភ្ញាក់ផ្អើល​កាលពី​វេលា​ម៉ោង​១២​ និង​២០​នាទី​រំលង​អា​ធ្រា​ត​ឈាន​ចូល​ថ្ងៃ​ទី​៧ ខែ​មិ​នា នៅ​ក្នុង​ក្លឹ​ប C2', 'http://kohsantepheapdaily.com.kh/article/116139.html', 'http://i.kspg.co/2015/3/aa0f851d-5ecf-46b7-a6d6-5957479b288f.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('69', 'cl000002', 'u0000003', 'ខឹង​រោ​ទិ៍​ម៉ូតូ​ថ្លង់​ពេក​ដក​កាំភ្លើង​ភ្ជង់​វាយ​បែកក្បាល​ម្នាក់', 'រាជធានីភ្នំពេញ ៖ យុវ​ជន​មួយ​ក្រុម​ជិះ​ម៉ូតូ​រោ​ទិ៍​ថ្លង់​គេឯង​ ធ្វើ​ឲ្យ​ជន​មួយ​ក្រុម​ទៀត​ខឹង​សួរនាំ​បង្ក​ជា​ជម្លោះ​ ដក​កាំភ្លើង​ភ្ជង់​វាយ​បែក​ក្បាល​ហូរឈាម​​កាលពី​វេលា​ម៉ោង​១២​ និង​៣០​នាទី​រំលង​អធ្រាត្រ​ឈាន​ចូល​ថ្ងៃ​ទី​៨ ខែ​មិ​នា ឆ្នាំ​២០១៥', 'http://kohsantepheapdaily.com.kh/article/116134.html', 'http://i.kspg.co/2015/3/33ccc390-b143-4aee-853e-685757ba13e3.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('70', 'cl000002', 'u0000003', 'កម្លាំង​អាវុធហត្ថ​ចាប់​គោ​យន្ត​១​គ្រឿង​ដឹកឈើ​ប្រណិត​២៦​កំណាត់', 'ខេត្តមណ្ឌលគិរី​៖​គោ​យន្ត​កែ​ច្នៃ​ដឹកឈើ​ប្រណិត​ចម្រុះ​មួយ​គ្រឿង ត្រូវ​បាន​ជំនាញ​មេព្រៃ​សហការ​ជាមួយ​កងកម្លាំង​អាវុធហត្ថ​ប្រចាំ​នៅ​ស្នាក់​ការ​អភិរក្ស​ជីវចម្រុះ​សីមា ចាប់​បង្ក្រាប​បាន ខណៈ​កំពុង​ធ្វើ​សកម្មភាព ដឹក​ជញ្ជូន​ឈើ​ចេញពី​ដែន​ការពារ ', 'http://kohsantepheapdaily.com.kh/article/116123.html', 'http://i.kspg.co/2015/3/ddf3f036-ccd7-4526-bcf8-aabe87c24cc8.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('71', 'cl000002', 'u0000003', 'ប្តី​ចិត្ត​អប្រិយ​ទិវា​សិទ្ធ​នារី​អន្តរជាតិ ជូន​កាដូ​ប្រពន្ធ​១​ពូថៅ​របួសធ្ងន់', 'ខេត្ត​ត្បូងឃ្មុំ​៖​ដោយសារ​តែ​ថ្នាំ​ញៀន​ចូលខ្លួន​ជ្រុល​ហួស ជា​ហេតុ​ធ្វើ​ឲ្យ​បុរស​ជា​ប្តី​ចិត្ត​ចាប់​ឃុំខ្លួន​ស្ត្រី​ជា​ប្រ​ព​ន្ឋ​ទុក​នៅ​ក្នុងផ្ទះ​ធ្វើ​ទារុណកម្ម ខណៈ​មាន​ក្រុម​សាច់ញាតិ​និង​កម្លាំង​សមត្ថកិច្ច​ជួយ​អន្តរាគមន៍​រំដោះ​យ៉ាងណា ក៏​ដោយ​នោះ', 'http://kohsantepheapdaily.com.kh/article/116122.html', 'http://i.kspg.co/2015/3/471468db-66bc-435c-b5e4-3833fb2ab477.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('72', 'cl000002', 'u0000003', 'បទល្មើស​នេសាទ​កើត​មាន​ពេញបន្ទុក​នៅ​ផ្នែក​ចុង​ឃ្នៀស​និង​ផ្នែក​ក្រឡាញ់ ​សង្ស័យ​មានការ​បើកភ្លើង​ខៀវ​ពី​មន្ត្រី​ជលផល', 'ខេត្តសៀមរាប​៖ កាលពី​ពេល​ថ្មី​ៗ​មាន​ក្រុម​អ្នក​តាមដាន​បទល្មើស​នេសាទ​គ្រប់​ប្រភេទ​បាន​បង្ហាញ​ឲ្យ​ដឹង​ថា ឧបករណ៍​នេសាទ​ល្មើសច្បាប់​មាន​ដូច​ជាៈ​ប័​រ​,​អ៊ុយ​,​រុញ​ស៊ី​ប​,​អូស​អួន​,​អូស​យ៉ាំ​ង​កាវ​, សាច់​ស្បៃ​ម៉ុង​ក្រឡា​ញឹក​ជាដើម ', 'http://kohsantepheapdaily.com.kh/article/116121.html', 'http://i.kspg.co/2015/3/5664d419-e08c-405d-b6b5-0fa66b13c468.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('73', 'cl000002', 'u0000003', 'តម្រួត​និង​ទាហាន​ព្រមាន​ប៉ូលិស​ឲ្យ​បញ្ឈប់​ដើរ​ប្រមូលប្រាក់​ពី​តូប​អាជីវករ​នៅ​ផ្សារ​រុង​ក្លឿ', 'បន្ទាយ​មាន​ជ័យ​៖​តាម​ប្រភព​ពី​អាជីវករ​ខ្មែរ​លក់ដូរ​នៅ​ផ្សារ​រុង​ក្លឿ​ស្រុក​អារញ្ញ ខេត្ត​ស្រះ​កែវ​បាន​ប្រាប់​កោះសន្តិភាព​កាលពី​វេលា​ម៉ោង​ជិត​៤​រសៀល​ថ្ងៃ​ទី​៥ ខែ​មិ​នា ឆ្នាំ​២០១៥​ថា​តម្រួត​និង​ទាហាន​ឈុត​ខ្មៅ​ថៃ​បាន​ហាមឃាត់​ប៉ូលិស​ខេត្ត​ស្រះ​កែវ​ថៃ​ដូច​គ្នា​ជា​លើក​ដំបូង​មិន​ឲ្យ​ដើរ​ប្រមូលប្រាក់​ពី​តូប​អាជីវករ​ខ្មែរ​', 'http://kohsantepheapdaily.com.kh/article/116101.html', 'http://i.kspg.co/2015/3/57bca555-ed2f-4330-b26b-baa5978de056.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('74', 'cl000002', 'u0000003', 'បង្ក្រាប​ក្មេង​ទំនើង​៨​នាក់​កែ​ច្នៃ​បំពង់​ស៊ី​ម៉ាំង​បំពង​សម្លេង​បំពាក់​លើ​ម៉ូតូ', 'ខេត្តបន្ទាយមានជ័យ​៖​	​ដើម្បី​រក្សា​ការពារ​សន្តិសុខ​សណ្តាប់ធ្នាប់​សាធារណៈ​និង​របៀបរៀបរយ​ក្នុង​ខេត្ត ឯកឧត្តម​គោ​ស៊ុំ សា​រឿ​ត ប្រធាន​គណៈ​បញ្ជាការ​ឯកភាព​ខេត្ត​និង​ជា​អភិបាល​នៃ​គណៈ​អភិបាល​ខេត្តបន្ទាយមានជ័យ បាន​ដាក់​ផែនការ​ឲ្យ​កងកម្លាំង​នគរបាល​ខេត្ត', 'http://kohsantepheapdaily.com.kh/article/116100.html', 'http://i.kspg.co/2015/3/e434eef5-03b4-4ff5-824d-703318a1b516.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('75', 'cl000002', 'u0000003', 'កម្លាំង​ចម្រុះ​ស្រុក​លើក​ដែក ដេញ​ទូក​នេសាទ​វៀតណាម ២៤គ្រឿង​ចេញពី​តំបន់​អភិរក្ស', 'ខេត្តកណ្តាល ៖ កាលពី​ថ្ងៃ​ទី​៣ មីនា កន្លង​ទៅ​ថ្មី​ៗ​នេះ កម្លាំង​ចម្រុះ​របស់​គណៈ​បញ្ជាការ​ឯកភាព​ស្រុក​លើក​ដែក មាន​នគរបាល ប៉េ​អឹ​ម យោធា ជលផល និង​សហគមន៍​សរុប​២១​នាក់​', 'http://kohsantepheapdaily.com.kh/article/116058.html', 'http://i.kspg.co/2015/3/09663476-eae5-4cb0-8977-351e26560815.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('76', 'cl000002', 'u0000003', 'សមត្ថកិច្ចឃាត់ខ្លួន​ម្ចាស់រថយន្តដែលដឹកសព​កម្មការិនីរូបស្រស់ម្នាក់​យកទៅទម្លាក់ចោលនៅស្រុកពញាឮ', 'រាជធានីភ្នំពេញ​ ៖នគរបាលព្រហ្មទណ្ឌ​ក្រសួងមហាផ្ទៃ​សហការជាមួយកម្លំាង​នគរបាលព្រហ្មទណ្ឌ​ខេត្តកណ្តាល ​នៅយប់ថ្ងៃទី០៥ ​មីនា ២០១៥ ​បានឃាត់​', 'http://kohsantepheapdaily.com.kh/article/116056.html', 'http://i.kspg.co/2015/3/66e5e83c-52ef-4447-938e-7477abc4353b.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('77', 'cl000002', 'u0000003', 'អតីត​ប្តី​តាម​រករឿង​ប្រ​ព​ន្ឋ​ក្រោយ​ពី​លែងលះ​គ្នា​បាន​១​ឆ្នាំ(មាន​វី​ដេ​អូ​)', 'រាជធានី​ភ្នំពេញ​៖​អតីត​ប្តី​កំហូច​ម្នាក់​បាន​ទៅ​តាម​រករឿង​ប្រពន្ធ​ដែល​លែងលះ​គ្នា​ដល់​កន្លែងធ្វើការ បង្ក​អោយ​មាន​ជម្លោះ​ជាមួយ​បុរស​ជនជាតិ​ជប៉ុន​ដែល​ជា​ម្ចាស់​ហាង​ម៉ាស្សា រហូត​ឈាន​ទៅ​វាយតប់​គ្នា​បង្ក​ឲ្យ​មានការ​ភ្ញាក់ផ្អើល កាលពី​វេលា​ម៉ោង​២​និង​៥០​នាទី​យប់​ឈាន​ចូល​ថ្ងៃ​ទី​៦', 'http://kohsantepheapdaily.com.kh/article/116029.html', 'http://i.kspg.co/2015/3/ebe04097-af83-41e3-a21e-94e7dab82b6d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('78', 'cl000002', 'u0000003', 'អនិច្ចា​រ្យ​រថយន្ត​កិន​ទារិកា​ម្នាក់​ស្លាប់​ក្រោយ​ពេល​ម្តាយ​យក​ទៅ​បោះចោល​នៅ​លើ​ផ្លូវ(មាន​វី​ដេ​អូ​)​', 'រាជធានី​ភ្នំពេញ​៖​គួរ​ឲ្យ​អ​នោ​ច​អធម្ម​ពេក​ក្រៃ​ដោយសារ​តែ​អ្នក​ភូមិ​មានការ​ភ្ញាក់ផ្អើល​ពេល​ដែល​សាកសព​ទារិកា​ម្នាក់​ត្រូវ​បាន​រថយន្ត​កិន​ដាច់​សុះសាច់​មួយ​កំណាត់​ក្បាល ក្រោយ​ពី​ម្តាយ​ចិត្ត​អប្រិយ​យក​កូន​ទៅ​បោះចោល​នៅ​លើ​ផ្លូវ ', 'http://kohsantepheapdaily.com.kh/article/116028.html', 'http://i.kspg.co/2015/3/99982074-5ff3-48fd-b37d-84409f68c5dd.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('79', 'cl000002', 'u0000003', 'ឈ្លោះ​គ្នា​ជាមួយ​​ប្តី​ស្ត្រី​ជា​ប្រ​ព​ន្ឋ​រត់​ចូល​រថយន្ត​ប៉ង​សម្លាប់​ខ្លួន', 'រាជធានី​ភ្នំពេញ​៖​ប្តី​ប្រ​ព​ន្ឋ​២​នាក់​បាន​ឈ្លោះប្រកែក​គ្នា​យ៉ាង​តឹង​ស​សៃ ក ដោយ​ស្ត្រី​ជា​ប្រ​ព​ន្ឋ​ខឹង​ខ្លាំង​ពេក ក៏​រត់​ចូល​រថយន្ត​ប៉ង​សម្លាប់​ខ្លួន តែ​ត្រូវ​បុរស​ជា​ប្តី​ចាប់​ឃាត់​ទាន់​ពេលវេលា កុំ​អី​និង​មាន​គ្រោះថ្នាក់​ដល់​ជិ​វិត', 'http://kohsantepheapdaily.com.kh/article/116027.html', 'http://i.kspg.co/2015/3/f38d5a2b-24f1-4262-ad69-d9214e3b8a39.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('80', 'cl000002', 'u0000003', 'ស្រី​ស្អាត​ម្នាក់​លោត​ពីលើ​ផ្ទះ​ជាន់​ទី​១​ធ្វើ​អត្តឃាត​មិនដឹង​មូលហេតុ(មាន​វី​ដេ​អូ​)​', 'រាជធានី​ភ្នំពេញ​៖ ស្រី​ស្អាត​ម្នាក់​បាន​លោត​ពីលើ​ផ្ទះ​ជាន់​ទី​១​របស់​ខ្លួន​ធ្វើ​អត្តឃាត ខណៈ​ពេល​ដែល​ម្តាយ​មិន​នៅផ្ទះ ហើយ​ឪពុក​នៅ​កំពុង​ជជែក​គ្នា​ជាមួយ​អ្នក​ភូមិ​នៅ​ជាន់​ផ្ទាល់​ដី​នោះ ។ ស្រី​ស្អាត​ខាងលើ​បាន​រង​របួសធ្ងន់ ហើយ​ត្រូវ​បាន​រថយន្ត​សា​មុ​យ​ដឹក​យក​ទៅ​សង្គ្រោះ', 'http://kohsantepheapdaily.com.kh/article/116026.html', 'http://i.kspg.co/2015/3/601ce4ce-654a-437e-8fe5-2567d14575a9.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('81', 'cl000002', 'u0000003', 'ខឹង​ម្តាយ​ស្តីបន្ទោស​ប៉ង​លោត​សម្លាប់​ខ្លួន​ពីលើ​ស្ពាន​ព្រះ​មុនី​វង្ស​ថ្មី', 'រាជធានី​ភ្នំពេញ​៖ បុរស​ម្នាក់​បាន​ដើរមក​បម្រុង​លោត​សម្លាប់​ខ្លួន​ពីលើ​ស្ពាន​ព្រះ​មុនី​វង្ស​ថ្មី ព្រោះតែ​ខឹង​ម្តាយ​ស្តីបន្ទោស​ឲ្យ​តិចតួច ។​ប៉ុន្តែ​ជា​សំណាង់​នៅ​ពេល​ដែល​សមត្ថកិច្ច​ប៉ូលិស​ប្រចាំ​នៅ​គោលដៅ​ក្រោម​ស្ពាន​ឃើញ​បាន​ជួយ​អន្តរាគមន៍​ឃាត់​ភ្លាម​ៗ', 'http://kohsantepheapdaily.com.kh/article/116025.html', 'http://i.kspg.co/2015/3/04244deb-b14d-4816-9a78-032fa549e021.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('82', 'cl000002', 'u0000003', 'ទំនាស់​ពាក្យ​សម្តី​ឈាន​ទៅ​វាយតប់​គ្នា​ផ្អើល​សមត្ថកិច្ច ព្រោះ​រឿង​លក់​គ្រឿង​ភេសជ្ជៈ​ដូច​គ្នា', 'រាជធានី​ភ្នំពេញ​៖​មនុស្ស​២​ក្រុម​បាន​មាន​ទំនាស់​ពាក្យ​សម្តី​គ្នា រហូត​ឈាន​ទៅ​វាយតប់​គ្នា​ផ្អើល​សមត្ថកិច្ច​ប៉ូលិស ព្រោះតែ​មាន​តូប​លក់​ភេសជ្ជៈ​នៅ​ជាប់​គ្នា មានការ​កាច់​កុង​គ្នា ខណៈ​ពេល​លក់​ដូច​គ្នា ហើយ​ឃើញ​ភាគី​តូប​ម្ខាង​ទៀត​លក់ដាច់​ជាង', 'http://kohsantepheapdaily.com.kh/article/116024.html', 'http://i.kspg.co/2015/3/1361f2fd-39b9-41f7-8001-cebfa40273b5.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('83', 'cl000002', 'u0000003', '​ចោរ​​៤​នាក់​ភ្ជង់​ និង​វាយ​ស្ត្រី​មេ​ម៉ាយ​ម្នាក់ប្លន់​យក​មាស​អស់​២​ដំឡឹង​៦​ជី​៥​ហ៊ុន​នៅ​ក្រុង​ស្វាយ​រៀង', 'ខេត្តស្វាយរៀង៖ ស្ត្រី​មេម៉ា​យ​ម្នាក់​ត្រូវ​ក្រុម​ចោរប្លន់​ប្រដាប់​អាវុធ​ចូល​ភ្ជង់​ និង​វាយ​បែកក្បាល​ប្លន់​យក​មាស​អស់​២​ដំឡឹង​៦​ជី៥​ហ៊ុន ​រួច​នាំ​គ្នា​ឡើង​ជិះ​ម៉ូតូ​គេចខ្លួន​បាត់​ភ្លាម​ៗ​ដោយ​', 'http://kohsantepheapdaily.com.kh/article/115968.html', 'http://i.kspg.co/2015/3/42024bdd-99f4-449c-a2bf-53fb85dfc415.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('84', 'cl000002', 'u0000003', 'ទំនង​ប្រើប្រាស់​គ្រឿងញៀន​ហួសកំណត់ បុរស​ម្នាក់​បាន​ឡើង​ទៅ​លើ​ចុង​ដើម​ក្ងោក​និយាយ​ម្នាក់ឯង', 'រាជធានី​ភ្នំពេញ​៖ បុរស​ម្នាក់​ទំនងជា​ប្រើប្រាស់​គ្រឿងញៀន​ច្រើន​ហួសកំណត់ បាន​ឡើង​ទៅ​លើ​ចុង​ដើម​ក្ងោក​និយាយ​ម្នាក់ឯង បង្ក​ធ្វើ​ឲ្យ​មានការ​ភ្ញាក់ផ្អើល​ដល់​សមត្ថកិច្ច​និង​ប្រជាពលរដ្ឋ​ជួយ​ធ្វើ​អន្តរាគមន៍​ហៅ​ចុះ​មក​ក្រោម ឯ​អ្នក​ភូមិ​ដែល​មានផ្ទះ​នៅ​ជិត​នោះ​បាន​យក​ទឹក​ជះ​ពីលើ​ផ្ទះ', 'http://kohsantepheapdaily.com.kh/article/115962.html', 'http://i.kspg.co/2015/3/b4f61a74-e235-41a5-804e-a5e9618a39c4.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('85', 'cl000002', 'u0000003', 'ប៉ូលិស​ខណ្ឌដូនពេញ​ដេញចាប់​មុខសញ្ញា​លួច បែកធ្លាយ​ចែកចាយ​គ្រឿងញៀន​ឃាត់ខ្លួន​២​នាក់', 'រាជធានី​ភ្នំពេញ​៖​កម្លាំង​ប៉ូលិស​ល្បាត​ខណ្ឌដូនពេញ​បាន​តាម​ប្រ​ម៉ា​ញ់​មុខសញ្ញា​បន្លំ​លួច​អាយ​ភែ​ត​ពី​ម្តុំ​មាត់ទន្លេ ឃាត់ខ្លួន​បាន​ប្រុស​ស្រី​២​នាក់ បែកធ្លាយ​ជនសង្ស័យ​ទាំង​២​នាក់ ក៏​ជា​មុខសញ្ញា​ចែកចាយ​គ្រឿងញៀន​យ៉ាង​សកម្ម​ផង​ដែរ ', 'http://kohsantepheapdaily.com.kh/article/115960.html', 'http://i.kspg.co/2015/3/87f80556-859a-4d63-8910-9dc01afe01f8.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('86', 'cl000002', 'u0000003', 'បុរស​ជនជាតិ​ចិន​ម្នាក់ ដេក​ស្លាប់​ក្នុង​បន្ទប់​ផ្ទះសំណាក់​មនោ​រម្យ', 'ខេត្តកណ្ដាល​៖ បុរស​ជនបរទេស​ម្នាក់​បាន​ដេក​ស្លាប់​ក្នុង​បន្ទប់​ផ្ទះសំណាក់​មនោ​រម្យ ធ្វើ​ឲ្យ​មានការ​ភ្ញាក់ផ្អើល កាលពី​វេលា​ម៉ោង​៣​និង​៣០​នាទី​រសៀល ថ្ងៃ​ទី​០៣ ខែ​មិ​នា ចំណុច​ផ្ទះសំណាក់​មនោ​រម្យ បន្ទប់​លេខ​១១១​ស្ថិត​ក្នុងភូមិ​ថ្មី​២ សង្កាត់​តា​ខ្មៅ ក្រុង​តា​ខ្មៅ', 'http://kohsantepheapdaily.com.kh/article/115959.html', 'http://i.kspg.co/2015/3/a5cbae3f-b886-476f-99e7-2aecf06f05e0.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('87', 'cl000002', 'u0000003', 'អាវុធហត្ថ​រាជធានីសហការ​ជាមួយ​អាវុធហត្ថ​២​ខណ្ឌបំបែក​ក្រុម​ឆបោក​លុយតាម​ប្រព័ន្ធវី​ងបាន​៥​នាក់នៅ​សង្កាត់​បឹង​ទំពុន', 'រាជ​ធានី​ភ្នំពេញ ៖ កម្លាំង​កង​រាជ​អាវុធ​រាជធានី​ភ្នំពេញ​ដឹកនាំ​ដោយ​លោក​វរសេនីយ៍ទោ មាំ​ង សុខ​លុ​ច នាយការិយាល័យ​ស្រាវជ្រាវ​បទល្មើស​ព្រហ្មទណ្ឌ​នៃ​ទីបញ្ជាការដ្ឋាន​', 'http://kohsantepheapdaily.com.kh/article/115943.html', 'http://i.kspg.co/2015/3/3752e08b-8f2a-46b5-899e-27502b8bbb38.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('88', 'cl000002', 'u0000003', 'កាប់​ប្រហារ​ក្មេង​ស្រី​ជំទង់​វ័យ​១៥​ឆ្នាំ​ប៉ង​សម្លាប់​ពេលដេក​​ចាំផ្ទះ​ជាមួយ​ប្អូនស្រី', 'ខេត្តកំពង់ចាម ​៖ ស្រប​ពេល​ដែល​ម្តាយ​ជាប់រវល់​​ទៅ​ចូលរួមពិធី​កាត់​ចំណងដៃ​កូនស្រី​នៅ​ខេត្តកំពង់សោម​ដោយ​ទុក​ឲ្យ​កូនស្រី​២​នាក់​ដេកចាំ​ផ្ទះបែរជា​មាន​ជន​មិន​ស្គាល់​អត្ត​', 'http://kohsantepheapdaily.com.kh/article/115936.html', 'http://i.kspg.co/2015/3/888740ec-2165-414e-b8fe-c8df028e7f57.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('89', 'cl000002', 'u0000003', 'ដើមឆ្នាំ​2015​នេះ វិស័យ​ដីធ្លី​មាន​តម្លៃ​ខ្ពស់ តែ​សកម្មភាព​ទិញ​លក់​តិចតួច', 'រាជធានី​ភ្នំពេញ ៖ វិស័យ​ដីធ្លី​ក្នុង​រយៈពេល​២​ខែ​ដើមឆ្នាំ​២០១៥​នេះ ឃើញ​ថា តម្លៃ​បាន​កើនឡើង​ខ្ពស់ តែ​ចំពោះ​សកម្មភាព​ទិញ​លក់​វិញ​ហាក់ដូចជា​មាន​កម្រិត​តិចតួច បើ​ធៀប​នឹង​ឆ្នាំ​កន្លង​មក ។', 'http://kohsantepheapdaily.com.kh/article/116137.html', 'http://i.kspg.co/2015/3/7bfbe2e0-b737-4593-8852-4b465bac82d6.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('90', 'cl000002', 'u0000003', 'តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ ស​ប្តា​ហ៍​ទី​១ ខែមីនា ២០១៥', 'រាជធានី​ភ្នំពេញ ៖ តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ​នៅ​ផ្សារ​ធំ​ថ្មី​ក្នុង​ស​ប្តា​ហ៍​ទី​១ ខែមីនា ២០១៥ នេះ ឃើ​ញា​ថា មុខទំនិញ​ដូច​សាច់​គោបន្លែ និង​ផ្លែ​ឈើ មានការ​ប្រែប្រួល​តម្លៃ​តិចតួច  តែ​ដោយឡែក គ្រឿង​សមុទ្រ ', 'http://kohsantepheapdaily.com.kh/article/116136.html', 'http://i.kspg.co/2015/3/f1dfe1b0-3440-4cea-9e0c-72239e2044ea.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('91', 'cl000002', 'u0000003', 'លទ្ធផល​តម្លៃ​មូល​ប​ត្រ​បិទ​ទីផ្សារ​ប្រចាំ​ថ្ងៃ​ទី០៥ មីនា ២០១៥', 'រាជធានី​ភ្នំពេញ ៖  ក្រុមហ៊ុន​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា​បាន​ប្រកាស​លទ្ធផល​ការ​ទិញ​លក់​ភាគហ៊ុន​រដ្ឋាករទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​លើក​ទី​១ និង​លើក​ទី​២ ដែល​បញ្ចប់​នៅ​វេលា​ម៉ោង​១១ និង​៣០​នាទី​ព្រឹក សម្រាប់​ថ្ងៃ​ទី០៥ មីនា ២០១៥ ដល់​សាធារណជន​ឱ្យ​បាន​ជ្រាប​ថា តម្លៃ​មូល​ប​ត្រ​ក្នុង​ការ​ទិញ​ដូរ​ទាំង​២​លើក ៖', 'http://kohsantepheapdaily.com.kh/article/115991.html', 'http://i.kspg.co/2015/3/b438b54e-065a-4ac5-9287-15a51fd08d7b.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('92', 'cl000002', 'u0000003', 'លទ្ធផល​តម្លៃ​មូល​ប​ត្រ​បិទ​ទីផ្សារ​ប្រចាំ​ថ្ងៃ​ទី០៤ មីនា ២០១៥', 'រាជធានី​ភ្នំពេញ ៖  ក្រុមហ៊ុន​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា​បាន​ប្រកាស​លទ្ធផល​ការ​ទិញ​លក់​ភាគហ៊ុន​រដ្ឋាករទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​លើក​ទី​១ និង​លើក​ទី​២ ដែល​បញ្ចប់​នៅ​វេលា​ម៉ោង​១១ និង​៣០​នាទី​ព្រឹក សម្រាប់​ថ្ងៃ​ទី០៤ មីនា ២០១៥ ដល់​សាធារណជន​ឱ្យ​បាន​ជ្រាប​ថា តម្លៃ​មូល​ប​ត្រ​ក្នុង​ការ​ទិញ​ដូរ​ទាំង​២​លើក ៖', 'http://kohsantepheapdaily.com.kh/article/115917.html', 'http://i.kspg.co/2015/3/df032b7a-f49b-4e7e-b039-7047e1561b9a.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('93', 'cl000002', 'u0000003', 'លទ្ធផល​តម្លៃ​មូល​ប​ត្រ​បិទ​ទីផ្សារ​ប្រចាំ​ថ្ងៃ​ទី០៣ កុម្ភៈ ២០១៥', 'រាជធានី​ភ្នំពេញ ៖  ក្រុមហ៊ុន​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា​បាន​ប្រកាស​លទ្ធផល​ការ​ទិញ​លក់​ភាគហ៊ុន​រដ្ឋាករទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​លើក​ទី​១ និង​លើក​ទី​២ ដែល​បញ្ចប់​នៅ​វេលា​ម៉ោង​១១ និង​៣០​នាទី​ព្រឹក សម្រាប់​ថ្ងៃ​ទី០៣ កុម្ភៈ ២០១៥ ដល់​សាធារណជន​ឱ្យ​បាន​ជ្រាប​ថា តម្លៃ​មូល​ប​ត្រ​ក្នុង​ការ​ទិញ​ដូរ​ទាំង​២​លើក ៖', 'http://kohsantepheapdaily.com.kh/article/115842.html', 'http://i.kspg.co/2015/3/7f3fc019-614a-425d-8034-13e4b3644f68.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('94', 'cl000002', 'u0000003', 'ការ​​ជួញដូរ​​ភាគហ៊ុន និង​​ល្បែង​ស៊ីសង', 'ប្រធានបទ​ស​ប្តា​ហ៍​នេះ គឺជា​ប្រធានបទ​ដែល​គេ​តែង​បានឮ​ប្រជាជន​ខ្មែរ​និយាយ​ជា​ញឹកញាប់ ។ មនុស្ស​ភាគច្រើន​នៅ​មិន​ទាន់​ញែក​ដាច់​ពី​គ្នា​នៅឡើយ​រវាង​ការ​ជួញដូរ​ភាគហ៊ុន និង​ល្បែងស៊ីសង ។ ការ​ផ្សារ​ភ្ជាប់​ទ​ង្វើ​ទាំង​ពីរ​នេះ​ជាមួយ​គ្នា ភាគច្រើន​គឺ​ផ្អែក​លើ​ការ​ធ្លាប់​', 'http://kohsantepheapdaily.com.kh/article/115784.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('95', 'cl000002', 'u0000003', 'តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ​ស​ប្តា​ហ៍​ទី​4​នៃ​ខែកុម្ភៈ 2015', 'រាជធានី​ភ្នំពេញ ៖  តម្លៃ ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ​នៅ​ផ្សារ​ធំ​ថ្មី​ក្នុង​ស​ប្តា​ហ៍​ទី​៤​ខែកុម្ភៈ នេះ ឃើញ​ថា​ទំនិញ​ចាំបាច់​មួយ​ចំនួន​ដូច​ជា គ្រឿង សមុទ្រ​បន្លែ ផ្លែ​ឈើ បាន​ប្រែប្រួល​មក​រក​ថ្លៃដើម​វិញ', 'http://kohsantepheapdaily.com.kh/article/115705.html', 'http://i.kspg.co/2015/3/b4afe24d-3590-42d0-ad92-b9d10701ac34.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('96', 'cl000002', 'u0000003', 'ផលិតផល​កំប្លោក​ពង្រីក​ខ្លួន​ចូល​តំបន់​សក្តានុពល​ទេសចរណ៍ ខណៈ​មានការ​គាំទ្រ​ច្រើន', 'រាជធានី​ភ្នំពេញ ៖ សិប្បកម្ម​កំប្លោក​កម្ពុជា បន្ទាប់​ពី​ដំណើរការ​ជា​ច្រើន​ឆ្នាំ​មក ឃើញ​ថា ផលិតផល​មួយ​នេះ​ទទួល​បានការ​គាំទ្រ​ច្រើន​នៅ​លើ​ទីផ្សារ​ក្នុង​ស្រុក​ដូច​ជា សណ្ឋាគារ ផ្ទះសំណាក់ ', 'http://kohsantepheapdaily.com.kh/article/115704.html', 'http://i.kspg.co/2015/3/77c0e505-93d7-4903-abe8-c6cd7b7cd517.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('97', 'cl000002', 'u0000003', 'អ្នក​ភូមិ​ព្រែក​ក្របៅ​ទី​2 ងាក​មក​ចាប់​របរ​ត្បាញ​ក្រមា​វិញបើ​ទោះជា​តម្លៃ​លក់​ចេញ​លើ​ទីផ្សារ​បាន​ថោក​ក៏​ដោយ', 'ខេត្តកណ្តាល ៖ អ្នក​ភូមិ​ព្រែក​ក្របៅ​ទី​២ មួយ​ចំនួន​ធំ​បាន​ផ្អាក​របរ​ត្បាញ​ក្រមា​អស់​រយៈពេល​ជា​ច្រើន​ឆ្នាំ​មក​ហើយ ដោយសារ​តែ​ផលិតផល​របស់​ខ្លួន​បាត់បង់​ការ​គាំទ្រ​នៅ​លើ​ទីផ្សារ​។', 'http://kohsantepheapdaily.com.kh/article/115703.html', 'http://i.kspg.co/2015/3/507f8183-5d34-49fd-8c25-c34547296dfb.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('98', 'cl000002', 'u0000003', 'លទ្ធផល​តម្លៃ​មូល​ប​ត្រ​បិទ​ទីផ្សារ​ប្រចាំ​ថ្ងៃ​ទី២៦ កុម្ភៈ ២០១៥', '​រាជធានី​ភ្នំពេញ ៖  ក្រុមហ៊ុន​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា​បាន​ប្រកាស​លទ្ធផល​ការ​ទិញ​លក់​ភាគហ៊ុន​រដ្ឋាករទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​លើក​ទី​១ និង​លើក​ទី​២ ដែល​បញ្ចប់​នៅ​វេលា​ម៉ោង​១១ និង​៣០​នាទី​ព្រឹក', 'http://kohsantepheapdaily.com.kh/article/115592.html', 'http://i.kspg.co/2015/2/ef1ede5f-7126-46b2-991f-152d240a4df1.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('99', 'cl000002', 'u0000003', 'លទ្ធផល​តម្លៃ​មូល​ប​ត្រ​បិទ​ទីផ្សារ​ប្រចាំ​ថ្ងៃ​ទី២៥ កុម្ភៈ ២០១៥', 'រាជធានី​ភ្នំពេញ ៖  ក្រុមហ៊ុន​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា​បាន​ប្រកាស​លទ្ធផល​ការ​ទិញ​លក់​ភាគហ៊ុន​រដ្ឋាករទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​លើក​ទី​១ និង​លើក​ទី​២ ដែល​បញ្ចប់​នៅ​វេលា​ម៉ោង​១១ និង​៣០​នាទី​ព្រឹក សម្រាប់​ថ្ងៃ​ទី២៥ កុម្ភៈ ២០១៥ ដល់​សាធារណជន​ឱ្យ​បាន​ជ្រាប​ថា តម្លៃ​មូល​ប​ត្រ​ក្នុង​ការ​ទិញ​ដូរ​ទាំង​២​លើក ៖', 'http://kohsantepheapdaily.com.kh/article/115516.html', 'http://i.kspg.co/2015/2/88b9feb4-7fb7-4c2c-abd4-f5fd95d5f92c.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('100', 'cl000001', 'u0000003', 'លទ្ធផល​តម្លៃ​មូល​ប​ត្រ​បិទ​ទីផ្សារ​ប្រចាំ​ថ្ងៃ​ទី២៤ កុម្ភៈ ២០១៥', 'រាជធានី​ភ្នំពេញ ៖  ក្រុមហ៊ុន​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា​បាន​ប្រកាស​លទ្ធផល​ការ​ទិញ​លក់​ភាគហ៊ុន​រដ្ឋាករទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​លើក​ទី​១ និង​លើក​ទី​២ ដែល​បញ្ចប់​នៅ​វេលា​ម៉ោង​១១ និង​៣០​នាទី​ព្រឹក សម្រាប់​ថ្ងៃ​ទី២៤ កុម្ភៈ ២០១៥ ដល់​សាធារណជន​ឱ្យ​បាន​ជ្រាប​ថា', 'http://kohsantepheapdaily.com.kh/article/115455.html', 'http://i.kspg.co/2015/2/0607d6e5-2c67-4d8f-bcba-ef719b338ea2.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('101', 'cl000002', 'u0000003', 'ស្ករត្នោត​កំពង់ស្ពឺ​កំពុង​មាន​ទីផ្សារ​ក្នុងស្រុក​បន្ត​ហក់​ចូល​ទីផ្សារ​បរទេស', 'ខេត្តកំពង់ស្ពឺ ៖ ដើម​ត្នោត​ដែល​ដុះ​ឡើង​ត្រដែត​នៅ​វាលស្រែ​នៃ​ប្រទេស​កម្ពុជា ជា​និមិត្តរូប​នៃ​ទេសភាព​ស្រុក​ខ្មែរ ។ ដើម​ត្នោត​មាន​ប្រភព​មក​ពី​ប្រទេស​ឥណ្ឌា និង​មក​ដល់​អាស៊ី​', 'http://kohsantepheapdaily.com.kh/article/115417.html', 'http://i.kspg.co/2015/2/6da25615-ffa5-49d4-bb63-a4b803430e2d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('102', 'cl000008', 'u0000003', 'លទ្ធផល​តម្លៃ​មូល​ប​ត្រ​បិទ​ទីផ្សារ​ប្រចាំ​ថ្ងៃ​ទី23 កុម្ភៈ ២០១៥', 'រាជធានី​ភ្នំពេញ ៖  ក្រុមហ៊ុន​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា​បាន​ប្រកាស​លទ្ធផល​ការ​ទិញ​លក់​ភាគហ៊ុន​រដ្ឋាករទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​លើក​ទី​១ និង​លើក​ទី​២ ដែល​បញ្ចប់​នៅ​វេលា​ម៉ោង​១១ និង​៣០​នាទី​ព្រឹក សម្រាប់​ថ្ងៃ​ទី២៣ កុម្ភៈ ២០១៥', 'http://kohsantepheapdaily.com.kh/article/115380.html', 'http://i.kspg.co/2015/2/1f8bafc4-457e-4f01-8535-e0480f4e775d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('103', 'cl000002', 'u0000003', 'ខណៈ​ដំណាំ​តា​ង៉ែ​ន​ហៅ​មៀន​ប៉ៃលិនមាន​ហាងឆេង​ខ្ពស់ រថយន្ត​ថៃ​រាប់​សិប​គ្រឿង​ចូល​មក​ម៉ៅ​ទិញ​ដល់​ចំការ​​', 'ខេត្ត​ប៉ៃលិន ៖ គេ​សង្កេត​ឃើញ​ថា រថយន្ត​ថៃ​រាប់​សិប​គ្រឿង​បាន​បើក​ចូល​មក​ទិញ​ផ្លែ​តា​ង៉ែ​ន ហៅ​មៀន​ប៉ៃលិន​តាម​ច្រកព្រំដែន​អន្តរជាតិ​កម្ពុជា-ថៃ ខណៈ​ដំណាំ​មួយ​ប្រភេទ​នេះ​មាន​ហាងឆេង​ខ្ពស់​គួរ​ឱ្យ​កត់សម្គាល់', 'http://kohsantepheapdaily.com.kh/article/115357.html', 'http://i.kspg.co/2015/2/fa2ad671-b4a9-4e90-bd7b-0908d4de1e1d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('104', 'cl000008', 'u0000003', 'យល់​ដឹង​ពី​ការ​ជួញដូរ​ផ្សា​រមូល​ប​ត្រ​នៅ​កម្ពុជា', 'កត្តា​អ្វីខ្លះ​ដែល​អាច​ធ្វើ​ឱ្យ​ថ្លៃ​ហ៊ុន​ប្រែប្រួល ? ជា​ទូទៅ ថ្លៃ​ភាគហ៊ុន​ប្រែប្រួល​ជា​រៀងរាល់ថ្ងៃ ។ ការ​ប្រែប្រួល​នេះ​អាច​និយាយ​បាន​យ៉ាង​សាមញ្ញ​ថា ដោយសារ​កម្លាំង​នៃ​ការ​ផ្គត់ផ្គង់​និង​តម្រូវការ​នៅ​លើ​', 'http://kohsantepheapdaily.com.kh/article/115308.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('105', 'cl000008', 'u0000003', 'បរិមាណ​ទំនិញ​ឆ្លងកាត់​កំពង់​ផែ​ស្វ័​យ​ត​ក្រុងព្រះសីហនុ​ក្នុង​ខែមករា ធ្លាក់​ចុះ​1,93%', 'ខេត្ត​ព្រះ​សីហ​នុ ៖ ប្រធាន​ការិយាល័យ​ស្ថិតិ​និង​ទំនាក់​ទ​ន​ង​នៃ​កំពង់​ផែ​ស្វ័​យ​ត​ក្រុងព្រះសីហនុ លោក អ៊ុ​ក តុ​មេធី បាន​ឲ្យ​ដឹង​ថា ក្នុង​ខែមករា​កន្លង​ទៅ​នេះ បរិមាណ​ទំនិញ​ឆ្លងកាត់​កំពង់​ផែស្វ័​យ​ត​ក្រុងព្រះសីហនុ ', 'http://kohsantepheapdaily.com.kh/article/115290.html', 'http://i.kspg.co/2015/2/9da70852-3d21-4573-b209-900a3e269b5c.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('106', 'cl000008', 'u0000003', 'លទ្ធផល​តម្លៃ​មូល​ប​ត្រ​បិទ​ទីផ្សារ​ប្រចាំ​ថ្ងៃ​ទី១៩ កុម្ភៈ ២០១៥', 'រាជធានី​ភ្នំពេញ ៖  ក្រុមហ៊ុន​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា​បាន​ប្រកាស​លទ្ធផល​ការ​ទិញ​លក់​ភាគហ៊ុន​រដ្ឋាករទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​លើក​ទី​១ និង​លើក​ទី​២ ដែល​បញ្ចប់​នៅ​វេលា​ម៉ោង​១១ និង​៣០​នាទី​ព្រឹក សម្រាប់​ថ្ងៃ​ទី១៩ ធ្នូ ២០១៥ ', 'http://kohsantepheapdaily.com.kh/article/115161.html', 'http://i.kspg.co/2015/2/015e289b-b920-4675-b01a-02507267404e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('107', 'cl000008', 'u0000003', 'តម្លៃ​ទំនិញប្រើប្រាស់​ប្រចាំ​ថ្ងៃ​នៅ​ស​ប្តា​ហ៍​ទី​៣​ដើមខែ កុម្ភៈ ២០១៥', 'រាជធានី​ភ្នំពេញ​៖ តម្លៃ​ទំនិញប្រើប្រាស់​ក្នុង​ឱកាស​បុណ្យចូលឆ្នាំ​ចិន នា​ថ្ងៃ​ទី​១៧ កុម្ភៈ នៅ​ផ្សារ​ធំ​ថ្មី ។ ចំពោះ​ទំនិញ​គ្រឿង​សមុទ្រ ចាំបាច់​មួយ​ចំនួន និង​ត្រី សាច់ បន្លែ និង​ផ្លែ​ឈើ ឃើញ​ថា​ឡើងថ្លៃ​ទាំងអស់ ', 'http://kohsantepheapdaily.com.kh/article/115066.html', 'http://i.kspg.co/2015/2/56bd3cf0-68da-40f0-a25b-c2fb596ea6a3.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('108', 'cl000008', 'u0000003', 'លទ្ធផល​តម្លៃ​មូល​ប​ត្រ​បិទ​ទីផ្សារ​ប្រចាំ​ថ្ងៃ​ទី១៧ កុម្ភៈ ២០១៥', 'រាជធានី​ភ្នំពេញ ៖  ក្រុមហ៊ុន​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា​បាន​ប្រកាស​លទ្ធផល​ការ​ទិញ​លក់​ភាគហ៊ុន​រដ្ឋាករទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​លើក​ទី​១ និង​លើក​ទី​២ ដែល​បញ្ចប់​នៅ​វេលា​ម៉ោង​១១ និង​៣០​នាទី​ព្រឹក សម្រាប់​ថ្ងៃ​ទី១៧ កុម្ភៈ ២០១៥ ដល់​សាធារណជន​ឱ្យ​បាន​ជ្រាប​ថា ', 'http://kohsantepheapdaily.com.kh/article/115042.html', 'http://i.kspg.co/2015/2/d8743182-2673-4b52-a571-3cf1d9acdc92.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('109', 'cl000008', 'u0000003', 'លទ្ធផល​តម្លៃ​មូល​ប​ត្រ​បិទ​ទីផ្សារ​ប្រចាំ​ថ្ងៃ​ទី១៦ កុុម្ភៈ ២០១៥', 'រាជធានី​ភ្នំពេញ ៖  ក្រុមហ៊ុន​ផ្សា​រមូល​ប​ត្រ​កម្ពុជា​បាន​ប្រកាស​លទ្ធផល​ការ​ទិញ​លក់​ភាគហ៊ុន​រដ្ឋាករទឹក​ស្វយ័ត​រាជធានី​ភ្នំពេញ​លើក​ទី​១ និង​លើក​ទី​២ ដែល​បញ្ចប់​នៅ​វេលា​ម៉ោង​១១ និង​៣០​នាទី​ព្រឹក ', 'http://kohsantepheapdaily.com.kh/article/114976.html', 'http://i.kspg.co/2015/2/6029c555-6e67-4a0b-9a59-3cd1292c9afb.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('110', 'cl000002', 'u0000003', '​តើ​ធ្វើ​ដូច​ម្តេច​ដើម្បី​ទទួល​បាន​ព័ត៌មាន​ទាក់ទង​នឹង​ផ្សារ​ហ៊ុន?', 'ផ្សារ​ហ៊ុន​បច្ចុប្បន្ន ជា​ផ្សារ​អេ​ឡិច​ត្រូ​និក ងាយស្រួល​ក្នុង​ការ​ចូល​/​ចេញ (​ទិញ​/​លក់​) និង​អាច​ប្រែប្រួល​យ៉ាង​ឆាប់រហ័ស ។ ដំណើរ​ឡើង​ចុះ​របស់​វា គឺ​ផ្អែក​លើ​ព័ត៌មាន​ដែល​ទទួល​បាន​ដោយ​វិនិយោគិន ', 'http://kohsantepheapdaily.com.kh/article/114933.html', 'http://i.kspg.co/2015/2/c819f136-ece3-4e2b-bb74-46b4470516d7.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('111', 'cl000008', 'u0000003', 'ក្នុង​ខែមករា កម្ពុជា​នាំ​ចេញ​អង្ករទៅ​បរទេស​មានការ​កើនឡើង​ជិត​67%', 'រាជធានី​ភ្នំពេញ ៖ អនុប្រធាន​សហព័ន្ធ​ស្រូវ​អង្ករ​កម្ពុជា លោក ឧកញ៉ា គឹ​ម សា​វុ​ធ បាន​មាន​ប្រសាសន៍​ថា  ក្នុង​ខែមករា​កន្លង​ទៅ កម្ពុជា​នាំ​ចេញ​អង្ករ​ទៅ​បរទេស​កើនឡើង​ជិត​៦៧% បើ​ធៀប​នឹង​រយៈពេល​ដូច​គ្នា​', 'http://kohsantepheapdaily.com.kh/article/114890.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('112', 'cl000008', 'u0000003', 'ការ​នាំ​ចេញ​មៀន​ប៉ៃលិន​ទៅ​ទីផ្សារ​ប្រទេសចិន​កើនឡើង​ក្នុង​រដូវ​បុណ្យចូលឆ្នាំ​ចិន​នេះ', 'ខេត្ត​ប៉ៃលិន ៖ បច្ចុប្បន្ន មៀន​ប៉ៃលិន(​ផ្លែ​តា​ង៉ែ​ន​) ត្រូវ​បាន​ក្រុមហ៊ុន​ចិន និង​អាជីវករ​ក្នុងស្រុក កុម្ម៉ង់​ទិញ​យ៉ាងច្រើន​សម្រាប់​ផ្គត់ផ្គង់​តម្រូវការ​ក្នុង„ҡހាស​បុណ្យចូលឆ្នាំ​ចិន​ខាង​មុខ​នេះ  ។', 'http://kohsantepheapdaily.com.kh/article/114889.html', 'http://i.kspg.co/2015/2/bc43e3b2-7885-4b18-8633-2b37099d2a3d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('113', 'cl000008', 'u0000003', 'ឆ្នាំ​2014 រថយន្ត PORSCHE លក់​កើនឡើង15% ខណៈ​ឆ្នាំ​2015 នេះ​មានការ​កុំម៉ង់​ទិញ​ច្រើន', 'រាជធានី​ភ្នំពេញ ៖ រថយន្ត Porsche ដែល​ជា​ប្រភេទ​រថយន្ត​ទំនើប​ផលិត​នៅ​ប្រទេស​អា​ល្លឺ​ម៉​ង់ ហើយ​បាន​ដាក់​លក់​លើ​ទីផ្សារ​កម្ពុជា​ក្នុង​រយៈពេល​ប៉ុន្មាន​ឆ្នាំ​ចុង​ក្រោយ​នេះ​មាន អតិថិជន​គាំទ្រ​កាន់តែ​ច្រើន​ទៅ​ៗ ។ ', 'http://kohsantepheapdaily.com.kh/article/114888.html', 'http://i.kspg.co/2015/2/0b5d7c5c-5c82-4764-bb01-fceb1578e2fa.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('114', 'cl000002', 'u0000003', 'ម៉ោង៣រសៀលនេះ ​ក្រុមងការងារ​ CPP និង CNRP ​បានចូលដល់រដ្ឋសភា​និងផ្តើមជជែកគ្នាហើយ', 'រាជធានីភ្នំពេញ ៖​ ដូចតាមការគ្រោងទុក ​ក្រុមការងារ​ចរចារវាង​គណបក្សប្រជាជនកម្ពុជា​ (CPP) ​និង ​គណបក្សសង្គ្រោះជាតិ​ (CNRP)​ បានចាប់ផ្តើមជជែក​', 'http://kohsantepheapdaily.com.kh/article/115781.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('115', 'cl000002', 'u0000003', 'សម្តេចតេជោ ​ហ៊ុន សែន ៖ ​រសៀលនេះ​ក្រុមការងារទំាងពីរ​នឹងសរសេរបន្ថែម​បទប្បញ្ញត្តិ​ទៅក្នុង​សេចក្តីព្រាងស្នើ​វិសោធនកម្ម​ច្បាប់បោះឆ្នោត', 'រាជធានីភ្នំពេញ ​៖ សម្តេច​អគ្គមហាសេនាបតីតេជោ ​ហ៊ុន សែន ​នាយករដ្ឋមន្រ្តីនៃ​ព្រះរាជាណាចក្រកម្ពុជា​បានមានប្រសាសន៍​អបអរសាទរ​ចំពោះលទ្ឋផល​', 'http://kohsantepheapdaily.com.kh/article/115774.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('116', 'cl000002', 'u0000003', 'រសៀលនេះ​ ក្រុមការងារ CPP ​និង CNRP ​បន្តជជែកគ្នា​លើសេចក្តីស្នើវិសោធនកម្ម​ច្បាប់បោះឆ្នោត', 'រាជធានីភ្នំពេញ ៖ ​ក្រុមការងារ​ចរចារវាង​គណបក្សប្រជាជនកម្ពុជា ​(CPP) ​និង គណបក្សសង្គ្រោះជាតិ​ (CNRP) ​បានបន្តពិភាក្សាគ្នា​ទៀតលើសេចក្តីស្នើ​', 'http://kohsantepheapdaily.com.kh/article/115409.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('117', 'cl000002', 'u0000003', 'ជម្លោះ​ហ្វ៊ុ​ន​ស៊ិន​ប៉ិ​ច លោក​ញឹក ប៊ុ​ន​ឆៃ អនុប្រធាន​ទី​២​ប្រើ​ត្រា​មិន​បាន ខណៈ​ប្រធាន​និង​អនុ​ទី​២​នៅ​ក្រៅប្រទេស', 'រាជធានី​ភ្នំពេញ ៖ លោក សាយ ហាក់ អគ្គលេខាធិការ​ប្រតិបត្តិ និង​ជា​មន្ត្រីជាន់ខ្ពស់​គណបក្ស​ហ្វ៊ុ​ន​ស៊ិ​ន​ប៉ិច កាលពី​ថ្ងៃ​ទី​២០ ខែកុម្ភៈ ឆ្នាំ​២០១៥ បាន​ចេញ​សេចក្តី​ប្រកាស', 'http://kohsantepheapdaily.com.kh/article/115382.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('118', 'cl000002', 'u0000003', 'CNRP​ ស្នើ​មិនឲ្យមន្រ្តីតុលាការ​​ យោធា​ និង ប៉ូលិស​ចូលរួមឃោសនា​បោះឆ្នោត​ តែ CPP មិនព្រម', 'រាជធានីភ្នំពេញ ​៖ ក្រុមការងារ​ចរចាខាងគណបក្សសង្គ្រោះជាតិ​(CNRP) ​បានស្នើសមភាគី​មិនឲ្យមន្រ្តីតុលាការ ​យោធា ​និង​ ប៉ូលិសចូលរួមក្នុងការ​', 'http://kohsantepheapdaily.com.kh/article/115011.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('119', 'cl000002', 'u0000003', 'CNRP ​ដាក់លោក ​ដាង ចំរើន​ ជំនួសលោក វ៉ាន​ សំអៀង ​ដែលលាលែងតំណែង', 'រាជធានីភ្នំពេញ​ ៖ គណបក្សសង្គ្រោះជាតិ​បានសម្រេច​បញ្ចូលឈ្មោះលោក ​ដាង ចំរើន ​ដែលឈរលេខរៀងទី៤​ជាបេក្ខភាព​តំណាងរាស្រ្ត​មណ្ឌល​កំពត​', 'http://kohsantepheapdaily.com.kh/article/115010.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('120', 'cl000002', 'u0000003', 'ក្រុមការងារ ​CPP និង CNRP ​បន្តពិភាក្សាគ្នា​លើចំណុចដែលមិនទាន់ឯកភាពគ្នា', 'រាជធានីភ្នំពេញ ​៖ ក្រុមការងារចរចា​រវាងគណបក្ស​ប្រជាជនកម្ពុជា(CPP) ​និងក្រុមការងារគណបក្ស​សង្គ្រោះជាតិ(CNRP) ​បានបន្តជជែកគ្នា​លើវិសោធនកម្ម​', 'http://kohsantepheapdaily.com.kh/article/114995.html', 'http://i.kspg.co/2015/2/44da7b29-332e-4396-82f0-a58ff678607f.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('121', 'cl000002', 'u0000003', 'សម្តេច​ក្រុមព្រះ​ស្ន✢ߡ޲្"ޙ​ "ទទក" ​នៅកណ្តាល', 'រាជធានីភ្នំពេញ ​៖ សម្តេចក្រុម​ព្រះ នរោត្តមរណប្ញទ្ឋិ ​ព្រះប្រធាន​គណបក្សហ្វ៊ុនស៊ិនប៉ិច​បានស្នើឲ្យ​ទូរទស្សន៍ជាតិកម្ពុជា​(ទទក)​ ស្ថិតនៅអព្យាក្រឹត​ក្នុងការ​ផ្សាយ​​ព័ត៌មាន​របស់ខ្លួន ។ ', 'http://kohsantepheapdaily.com.kh/article/114992.html', 'http://i.kspg.co/2015/2/d74868c3-1c3c-4f41-9ec5-34fbedc0a543.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('122', 'cl000002', 'u0000003', 'CPP​ និង CNRP​ ព្រមព្រៀងគ្នា​កំណត់រយៈពេល២០ថ្ងៃ​សម្រាប់ឃោសនាបោះឆ្នោត​ និង ហែក្បួន៤ថ្ងៃ', 'រាជធានីភ្នំពេញ​ ៖ ក្រុមការងារ​ចរចា​រវាង​គណបក្សប្រជាជនកម្ពុជា​​ ( CPP ) ​​និង​គណបក្សសង្គ្រោះជាតិ​ ​( CNRP ) ​​បានព្រមព្រៀងគ្នា​លើសេចក្តី​ស្នើ​', 'http://kohsantepheapdaily.com.kh/article/114838.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('123', 'cl000002', 'u0000003', 'តំណាងរាស្រ្ត ​CNRP ​លោក វ៉ាន សំអឿន​ សុំលាលែងពីតំណែង ​ទំនងជាមក​ចូលរួមជាមួយ CPP', 'រាជធានីភ្នំពេញ ​៖ តំណាងរាស្រ្ត​មណ្ឌលកំពតមកពី​គណបក្សសង្គ្រោះជាតិ​ ( CNRP) ​លោក វ៉ាន សំអឿន ​បានដាក់លិខិត​លាលែងពីតំណាងរាស្រ្ត​ហើយ​នៅថ្ងៃទី១៣ ​កុម្ភៈ ​២០១៥ ។ ', 'http://kohsantepheapdaily.com.kh/article/114834.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('124', 'cl000002', 'u0000003', 'CNRP​ ស្នើឲ្យបង្កើតទីតំាង​ចុះឈ្មោះបោះឆ្នោត​នៅព្រំដែន​សម្រាប់ពលករធ្វើការនៅថៃ តែ​ CPP ​មិនទាន់ឯកភាព', 'រាជធានីភ្នំពេញ ៖​ ក្រុមការងារ​ចរចាខាងគណបក្សសង្គ្រោះជាតិ​ (CNRP) ​បានស្នើឲ្យមាន​ការបង្កើតទីតំាង​ចុះឈ្មោះបោះឆ្នោត​មួយកន្លែង​​នៅក្បែរ​ព្រំដែន', 'http://kohsantepheapdaily.com.kh/article/114532.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('125', 'cl000002', 'u0000003', 'ក្រុមការងារ​ CPP​ និង CNRP​ បន្តពិភាក្សាគ្នា​នូវចំណុចដែល​មិនទាន់ព្រមព្រៀងគ្នា', 'រាជធានីភ្នំពេញ ៖ ​ក្រុមការងារចរចា​រវាងគណបក្សប្រជាជនកម្ពុជា​ ( CPP ) និង ​គណបក្សសង្គ្រោះជាតិ​ ( CNRP )​ បានបើកការ​ជជែកគ្នា​បន្តទៀតលើ​ចំណុច​ដែល​​សេសសល់មិនទាន់ព្រមព្រៀងគ្នាលើ​សេចក្តីស្នើវិសោធនកម្ម​ច្បាប់បោះឆ្នោត ។ ', 'http://kohsantepheapdaily.com.kh/article/114521.html', 'http://i.kspg.co/2015/2/37e78361-b605-40f1-92df-07b8047a8ccc.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('126', 'cl000002', 'u0000003', 'ការបន្ថែមចំនួន​អាសនៈសភា​និងការកំណត់​អត្តសញ្ញាណអ្នកបោះឆ្នោត ​ក្រុមការងារទំាងពីរ​នៅតែមិនទាន់​ឯកភាពគ្នា', 'រាជធានីភ្នំពេញ ៖​ ការពិភាក្សាលើ​ចំណុច​សេសល់ដែលមិនទាន់​ឯកភាពគ្នាលើសេចក្តីស្នើវិសោធនកម្ម​ច្បាប់បោះឆ្នោត​រវាងក្រុមការងារ​គណបក្ស​ប្រជាជន​', 'http://kohsantepheapdaily.com.kh/article/114379.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('127', 'cl000002', 'u0000003', 'ម៉ោង៣រសៀលនេះ​ ក្រុមការងារ​ CPP និង CNRP​ បន្តជជែកគ្នា​លើចំណុច​ដែលមិនទាន់ឯកភាពគ្នា', 'រាជធានីភ្នំពេញ ៖​ ក្រុមការងារ​ចរចារវាងគណបក្សប្រជាជនកម្ពុជា ​( CPP ) ​និង ​គណបក្សសង្គ្រោះជាតិ ​( CNRP ) ​បានបន្តពិភាក្សាគ្នា​ចំណុចសេសសល់​ដែលមិនទាន់​បានឯកភាពគ្នា​លើសេចក្តីស្នើវិសោធនកម្ម​ច្បាប់បោះឆ្នោត ។ ', 'http://kohsantepheapdaily.com.kh/article/114365.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('128', 'cl000002', 'u0000003', 'ក្រុមការងារCPP​ និងCNRP​ មិនទាន់ឯកភាពគ្នា​រឿងសង្គមស៊ីវិល​បញ្ចេញមតិរិះគន់​បេក្ខជនតំណាងរាស្រ្ត', 'រាជធានីភ្នំពេញ ​៖ ក្រុមការងារ ​CPP និង CNRP ​បានបញ្ចប់ការជជែកគ្នា​នៅក្នុងជំពូកទី៨​ នៃសេចក្តីស្នើ​វិសោធនកម្មច្បាប់​បោះឆ្នោតហើយនៅ​ល្ងាចថ្ងៃទី០២ ', 'http://kohsantepheapdaily.com.kh/article/114075.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('129', 'cl000002', 'u0000003', 'ក្រុមការងារ ​CPP ​និង CNRP​ បន្តជជែកគ្នា​អំពីសេចក្តីស្នើ​វិសោធនកម្ម​ច្បាប់បោះឆ្នោត', 'រាជធានីភ្នំពេញ ៖ ​ក្រុមការងារ​ចរចារវាង​គណបក្សប្រជាជនកម្ពុជា​ (CPP)​ និង ​គណបក្សសង្គ្រោះជាតិ ​(CNRP)​ បន្តពិភាក្សាគ្នា​ទៀតនៅជំពូកទី៨នៃ​', 'http://kohsantepheapdaily.com.kh/article/114059.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('130', 'cl000002', 'u0000003', 'សម្តេចក្រុមព្រះ​គំាទ្រច្បាប់ដែលមិន​អនុញ្ញាតឲ្យ​ព្រះសង្ឃចូលរួមបោះឆ្នោត', 'រាជធានីភ្នំពេញ ​៖ សម្តេចក្រុមព្រះ​នរោត្តមរណប្ញទ្ឋិ ​ព្រះប្រធាន​គណបក្សហ្វ៊ុនស៊ិនប៉ិច​បានមានបន្ទូលគំាទ្រ​ប្រសាសន៍សម្តេចតេជោ​ ហ៊ុន សែន ​ដែលគ្រោង​', 'http://kohsantepheapdaily.com.kh/article/113950.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('131', 'cl000002', 'u0000003', 'សម្តេចក្រុមព្រះ​ ៖ គណបក្សហ្វ៊ុនស៊ិនប៉ិច​នឹងទទួលបាន​អាសនៈសភាពី​០៨ទៅ១០កៅអី​នៅអាណត្តិក្រោយ', 'រាជធានីភ្នំពេញ​ ៖ ព្រះប្រធាន​គណបក្សហ្វ៊ុនស៊ិនប៉ិច​ សម្តេចក្រុមព្រះ​នរោត្តមរណប្ញទ្✢̡޷​បានមានបន្ទូលថា​ "មានគេទស្សន៍ទាយថា​ នៅក្នុង​ការបោះឆ្នោត​អាណត្តិទី៦​​ ឆ្នាំ២០១៨ខាងមុខ​ គណបក្សហ្វ៊ុនស៊ិនប៉ិច​ន✢ڡބទទួលបាន​អាសនៈសភា​ដោយខ្លះថា៨ ​ខ្លះថា១០កៅអី " ។ ', 'http://kohsantepheapdaily.com.kh/article/113949.html', 'http://i.kspg.co/2015/1/00718fb5-826f-43f2-bfdb-576e8c86f9ce.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('132', 'cl000002', 'u0000003', 'រសៀលនេះ ​CPP បើកមហាសន្និបាត​វិសាមញ្ញនៅកោះពេជ្រ ​ខណៈមេបក្សប្រឆាំង​ទំាងពីរទៅជួប​សកម្មជន​នៅកំពង់ចាម', 'រាជធានីភ្នំពេញ ៖ ​គណបក្សប្រជាជនកម្ពុជា​ (CPP) ​បើកមហាសន្និបាត​វិសាមញ្ញតំណាងទូទំាងប្រទេស​ នឹងចាប់បើក​ជាផ្លូវការនៅម៉ោង២និង៣០នាទី​រសៀលថ្ងៃទី៣០​ មករា ២០១៥ ​នៅមជ្ឈមណ្ឌលកោះពេជ្រ ​ក្នុងរាជធានីភ្នំពេញ ។ ', 'http://kohsantepheapdaily.com.kh/article/113927.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('133', 'cl000002', 'u0000003', 'សម្តេចក្រុមព្រះ​នឹងសំណេះសំណាល​ជាមួយសមាជិក​គណៈកម្មការ​នាយកជាង​២០០នាក់នៅរសៀលនេះ', 'រាជធានីភ្នំពេញ ៖ បើគ្មានអ្វីប្រែប្រួលនោះទេ​ សម្តេចក្រុមព្រះ​នរោត្តមរណប្ញទ្ឋិ ​ព្រះប្រធាន​គណបក្សហ្វ៊ុនស៊ិនប៉ិច ​នឹងជួបសំណេះសំណាល​ជាមួយ​សមាជិក​', 'http://kohsantepheapdaily.com.kh/article/113916.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('134', 'cl000002', 'u0000003', 'ជំនួប៤៥នាទី​រវាងលោក ​ស ខេង ​និង លោកសម រង្ស៊ី​ បានត្រឹមរក្សា​កិច្ចព្រមព្រៀង​ថ្ងៃ២២កក្កដា', 'រាជធានីភ្នំពេញ​ ៖ ទីបំផុតទៅជំនួបរវាងលោក ​ស ខេង​ និង លោក សម រង្ស៊ី ​នៅវិមានរដ្ឋសភាជាតិ​​បានបិទបញ្ចប់ហើយ​​នៅម៉ោង៩និង៤៥នាទីព្រឹក​ថ្ងៃទី២៩​មករា​នេះ​​ដោយប្រើពេល​ប្រមាណ៤៥នាទីប៉ុណ្ណោះ ។ ', 'http://kohsantepheapdaily.com.kh/article/113849.html', 'http://i.kspg.co/2015/1/b850ddf7-a0fc-4a1e-a641-10e3d232763e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('135', 'cl000002', 'u0000003', 'CPP​ ធ្វើសមាជ០៣ថ្ងៃ​ ដោយចាប់ផ្តើម​ពីថ្ងៃស្អែក', 'រាជធានីភ្នំពេញ ៖​ គណបក្សប្រជាជនកម្ពុជា​ (CPP) ​នឹងរៀបចំធ្វើ​សមាជវិសាមញ្ញ​តំណាងទូទំាងប្រទេស​​ ដើម្បីធ្វើការកែទម្រង់​មួយចំនួន​ទៅលើកម្មវិធី​គោល​នយោបាយ​ និងពិនិត្យឡើងវិញ​នូវសកម្មភាព​ការងារបក្សនាពេលកន្លងមក ។ ', 'http://kohsantepheapdaily.com.kh/article/113835.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('136', 'cl000002', 'u0000003', 'លោក ស ខេង​ ​និង លោក សម រង្ស៊ី​ ​ជួបពិភាក្សាគ្នា​​នៅព្រឹកស្អែក', 'រាជធានីភ្នំពេញ​​ ៖ បើគ្មានអ្វីប្រែប្រួលនោះទេ​​ លោកឧបនាយករដ្ឋមន្រ្តី​ ​ស ខេង​ ​និងជាមន្រ្តីជាន់ខ្ពស់​​គណបក្សប្រជាជនកម្ពុជា​ ​និង ​​លោក សម រង្ស៊ី ', 'http://kohsantepheapdaily.com.kh/article/113774.html', 'http://i.kspg.co/2015/1/32e71ef8-1403-493f-8cd7-5852e667489f.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('137', 'cl000002', 'u0000003', 'មេបក្សប្រឆាំងទំាងពីររូបចុះសំណេះសំណាលជាមួយសកម្មជននៅខេត្តរតនគិរី ស្ទឹងត្រែង និង ខេត្តកំពត', 'រាជធានីភ្នំពេញ ៖ ប្រធាននិងអនុប្រធានគណបក្សសង្គ្រោះជាតិ ​លោក សម រង្ស៊ី និង លោក កឹម សុខា បាននិងកំពុង​ចុះសំណេះសំណាល​ជាមួយ​សកម្មជន​​​​​និងក្រុមអ្នកគំាទ្រ​ដាច់ដោយឡែក​ពីគ្នា​​នៅក្នុងខេត្តរតនគិរី​ ស្ទឹងត្រែង ​និង ​ខេត្តកំពត ​នៅថ្ងៃទី២៥ មករា ២០១៥ ។ ', 'http://kohsantepheapdaily.com.kh/article/113529.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('138', 'cl000002', 'u0000003', 'មេបក្សប្រឆាំង​ទំាងពីរចុះសំណេះសំណាល​ជាមួយសកម្មជន​នៅខេត្តត្បូងឃ្មុំ​និងក្រចេះ', 'រាជធានីភ្នំពេញ ៖​ ប្រធាននិង​អនុប្រធានគណបក្សសង្គ្រោះជាតិ ​លោក សម រង្ស៊ី ​និង លោក កឹម​ សុខា ​នៅព្រឹកថ្ងៃទី២៣ ​មករា ២០១៥ ​បានចុះទៅសំណេះ​', 'http://kohsantepheapdaily.com.kh/article/113470.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('139', 'cl000002', 'u0000003', 'រថយន្ត ​Luxus 570 ​បុកគោយន្តនិង​រេទៅប៉ះរថយន្តក្រុង​ បង្កការកកស្ទះ​នៅកំណាត់​ផ្លូវជាតិលេខ៤', 'ខេត្តព្រះសីហនុ ​៖ បើតាមអ្នកឆ្លើយឆ្លង​ព័ត៌មានកោះសន្តិភាព​បានឲ្យដឹងនៅប៉ុន្មាននាទី​មុននេះថា រថយន្តLuxus 570 មួយគ្រឿង បានបុកគោយន្ត​និង​រេ​', 'http://kohsantepheapdaily.com.kh/article/116180.html', 'http://i.kspg.co/2015/3/0998c74c-787a-4901-82f4-8f7d215f76dc.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('140', 'cl000002', 'u0000003', 'ហ៊ីយ៉ាន់​ដាយ​បុក​ម៉ូតូ​ឌ្រីម​សេ​១២៥​ស្លាប់​២នាក់​របួស​ធ្ងន់​​ម្នាក់', 'ខេត្តកំពង់ឆ្នាំង ៖ មនុស្ស​ម្នាក់​បាន​ស្លាប់​នៅ​នឹង​កន្លែង និង​២​នាក់​ទៀត​បាន​រង​របួស​យ៉ាងធ្ងន់ធ្ងរ ក្នុង​នោះម្នាក់​ទៀត​បាន​ស្លាប់​ពេល​ដឹក​បញ្ជូន​ទៅ​សង្គ្រោះ​នៅ​មន្ទីរពេទ្យ​ក្រុងភ្នំពេញ​ក្នុង​ហេតុ​ការណ៍​​គ្រោះថ្នាក់ចរាចរ​ណ៏​មួយ​​ដែល​បាន​កើតឡើង​​កាល​ពី​វេលា​ម៉ោង​៥​ និង​៣០​នាទី​ល្ងាច ថ្ងៃ​ទី​០៧ ខែ​មីនា ឆ្នាំ​២០១៥', 'http://kohsantepheapdaily.com.kh/article/116145.html', 'http://i.kspg.co/2015/3/32c315de-c7bf-417f-b435-55690f9cbd49.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('141', 'cl000002', 'u0000003', 'ស្រវឹង​ជិះ​ម៉ូតូ​ចេញពី​រោងការ​ទៅ​ផ្ទះ​បុក​គូទ​រថយន្ត​ស្លាប់', 'ខេត្ត​ត្បូងឃ្មុំ ៖ យុវជន​វ័យ​២២​ឆ្នាំ​ម្នាក់​ឈ្មោះ​អាត អង់ នៅ​ភូមិ​ថ្មី ឃុំ​ត្រពាំង​ផ្លុ​ង ស្រុក​ពញាក្រែក បាន​ស្លាប់​ភ្លាម​ៗ​យ៉ាង​អ​ណោ​ច​អា​ធ័ម​ដោយ​សារ​ជិះ​ម៉ូតូ​​ទៅ​បុក​រថយន្ត​ម៉ាក DONG.A.MOTO ពាក់​ស្លាក​លេខ​កំពង់ចាម 5A-0001', 'http://kohsantepheapdaily.com.kh/article/116130.html', 'http://i.kspg.co/2015/3/dfeefe3e-d26d-4fee-82e4-27c1fe1792bf.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('142', 'cl000002', 'u0000003', 'បើករថយន្ត​បុក​ក្បាល​ព្រួញ​ពុះ​ចែក​ទ្រូងផ្លូវ រួច​បើក​រត់​អាវុធហត្ថ​ចាប់​សុំ​សម្រួល​មិន​បាន​ឈ្លោះ​ជាមួយ​សមត្ថកិច្ច​អាងខ្នង​អ្នកធំ', 'រាជធានី​ភ្នំពេញ​៖​បុរស​ម្នាក់​បាន​បើករថយន្ត​ព្រូស​ជ្រុល​ទៅ​បុក​ក្បាល​ព្រ​ញ​ថ្ម​ពុះ​ចែក​ទ្រូងផ្លូវ បណ្តាល​ឲ្យ​រង​ការ​ខូច​ខា ហើយ​បាន​ព្យាយាម​បើក​រត់គេច​ទៅ​ប្រមា​ន​២០០​ម៉ែត្រ ក៏​ត្រូវ​អាវុធហត្ថ ខណ្ឌចំការមន​ធ្វើការ​ឃាត់ រួច​ប​ញ្ចូ​ន​អ្នកបើកបរ​ទៅ​កន្លែង​កើតហេតុ​វិញ', 'http://kohsantepheapdaily.com.kh/article/116125.html', 'http://i.kspg.co/2015/3/8776d6a2-67c2-4eb8-8242-14291521ec73.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('143', 'cl000002', 'u0000003', 'រថយន្ត​និង​ម៉ូតូ​បុក​គ្នា​នៅ​ផ្លូវជាតិ​លេខ​៥​របួសធ្ងន់​២​នាក់​ស្លាប់​ម្នាក់', 'ខេត្ត​កំពង់​ឆ្នាំ​៖ យ៉ាង​ហោច​ណាស់​មនុស្ស​២​នាក់​បាន​រង​របួស​ធ្ងន់ធ្ងរ និង​ម្នាក់​ទៀត​បាន​ស្លាប់​នៅ​និង​កន្លែង ខណៈ​ពេល​ដែល​រថយន្ត​ហ៊ី​យ៉ា​ន់​ដាយ​និង​ម៉ូតូ​ហុងដា​សេ​១២៥​បុក​គ្នា​យ៉ាង​ពេញទំហឹង រួច​បន្ត​ទៅ​បុក​បង្គោលគីឡូម៉ែត្រ​ជ្រុល​រថយន្ត​ធ្លាក់​ភ្លឺស្រែ​', 'http://kohsantepheapdaily.com.kh/article/116119.html', 'http://i.kspg.co/2015/3/ce7b8e49-1416-4913-be4a-880c742f2a67.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('144', 'cl000002', 'u0000003', 'នាយ​រង​​ផ្នែកនគរបាល​ចរាចរ​ណ៏​ម្នាក់​ ស្លាប់​ដោយ​គ្រោះ​ថា្ន​ក់​ចរាចរ​ណ៏', 'ខេត្តកំពង់ឆ្នាំង ៖ នាយ​រង​នគរបាល​ចរាចរ​ណ៏ ស្រុក​បរិបូ​ណ៏​ម្នាក់ ​បាន​ស្លាប់ដោយ​គ្រោះថ្នាក់​ចរាចរ​ណ៏មួយ ​បាន​កើតឡើង​កាលពី​​ម៉ោង១០ និង​១៥​នាទី​យប់​ថ្ងៃ​ទី​៦ ខែ​មី​នា ឆ្នាំ​២០១៥', 'http://kohsantepheapdaily.com.kh/article/116117.html', 'http://i.kspg.co/2015/3/931ea99c-9a81-40fb-bd6a-7426a1f6ea43.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('145', 'cl000002', 'u0000003', 'ត្រឡប់​ពី​ផឹក​ស៊ី​ស្រវឹង​បើក​ម៉ូតូ​ចូល​ឡាន​បុក​ស្លាប់​ភ្លាម​', 'ខេត្ត​កំពង់ស្ពឺ ៖ បុរស​ម្នាក់​ទើប​ត្រឡប់​មក​ពី​ផឹក​ស៊ី​បាន​​ជិះ​ម៉ូតូ​ទៅ​បុក​ឡាន​ដែល​ធ្វើ​ ដំណើរ​បញ្ច្រាស​ទិ​គ្នា​ចំ​ពីមុខ​ពេញ​មួយ​ទំហឹង​បណ្តាល​ឱ្យ​ស្លាប់ភ្លាមៗ​នៅ ​នឹង​កន្លែង​។', 'http://kohsantepheapdaily.com.kh/article/116065.html', 'http://i.kspg.co/2015/3/a92d81fe-b7d7-4e0a-8ae6-1f904f2f3f66.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('146', 'cl000002', 'u0000003', '​ឡង់​គ្រី​ស្ស័រគេច​​ឧបសគ្គជ្រុល​ទៅ​បុក​​​រថយន្ត​​ទួ​រី​ស​​ ​មនុស្ស​៩​នាក់​​​រង​របួសធ្ងន់​-​​ស្រាល', 'ខេត្តកំពង់ឆ្នាំង ៖ យ៉ាង​​ហោច​ណាស់​​មនុស្ស​៩​នាក់​ បាន​រង​របួសធ្ងន់-​ស្រាល ​ក្នុង​ហេតុ​ការ​ណ៍​គ្រោះថ្នាក់​ចរាចរ​ណ៍​​មួយ​កាលពី​វេលា​ម៉ោង​០៣ ​និង​១០​នាទី​រសៀល​ថ្ងៃ​ទី​០៥ មីនា ២០១៥ ស្ថិតនៅ​​លើ​កំណាត់​​ផ្លូវ​ជាតិ​​លេខ​​៥​ ​​ក្នុងភូមិ​​ស្រះ​​កែវ​ ឃុំ​ស្វាយ​​ជ្រុំ ស្រុក​​រលា​ប្អៀរ', 'http://kohsantepheapdaily.com.kh/article/116064.html', 'http://i.kspg.co/2015/3/f2bb6632-9906-48de-8f3a-83e762de039b.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('147', 'cl000002', 'u0000003', 'ត្រឡប់​ពី​ផឹក​ស៊ី​ស្រវឹង​បើក​ម៉ូតូ​ចូល​ឡាន​បុក​ស្លាប់​ភ្លាម​', 'ខេត្ត​កំពង់ស្ពឺ ៖ បុរស​ម្នាក់​ទើប​ត្រឡប់​មក​ពី​ផឹក​ស៊ី​បាន​​ជិះ​ម៉ូតូ​ទៅ​បុក​ឡាន​ដែល​ធ្វើ​ដំណើរ​បញ្ច្រាស​ទិ​គ្នា​ចំ​ពីមុខ​ពេញ​មួយ​ទំហឹង​បណ្តាល​ឱ្យ​ស្លាប់ភ្លាមៗ​នៅ​នឹង​កន្លែង​។', 'http://kohsantepheapdaily.com.kh/article/116055.html', 'http://i.kspg.co/2015/3/4b7d7b51-7e3e-40c8-ae03-29ea2edf8c65.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('148', 'cl000002', 'u0000003', 'ស្រវឹងស្រា​ជិះ​ម៉ូតូ​ទៅ​បុក​គូទ​រថយន្ត​គេហើយ​ចោទ​សមត្ថកិច្ច​ថា​ គំរាម​ទារប្រាក់​ទៀត', 'រាជ​ធានីភ្នំពេញ ៖ បុរស​ម្នាក់​បាន​ផឹកស្រា​ស្រវឹង​ជោគ​ជាំ​ជិះ​ម៉ូតូ​មួយ​គ្រឿង​រេ​ពេញ​ផ្លូវ​ជ្រុល​ទៅ​បុក​​​គូទ​រថយន្ត​ដែល​គេ​ចត​ទុក​ចោល​បណ្តាល​ឱ្យដួល​សន្លប់​មួយសន្ទុះ​ហើយ​', 'http://kohsantepheapdaily.com.kh/article/116041.html', 'http://i.kspg.co/2015/3/bb4e967f-affc-4212-8fa5-e8390c6e64e4.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('149', 'cl000002', 'u0000003', 'រ៉ឺម៉ក​ម៉ូតូ​​ដឹក​ទឹក​បរិសុទ្ធ​ឱ្យ​ម៉ូយ​តាម​ស្រុក ក្រឡាប់​សង្កត់​លើ​បុរស​ម្នាក់​ស្លាប់​​', 'ខេត្តកណ្តាល ៖ បុរស​ម្នាក់​បាន​ស្លាប់​ភ្លាម​ៗ​នៅ​កន្លែង​កើតហេតុ​យ៉ាង​អ​ណោ​ច​អ​ធ័​ម បន្ទាប់​ពី​​ត្រូវរ៉ឺម៉ក​ម៉ូតូ​​ដឹក​ទឹកបរិសុទ្ធ​បើក​កៀរ​ និងក្រឡាប់​សង្កត់ពីលើ ខណៈ​កំពុង​ជិះ​ម៉ូតូ​', 'http://kohsantepheapdaily.com.kh/article/116021.html', 'http://i.kspg.co/2015/3/e451ba2e-5246-4981-866a-64c6f7324d13.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('150', 'cl000002', 'u0000003', 'ក្រឡាប់​រថយន្ត​បាឡែនស្លាប់អាណិកជន​ខ្មែរ​​អា​មេ​រិ​កម្នា​ក់​​​ ​និង​៣​នាក់​ទៀត​​របួសស្រាល', 'ខេត្តស្ទឹងត្រែង ៖ ​ឧប្បតិ្តហេតុ​គ្រោះថ្នាក់​ចរាច​ណ៍​មួយ​បាន​កើតឡើង​កាលពី​រសៀល​ថ្ងៃ​ទី​៤ មីនា​​២០១៥ ​​នៅ​ចំណុច​វាល​ប៉ា​រ៉ា ភូមិ​ដុ​ង​ក្រឡ ក្រុង​ស្ទឹង​ត្រែង ខេត្តស្ទឹងត្រែង ', 'http://kohsantepheapdaily.com.kh/article/116004.html', 'http://i.kspg.co/2015/3/189875ec-6f58-4a17-b2e0-a0d62b6986ed.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('151', 'cl000002', 'u0000003', '​កុងតឺន័រ​បើកបុក​ឡិចស៊ីស RX300 ខ្ទេច​អស់​មួយចំហៀងនៅក្រុង​កំពង់ឆ្នាំង​', 'ខេត្តកំពង់ឆ្នាំង​ ៖ រថយន្ត​ម៉ាក​ឡិស​ស៊ី​ស ​RX300 មួយ​គ្រឿង​ បាន​រង​ការ​ខូចខាត​​ខ្ទេច​​អស់​មួយ​ចំ​ហៀ​ង​ ​បន្ទាប់ពី​ត្រូវ​រថយន្ត​កុងតឺន័រ​បើក​បុក​​ កាលពី​វេលា​ម៉ោង១២​ និង​១៥​នាទី​រំលង​អ​ធ្រា​ត្រឈានចូល​ថ្ងៃ​ទី​០៤ មីនា ​​២០១៥ ស្ថិតនៅ​ចំណុច​ចំណត​រថ​យន្ត​​ផ្សា​​រលើ ​​', 'http://kohsantepheapdaily.com.kh/article/115999.html', 'http://i.kspg.co/2015/3/612cd289-842b-45a8-bc95-ff7b8ed1ae3b.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('152', 'cl000002', 'u0000003', 'កាណូត​​ដឹកគោ​យន្ត​បុក​កាណូត​​ដឹក​ត្រី​​​ពេលយប់ ​មនុស្ស​៥​នាក់​របួសធ្ងន់-​ស្រាល', 'ខេត្តកំពង់ឆ្នាំង​ ៖​ មនុស្ស​ប្រុស​-ស្រី​៥​នាក់រង​របួសធ្ងន់-​ស្រាល​ ​ក្នុងហេតុការណ៍គ្រោះថ្នាក់​ចរាចរណ៍​ផ្លូវទឹក​មួយ ​​កាលពីវេលាម៉ោង០​៧​និង​១០​នាទី​យប់​​ថ្ងៃ​ទី​០៣ មីនា ​​២០១៥ ស្ថិតនៅ​ក្នុង​ភូមិ​អន្លង់​កញ្ចុះ ឃុំ​ប្រឡាយ​​មាស ស្រុក​កំពង់​លែង ខណៈ​ដែល​កាណូត​​ឌុ​ក​ដឹក​ត្រី', 'http://kohsantepheapdaily.com.kh/article/115983.html', 'http://i.kspg.co/2015/3/cc221bd2-d5c6-4e64-bf86-350d4f6d89ff.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('153', 'cl000002', 'u0000003', 'ក្មេង​ប្រុស​​ជំទង់​ជិះ​ម៉ូតូ MSX ត្រូវរថយន្ត​កៀរ​​​ទៅ​បុក​ពំនូក​ថ្ម​​បាក់​ស្មង​ជើង', 'រាជ​ធានីភ្នំពេញ ៖ ខណៈ​ពេល​កំពុង​ជិះ​ម៉ូតូ​ ត្រូវ​រថយន្ត​បើក​​កៀរ​​បណ្តាល​រេ​ចង្កូត​បុក​ខឿន​​ចិញ្ចើម​ផ្លូវ​ជ្រុល​ទៅ​បុក​​ពំនូក​ថ្ម​គរ​​នៅ​ចិញ្ចើម​ផ្លូវ​អូស​ជិត​១០​ម៉ែត្រ ​​កាល​ពី​វេលា​ម៉ោង​០៩ និង​៤០​នាទី​យប់​​ថ្ងៃ​ទី០​៤ មីនា ២០១៥ ស្ថិត​តាម​បណ្តោយ​ផ្លូវ​កម្ពុជា​​ក្រោម ទល់មុខ​សា​', 'http://kohsantepheapdaily.com.kh/article/115970.html', 'http://i.kspg.co/2015/3/19112370-e129-4b41-ac99-a95733829553.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('154', 'cl000002', 'u0000003', 'ជិះ​ម៉ូតូ​លឿន​ទៅ​បុក​ម៉ូតូ​កង់​បី​របួសធ្ងន់​២​នាក់', 'រាជធានី​ភ្នំពេញ​៖ បុរស​២​នាក់​បាន​រង​របួសធ្ងន់​ដេក​សន្លប់​ស្តូកស្តឹង​នៅ​និង​កន្លែង​កើតហេតុ ខណៈ​បុរស​ម្នាក់​បាន​ជិះ​ម៉ូតូ​ម៉ាក​ហុងដា​សេ​១២៥ ចេញពី​ផ្លូវ​បេតុង​ក្នុង​ល្បឿន​លឿន​បត់​ឡើង​មក​ផ្លូវជាតិ​លេខ​៤ ជាន់​ហ្វ្រាំង​មិន​ទាន់ ក៏​ទៅ​បុក​ម៉ូតូ​កង់​បី​លឿន​ដូច​គ្នា', 'http://kohsantepheapdaily.com.kh/article/115961.html', 'http://i.kspg.co/2015/3/89fb0636-83a1-4150-b3c3-e3ce2b62ded4.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('155', 'cl000002', 'u0000003', 'តួសម្តែងខារ៉ាអូខេ​ម្នាក់រងរបួសធ្ងន់​ដោយគ្រោះថ្នាក់​ចរាចរណ៍នៅ​ស្រុកកំពង់សិលា', 'ខេត្តព្រះសីហនុ ​៖ ស្រ្តីពីរនាក់បានរង​បួសធ្ងន់ស្រាល ​ដោយសារគ្រោះថ្នាក់​ចរាចរណ៍រថយន្ត​រេចង្កូតបុកគល់ឈើ ។ ', 'http://kohsantepheapdaily.com.kh/article/115958.html', 'http://i.kspg.co/2015/3/d663e69b-64f7-4832-940d-4cd55d7cd54e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('156', 'cl000002', 'u0000003', 'រថយន្ត​ហ្វត​បុកម៉ូតូ​ហោះទៅបុកឥដ្ឋការ៉ូដែលដាក់​តាំងលក់​មុខផ្ទះបាក់ជើង​២កំណាត់​', 'រាជ​ធានី​ភ្នំពេញ ៖ រថយន្ត​ហ្វ​តមួយគ្រឿង ខណៈម​ក​ដល់​ចំណុច​ផ្លូវ​កែង​បានជាន់​ហ្គែរ​​ហោះ​ទៅ​បុក​ម៉ូតូ​ ហើយ​ម៉ូតូផ្លោង​​ទៅ​បុក​ឥដ្ឋការ៉ូ​ដែល​​គេ​ដាក់​តាំង​លក់​នៅ​មុខ​ផ្ទះ​​ បណ្តាល​ឱ្យ​ដួល​បាក់​ជើង​២​កំណាត់​ ហើយ​រថយន្ត​បង្កនោះ​រត់គេច​​បាត់​ តែជ្រុះ​កាង​ជាប់​​ស្លាក​លេខ​', 'http://kohsantepheapdaily.com.kh/article/115896.html', 'http://i.kspg.co/2015/3/db49a819-fae3-4923-91f5-5ca0a9d6892c.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('157', 'cl000002', 'u0000003', 'កកស្ទះជាង២ម៉ោង​ដោយសារ​រថយន្ត​កុងតឺន័របុកជាមួយ​រថយន្ត​ក្រុង​នៅផ្លូវជាតិលេខ១', 'ខេត្តព្រៃវែង ៖ មានហេតុការណ៍គ្រោះថ្នាក់ចរាចរណ៍មួយ​ បន្ទាប់ពី​មានរថយន្ត​កុងតឺន័រ​មួយគ្រឿង​បើក​បុកគូទ​ជាមួយ​​រថយន្តក្រុង​យ៉ាង​​ពេញ​ទំហឹង បណ្តាល​ឱ្យរថយន្ត​កុង​តឺន័រខ្ទេច​ផ្នែក​ខាងមុខ និង​អ្នកបើកបរ​រងរបួសធ្ងន់-ស្រាល​ ហើយហេតុការណ៍នេះក៏បានបង្ក​', 'http://kohsantepheapdaily.com.kh/article/115889.html', 'http://i.kspg.co/2015/3/44364130-a5e4-4c46-9274-2620807df96c.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('158', 'cl000002', 'u0000003', 'នារី​ម្នាក់​បើករថយន្ដ​លឿន​ជ្រុល​ទៅ​បុក​ម៉ូតូ​កង់​បី​ហើយ​ខ្ទាត់​ទៅ​ប៉ះ​ម៉ូតូ​២​គ្រឿង​ទៀត​កំពុង​ចត់​ចោល(​មាន​វី​ដេ​អូ​)​', 'រាជធានី​ភ្នំពេញ​៖ នារី​ម្នាក់​បើករថយន្ដ​ម៉ាក​កាម​រី​មួយ​គ្រឿង​លឿន ហើយ​វ៉ា​រថយន្ដ​ហា​យ​ឡែ​ន​ឌ័​រ​មួយ​គ្រឿង​ទៀត ក៏​ជ្រុល​ទៅ​បុក​ម៉ូតូ​កង់​បី​មួយ​គ្រឿង ក៏​ខ្ទាត់​ទៅ​ប៉ះ​ម៉ូតូ​២​គ្រឿង​ទៀត​ដែល​កំពុង​ចត់​នៅ​លើ​ជ​ញ្ចើ​ម​ផ្លូវ បណ្ដាល​ឲ្យ​ម៉ូតូ​ទាំង​២​គ្រឿង​និង​ម៉ូតូ​កង់​បី', 'http://kohsantepheapdaily.com.kh/article/115888.html', 'http://i.kspg.co/2015/3/090831a1-ef94-46b3-ac94-949dd1a9850f.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('159', 'cl000002', 'u0000003', 'ទំនង​ស្រវឹង​បើករថយន្ត​បុក​របាំង​ថ្ម​ពុះ​ចែក​ទ្រូងផ្លូវ​បាក់​អស់​៣​កង់(​មាន​វី​ដេ​អូ​)​', 'រាជធានី​ភ្នំពេញ​៖​បុរស​ម្នាក់​ទំនង​ស្រវឹង​បាន​បើករថយន្ត​កូ​រ៉ូ​ឡា មួយ​គ្រឿង ទៅ​បុក​របាំង​ថ្ម​ពុះ​ចែក​ទ្រូងផ្លូវ​បាក់​អស់​០៣ កង់ ហើយ​ទ្រូង​រថយន្ត​កល់​ជាប់​និង​របាំង​ថ្ម​ទៅ​មុខ​លែង​រួច ។ ហេតុការណ៍​នេះ​កើតឡើង​នៅ​វេលា​ម៉ោង ១​និង​៤០​នាទី', 'http://kohsantepheapdaily.com.kh/article/115886.html', 'http://i.kspg.co/2015/3/76f6849c-109a-4cb7-90ca-b910e2af7692.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('160', 'cl000002', 'u0000003', 'រយៈពេល​១​ម៉ោង​មាន​ករណី​បុក​របាំង​ថ្ម​ចំនួន​២​នៅ​កន្លែង​តែ​មួយ', 'រាជធានី​ភ្នំពេញ​៖ គ្រោះថ្នាក់​ចរាចរ​ក្នុង​១​ម៉ោង​មាន​២​ករណី ដោយ​បើករថយន្ត​គេច​ម៉ូតូ​ជិះ​បញ្ច្រាស់​ផ្លូវ​ជ្រុល​ទៅ​បុក​របាំង​ថ្ម​ពុះ​ចែក​ទ្រូងផ្លូវ បណ្តាល​ឲ្យ​បាក់​រង​ការ​ខូចខាត​ចំនួន​៦​កង់ ប៉ុន្តែ​មិន​បង្ក​ឲ្យ​រង​របួស​ដល់​មនុស្ស​ណា​ម្នាក់​នោះ​ទេ', 'http://kohsantepheapdaily.com.kh/article/115884.html', 'http://i.kspg.co/2015/3/82728004-70a8-4c8f-94c8-2f2e1912008e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('161', 'cl000002', 'u0000003', 'កាណូត​បុក​គ្នា​របួសធ្ងន់-​ស្រាល​២​នាក់​ឪពុក​កូន កូនប្រសាបាត់​ខ្លួន​មួយ​យប់​មួយ​ថ្ងៃទើប​ប្រទះ​ឃើញ​សព​​', 'ខេត្តកំពង់ឆ្នាំង ៖ បុរស​ម្នាក់​បាន​បាត់​ខ្លួន និង​២​នាក់​ទៀត​ជា​ឪពុក​ និង​កូន​បាន​បរង​របួសក្នុង​ហេតុ​ការ​ណ៍គ្រោះថ្នាក់​ចរាចរ​ណ៏​ផ្លូវទឹក​មួយ​បាន​កើតឡើង កាលពី​វេលា​ម៉ោង​៨', 'http://kohsantepheapdaily.com.kh/article/115879.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('162', 'cl000002', 'u0000003', 'គ្រោះថ្នាក់ចរាចរណ៍​២ករណីផ្សេង​គ្នា ស្លាប់​១ របួស​ធ្ងន់-ស្រាល​៩នាក់', 'ខេត្តកំពង់ចាម ​៖​ បុរស​ម្នាក់​​​ស្លាប់​យ៉ាង​​អាណោ​ច​អា​ធ័ម និង​៩​នាក់​ទៀត​​​​រង​របួសធ្ងន់-ស្រាល​ ​​ក្នុងហេតុការណ៍គ្រោះថ្នាក់​ចរាចរ​ណ៍២​កន្លែង​ផ្សេង​​គ្នា ​កាលពីថ្ងៃ​ទី​០២ មីនា ២០១៥ ស្ថិតនៅ​ស្រុក​ព្រៃឈរ​ និង​ស្រុក​ស្រី​សន្ធរ ដោយជន​រង​គ្រោះ​ ត្រូវ​​​គេ​បញ្ជូន​ទៅ​សង្គ្រោះ​', 'http://kohsantepheapdaily.com.kh/article/115844.html', 'http://i.kspg.co/2015/3/2f65d666-fd8c-4f5f-98e1-f4bf3704e3f9.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('163', 'cl000002', 'u0000003', 'រយៈពេល​ជាង​20​ឆ្នាំ​គ្រួសារ​មួយ​កសាង​ជីវភាព​ឱ្យ​ធូរធារ​ដោយ​របរ​ដាំ​ផ្កា', 'ខេត្តតាកែវ ៖ ក្រុម​គ្រួសារ​មួយ​បាន​ចាប់​យក​អាជីព​ជា​អ្នក​ដាំ​ផ្កា​លក់​អស់​រយៈពេល​ជាង​២០​ឆ្នាំ​មក​ហើយ ។ របរ​ដាំ​ផ្កា​នេះ​ជួយ​ធ្វើ​ឱ្យ​ជីវភាព​គ្រួសារ​ធូរធារ ។ លោក​ជុំ វី​វុ​ទ្ធី អាយុ​៤៧​ឆ្នាំ រស់នៅ​ភូមិ​ចក ', 'http://kohsantepheapdaily.com.kh/article/115830.html', 'http://i.kspg.co/2015/3/cd1edfb8-87f8-4462-a001-bc040a846762.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('164', 'cl000002', 'u0000003', 'កែ​ច្នៃ​លាមក​ជ្រូក​បង្កើត​ជា​ហ្គា​ស​សម្រាប់​ផ្គត់ផ្គង់​ការ​ប្រើប្រាស់ និង​ត្រៀម​ចែកចាយ', 'ខេត្ត​ប៉ៃលិន ៖ កសិដ្ឋាន​ចិញ្ចឹម​ជ្រូក​ដ៏​ធំ​មួយ ដែល​សាងសង់​ស្រប​តាម​ស្តង់ដា​បច្ចេកទេស មិន​បង្ក​ក្លិនស្អុយ​ធ្វើ​ឱ្យ​ប៉ះពាល់​ដល់​បរិស្ថាន​រស់នៅ​របស់​ពលរដ្ឋ​នៅ​ជុំវិញ គឺ​ស្ថិត​នៅ​ជាប់​ផ្លូវជាតិ​លេខ​៥៧ ', 'http://kohsantepheapdaily.com.kh/article/115294.html', 'http://i.kspg.co/2015/2/60f30523-9bba-4ffc-a020-f492c2a85bf9.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('165', 'cl000002', 'u0000003', 'ប្រធាន​មន្ទីរ​កសិកម្ម​ខេត្ត​ចុះ​ត្រួតពិនិត្យ​ការ​ប្រមូល​ផល​ដំឡូង​មី​របស់​កសិករ ប្រភេទ​ពូជ​បង្កាត់​ថ្មី​ឈ្មោះ​៨៩ ដែល​នាំ​ពី​ប្រទេស​ថៃ', 'null', 'http://kohsantepheapdaily.com.kh/article/114534.html', 'http://i.kspg.co/2015/2/1a6d0358-f5a4-4f01-b1e7-385d298dbd3d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('166', 'cl000002', 'u0000003', 'វិធីសាស្ត្រ​ក្នុង​ការ​ដាំ​ត្រឡាច​បាន​ផ្លែ​ច្រើន', 'ខេត្តតាកែវ ៖ ការ​ដាំ​ដំណាំ​ទៅ​តាម​ទម្លាប់​ដែល​គ្មាន​ចំណេះ​ជំនាញ​ខាង​បច្ចេកទេស មិនមែន​ថា​មិន​ទទួលផល​ទេ គ្រាន់តែ​មិន​បាន​ដូច​បំណង​របស់​យើង ។ ប៉ុន្តែ​បើ​យើង​មានបទពិសោធន៍ គួប​ផ្សំ​នឹង​បច្ចេកទេស ', 'http://kohsantepheapdaily.com.kh/article/113984.html', 'http://i.kspg.co/2015/2/0c4487de-e4ff-4c9d-af93-7da5f773cad7.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('167', 'cl000002', 'u0000003', 'ប្រជាកសិករ​ កាក់កប​នឹងរបរ​ដាំដំណាំ​សាវម៉ាវ ​នៅក្នុង​ខេត្តប៉ៃលិន', 'ខេត្តប៉ៃលិន ៖ សាវ​​ម៉ាវជា​ឈ្មោះរុក្ខជា​​តិហូបផ្លែមួយប្រ​ភេទ ​ដែល​មាន​ស្លឹក​ស្រដៀ​ងទៅនឹ​ងបំពេញ​រាជ​ តែ​មាន​ស្លឹកតូច​ៗ ជាង​បំពេញ​រាជ ហើយ​ផ្លែ​វាមាន​លក្ខណៈ​ជាចង្កោម​ សំបក​ផ្លែមាន​ព្រុយ​ស្មាញ គេ​បរិភោគ​វាជា​ចំណី ដោយ​សាច់ខ្លះ​មាន រស​ជាតិជូរ​អែម ខ្លះទៀត​រស​ជាតិ​ផ្អែមឆ្ងាញ់, ​ជារុក្ខជាតិ​មានប្រយោ​ជន៍ គួរ​ដាំ​ឲ្យ​ក្រាស់​ក្រែល​ក្នុងស្រុក ​។ ជាក់​ស្តែង​នៅ​ក្នុង', 'http://kohsantepheapdaily.com.kh/article/98773.html', 'http://i.kspg.co/2014/6/75af73fb-c10e-49af-a2f9-7a9e28a16dcd.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('168', 'cl000002', 'u0000003', 'ប្រជាពលរដ្ឋ​កំពុងព្រួយបារម្ភ​ពីបញ្ហាគ្រោះ​រាំងស្ងួត ​បណ្តាលឱ្យ​ខូចខាត​ដំណាំ​នៅក្នុង​ខេត្តប៉ៃលិន​', '​ខេត្ត​​ប៉ៃលិន ​ ៖ ប្រ​ជាពលរដ្ឋ​រស់នៅ​ក្នុងខេត្ត​ប៉ៃលិន​ទាំងមូល​កំពុង​តែព្រួយ​បារម្ភ​យ៉ាងខ្លាំង​ចំពោះ​បញ្ហារាំង​ស្ងួត ដែល​បាន​​អូស​បន្លាយ​អស់រយៈ​ពេល​យ៉ាងយូរ​ ធ្វើឱ្យ​ដំណាំ​ពោត ​ សណ្តែក​ ដំឡូងមី​ និង​ដំណាំ​ផ្សេងៗ​មួយ​ចំនួន​ទៀត​ស្ទើរ​តែស្វិត​​ស្រ​ពោន​ខូច​​​ខាត​អស់ ​។ ​ជាពិ​សេស​ប្រជា​ពល​រដ្ឋរស់​នៅក្នុង​ភូមិអូររអិល​ ឃុំស្ទឹង​កាច់ ​ស្រុកសាលា​ក្រៅ​ ពួក​គាត់​ម្នាក់​ៗ បាន​ត្អូញ​ត្អែរ​ពី​', 'http://kohsantepheapdaily.com.kh/article/96661.html', 'http://i.kspg.co/2014/5/3e62a631-fd65-45d4-a376-a465e2852ace.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('169', 'cl000002', 'u0000003', 'នៅតាមចម្កា​រប្រជាកសិក​រប្តូរមកដាំ​ដំណាំដំឡូងមី​ច្រើនជាង​ដំណាំពោតក្រហម​នៅខេត្តប៉ៃ​លិន', 'ខេត្ត​ប៉ៃលិន​ ៖ តាំងពីដើ​មរៀងមក​ប្រជាកសិក​រភាគច្រើន​រស់នៅ​ក្នុងខេត្ត​ប៉ៃលិន​និយម​ ដាំដំណាំ​ពោត​ក្រហមច្រើន​​ជាង ដំណាំ​ណាៗទាំង​អស់ ។ ​ប៉ុន្តែនៅចុងក្រោយ​នេះ គេសង្កេតឃើញ​ថា ចាប់តាំងពី​ចុងឆ្នាំ២០១៣​ រហូតមកដល់​ ដើម​ឆ្នាំ ២០១៤​ ប្រជាពលរដ្ឋ​ស្ទើរតែទាំងអ​ស់បានងាក​បែរ ពីការដាំ​ដំណាំពោត​ក្រហម មកសម្រុក​ដាំដំណាំដំឡូង​មីទៅវិញ ​។', 'http://kohsantepheapdaily.com.kh/article/96134.html', 'http://i.kspg.co/2014/5/9eea7e7f-5eb9-412b-81f2-2fcb54cfab05.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('170', 'cl000002', 'u0000003', 'ប្រជាកសិករ ​១គ្រួសារខិត​ខំប្រឹងប្រែង​​ដាំបន្លែបន្ទាប់​បន្សំបន្ថែម​ដើម្បីទ្រទ្រង់ជីវភាព​រស់នៅប្រចាំ​ថ្ងៃ', 'ខេត្ត​ប៉ៃលិន​ ៖ គេសង្កេត​ឃើញមាន​ប្រជាកសិករ​១គ្រួសារ​ រស់នៅ​ក្នុងភូមិ​ដីក្រហម​ ស​ងា្កត់អូរតា​វ៉ៅ ក្រុង​ប៉ៃលិន​ បើទោះ​បីជា ពួក​គេមាន​ភាពរវល់​ មមាញឹក​យ៉ាង​ណាក៏​ដោយ​នៅតែ​ខិតខំប្រឹង​ប្រែងដាំបន្លែ​បន្ទាប់បន្សំ​បន្ថែមទៀត​ជាច្រើន​ដើម្បីបាន​ផល យក​ទៅលក់បាន​ប្រាក់កម្រៃ​សម្រាប់​ទ្រទ្រង់ជីវភា​ពរស់នៅ​ប្រចាំថ្ងៃជា​ទីពេញចិត្ត​ដោយបន្ទាប់​ពីពួក​គេបាន​ដាំដំណាំ​ដំឡូង​មី ', 'http://kohsantepheapdaily.com.kh/article/96021.html', 'http://i.kspg.co/2014/5/0099c2c6-6cde-4402-a0be-7126dfb439bc.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('171', 'cl000002', 'u0000003', 'ស្រូវ​ប្រាំង​រាប់រយ​ហិចតា​នៅ​ឃុំគោក​បាល្លង័្គកំពុង​ប្រ​ឈម​និង​ការ​ខ្វះ​ខាត​ទឹក​ដី​ស្រែ​មួយ​ចំនួន​ប្រេះ​ក្រហែង​ស្រូវ​ប្រាំង​ចេញ​មិន​រួច​ងាប់​ជា​បណ្តើរៗ​ហើយ', 'ខេត្ត​​បន្ទាយ​មាន​ជ័យ​ ៖​ ស្រូវ​ប្រាំង​​រាប់​រយ​ហិច​តា​របស់​ប្រ​ជា​ពល​រដ្ឋ​នៅ​ក្នុង​ឃុំ​គោក​បា​ល្លង្គស្រុក​មង្គល​បូរី​កំពុង​ជួប​​ប្រទះ​ការ​ខ្វះ​ខាត​ទឹក​ហើយ​ដី​ស្រែ​មួយ​ចំនួន​កំពុង​ប្រេះ​ក្រ​ហែង​និង​ស្រូវ​ប្រាំង​ចេញ​មិន​រួច​ដែល​ប្រឈម​ទៅ​និង​ការ​ងាប់​បណ្តើរៗ​។​ប្រ​ជា​ពល​រដ្ឋ​​មាន​កា​រ​ព្រួយ​បារម្ភ​ជា​ខ្លាំង​ដោយ​សំ​ណូម​ពរ​ទៅ​មន្រ្តី​ជំនាញ​ឱ្យ​ជួយ​អ​ន្តរា​គមន៏​ផង​។', 'http://kohsantepheapdaily.com.kh/article/91859.html', 'http://i.kspg.co/2014/3/dd14ba76-269e-4d99-beaa-bdbd1f210250.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('172', 'cl000002', 'u0000003', 'ពូជ​គោ​សាន់​តា​មក​ពី​សហ​រដ្ឋ​អា​មេ​រិក ត្រូវ​បាន​កសិករ​ចាប់​អារម្មណ៍​ចិញ្ចឹម ​', 'ខេត្ត​កំពង់​ចាម ៖​ ពូជ​គោ​ប្រភេទ​សាន់​តា ​ផ្តល់​សាច់​ច្រើន​ក្នុង ​១ក្បាល ​អាច​មាន​ទម្ងន់​រហូត​ដល់ជា​ង ​១តោ​ន ​នាំ​មក​ពី​សហ​រដ្ឋ​អាមេ​រិក ​ត្រូវ​បាន​បង្កាត់​តាម​បែប​វិទ្យា​សាស្រ្ត ​(ស្ពែម) ​កំពុង​ត្រូវ​បាន ​ប្រជា​កសិករ​ចាប់​អារម្មណ៍​ពី​តំបន់​មួយ​ទៅ​តំបន់​មួយ​ ជាក់​ស្តែង​ដូច​ជា​នៅ​ភូមិ​ទួល​វិហារ​ ឃុំ​ជីរោទ៍​ទី២​ ស្រុក​ត្បូង​ឃ្មុំ ​បង្កាត់​បាន​ចំនួន​ ៣​ក្បាល ​ទើប​តែ​មាន​លើក​ដំបូង​ ។ ', 'http://kohsantepheapdaily.com.kh/article/76543.html', 'http://i.kspg.co/2013/7/6acae98f-5160-4444-a7d1-496ea9dd7903.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('173', 'cl000002', 'u0000003', 'មុខ​របរ​ចិញ្ចឹម​ចង្រិត​លក់​របស់​អ្នក​ខេត្ត​ព្រៃវែង​', 'ខេត្ត​ព្រៃវែង ៖ ​ចង្រិត​កំពុង​ត្រូវ​បាន​ប្រជា​ពល​រដ្ឋ​​នៅតាម​បណ្តា​ខេត្ត​មួយ​ចំនួន​ផ្អើល​នាំ​គ្នា​ចិញ្ចឹម​​ដោយ​សារ​តែ វា​ជា​មុខ​របរ​ដែល​ប្រើ​ប្រាស់​ដើម​ទុន​តិច ពេល​វេលា​ខ្លី តែ​ទទួល​បាន​ទិន្ន​ផល​ច្រើន។ ជាក់​ស្តែង​​នៅ​ក្នុង​តំបន់​អ្នក​លឿង ដែល​ស្ថិត​​ក្នុង​ស្រុក​ពាមរក៍ ​ខេត្តព្រៃវែង ​គ្រាន់តែ​រយៈ​ពេល​ជាង​១​​ខែ​ប៉ុណ្ណោះ​ ប្រជា​ពល​រដ្ឋ​ដែល​បាន​ចាប់​យក​មុខ​របរ​នេះ​បាន​កើន​ឡើង​ជាង​១០​គ្រួសារ​ទៅ​ហើយ ។ ', 'http://kohsantepheapdaily.com.kh/article/47493.html', 'http://i.kspg.co/2012/7/8e80c740-6a18-4507-9b98-9644638d3bb3.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('174', 'cl000002', 'u0000003', 'ដំណាំពោត​របស់​កសិករនៅ​ខេត្ត​​ប៉ៃលិន ​​ខូចខាត​ជាង​៣.០០០ហិកតាដោយ​សារ​​គ្មាន​​ភ្លៀង​', '​ខេត្ត​ប៉ៃលិន ៖ ​ពោត​​ជា​ដំណាំ​​​កសិ​ផល​មួយ​ប្រភេទ​ដែល​កសិករនៅ​ខេត្ត​​ប៉ៃលិន​ភាគ​ច្រើន​និយមដាំ​​ដែលក្នុង​មួយ​​ឆ្នាំដាំបាន​២​ដង គឺ​នៅ​ដើម និង​ចុង​រដូវ។ ​តែឆ្នាំ​នេះ​គេសង្កេត​ឃើញ​ថា ​ភ្លៀង​​ផ្តើម​​ធ្លាក់̡ޅាប់​​តាំងពី​ដើម​​ខែ​កុម្ភៈ​​រហូត​មក​។  ពេលដំណាំ​​ពោត​លូត​លាស់​បាន​យ៉ាង​ល្អ​​ស្រស់​បំព្រង ដណ្តើម​​គ្នា​ចេញ​​ផ្កា​​​ពាស​ពេញ​​ក្នុងផ្ទៃ​ចម្ការ និងដាក់​ស្នៀត​​គ្រប់​ដើម​​ទៅ​ហើយ ស្រាប់តែ​ជួប​គ្រោះ​​', 'http://kohsantepheapdaily.com.kh/article/44511.html', 'http://i.kspg.co/2012/6/1fc144a3-5df0-42c1-8c36-9686ddc17602.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('175', 'cl000002', 'u0000003', 'កសិករ​ដាំ​ពោតក្រហម​សំណូម​ពរ​ឱ្យ​ក្រសួង​កសិកម្ម​ផលិត​ពូជ​ផ្តល់​ទិន្ន​ផល​ខ្ពស់​', 'ខេត្ត​កណ្តាល​ ៖ ដំណាំ​ពោតក្រហមកំពុង​ពេញ​និយម​សម្រាប់​​ប្រជា​កសិករ​ដែល​រស់នៅ​តាម​ដង​ទន្លេមេគង្គ​ក្នុងភូមិ​សាស្ដ្រ​ស្រុក​លើក​ដែក ខេត្ដកណ្ដាល និង​បាន​ក្លាយទៅជា​ដំណាំ​លេខ​១ ខណៈ​ដែល​ស្រូវ​បាន​ក្លាយ​ជា​ដំណាំ​លេខ​​២ ។ ប៉ុន្ដែ​​ស្រប​ពេល​ជាមួយ​គា្ន​នេះ គ្រាប់ពូជ​ពោត​ក្រហម​ក៏​កំពុង​តែ​ចោទ​ជា​បញ្ហា​ផង​ដែរ ដោយ​សារ​​តែ​នៅ​ក្នុងស្រុក​ពុំ​ទាន់​មាន​គ្រាប់ពូជ​សម្រាប់​​ប្រជា​កសិករ​ទិញ​យក​មក​​ដាំ​​ឱ្យ​បាន​គ្រប់​គ្រាន់​ ។ ', 'http://kohsantepheapdaily.com.kh/article/43559.html', 'http://i.kspg.co/2012/5/73d5a1d1-0b27-48a1-b0d7-63599140717c.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('176', 'cl000002', 'u0000003', 'ដំណាំ​​ម្រេច​​មាន​អាយុ​កាល​វែង​ដល់​ទៅជាង​ម្ភៃ​ឆ្នាំ', 'null', 'http://kohsantepheapdaily.com.kh/article/31260.html', 'http://i.kspg.co/2011/12/0c869d06-f577-488d-aee8-73f05fb328e8.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('177', 'cl000002', 'u0000003', 'ក្រសួង​​​កសិកម្ម​​តម្រូវ​​ឱ្យ​ក្រុមហ៊ុន​ទាំងអស់​​​​បក​ប្រែភាសាខ្មែរ​​លើផលិតផល', 'រាជធានីភ្នំពេញ ៖​ ក្រសួង​​កសិកម្ម​​បាន​ចេញ​​លិខិត​​​ជូន​ដំណឹង​​​ដល់​​​គ្រប់​​ក្រុម​ហ៊ុន​​​ អាជីវ​កម្ម​​​នាំ​ចូល​ ​ផលិត​ និង​​វេច​ខ្ចប់​​ជាថ្មី​លើ​សម្ភារ​​​កសិកម្ម​​​ទាំង​អស់​​រួ​ម​មាន​​​​ ជីកសិកម្ម ថ្នាំ​​កសិកម្ម​ គ្រាប់​ពូជ​ដំណាំ​ ចំណី​សត្វ​ និង​បសុឱសថ​ត្រូវ​​ធ្វើ​ការ​​ចុះ​​បញ្ជី​​​នូវ​មុខ​​សម្ភារ​​​​កសិកម្ម​ ទាំងអស់​​នោះ​ និង​បំពេញ​​នូវ​​​លក្ខខណ្ឌ​​​បច្ចេកទេស​​មួយចំនួន​ទៀត។​ ​ក្នុង​នោះ​​​​បាន​​តម្រូវ​​ឱ្យ​មាន​​​ការ​​បក​ប្រែ​​ជា​ភាសា​​​ខ្មែរ​​នៅ​ លើ​​​សំបក​​​វេច​​ខ្ចប់​​សម្ភារ​​កសិកម្ម​​ ក្នុង​', 'http://kohsantepheapdaily.com.kh/article/27004.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('178', 'cl000002', 'u0000003', 'រដ្ឋ​មន្ត្រី​ក្រសួង​​​កសិ​កម្ម​ រុក្ខា​ប្រមាញ់​ និង​នេសាទ​​ត្រួត​ពិនិត្យ​​ព្រៃ​សហ​គមន៍​', 'ខេត្តឧត្តរមានជ័យ ៖ កាលពី​ថ្ងៃ​ទី​៣០ កក្កដា ​ឆ្នាំ​២០១១ កន្លង​មក​នេះ គណៈ​ប្រតិភូ​​មួយ​ក្រុម​​​ដឹក​នាំ​ដោយ​លោក​ច័ន្ទ សារុន រដ្ឋ​មន្ត្រី​​ក្រសួង​កសិ​កម្ម​ រុក្ខា​ប្រមាញ់​ និង​នេសាទ​ បាន​អញ្ជើញ​​ចុះ​ទៅពិនិត្យ​តំប​ន់​ព្រៃ​​សហ​​គមន៍​​សង្ឃ​រុក្ខា​វនី ស្ថិត​នៅ​ក្នុង​ឃុំ​កូន​ក្រៀល ស្រុក​​សំរោង ដែល​មាន​ផ្ទៃ​ដី​ចំនួន​', 'http://kohsantepheapdaily.com.kh/article/22260.html', 'http://i.kspg.co/2011/8/de3b0dc3-3969-4e3c-a380-2f50ba4d5387.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('179', 'cl000002', 'u0000003', '​ដាំ​សណ្តែក​បាយខុសរដូវ​តែអាច​ទទួល​ផល​ដូចគ្នាទាំង​ប្រាំង-វស្សា​', '​ខេត្តប៉ៃលិន​​​ ៖ ​​សណ្តែក​បាយ​​គឺជា​ដំណាំ​មួយ​ប្រភេទ ​​មានលក្ខណៈ​មិនខុស​​ពី​ដំណាំ​​ផ្សេងៗ​ទៀត​ប៉ុន្មានដែរ​​​ ​ដោយសារ​ដំណាំ​​នេះ​មាន​ដើម​​នៅទាប​លើ​ដី និង​មានផ្លែ​​​បន្ទាប់​ពីផ្កា​​រួច។ ​ចំណែក​​ការដាំ​មិន​ពិបាកក្នុង​​ការ​ថែទាំ​ស្រោច​​ទឹក​​ទេ វាអាច​រស់នៅបាន​​​ដោយសារ​ទឹក​​ភ្លៀង​​ ឬទឹក​សន្សើម។​ ​លើសពីនេះ​ទៅទៀត ​​សណ្តែក​​បាយ​​​ត្រូវ', 'http://kohsantepheapdaily.com.kh/article/21921.html', 'http://i.kspg.co/2011/7/e6794ff6-34e6-4b39-a5d6-51c4970dd7fb.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('180', 'cl000002', 'u0000003', '​ជីធម្ម​ជាតិ​ដី​ល្អ​សញ្ញា​រូបក្ងោក​​កសិករ​ទទួលស្គាល់​ថា ​មានគុណ​ភាព​​ខ្ពស់​លើដំណាំស្រូវ', 'ខេត្តតាកែវ ៖ ​​ប្រជាកសិករ​​​រស់នៅក្នុង​​​ឃុំ​ចារ ស្រុកព្រៃ​កប្បាស ​​ខេត្តតាកែវ ​​មាន​សេចក្តី​រំភើប​រីករាយ​​​ជា​ខ្លាំង​ក្នុង​ពេល​ស្រូវវស្សារបស់​ពួកគាត់​ កំពុង​តែ​ប្រមូល​ផល​ ជាពិសេស​​ការ​​ប្រមូល​ទិន្ន​ផល​​ស្រូវ​នេះ​មានគុណភាព​​ខ្ពស់​​បន្ទាប់​ពី​​ ពួក​គាត់​ប្រើ​ជីធម្មជាតិ​ដី​ល្អសញ្ញា​​រូបក្ងោក ។ ​នៅព្រឹកថ្ងៃទី២១ កក្កដា ​ឆ្នាំ​២០១១ ​តំណាង​​​ក្រុមហ៊ុន​', 'http://kohsantepheapdaily.com.kh/article/21702.html', 'http://i.kspg.co/2011/7/c9ad0882-06e8-4619-8ca3-37889fb93ab2.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('181', 'cl000002', 'u0000003', 'សមត្ថ​កិច្ច​​ចម្រុះ​បង្ក្រាប​ការ​ចិញ្ចឹម​ឆ្តោ​តាម​ស្រះ​​ខុស​ច្បាប់', 'ខេត្ត​កណ្តាល ៖ កន្លែង​​ចិញ្ចឹម​ត្រី​ឆ្តោ​ខុស​ច្បាប់​លួច​លាក់​ចំ​នួន​៧​ស្រះ​នៅ​ក្នុង​ ភូមិ​សាស្ត្រ​ឃុំ​ត្រើយ​ស្លា​ និង​ឃុំ​តា​លន់​ ស្រុក​ស្អាង​ ត្រូវ​បាន​កម្លាំង​​ជល​ផល​សហ​ការ​ជាមួយ​​អាជ្ញា​ធរ​ស្រុក​ស្អាង​ និង​កង​រាជ​អាវុធ​ហត្ថ​ ប៉ូលិស​​ដឹក​នាំ​ដោយ​លោក​យ៉ែម វត្ថា​ អភិ​បាល​រង​ស្រុក​ស្អាងបើក​​យុទ្ធ​នា​ការ​បង្ក្រាបកាល​ពី​ព្រឹក​ថ្ងៃ​ទី​២១ ខែ​កក្កដា។ ', 'http://kohsantepheapdaily.com.kh/article/21690.html', 'http://i.kspg.co/2011/7/6d018063-81c5-4f05-ab5b-04328c164cf6.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('182', 'cl000002', 'u0000003', 'ចាក់​ថ្នាំ​ការ​ពារ​ជំ​ងឺ​សារ​ទឹក​លើ​សត្វ​ពា​ហ​នៈ​នៅ​ស្រុក​កំពង់​ត្រ​ឡាច', 'ខេត្ត​កំពង់​ឆ្នាំង ៖ លោក​កែវ រិទ្ធី អនុ​​ប្រ​ធាន​មន្ទីរ​កសិក​ម្ម​ខេត្ត​ និងលោក​ក្រឹង​ សំ​អាត ប្រ​ធាន​ការិយា​ល័យ​បសុ​ព្យា​បាល កាល​ពី​ថ្ងៃ​ទី​១៤ កក្កដា បាន​ដឹក​​នាំ​មន្ត្រី​ពេទ្យ​សត្វ​ចុះ​ចាក់​ថ្នាំ​​ការ​ពារ​ជំងឺ​សារ​ទឹក​ សត្វ​​គោ​របស់​ប្រ​ជា​ពល​រដ្ឋ​ចំនួន​៤៩៥​ក្បាល​ ស្ថិត​ក្នុង​ភូមិ​ចំនួន​៣ ក្នុង​ឃុំ​សែប ស្រុក​​កំពង់​ត្រ​ឡាច។ ', 'http://kohsantepheapdaily.com.kh/article/21309.html', 'http://i.kspg.co/2011/7/6018ccef-412a-4064-b891-3cd0785ee22e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('183', 'cl000002', 'u0000003', 'ស្រះ និង​បែចិញ្ចឹម​ត្រី​ឆ្តោចាប់​ផ្តើ​ម​ដាក់​កូន​ថ្មី​ក្រោយ​ពី​ចាប់​លក់​អស់​', 'ខេត្ត​កណ្តាល​ ៖ ​ច្បាប់​ជល​ផល​បាន​ហាម​មិន​ឱ្យ​ចិញ្ចឹមត្រី​ឆ្តោ​ដោយ​សារ​តែវា​ស៊ីត្រី​​ ផ្សេងៗ​ជា​ចំណី​នាំ​ឱ្យ​វិនាស​ពូជ​ត្រី​ ម្យ៉ាង​បើ​មាន​ការ​ចិញ្ចឹម​ត្រី​ឆ្តោ​បទ​ល្មើស​​នេសាទ​​ប្រាកដ​ជា​កើត​មាន​ មិន​ខាន​ដោយ​សារ​តែ​អ្នក​ចិញ្ចឹម​រក​វិធី​នេសាទ​មិន​ខ្វល់​ពី​ផ្លូវ​ច្បាប់​ ដើម្បី​បាន​ចំណី​ឱ្យ​ត្រី​ឆ្តោ​សីុ។ បើ​ទោះ​ជា​​មាន​ច្បាប់​ហាម​ឃាត់​យ៉ាង​ណា​ក៏​ដោយ​', 'http://kohsantepheapdaily.com.kh/article/21055.html', 'http://i.kspg.co/2011/7/49a0f4af-2b94-4d77-acc3-9be72a938524.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('184', 'cl000002', 'u0000003', 'លែង​កូន​ត្រីពូជ​​3ម៉ឺន​ក្បាល មេត្រី​ពូជ​100គ.ក្រនិង​ដាំកូន​រាំង​100ដើម', 'ខេត្តព្រះសីហនុ ៖ កាលពីព្រឹកថ្ងៃទី៧ កក្កដាកន្លង​មក នៅបឹងត្រាវ ស្ថិតក្នុងភូមិ​តា​អោងធំ ឃុំសាមគ្គី ស្រុកព្រៃនប់​ មានរៀបចំ​​ប្រារព្ធ​ទិវាមច្ឆជាតិ១ កក្កដា ​ក្រោម​អធិបតីភាព​លោកស្បោង សារ៉ាត​ អភិបាល​ខេត្ត និងលោក​ណៅ ធួក ប្រធានរដ្ឋបាលជលផល​ ។ ​អញ្ជើញ​​ចូល​រួម​ក្នុង​​ពិធី​នេះ​ដែរ​មានក្រុម​ប្រឹក្សាខេត្ត-ស្រុក ប្រធាន​', 'http://kohsantepheapdaily.com.kh/article/21052.html', 'http://i.kspg.co/2011/7/261bd2e9-3a68-45a7-9d34-a93c658170e7.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('185', 'cl000002', 'u0000003', 'ចែកកូនឈើនិងដាំកូនឈើជាង1ម៉ឺនដើមអបអរបុណ្យរុក្ខទិវា', 'ខេត្តកណ្តាល ៖ សង្កាត់រដ្ឋបាលព្រៃ​ឈើ​ខ្សាច់កណ្តាលបានរៀបចំពិធីចែកចាយ និងដាំ កូនឈើជាង១ម៉ឺនដើមដល់វត្តអារាម និង​ប្រជា​ពលរដ្ឋ ព្រមទាំង​អាជ្ញាធរ ដើម្បី​អប​អរ​បុណ្យ​រុក្ខទិវា ៩ កក្កដា ២០១១ ដោយ​មាន​ការ​អញ្ជើញ​ចូលរួមពីផ្នែករដ្ឋបាលព្រៃឈើកណ្តាល លោកមេឃុំ លោកភូមិ និងប្រជាពលរដ្ឋយ៉ាង​ច្រើន​', 'http://kohsantepheapdaily.com.kh/article/20977.html', 'http://i.kspg.co/2011/7/deea66fd-cbf2-4c58-8f0d-3d0e0b53c012.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('186', 'cl000002', 'u0000003', 'អាជ្ញាធរ​​ខណ្ឌ​ច្បារ​អំពៅ​ចុះ​សម្អាត​បរិស្ថាន​តាម​ដងផ្លូវ​​មួយ​ចំនួន​ក្នុងភូមិ​សាស្ត្រ ក្រោម​ប្រធាន​បទ «ទីក្រុង​ស្អាត»', 'រាជ​ធានី​ភ្នំពេញ ៖ អាជ្ញាធរ​ខណ្ឌ​​ច្បារ​អំពៅ និង​គ​ណៈ​​ក​ម្ម​ការ​ចម្រុះ​ ​ដែល​ដឹកនាំ​​​លោកអ៊ាង ស៊ី​ផា​ន អភិបាល​នៃ​​គ​ណៈ​​អភិបាល​ខណ្ឌ​​ច្បារ​​អំពៅ និង​មានការ​អញ្ជើញ​ចូលរួម​​ពី​សំណាក់​អភិបាល​រង​​ខណ្ឌ, ចៅសង្កាត់​, កម្លាំង​កង​រាជ​អាវុធ, កម្លាំង​នគរបាល, មន្ត្រី​', 'http://kohsantepheapdaily.com.kh/article/115665.html', 'http://i.kspg.co/2015/2/82aac5e5-2f5c-48bb-be38-193b82b886f6.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('187', 'cl000002', 'u0000003', '​សំអាតក្រុង​កែប​លើ​ទឹកដី​ជាប់មាត់​សមុទ្រ​ចាប់ផ្តើម​នៅ​ថ្ងៃ​សៅរ៍​ចុង​ស​ប្តា​ហ៍​នេះ​ហើយ', 'រាជធានី​ភ្នំពេញ ៖ ព្រឹត្តិការណ៍ កែប សំអាត កែប (Kep Kep Clean) លើក​ទី​២ នៅ​ខេត្ត​កែប​នឹង​ចាប់ផ្តើម​ធ្វើ​សកម្មភាព​នៅ​ព្រឹក​ថ្ងៃ​ទី​១៣ ធ្នូ ក្រោម​គំនិត​ផ្តួចផ្តើម​ពី​អង្គការ​មូលនិធិ​ទឹកចិត្ត​ទាំង​បី ដើម្បី​បង្កើន​សោភ័ណ​ភាព និង​ភាព​ទាក់ទាញ ', 'http://kohsantepheapdaily.com.kh/article/110618.html', 'http://i.kspg.co/2014/12/c207d63e-b02a-47b0-9c80-17d1a0f5b291.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('188', 'cl000002', 'u0000003', 'ប្រធាន​ផ្សារ​​ព្រំ​​រង​ការ​រិះគន់​​​ថា ទុកសំរាម​ចោល​រហូតដល់​ស្អុយ​រលួយ​ មិនព្រម​ដឹក​ចេញ​', 'ខេត្ត​ប៉ៃ​លិន ៖ ប្រធាន​​ផ្សារ​​ព្រំ​​លើ​រង​ការ​​រិះគន់​​ពី​សំណាក់​​ប្រជាពលរដ្ឋ និង​ក្រុម​អាជីវ​ករ​ ដែល​លក់ដូរ​នៅ​ក្នុង​​ផ្សារ​គ្រប់​ៗ​គ្នា​ថា មិន​គួរ​ទុក​សំរាម​​​​ចោល​​រយៈ​ពេល​យូរ​រហូតដល់​​ក្លាយទៅ​ជា​​រលួយ​ជះ​ក្លិន​ស្អុយ​​​​អាសោចន៍​​ និង​បង្ក​ទៅ​​ជា​ដង្កូវ​រ​វើក​​ពាសពេញ​​ក្នុង​គំនរ​សម្រាប់​​​នៅ​​​ជុំវិញ​ផ្សារ​​បែប​នេះឡើយ', 'http://kohsantepheapdaily.com.kh/article/104696.html', 'http://i.kspg.co/2014/9/7b15b015-def4-4ed7-8661-72fb320b9caf.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('189', 'cl000002', 'u0000003', 'ក្រុម​​យុវជន​​កម្ពុជា​​ខេត្តពោធិ៍​សាត់​នាំ​​គ្នា​ដាំ​ដើម​ឈើ​លើក​កម្ពស់​​បរិស្ថាន​​ធម្មជាតិ', 'ខេត្តពោធិ៍សាត់ ​៖​ ក្រោម​យុទ្ធនាការ​យុវជន​ម្នាក់​ដើមឈើ​មួយ​ដើម​ កាលពី​ថ្ងៃ​ទី​២៤ ​សីហា ២០១៤ ​សហភាព​សហ​ព័ន្ធ​យុវជន​កម្ពុជា​ខេត្តពោធិ៍សាត់​ដឹកនាំ​ដោយ​លោក​ហេង សុខ​គង់ ​ជា​ប្រធាន​សហការ​ជាមួយ​ខណ្ឌ​រដ្ឋបាល​ព្រៃឈើ កងកម្លាំងប្រដាប់អាវុធ ព្រះសង្ឃ អាជ្ញាធរ​ភូមិ​-ឃុំ ​ស្រុក​ និង​ប្រជាពលរដ្ឋ​បាន​ចូលរួម​ដាំ​ដើមឈើ​នៅ​ឃុំ​បឹង​កន្ទួត ', 'http://kohsantepheapdaily.com.kh/article/103876.html', 'http://i.kspg.co/2014/8/788d2a93-9bc0-4468-821d-13c2fef1dcf6.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('190', 'cl000002', 'u0000003', 'លោក​ជាវ តាយ ដឹក​នាំ​មន្ត្រី​រាជ​ការ​ជុំ​វិញ​ខេត្ត​ប្រារព្ធ​ទិវា​បរិស្ថាន​​នៅ​ស្រុក​លើក​ដែក​', 'ខេត្ត​កណ្តាល​ ៖ លោក​ជាវ តាយ អភិ​បាល​រង​ខេត្ត​កាល​ពី​ព្រឹក​ថ្ងៃ​ទី​៤ មិថុនា ២០១៤ បាន​ដឹក​នាំ​​មន្ត្រី​រាជ​ការ​ជុំ​វិញ​សាលា​ខេត្ត​ប្រារព្ធ​ទិវា​បរិស្ថាន​ពិភព​លោក​នៅ​សាលា​ស្រុក​លើក​ដែក​ ដោយនាំ​យក​​កូន​ឈើ​ប្រភេទ​ឈើ​ទាល​ និង​គគីចំនޡޓ​១.៥០០​ដើម​ទៅ​ដាំ និង​ចែក​ជូន​ប្រជា​ពលរដ្ឋ​​។  ពិធី​នេះ​មានការ​ចូលរួម​ពី​​ប្រជា​ពលរដ្ឋ​ លោក​គ្រូ អ្នក​គ្រូ សិស្សា​នុសិស្ស​ និង​កង​កម្លាំង​ប្រដាប់​អាវុធ​​យ៉ាង​ច្រើន​កុះ​ករ​។', 'http://kohsantepheapdaily.com.kh/article/97462.html', 'http://i.kspg.co/2014/6/441dd1e1-f93d-4732-8937-414f8e0d058e.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('191', 'cl000002', 'u0000003', 'ទិវា​បរិស្ថាន​ពិភព​លោក​ថ្ងៃទី​៥ មិថុនា កម្ពុជា​រៀបចំ​នៅ​ខេត្ត​កែប ក្រោម​ប្រធាន​បទ «រួម​គ្នា​ទប់​ស្កាត់​កំណើន​កម្ពស់​ទឹក​សមុទ្រ»', 'ទិវា​បរិស្ថាន​ពិភព​លោក​ថ្ងៃទី​៥ មិថុនា កម្ពុជា​រៀបចំ​នៅ​ខេត្ត​កែប ក្រោម​ប្រធាន​បទ «រួម​គ្នា​ទប់​ស្កាត់​កំណើន​កម្ពស់​ទឹក​សមុទ្រ»', 'http://kohsantepheapdaily.com.kh/article/97444.html', 'http://i.kspg.co/news_pic_kh.png?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('192', 'cl000002', 'u0000003', 'កសិ​ដ្ឋាន​ចិញ្ចឹម​ជ្រូក​ មាន់​៥​កន្លែង​ក្នុង​ស្រុក​កៀន​ស្វាយ​នៅ​តែ​បង្ក​ផល​ប៉ះ​ពាល់​បរិស្ថាន​ធ្ងន់​ធ្ងរ​ មិន​ឃើញ​មន្ត្រី​ជំនាញ​ចុះ​ពិនិត្យ', 'ខេត្ត​កណ្តាល​ ៖ ពាក់​ព័ន្ធ​នឹង​កសិ​ដ្ឋាន​ចិញ្ចឹម​ជ្រូក ​មាន់​៥​កន្លែង​ក្នុង​ស្រុក​កៀន​ស្វាយ​ដែលសារ​​ព័ត៌​មាន​បាន​ចុះ​ផ្សាយ​២​លេខ​រួច​ហើយ​នោះ ឥឡូវ​​នេះ​រឹត​តែ​បង្ក​ការ​ប៉ះ​ពាល់​ដល់​បរិស្ថាន​យ៉ាង​ធ្ងន់​ធ្ងរ​លើស​មុនទៅ​ទៀត​ ហើយ​មិន​ទាន់​ឃើញ​មាន​អាជ្ញា​ធរ​ សមត្ថ​កិច្ច​ មន្ត្រី​ជំនាញ ស្ថាប័ន​ពាក់​ព័ន្ធ​ណា​មួយ', 'http://kohsantepheapdaily.com.kh/article/97434.html', 'http://i.kspg.co/2014/6/01c04454-f8a0-414a-98f5-efffc615b386.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('193', 'cl000002', 'u0000003', 'អូរព្រំដែនជាប់​ច្រកទ្វារអន្តរជាតិប៉ោយប៉ែ​តកំពុងក្លាយជាកន្លែងចាក់​សំរាម', 'ខេត្ត​បន្ទាយ​មាន​ជ័យ ៖ ​អូរ​ព្រំដែន​ជាប់​និង​ខាង​ជើង​ច្រក​ទ្វារ​អន្តរ​ជាតិ​ប៉ោយ​ប៉ែត​ ដែល​ភ្ញៀវ​អន្តរ​ជាតិ​ឆ្លង​កាត់​ កំពុង​តែ​ក្លាយ​ទៅ​ជា​កន្លែង​ចាក់​សំរាម​ដែល​បង្ក​ឱ្យ​មាន​ការ​ប៉ះ​ពាល់​ ដល់​បរិស្ថាន​និង​ធ្វើ​ឱ្យ​បាត់​បង់​សោ​ភ័ណ​ភាព​ទី​ក្រុង ។  ក្រុង​ប៉ោយ​ប៉ែត​គឺ​ជា​ក្រុង​មួយ​​ដែល​មាន​ព្រំ​ដែន​​ជាប់​ទៅ​និង​ប្រ​ទេស​ថៃ​ ហើយ​ជា​ក្រុង​មួយ​ក្នុង​ចំណម​ស្រុក​ក្រុង​ទាំង​៩ ដែល​', 'http://kohsantepheapdaily.com.kh/article/97332.html', 'http://i.kspg.co/2014/6/bcf044a6-802e-461f-93ed-0875950221e4.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('194', 'cl000002', 'u0000003', 'អ្នករត់ម៉ូតូ​ឌុបម្នាក់​ ស៊ីឈ្នួល​ដឹកសម្រា​មយកទៅ​ចាក់ចោល​ខាងជើង​ភូមិប៉ា​ហ៊ីជើង​ យ៉ាង​អនាធិបតេយ្យ​នៅក្រុង​ប៉ៃលិន', 'ខេត្ត​ប៉ៃលិន​ ៖ ​គេសង្កេត​ឃើញ​សម្រាម​ជាច្រើន​ការ៉ុង ​ ត្រូវ​បាន​អ្នក​រត់ម៉ូតូ​ឌុបម្នាក់​ស៊ីឈ្នួ​ល​អ្នកភូមិ​ដឹកយក​​ទៅចោល​នៅក្បែ​រ​ផ្លូវ​លំ បង្ក​ឱ្យជះក្លិន​យ៉ាង​អាសោច៍​​ប៉ះ​ពាល់​ដល់ការរ​ស់នៅ​របស់​ប្រជាពល​រដ្ឋនៅ​ក្បែរភូមិ ​ និងអ្នក​រស់នៅ​ក្បែរគំនរ​សម្រាម​ ដែល​​ការដឹក​សម្រាម​យកទៅ​ចោល​យ៉ាង​អនាធិបតេយ្យ​នេះ ​ បានកើត​ឡើង​ជារៀង​រាល់ថ្ងៃ ​ស្ថិត​នៅក្បែរ​ផ្លូវលំ​មួយ​កន្លែង​​ក្នុង​', 'http://kohsantepheapdaily.com.kh/article/96821.html', 'http://i.kspg.co/2014/5/60097ab7-dd4f-4964-a279-74ff826d6748.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('195', 'cl000002', 'u0000003', 'នារី​​ម្នាក់​​ស្រវឹងស្រា​បើក​រថ​យន្ត​បុក​បង្គោល​ស្លាក​ប៉ាណូ​ក្រុម​ហ៊ុន​ទូរស័ព្ទមេត​ហ្វូន​ រួច​ព្យាយាម​បើក​រត់​ត្រូវ​សមត្ថ​កិច្ច​ឃាត់​បាន​', 'រាជ​ធានី​ភ្នំពេញ​ ៖ នារី​ម្នាក់​អាយុ​ប្រមាណជាង​​ ២​០​ឆ្នាំ ទំនង​​​ស្រវឹង​​ស្រា​ បាន​បើក​រថ​យន្ត​កាមរី​បាឡែន​ ពណ៌​ស្ករ​ ១​គ្រឿង​ ពាក់​ស្លាក​លេខ​ ភ្នំពេញ​ 2W-1106 ធ្វើ​ដំណើរ​តាម​បណ្តោយ​ផ្លូវ​ជាតិ​លេខ​៦A ខណៈ​មក​ដល់​ចំណុច​ខាង​កើត​ស្ពាន​ជ្រោយ​ចង្វា​ ក៏​បាន​រេច​ង្កូត​ជ្រុ​លទៅ​បុក​​បង្គោល​ស្លាក​ប៉ាណូ​របស់​ក្រុម​ហ៊ុន​ទូរស័ព្ទ​មេត​ហ្វូន​ បណ្តាល​ឲ្យ​រង​ការ​ខូច​ខាត​ ហើយ​រថយន្តបាន​ព្យាយាម​បើក​រត់​មក​ដល់​សង្កាត់​បឹ​ងព្រលិត​ ខណ្ឌ​ ៧មករា​ ទើប​ត្រូវ​សមត្ថ​កិច្ច​ឃាត់​ចាប់​បាន​។', 'http://kohsantepheapdaily.com.kh/article/90807.html', 'http://i.kspg.co/2014/2/bc8892e8-73e6-4106-b902-fe65638648c9.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('196', 'cl000002', 'u0000003', 'ម៉ូតូ​​លក់​​នំប៉ាវ​​ជិះ​ប​ុក​ម៉ូតូ​គុប​ជ្រុង​ចំ​ពាក់​កណ្តាល​', 'រាជធានី​ភ្នំពេញ ៖ ម៉ូតូ​សង់​កូរ៉េ ពណ៌​ក្រហម ដឹក​នំ​ប៉ាវ​លក់​ជិះ​ទៅ​បុក​ម៉ូតូ​គុប​ជ្រុង​​៥០ ពណ៌ស្លែ ​ជិះគ្នា​​២​នាក់​ជា​ស្ត្រី បណ្តាល​ឱ្យ​ដួល​រង​របួស​ធ្ងន់​​២​នាក់ និងស្រាល​ម្នាក់ ។ គ្រោះថ្នាក់​នេះ​កើតឡើង​នៅចំណុច​ខាង​​ត្បូង​មន្ទីរពេទ្យ​មិត្តភាព​ខ្មែរ-សូវៀត ​តាម​ផ្លូវ​លេខ​២៧១ មុខ​ផ្ទះ​លេខ​៤០២ ​សង្កាត់​ទំនប់​ទឹក ខណ្ឌចំការ​មន ​កាលពីវេលា​ម៉ោង​១​និង​១០​នាទីរំលង​អធ្រាត្រ​ឈានចូល​ថ្ងៃទី​២ មករា ២០១៣។', 'http://kohsantepheapdaily.com.kh/article/60807.html', 'http://i.kspg.co/2013/1/b01bf2bd-cc41-46ee-895f-d25ccebe7caf.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('197', 'cl000002', 'u0000003', 'កំណាត់​​ផ្លូវ​​ជាតិលេខ​​1ក្លាយ​ជា​ធុង​ដាក់​សំរាម​​របស់​អ្នក​ស្រុក​​នៅផ្សា​រ​​ក​អណ្តើក', 'ខេត្ត​ព្រៃ​វែង ៖ សំរាម​​ និង​​កាក​​សំណល់​ផ្សេង​ៗ ​​ត្រូវ​​បាន​​អ្នក​ស្រុក​​ដែល​រស់​នៅ​​ជុំ​វិញ​​​​​ផ្សារ​​​ក​អណ្តើក​នាំ​គ្នា​​ប្រមូល​ពី​តាម​ផ្ទះ​​របស់​ខ្លួន​​​និង​ពី​ក្នុង​ផ្សារ ​​យក​​មក​ចាក់​គរ​​លើ​គ្នា​​ជា​រៀង​រាល់​ថ្ងៃ នៅ​លើ​ចិញ្ចើម​​​​ផ្លូវ​ជាតិ​​លេខ​​១ ត្រង់​​ចំណុច​​មុខ​​វត្ត​​ក​អណ្តើក ​​ស្ថិត​​​ក្នុង​ឃុំ​​ប្រា សាទ ស្រុក​​កំពង់​ត្របែក ។', 'http://kohsantepheapdaily.com.kh/article/53276.html', 'http://i.kspg.co/2012/9/55d39b88-2ca2-49f7-afdb-6ac85779a9f5.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('198', 'cl000002', 'u0000003', 'គំនរសំរាម​និងក្លិនស្អុយ​កំពុង​តាំងពិព័រណ៍​យ៉ាងសង្ហា​នៅ​ផ្សារ​អ្នក​លឿង​​', '​ខេត្ត​ព្រៃវែង ៖ គំនរសំរាម​ដែល​គេ​យក​​មក​គរ​ទុក​នៅ​មុខ​ផ្សារ​អ្នកលឿង​ត្រើយ​ខាង​កើត​ចំនួន​២កន្លែង​ជាប់​ផ្លូវជាតិលេខ​១ ស្ថិត​ក្នុង​ភូមិ​៤ ឃុំ​ព្រែកខ្សាយ​«ខ» ស្រុក​ពាមរក៍​​​ ​មាន​រយៈ​ពេល​​៣​ថ្ងៃ​មក​ហើយ​ ធ្វើ​ឱ្យ​គំនរ​សំរាម​កាន់​តែ​ខ្ពស់​ទៅ​ៗ​ដូច​កូន​ភ្នំ​​​។ ម្យ៉ាង​ទៀត​ពេលត្រូវ​ថ្ងៃ​ ត្រូវ​ភ្លៀង​ ត្រូវ​ខ្យល់​ ​ធ្វើឱ្យ​កាកសំណល់​សំរាម​ទាំងនេះ​​ស្អុយ​​​រលួយ ​​ជះ​​​ក្លិន​ទៅ​លើ​ប្រជាពលរដ្ឋ​​ដែល​រស់​នៅ​ជិត​នោះ ', 'http://kohsantepheapdaily.com.kh/article/51637.html', 'http://i.kspg.co/2012/8/dbdc80a3-e80a-448c-abfa-84e283d1852d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('199', 'cl000002', 'u0000003', 'ក្រុម​អាជីវករ​ផ្សារ​ស្វាយ​​ជ្រំ​​រអ៊ូរទាំ​ថា​​ អ្នក​​ម៉ៅ​ការ​​ផ្សារ​ទុក​សំរាម​​មិន​​​កើប​ចោល', 'ខេត្ត​ស្វាយ​រៀង​ ៖​​ ​ក្រុម​អាជីវ​ករ​ជា​ច្រើន​​នាក់​ដែល​លក់​​ដូរ​នៅ​ផ្សារ​ស្វាយ​ជ្រំ​​ ​​ស្រុក​ស្វាយ​ជ្រំ និយាយ​ថា ក្នុង​១​ថ្ងៃ​ៗ​ផ្សារ​នេះ​​មាន​សំរាម​​មិន​​ជា​ច្រើន​ប៉ុន្មាន​​​​ទេ ដោយ​សារ​តែ​​​អាជីវ​ករ​​​លក់​ដូរ​នៅ​ផ្សារ​នេះ​​មាន​ចំនួន​តិច​​ ហើយ​បើ​គេ​​កើប​​សំរាម​​ទាំង​នោះ​យក​ទៅ​ចោល​ជា​រៀង​រាល់​ថ្ងៃ ក៏​ប្រាកដ​​ជា​គ្មាន​បញ្ហា​​ចោទ​​​ដែរ ។ ប៉ុន្តែ​​ដោយ​​សារ​តែ​ក្រុម​​អនា​ម័យ​របស់​​​អ្នក​ម៉ៅ​ការ​​ផ្សារ​មិន​​ព្រម​កើប​រាល់​ថ្ងៃ គឺ​ទុក​​', 'http://kohsantepheapdaily.com.kh/article/51149.html', 'http://i.kspg.co/2012/8/8514252d-dc68-40ae-a238-712860bc2009.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('200', 'cl000002', 'u0000003', 'អង្គការ​​​ចលនា​​ដើម្បី​សុខភាព​​ចែក​រង្វាន់ជូនដល់​អ្នក​​ប្រកួតឈប់​​ជក់បារី​', 'រាជធានីភ្នំពេញ ៖ ​​អង្គការ​ចលនា​​​ដើម្បី​​​សុខភាព ​កាលពី​រសៀល​ថ្ងៃទី​១៣ខែសីហា​កន្លង​មក​​ នៅមន្ទីរ​ពេទ្យ​​​​កុមារ​​ជាតិ​ បាន​​រៀបចំ​​ធ្វើ​​ពិធី​​​​ចែក​រង្វាន់​លើក​ទឹកចិត្ត​ដល់​ប្រជា​​ពលរដ្ឋ​​ចំនួន​៥​​គូ ដែល​បាន​ប្រកួត​ឈ្នះ​​​​នៅក្នុង​កម្ម​វិធី​ប្រឡង​​​ប្រជែង​ឈប់​ជក់​បារី​ ។​ ​លោក​វេជ្ជបណ្ឌិត​​មុំ គង់​ នាយ​ក​ប្រតិបត្តិ​​​​នៃ​អង្គការ​សុខភាព​​ពិភព​លោក​បាន​ឱ្យ​ដឹងថា កម្ម​វិធី​​នេះ​បាន​ធ្វើ​ឡើង​​​ចាប់​តាំងពី​ថ្ងៃទី​១ខែ​', 'http://kohsantepheapdaily.com.kh/article/50729.html', 'http://i.kspg.co/2012/8/2b38e35f-3849-4fb8-a5da-1d7bdc0024d0.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('201', 'cl000002', 'u0000003', 'រោងចក្រ​​​ចំហុយ​​ថ្នាំ​ជក់​​​នៅ​តា​ខ្មៅ​​បង្កផល​ប៉ះ​ពាល់​​​ដល់​សុខភាព​​​ប្រជាពលរដ្ឋ​​', 'ខេត្តកណ្តាល៖ រោង​ចក្រ​​ចំហុយ​ថ្នាំ​ជក់​​ដែល​​មាន​ទីតាំង​​​​នៅភូមិ​​តា​ខ្មៅ សង្កាត់​តា​ខ្មៅ ​ក្រុង​តា​​ខ្មៅ បច្ចុប្បន្ន​​​​កំពុង​ដំណើរ​ការ​​​សង្វាក់​​ផលិត​កម្ម​​របស់​ខ្លួន​​យ៉ាង​សកម្ម តែ​បញ្ហា​​​​ដែលកំពុង​កើត​​ឡើង​នោះគឺ​​​​​ធ្វើ​ឱ្យ​​មាន​ផល​ប៉ះ​ពាល់​​យ៉ាង​ខ្លាំង​​​ដល់​ផ៞ܡ޼វ​ដង្ហើម​​របស់​ប្រជា ពលរដ្ឋ​​​រស់នៅ​ក្បែរ​​​ខាង  នេះ​បើតាម​​​ការ​អះអាង​របស់​ប្រជាពល​រដ្ឋ​​​ ។', 'http://kohsantepheapdaily.com.kh/article/50555.html', 'http://i.kspg.co/2012/8/7edc07cb-0b5e-4627-b68b-89b48393490c.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('202', 'cl000002', 'u0000003', 'រោងចក្រ​​​ចំហុយ​​ថ្នាំ​ជក់​​​នៅ​តា​ខ្មៅ​​បង្កផល​ប៉ះ​ពាល់​​​ដល់​សុខភាព​​​ប្រជាពលរដ្ឋ​​', 'ខេត្តកណ្តាល៖ រោង​ចក្រ​​ចំហុយ​ថ្នាំ​ជក់​​ដែល​​មាន​ទីតាំង​​​​នៅភូមិ​​តា​ខ្មៅ សង្កាត់​តា​ខ្មៅ ​ក្រុង​តា​​ខ្មៅ បច្ចុប្បន្ន​​​​កំពុង​ដំណើរ​ការ​​​សង្វាក់​​ផលិត​កម្ម​​របស់​ខ្លួន​​យ៉ាង​សកម្ម តែ​បញ្ហា​​​​ដែលកំពុង​កើត​​ឡើង​នោះគឺ​​​​​ធ្វើ​ឱ្យ​​មាន​ផល​ប៉ះ​ពាល់​​យ៉ាង​ខ្លាំង​​​ដល់​ផ៞ܡ޼វ​ដង្ហើម​​របស់​ប្រជា ពលរដ្ឋ​​​រស់នៅ​ក្បែរ​​​ខាង  នេះ​បើតាម​​​ការ​អះអាង​របស់​ប្រជាពល​រដ្ឋ​​​ ។', 'http://kohsantepheapdaily.com.kh/article/50554.html', 'http://i.kspg.co/2012/8/a000db15-8185-49e4-85f1-8622bda1472d.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('203', 'cl000002', 'u0000003', '​រត់​ផង​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​រើស​​ផង​ កម្ម​វិធី​​រត់​ម៉ា​រ៉ា​​តុង​​​ ដើម្បី​​បរិស្ថាន​​សម្អាត​​​​ទី​ក្រុង​​​ថ្ងៃ​​ទី​12 សីហា​', 'រាជ​ធានីភ្នំពេញ​​​ ៖ ​ប្រជា​ពល​រដ្ឋ​​​​ សិល្ប​ករ​ និង​​ថ្នាក់​ដឹ​ក​នាំ​​សរុប​​​ប្រមាណ​​​​ជាង​​​៥០០​នាក់​​ បាន​ចូល​រួម​​ក្នុង​​​​​យុទ្ធ​​នា​ការ​​​បរិ​ស្ថាន​​​​មួយ​ដែល​​​​មាន​ឈ្មោះ​​​ថា រត់​​​​ផង​​​រើស​ផង​​ ដែល​ជា​កម្ម​វិធី​​​​​រត់​ម៉ា​រ៉ា​តុង​​​​ ដើម្បីសម្អាត​​​​​​ទី​ក្រុង​​​​នៅ​ថ្ងៃ​​ទី​​​១២ សីហា​។​ការ​រត់​​​​ម៉ា​រ៉ា​តុង​​​នេះ​ ចាប់​ផ្តើម​​​​​ពី​ម៉ោង​​​​​​៦​ដល់​ម៉ោង​​​​​១០​​និង​​​៣០​នាទី​​​​​​​ព្រឹក​​​​  នៅ​​ភោជ​នីយ​ដ្ឋាន​​​​​ភូមិ​​​​​ម្ហូប​​​​ខ្មែរ​​​​ ដែល​ស្ថិត​​​​នៅ​មុខ​សណ្ឋា​គារ​​​ណា​ហ្គា​​វ', 'http://kohsantepheapdaily.com.kh/article/50473.html', 'http://i.kspg.co/2012/8/312b3c43-774b-45e9-8115-1d6a41aed742.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('204', 'cl000002', 'u0000003', 'អ្នក​បើក​​បរ​រថយន្ត​កូរ៉េ​វិល​មុខ​បើក​ទៅ​បុក​រទេះ​លក់​បបរ​បែក​ទូ​កញ្ចក់​ខ្ចាយ​', 'រាជធានីភ្នំពេញ ៖ ស្ត្រី​ជន​ជាតិ​វៀត​ណាម​ម្នាក់​កំពុង​អង្គុយ​​លើ​ម៉ូតូ​រទេះ​លក់​បបរ​ឈប់​ចត​ស្ងៀម​​នៅ​លើ​ចិញ្ចើ​មផ្លូវ ស្រាប់តែ​​រថយន្ត​កូរ៉េ​ចំណុះ​២​តោន​​ អ្នក​បើក​រថយន្ត​វិល​មុខ​ជិះ​ទៅ​បុក​រទេះ​​លក់​បបរ​នោះ​បែក​កញ្ចក់​ទូ និង​ធ្លាក់​ស្ត្រី​នោះ​​ពី​លើ​ម៉ូតូ​ដួល​ធ្លោះ​ចង្កេះ ។ គ្រោះ​ថ្នាក់​ចរា​ចរណ៍​នេះ​​កើត​ឡើង​កាល', 'http://kohsantepheapdaily.com.kh/article/49972.html', 'http://i.kspg.co/2012/8/89d1ef2e-18f3-4dfd-8616-215d6d4b5c91.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('205', 'cl000002', 'u0000003', 'មន្ទីរ​សាធារណ​​ការ​ និង​ដឹក​ជញ្ជូន​សម្រុក​​ស្ថាបនា​ផ្លូវ​ចូល​​ភ្នំ​ប្រសិទ្ធិត​ភ្ជាប់​​ផ្លូវ​ជាតិ​​លេខ​5', 'រាជធានី​ភ្នំពេញ ៖​​ ​ដោយ​មាន​ការ​​ណែ​នាំ​​ពី​លោក​កែប ជុតិមា អភិបាល​រាជ​ធានី​​ភ្នំ​ពេញ​ មន្ទីរ​​​សាធា​រណ​ការនិង​ដឹក​ជញ្ជូនរាជ​ធានី​​​ បាន​និង​កំពុង​សម្រុក​ស្ថាបនា​​ផ្លូវ​ចូលភ្នំ​ប្រសិទ្ធិ៍ត​ភ្ជាប់​ពី​ផ្លូវ​ជាតិ​លេខŠតាម​អនុសាសន៍​​របស់​សម្តេច​​តេជោ ហ៊ុន សែន។  លោក​ហេង ងួន ប្រធាន​អង្គ​ភាព​ស្ពាន​ថ្នល់​នៃ​មន្ទីរ​សាធារណ​​ការ និង​ដឹក​ជញ្ជូន​​រាជ​ធានី​​ភ្នំ​​ពេញ​​ បាន​ឱ្យ​ដឹង​ថា រហូត​​មក​ដល់', 'http://kohsantepheapdaily.com.kh/article/49539.html', 'http://i.kspg.co/2012/8/54e93a3a-92d8-488c-90fc-736aeccf5629.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('206', 'cl000002', 'u0000003', '​អាជ្ញា​ធរ​​​ខណ្ឌ​ចែក​​កូន​ឈើ​​ដល់សង្កាត់​​​ទាំង​​1​​2នៅ​​​ខណ្ឌ​មាន​ជ័យ​​​​យក​ទៅ​ដាំ​', 'រាជធានីភ្នំពេញ​៖ កូន​ឈើ​​​​​ចំនួន​៤​មុខ​រួម​មាន​​ កូន​គគីរ​​​ លឿង​រាជ​ ឈើទាល​ និង​ក្ងោក​​​​ត្រូវ​បាន​​អាជ្ញាធរ​​​ខណ្ឌ​​មាន​ជ័យ​ដឹក​នាំ​ដោយ​​លោក​ ហៀក ​ចាន់​លាង អភិ​បាល​​​រង​ខណ្ឌ​ ជំនួស​មុខ​ឱ្យ​លោក គួច​ ចំរើន​ ​អភិបាល​​ខណ្ឌ​ធ្វើការ​​​បែង​ចែក​​​ជូន​ដល់​​​អាជ្ញាធរ​សង្កាត់​​​ទាំង​​​១២​ក្នុង​​ខណ្ឌ​ដើម្បី​​យក​ទៅ​ដាំ​តាម​​​វត្ត​អារាម​​ និង​តាមដ​ង​​ផ្លូវ​នានា​​​នៅក្នុង​មូល​ដ្ឋាន​ ។', 'http://kohsantepheapdaily.com.kh/article/49265.html', 'http://i.kspg.co/2012/7/8e169a08-9afe-4daf-8b4e-9716556b7e58.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('207', 'cl000002', 'u0000003', 'សាលា​ខណ្ឌ​ដូន​ពេញ​និង​សង្កាត់​ទាំង​១១​ធ្វើ​អនា​ម័យ​នៅ​ច្រាំងទន្លេ​មុខ​ផ្សារ​រាត្រី', 'រាជធានីភ្នំពេញ​ ៖ នៅ​ព្រឹក​ថ្ងៃ​ទី​២១ ​មិថុនា​ លោក​សុខ​ សម្បត្តិ​ ជំនួយ​ការ​សម្តេច​អគ្គមហា​សេនា​បតី​តេជោ​ហ៊ុន​ សែន​ និង​ជា​អភិបាល​ខណ្ឌ​ដូន​ពេញ​ ដឹក​នាំ​ចៅ​សង្កាត់​ទាំង​១១ ​និង​មន្ត្រី​សាលា​ខណ្ឌ​ប្រមាណ​ជា​១​រយ​នាក់​ប្រដាប់​ដោយ​កាំបិត​បាន​ចុះ​កាប់​ឆ្ការ​ព្រៃ​ត្រែង​នៅ​តាម​ច្រាំង​ទន្លេ​មុខ​ផ្សារ​រាត្រី​ ស្ថិត​ក្នុង​សង្កាត់​វត្តភ្នំ​ ខណ្ឌ​ដូន​ពេញ ​ព្រម​ទាំង​បាញ់​ទឹក​លាង​សំអាត​​ច្រាំង​ទន្លេ​ដែល​', 'http://kohsantepheapdaily.com.kh/article/45978.html', 'http://i.kspg.co/2012/6/ff2eacf3-8517-4972-b7f8-aa94cde12487.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('208', 'cl000002', 'u0000003', 'សំរាម​គរ​ច្រើន​​ឡើងៗ​ក្បែរ​ច្រមុះ​​មន្ត្រី​បរិ​ស្ថាន​​ខេត្ត​កំពង់​ស្ពឺ', 'ខេត្ត​កំពង់​ស្ពឺ ៖ ​គំនរ​សំរាម​ស្អុយ​រលួយ​បាន​កើន​​ច្រើន​​ឡើងៗ​​ដែល​ពុំ​មាន​អាជ្ញា​ធរ ឬ​អង្គ​ភាព​​បរិ​ស្ថាន​​​​គិត​គូរ​​ឡើយ។ គំនរ​សំរាម​​ទាំង​នេះ​ស្ថិត​​នៅ​ចំណុច​ចំហៀង​មុខ​មន្ទីរ​ព័ត៌​មាន​​ខេត្ត និង​ខាង​កើ​តអង្គ​ភាព​រតនា​គារ​ខេត្ត​កំពង់​ស្ពឺ ។ ', 'http://kohsantepheapdaily.com.kh/article/45706.html', 'http://i.kspg.co/2012/6/d4d2ce9e-de13-4905-a806-09c18a0e370a.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('209', 'cl000002', 'u0000003', 'ក្រុម​ហ៊ុន​​ទឹក​បរិ​សុទ្ធ​​វីតាល់​​ចូល​រួម​​ឧបត្ថម្ភ​​​យុទ្ធ​នា​​ការ​​សំអាត​ឆ្នេរ', 'ខេត្តព្រះសីហនុ ៖ ​កាល​ពី​ថ្ងៃទី​៤ និង​ទី៥ មិថុនា កន្លង​មក​នេះ ក្រុម​ហ៊ុន​អិន​វីស៊ី (​ទឹក​​បរិ​សុទ្ធ​វីតាល់) ​បាន​ចូលរួម​ឧបត្ថម្ភ គម្រោង Enrich ដើម្បីធ្វើ​យុទ្ធនាការ​​សំអាតឆ្នេរ​​អូរ​ឈើ​ទាល នៅក្នុង​ខេត្តព្រះសីហនុ ដែល​ត្រូវបាន​ចាត់​បញ្ចូល​ទៅ​ក្នុង​ចំណោម​ឆ្នេរ​​សមុទ្រ​ដែល​ស្អាត​ជាង​គេ​បំផុត​នៅ​ក្នុង​ពិភព​លោក ', 'http://kohsantepheapdaily.com.kh/article/19353.html', 'http://i.kspg.co/2011/6/11b98123-8a88-4400-943c-a9cdd3993b81.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('210', 'cl000002', 'u0000003', 'មានក្មេង​ស្ទាវ​អន្ធពាល​មួយ​ក្រុម នៅ​ក្បែរ​របង​មន្ទីរពេទ្យ​រុ​ស្ស៊ី', 'បើ​មេ​ប៉ុស្តិ៍​ទំនប់​ទឹក​ធ្វើ​ជន​អន្ធពាល​មួយ​ក្រុម​នេះ​មិន​បែក​ទេ គួរ​យក​ក្រុម​អ្នកលេង​នេះ​ឱ្យធ្វើ​មេ​ប៉ុស្តិ៍ ធ្វើ​មេ​អធិការដ្ឋាន​ខណ្ឌចំការមន​វិញ​ម្តង ទើប​អាច​ជឿជាក់​ថា​មាន​សន្តិសុខ និង​អស់​ភ័យ​ខ្លាច ', 'http://kohsantepheapdaily.com.kh/article/116037.html', 'http://i.kspg.co/2015/3/2f1b92f9-36ff-477b-a358-381d291a8155.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('211', 'cl000001', 'u0000003', 'សង្ស័យ​ត្រីងៀត', 'បើ​តាម​ពិនិត្យ​ឱ្យ​វា​ដិត​ដល់​ទៅ​... ​វា​ច្រើន​តែ​មាន​«​ខ្នង​» បង្កប់​ដោយ​អំពើ​ពុក​រលួយ​... ​ដែល​មិន​ខុស​ពី​ពាក្យ​ថា «​ត្រីងៀត​ឆ្លៀត​ពង​» ​នោះ​ឡើយ ។ មិន​ដែល​ចាប់​បាន​ត្រីងៀត​ទេ​...​ ណ្ហើយ​ទុក​ឱ្យ​វា​ឆ្លៀត​ពង​ពេញ​ទឹក ពេញ​ដី​តាម​អំណាច​ទៅ​ចុះ​......!', 'http://kohsantepheapdaily.com.kh/article/115812.html', 'http://i.kspg.co/2015/3/d071fa15-5301-4a52-9159-10f5641b5bc7.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('212', 'cl000001', 'u0000003', 'អាណា​ហ៊ាន​ក្អក​...', 'មានរឿង​អាស្រូវ​ធំ​មួយ​...​មាន​អ្នក​ច្បាប់​ម្នាក់​កំពុង​យក​ច្បាប់​ទ្រាប់​អង្គុយ​ហើយ​...​ទំនង​គិត​ថា ​គ្មាន​អាណា​ហ៊ាន​ធ្វើ​អី​អញ​ទេ​មើលទៅ​...​។​ល​។', 'http://kohsantepheapdaily.com.kh/article/115753.html', 'http://i.kspg.co/2015/3/745c75df-aa6c-484a-b78d-56eea6393fce.jpg?w=285&h=170&mode=carve', '20150308');
INSERT INTO "tbnews" VALUES ('213', 'cl000001', 'u0000003', 'អាសូរ​បឹង​ត្របែក', 'បឹង​ត្របែក​ជា​ឃ្លាំង​ផ្ទុក​ទឹក​សំអុយ ទឹកភ្លៀង​កុំឱ្យ​ជន់​លិច​រាជធានី​ភ្នំពេញ ។ ក្រុងភ្នំពេញ​ដែល​លិច​ទឹក​គ្រប់​រដូវវស្សា​រាល់​ៗ​ឆ្នាំ គ្រប់​អាណត្តិ​ចៅហ្វាយ​ក្រុង​កន្លង​មក ', 'http://kohsantepheapdaily.com.kh/article/115669.html', 'http://i.kspg.co/2015/2/b44ea279-3aa6-4d86-a814-f8674b3bb314.jpg?w=285&h=170&mode=carve', '20150308');
